    @Estado varchar(255),
    @SiglasDependencia varchar(255),
    @Trimestre INT,
    @Anio varchar(4),
    @CorreoUsuario varchar(255),
    @ComentarioSeguimiento NVARCHAR(MAX)
AS

--PROCEDIMIENTO para ingresar un nuevo usuario Tabla Usuario_General
CREATE PROCEDURE CreacionUsuario 
	@NombreUsuario varchar(255), 
	@Contrasena varchar(255),
	@Palabra_Aleatoria varchar(255),
	@Correo_Electronico varchar(255),
	@SiglasUnidadAcademica varchar(255),
	@Cargo varchar(255),
	@Numero_Telefono varchar(255),
	@Rol varchar(255),
	@SiglasDependencia_Evaluada varchar(255),
	@Grado varchar(255)
AS

BEGIN SET NOCOUNT ON; DECLARE @AccionFormativa TABLE ( ID_AccionFormativa INT IDENTITY(1, 1), AccionFormacion VARCHAR(255) COLLATE Latin1_General_CI_AI, Total_HomMuj INT );
INSERT INTO @AccionFormativa( AccionFormacion ) SELECT Desc_AccionFormativa FROM DFLE_AccionesFormativas WHERE id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio);
DECLARE @ContAcciones INT = 1; WHILE @ContAcciones <= ISNULL((SELECT COUNT(*) FROM @AccionFormativa),0) BEGIN DECLARE @AccionActual VARCHAR(255) = (SELECT AccionFormacion FROM @AccionFormativa WHERE ID_AccionFormativa = @ContAcciones); DECLARE @Hombres INT = 0; DECLARE @Mujeres INT = 0;
SET @Hombres = (SELECT Desc_Hombres FROM DFLE_AccionesFormativas WHERE Desc_AccionFormativa = @AccionActual AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)); SET @Mujeres = (SELECT Desc_Mujeres FROM DFLE_AccionesFormativas WHERE Desc_AccionFormativa = @AccionActual AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)); UPDATE @AccionFormativa SET Total_HomMuj = @Hombres + @Mujeres WHERE ID_AccionFormativa = @ContAcciones;
SET @ContAcciones = @ContAcciones + 1; END
SELECT daf.Desc_Tipo_Accion, daf.Desc_AccionFormativa, daf.Desc_Modalidad, did.Desc_Idioma, UA.Siglas, daf.Desc_Hombres, daf.Desc_Mujeres, af.Total_HomMuj FROM DFLE_AccionesFormativas daf JOIN @AccionFormativa af ON daf.Desc_AccionFormativa = af.AccionFormacion JOIN DFLE_Idiomas did ON daf.id_Idioma = did.ID_Idioma JOIN UnidadesAcademicas UA ON daf.id_UnidadAcademica = UA.ID_UnidadAcademica END

-- Numero de Formato:12 Acción formativa impartida a docentes de lenguas . ACUMULADO
CREATE PROCEDURE DFLE_AccionFormativa
    @anio INT,
    @id_Trimestre INT
AS

BEGIN SET NOCOUNT ON; DECLARE @CenlexSantoTomas VARCHAR(255)= 'CENLEX UST' DECLARE @CenlexZacatenco VARCHAR(255)= 'CENLEX Zacatenco'
DECLARE @TablaLenguasUA TABLE( Idioma VARCHAR(255) NOT NULL DEFAULT '',
CenlexH INT NOT NULL DEFAULT 0, CenlexM INT NOT NULL DEFAULT 0, CenlexTotal INT NOT NULL DEFAULT 0,
CelexH INT NOT NULL DEFAULT 0, CelexM INT NOT NULL DEFAULT 0, CelexTotal INT NOT NULL DEFAULT 0,
SubTotalH INT NOT NULL DEFAULT 0, SubTotalM INT NOT NULL DEFAULT 0, Total INT NOT NULL DEFAULT 0 ); INSERT INTO @TablaLenguasUA(Idioma) SELECT Desc_Idioma FROM DFLE_Idiomas;
DECLARE @ContadorLenguas INT =1;
DECLARE @CenlexH INT = 0; DECLARE @CenlexM INT = 0; DECLARE @CenlexTotal INT = 0;
DECLARE @CelexH INT = 0; DECLARE @CelexM INT = 0; DECLARE @CelexTotal INT = 0;
DECLARE @SubTotalH INT = 0; DECLARE @SubTotalM INT = 0; DECLARE @Total INT = 0;
WHILE @ContadorLenguas <= (SELECT COUNT(*) FROM @TablaLenguasUA) BEGIN IF @Acumulado = 'NO ACUMULADO' BEGIN SET @CenlexH = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica IN ((SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre), 0); SET @CenlexM = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica IN ((SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre), 0); SET @CenlexTotal = @CenlexH + @CenlexM;
SET @CelexH = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica NOT IN ((SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre), 0); SET @CelexM = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica NOT IN ((SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre), 0); SET @CelexTotal = @CelexH + @CelexM;
UPDATE @TablaLenguasUA SET CenlexH = @CenlexH, CenlexM = @CenlexM, CenlexTotal = @CenlexTotal, CelexH = @CelexH, CelexM = @CelexM, CelexTotal = @CelexTotal, SubTotalH = @CenlexH + @CelexH, SubTotalM = @CenlexM + @CelexM, Total = @CenlexTotal + @CelexTotal WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorLenguas);
SET @ContadorLenguas = @ContadorLenguas + 1; END ELSE BEGIN SET @CenlexH = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica IN ((SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0); SET @CenlexM = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica IN ((SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0); SET @CenlexTotal = @CenlexH + @CenlexM;
SET @CelexH = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica NOT IN ((SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0); SET @CelexM = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica NOT IN ((SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0); SET @CelexTotal = @CelexH + @CelexM;
UPDATE @TablaLenguasUA SET CenlexH = @CenlexH, CenlexM = @CenlexM, CenlexTotal = @CenlexTotal, CelexH = @CelexH, CelexM = @CelexM, CelexTotal = @CelexTotal, SubTotalH = @CenlexH + @CelexH, SubTotalM = @CenlexM + @CelexM, Total = @CenlexTotal + @CelexTotal WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorLenguas);
SET @ContadorLenguas = @ContadorLenguas + 1; END END
SELECT * FROM @TablaLenguasUA /*UNION ALL SELECT 'TOTAL', ISNULL(SUM(CenlexH), 0), ISNULL(SUM(CenlexM), 0), ISNULL(SUM(CenlexTotal), 0), ISNULL(SUM(CelexH), 0), ISNULL(SUM(CelexM), 0), ISNULL(SUM(CelexTotal), 0), ISNULL(SUM(SubTotalH), 0), ISNULL(SUM(SubTotalM), 0), ISNULL(SUM(Total), 0) FROM @TablaLenguasUA; */ END

