CREATE PROCEDURE DFLE_AlumnosAtendidosLenguasAcumulado
    @anio INT, 
    @id_Trimestre INT,
    @Acumulado VARCHAR(15)
AS

CREATE PROCEDURE DFLE_ComplementoEnsenanzaLenguasUA
    @SiglasUnidadAcademica VARCHAR(255),
    @anio INT,
    @id_Trimestre INT
AS

BEGIN SET NOCOUNT ON; DECLARE @CenlexSantoTomas VARCHAR(255)= 'CENLEX UST' DECLARE @CenlexZacatenco VARCHAR(255)= 'CENLEX Zacatenco' DECLARE @TablaLenguasUA TABLE( Lengua VARCHAR(255) NOT NULL DEFAULT '',
CenlexSantoTomasH INT NOT NULL DEFAULT 0, CenlexSantoTomasM INT NOT NULL DEFAULT 0, CenlexSantoTomasTotal INT NOT NULL DEFAULT 0,
CenlexZacatencoH INT NOT NULL DEFAULT 0, CenlexZacatencoM INT NOT NULL DEFAULT 0, CenlexZacatencoTotal INT NOT NULL DEFAULT 0,
SubTotalH INT NOT NULL DEFAULT 0, SubTotalM INT NOT NULL DEFAULT 0, Total INT NOT NULL DEFAULT 0 ); INSERT INTO @TablaLenguasUA(Lengua) SELECT Desc_Idioma FROM DFLE_Idiomas;
DECLARE @ContadorLenguas INT = 1;
DECLARE @CenlexSantoTomasH INT = 0; DECLARE @CenlexZacatencoM INT = 0; DECLARE @CenlexZacatencoH INT = 0; DECLARE @CenlexSantoTomasM INT = 0;
WHILE @ContadorLenguas <= (SELECT COUNT(*) FROM DFLE_Idiomas) BEGIN SET @CenlexSantoTomasH = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_DocentesAccionesFormativas WHERE id_UnidadCenlex = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre), 0);
SET @CenlexSantoTomasM = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_DocentesAccionesFormativas WHERE id_UnidadCenlex = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre), 0);
SET @CenlexZacatencoH = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_DocentesAccionesFormativas WHERE id_UnidadCenlex = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre), 0);
SET @CenlexZacatencoM = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_DocentesAccionesFormativas WHERE id_UnidadCenlex = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre), 0); UPDATE @TablaLenguasUA SET CenlexSantoTomasH = @CenlexSantoTomasH, CenlexSantoTomasM = @CenlexSantoTomasM, CenlexSantoTomasTotal = @CenlexSantoTomasH + @CenlexSantoTomasM,
CenlexZacatencoH = @CenlexZacatencoH, CenlexZacatencoM = @CenlexZacatencoM, CenlexZacatencoTotal = @CenlexZacatencoH + @CenlexZacatencoM,
SubTotalH = @CenlexSantoTomasH + @CenlexZacatencoH, SubTotalM = @CenlexSantoTomasM + @CenlexZacatencoM,
Total = (@CenlexSantoTomasH + @CenlexSantoTomasM) + (@CenlexZacatencoH + @CenlexZacatencoM) WHERE Lengua = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorLenguas); SET @ContadorLenguas = @ContadorLenguas + 1; END
SELECT * FROM @TablaLenguasUA UNION ALL SELECT 'Total Cenlex', SUM(CenlexSantoTomasH), SUM(CenlexSantoTomasM), SUM(CenlexSantoTomasTotal), SUM(CenlexZacatencoH), SUM(CenlexZacatencoM), SUM(CenlexZacatencoTotal), SUM(SubTotalH), SUM(SubTotalM), SUM(Total) FROM @TablaLenguasUA; END

-- Numero de Formato: 11 DOCENTES DE LENGUAS QUE PARTICIPAN EN ACCIONES DE FORMACIÓN
CREATE PROCEDURE DFLE_DocentesAccionesFormacion  
    @anio INT,
    @id_Trimestre INT
AS

BEGIN
    SET NOCOUNT ON;
    --**********************************************************************************
    --Declaraciones importantes a tomar en cuenta
    --**********************************************************************************

    --!Declaracion de la tabla provisional para organizar los datos que se mostraran con 
    --!procedimiento

    DECLARE @EnsenanzaAlumnosNivelEducativo TABLE(
        SiglasUnidadesAcademicas varchar(255),

        NMS_Hombres varchar(255) NOT NULL DEFAULT ' ',
        NMS_Mujeres varchar(255) NOT NULL DEFAULT ' ',
        NMS_Total varchar(255) NOT NULL DEFAULT ' ',

        NS_Hombres varchar(255) NOT NULL DEFAULT ' ',
        NS_Mujeres varchar(255) NOT NULL DEFAULT ' ',
        NS_Total varchar(255) NOT NULL DEFAULT ' ',

        Pos_Hombres varchar(255) NOT NULL DEFAULT ' ',
        Pos_Mujeres varchar(255) NOT NULL DEFAULT ' ',
        Pos_Total varchar(255) NOT NULL DEFAULT ' ',

        Egresados_Hombres varchar(255) NOT NULL DEFAULT ' ',
        Egresados_Mujeres varchar(255) NOT NULL DEFAULT ' ',
        Egresados_Total varchar(255) NOT NULL DEFAULT ' ',

        Empleados_Hombres varchar(255) NOT NULL DEFAULT ' ',
        Empleados_Mujeres varchar(255) NOT NULL DEFAULT ' ',
        Empleados_Total varchar(255) NOT NULL DEFAULT ' ',

        Subtotal_Hombres varchar(255) NOT NULL DEFAULT ' ',
        Subtotal_Mujeres varchar(255) NOT NULL DEFAULT ' ',
        Subtotal_Total varchar(255) NOT NULL DEFAULT ' ',

        PublicoGeneral_Hombres varchar(255) NOT NULL DEFAULT ' ',
        PublicoGeneral_Mujeres varchar(255) NOT NULL DEFAULT ' ',
        PublicoGeneral_Total varchar(255) NOT NULL DEFAULT ' ',

        PoblacionAtendida_Hombres varchar(255) NOT NULL DEFAULT ' ',
        PoblacionAtendida_Mujeres varchar(255) NOT NULL DEFAULT ' ',
        PoblacionAtendida_Total varchar(255) NOT NULL DEFAULT ' '
    )

    --!Declaramos la tabla que contendra las unidades academicas para poder iterar
    --!sobre ellas
    DECLARE @UnidadesAcademicas TABLE(
        ID_Unidad INT IDENTITY(1,1),
        SiglasUnidadAcademica varchar(255)
    );

    --!Declaramos el contador para recorrer las unidades academicas y los
    --!niveles educativos así como la variable que guarda el valor de 
    --!la unidad academica actual, el nivel educativo sera buscado por su ID
    DECLARE @ContadorUA INT = 1;
    DECLARE @ContadorNivelEducativo INT = 1;

    DECLARE @SiglasUnidadActual varchar(255);


    --!Declaramos las variables para guardar las cantidades de hombres, mujeres y total,
    --!además las varibles que llevaran el conteo del subtotal y el total de la poblacion atendida
    DECLARE @Hombres INT;
    DECLARE @Mujeres INT;
    DECLARE @TotalHM INT;

    SET @Hombres = 0;
    SET @Mujeres = 0;
    SET @TotalHM = 0;

    DECLARE @SubtotalHombres INT;
    DECLARE @SubtotalMujeres INT;
    DECLARE @SubtotalTotal INT;

    DECLARE @PoblacionAtendidaHombres INT;
    DECLARE @PoblacionAtendidaMujeres INT;
    DECLARE @PoblacionAtendidaTotal INT;


    --****************************************************************************
    --En esta parte se comienza el codigo para el relleno de la tabla provisional
    --****************************************************************************

    --!Se insertan las unidades academicas correspondientes al tipo solicitado
    INSERT INTO @UnidadesAcademicas(
        SiglasUnidadAcademica
    )
    SELECT Siglas 
    FROM UnidadesAcademicas
    WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica 
                                    FROM TipoUnidadAcademica
                                    WHERE Desc_SiglasTipo = @SiglasTipoUnidadAcademica)
    AND id_RamaConocimiento = (SELECT ID_RamaConocimiento 
                                FROM RamaConocimiento
                                WHERE Desc_SiglasRama = @SiglasRamaConocimiento);

    --!While para el recorrido de las unidades academicas
    WHILE @ContadorUA <= (SELECT COUNT(*) FROM @UnidadesAcademicas)
    BEGIN

        --!Asignacion de la unidad actual
        SET @SiglasUnidadActual = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE ID_Unidad = @ContadorUA);
        SET @ContadorNivelEducativo = 1;

        SET @SubtotalHombres = 0;
        SET @SubtotalMujeres = 0;
        SET @SubtotalTotal = 0;

        SET @PoblacionAtendidaHombres = 0;
        SET @PoblacionAtendidaMujeres = 0;
        SET @PoblacionAtendidaTotal = 0;

        --!Insertamos la unidad a la tabla provisional
        INSERT INTO @EnsenanzaAlumnosNivelEducativo(
            SiglasUnidadesAcademicas
        )VALUES(
            @SiglasUnidadActual
        );

        --!While para recorrer los niveles educativos de los alumnos de la DFLE
        WHILE @ContadorNivelEducativo <= (SELECT COUNT(*) FROM DFLE_NivelEducativo)
        BEGIN
            SET @Hombres = ISNULL((SELECT SUM(Desc_Hombres) 
                                    FROM DFLE_AlumnosEnsenanzaLenguas
                                    WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica 
                                                                FROM UnidadesAcademicas 
                                                                WHERE Siglas = @SiglasUnidadActual) 
                                    AND id_NivelEducativo = @ContadorNivelEducativo ), 0);

            SET @Mujeres = ISNULL((SELECT SUM(Desc_Mujeres) 
                                    FROM DFLE_AlumnosEnsenanzaLenguas
                                    WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica 
                                                                FROM UnidadesAcademicas 
                                                                WHERE Siglas = @SiglasUnidadActual) 
                                    AND id_NivelEducativo = @ContadorNivelEducativo ), 0);

            SET @TotalHM = @Hombres + @Mujeres;

            IF @ContadorNivelEducativo = 1
            BEGIN
                UPDATE @EnsenanzaAlumnosNivelEducativo
                SET NMS_Hombres = @Hombres,
                    NMS_Mujeres = @Mujeres,
                    NMS_Total = @TotalHM
                WHERE SiglasUnidadesAcademicas = @SiglasUnidadActual;

                SET @SubtotalHombres = @SubtotalHombres + @Hombres;
                SET @SubtotalMujeres = @SubtotalMujeres + @Mujeres;
                SET @SubtotalTotal = @SubtotalTotal + @TotalHM;
            END
            ELSE IF @ContadorNivelEducativo = 2
            BEGIN
                UPDATE @EnsenanzaAlumnosNivelEducativo
                SET NS_Hombres = @Hombres,
                    NS_Mujeres = @Mujeres,
                    NS_Total = @TotalHM
                WHERE SiglasUnidadesAcademicas = @SiglasUnidadActual;

                SET @SubtotalHombres = @SubtotalHombres + @Hombres;
                SET @SubtotalMujeres = @SubtotalMujeres + @Mujeres;
                SET @SubtotalTotal = @SubtotalTotal + @TotalHM;

            END
            ELSE IF @ContadorNivelEducativo = 3
            BEGIN
                UPDATE @EnsenanzaAlumnosNivelEducativo
                SET Pos_Hombres = @Hombres,
                    Pos_Mujeres = @Mujeres,
                    Pos_Total = @TotalHM
                WHERE SiglasUnidadesAcademicas = @SiglasUnidadActual;

                SET @SubtotalHombres = @SubtotalHombres + @Hombres;
                SET @SubtotalMujeres = @SubtotalMujeres + @Mujeres;
                SET @SubtotalTotal = @SubtotalTotal + @TotalHM;

            END
            ELSE IF @ContadorNivelEducativo = 4
            BEGIN
                UPDATE @EnsenanzaAlumnosNivelEducativo
                SET Egresados_Hombres = @Hombres,
                    Egresados_Mujeres = @Mujeres,
                    Egresados_Total = @TotalHM
                WHERE SiglasUnidadesAcademicas = @SiglasUnidadActual;

                SET @SubtotalHombres = @SubtotalHombres + @Hombres;
                SET @SubtotalMujeres = @SubtotalMujeres + @Mujeres;
                SET @SubtotalTotal = @SubtotalTotal + @TotalHM;

            END
            ELSE IF @ContadorNivelEducativo = 5
            BEGIN
                UPDATE @EnsenanzaAlumnosNivelEducativo
                SET Empleados_Hombres = @Hombres,
                    Empleados_Mujeres = @Mujeres,
                    Empleados_Total = @TotalHM
                WHERE SiglasUnidadesAcademicas = @SiglasUnidadActual;

                SET @SubtotalHombres = @SubtotalHombres + @Hombres;
                SET @SubtotalMujeres = @SubtotalMujeres + @Mujeres;
                SET @SubtotalTotal = @SubtotalTotal + @TotalHM;

            END
            ELSE IF @ContadorNivelEducativo = 6
            BEGIN
                UPDATE @EnsenanzaAlumnosNivelEducativo
                SET PublicoGeneral_Hombres = @Hombres,
                    PublicoGeneral_Mujeres = @Mujeres,
                    PublicoGeneral_Total = @TotalHM
                WHERE SiglasUnidadesAcademicas = @SiglasUnidadActual;

                DECLARE @PublicoGeneral_Hombres INT = @Hombres;
                DECLARE @PublicoGeneral_Mujeres INT = @Mujeres;
                DECLARE @PublicoGeneral_Total INT = @TotalHM;
            END

            SET @ContadorNivelEducativo = @ContadorNivelEducativo + 1;
        END

        SET @PoblacionAtendidaHombres = @SubtotalHombres + @PublicoGeneral_Hombres;
        SET @PoblacionAtendidaMujeres = @SubtotalMujeres + @PublicoGeneral_Mujeres;
        SET @PoblacionAtendidaTotal = @SubtotalTotal + @PublicoGeneral_Total;

        UPDATE @EnsenanzaAlumnosNivelEducativo
        SET Subtotal_Hombres = @SubtotalHombres,
            Subtotal_Mujeres = @SubtotalMujeres,
            Subtotal_Total = @SubtotalTotal,
            PoblacionAtendida_Hombres = @PoblacionAtendidaHombres,
            PoblacionAtendida_Mujeres = @PoblacionAtendidaMujeres,
            PoblacionAtendida_Total = @PoblacionAtendidaTotal
        WHERE SiglasUnidadesAcademicas = @SiglasUnidadActual;

        SET @ContadorUA = @ContadorUA + 1;
    END
    SELECT * FROM @EnsenanzaAlumnosNivelEducativo;
END

--Numero de Formato: 7 Enseñanza de lenguas por Nivel Educativo
CREATE PROCEDURE DFLE_EnsenanzaLenguasNivelEducativo
    @SiglasTipoUnidadAcademica varchar(255),
    @SiglasRamaConocimiento varchar(255),
    @anio INT,
    @id_Trimestre INT
AS

BEGIN SET NOCOUNT ON; DECLARE @Idiomas TABLE ( Idioma VARCHAR(255), ID_Idioma INT IDENTITY (1,1) ); INSERT INTO @Idiomas ( Idioma )SELECT Desc_Idioma FROM DFLE_Idiomas
DECLARE @contadorIdioma INT = 1;
DECLARE @PoblacionAtendida TABLE ( Idioma VARCHAR(255), NMSHombres VARCHAR(255) NOT NULL DEFAULT ' ', NMSMujeres VARCHAR(255) NOT NULL DEFAULT ' ', NMSTotal VARCHAR(255) NOT NULL DEFAULT '0',
NSHombres VARCHAR(255) NOT NULL DEFAULT ' ', NSMujeres VARCHAR(255) NOT NULL DEFAULT ' ', NSTotal VARCHAR(255) NOT NULL DEFAULT '0',
POSHombres VARCHAR(255) NOT NULL DEFAULT ' ', POSMujeres VARCHAR(255) NOT NULL DEFAULT ' ', POSTotal VARCHAR(255) NOT NULL DEFAULT '0',
EGHombres VARCHAR(255) NOT NULL DEFAULT ' ', EGMujeres VARCHAR(255) NOT NULL DEFAULT ' ', EGTotal VARCHAR(255) NOT NULL DEFAULT '0',
EIHombres VARCHAR(255) NOT NULL DEFAULT ' ', EIMujeres VARCHAR(255) NOT NULL DEFAULT ' ', EITotal VARCHAR(255) NOT NULL DEFAULT '0',
SUBHombres VARCHAR(255) NOT NULL DEFAULT '0', SUBMujeres VARCHAR(255) NOT NULL DEFAULT '0', SUBTotal VARCHAR(255) NOT NULL DEFAULT '0',
PGHombres VARCHAR(255) NOT NULL DEFAULT ' ', PGMujeres VARCHAR(255) NOT NULL DEFAULT ' ', PGTotal VARCHAR(255) NOT NULL DEFAULT '0',
PAHombres VARCHAR(255) NOT NULL DEFAULT '0', PAMujeres VARCHAR(255) NOT NULL DEFAULT '0', PATotal VARCHAR(255) NOT NULL DEFAULT '0' );
DECLARE @NMSHombres INT; DECLARE @NMSMujeres INT; DECLARE @NSHombres INT; DECLARE @NSMujeres INT; DECLARE @POSHombres INT; DECLARE @POSMujeres INT; DECLARE @EGHombres INT; DECLARE @EGMujeres INT; DECLARE @EIHombres INT; DECLARE @EIMujeres INT; DECLARE @PGHombres INT; DECLARE @PGMujeres INT;
WHILE @contadorIdioma <= (SELECT COUNT(*) FROM @Idiomas) BEGIN IF @Total = 'NO TOTAL' BEGIN SET @NMSHombres = ISNULL((SELECT Desc_Hombres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'MEDIO SUPERIOR')
AND id_Idioma = @contadorIdioma AND id_Competencia = (SELECT ID_Competencia FROM DFLE_NivelesCompetencia WHERE Desc_NivelCompetencia = @Competencia) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre), 0);
SET @NMSMujeres = ISNULL((SELECT Desc_Mujeres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'MEDIO SUPERIOR')
AND id_Idioma = @contadorIdioma AND id_Competencia = (SELECT ID_Competencia FROM DFLE_NivelesCompetencia WHERE Desc_NivelCompetencia = @Competencia) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre), 0);
SET @NSHombres = ISNULL((SELECT Desc_Hombres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'SUPERIOR')
AND id_Idioma = @contadorIdioma AND id_Competencia = (SELECT ID_Competencia FROM DFLE_NivelesCompetencia WHERE Desc_NivelCompetencia = @Competencia) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre),0);
SET @NSMujeres = ISNULL((SELECT Desc_Mujeres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'SUPERIOR')
AND id_Idioma = @contadorIdioma AND id_Competencia = (SELECT ID_Competencia FROM DFLE_NivelesCompetencia WHERE Desc_NivelCompetencia = @Competencia) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre),0);
SET @POSHombres = ISNULL((SELECT Desc_Hombres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'POSGRADO')
AND id_Idioma = @contadorIdioma AND id_Competencia = (SELECT ID_Competencia FROM DFLE_NivelesCompetencia WHERE Desc_NivelCompetencia = @Competencia) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre),0); SET @POSMujeres = ISNULL((SELECT Desc_Mujeres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'POSGRADO')
AND id_Idioma = @contadorIdioma AND id_Competencia = (SELECT ID_Competencia FROM DFLE_NivelesCompetencia WHERE Desc_NivelCompetencia = @Competencia) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre),0);
SET @EGHombres = ISNULL((SELECT Desc_Hombres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'EGRESADOS')
AND id_Idioma = @contadorIdioma AND id_Competencia = (SELECT ID_Competencia FROM DFLE_NivelesCompetencia WHERE Desc_NivelCompetencia = @Competencia) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre),0);
SET @EGMujeres = ISNULL((SELECT Desc_Mujeres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'EGRESADOS')
AND id_Idioma = @contadorIdioma AND id_Competencia = (SELECT ID_Competencia FROM DFLE_NivelesCompetencia WHERE Desc_NivelCompetencia = @Competencia) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre),0);
SET @EIHombres = ISNULL((SELECT Desc_Hombres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'EMPLEADOS')
AND id_Idioma = @contadorIdioma AND id_Competencia = (SELECT ID_Competencia FROM DFLE_NivelesCompetencia WHERE Desc_NivelCompetencia = @Competencia) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre),0);
SET @EIMujeres = ISNULL((SELECT Desc_Mujeres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'EMPLEADOS')
AND id_Idioma = @contadorIdioma AND id_Competencia = (SELECT ID_Competencia FROM DFLE_NivelesCompetencia WHERE Desc_NivelCompetencia = @Competencia) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre),0);
SET @PGHombres = ISNULL((SELECT Desc_Hombres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'No aplica')
AND id_Idioma = @contadorIdioma AND id_Competencia = (SELECT ID_Competencia FROM DFLE_NivelesCompetencia WHERE Desc_NivelCompetencia = @Competencia) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre),0);
SET @PGMujeres =ISNULL( (SELECT Desc_Mujeres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'No aplica')
AND id_Idioma = @contadorIdioma AND id_Competencia = (SELECT ID_Competencia FROM DFLE_NivelesCompetencia WHERE Desc_NivelCompetencia = @Competencia) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre),0);
END ELSE BEGIN SET @NMSHombres = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'MEDIO SUPERIOR')
AND id_Idioma = @contadorIdioma AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre), 0);
SET @NMSMujeres = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'MEDIO SUPERIOR')
AND id_Idioma = @contadorIdioma AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre), 0);
SET @NSHombres = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'SUPERIOR')
AND id_Idioma = @contadorIdioma AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre),0);
SET @NSMujeres = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'SUPERIOR')
AND id_Idioma = @contadorIdioma AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre),0);
SET @POSHombres = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'POSGRADO')
AND id_Idioma = @contadorIdioma AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre),0); SET @POSMujeres = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'POSGRADO')
AND id_Idioma = @contadorIdioma AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre),0);
SET @EGHombres = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'EGRESADOS')
AND id_Idioma = @contadorIdioma AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre),0);
SET @EGMujeres = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'EGRESADOS')
AND id_Idioma = @contadorIdioma AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre),0);
SET @EIHombres = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'EMPLEADOS')
AND id_Idioma = @contadorIdioma AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre),0);
SET @EIMujeres = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'EMPLEADOS')
AND id_Idioma = @contadorIdioma AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre),0);
SET @PGHombres = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'No aplica')
AND id_Idioma = @contadorIdioma AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre),0);
SET @PGMujeres =ISNULL( (SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_NivelEducativo = (SELECT ID_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = 'No aplica')
AND id_Idioma = @contadorIdioma AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre),0); END;
INSERT INTO @PoblacionAtendida ( NMSHombres, NMSMujeres, NMSTotal, NSHombres, NSMujeres, NSTotal, POSHombres, POSMujeres, POSTotal, EGHombres, EGMujeres, EGTotal, EIHombres, EIMujeres, EITotal, SUBHombres, SUBMujeres, SUBTotal, PGHombres, PGMujeres, PGTotal, PAHombres, PAMujeres, PATotal, Idioma )VALUES(
CASE WHEN @NMSHombres = 0 THEN ' ' ELSE CAST(@NMSHombres AS VARCHAR(255)) END,
CASE WHEN @NMSMujeres = 0 THEN ' ' ELSE CAST(@NMSMujeres AS VARCHAR(255)) END, @NMSHombres + @NMSMujeres,
CASE WHEN @NSHombres = 0 THEN ' ' ELSE CAST(@NSHombres AS VARCHAR(255)) END, CASE WHEN @NSMujeres = 0 THEN ' ' ELSE CAST(@NSMujeres AS VARCHAR(255)) END, @NSHombres + @NSMujeres,
CASE WHEN @POSHombres = 0 THEN ' ' ELSE CAST(@POSHombres AS VARCHAR(255)) END, CASE WHEN @POSMujeres = 0 THEN ' ' ELSE CAST(@POSMujeres AS VARCHAR(255)) END, @POSHombres + @POSMujeres,
CASE WHEN @EGHombres = 0 THEN ' ' ELSE CAST(@EGHombres AS VARCHAR(255)) END, CASE WHEN @EGMujeres = 0 THEN ' ' ELSE CAST(@EGMujeres AS VARCHAR(255)) END, @EGHombres + @EGMujeres,
CASE WHEN @EIHombres = 0 THEN ' ' ELSE CAST(@EIHombres AS VARCHAR(255)) END,
CASE WHEN @EIMujeres = 0 THEN ' ' ELSE CAST(@EIMujeres AS VARCHAR(255)) END, @EIHombres + @EIMujeres,
@NMSHombres + @NSHombres + @POSHombres + @EGHombres + @EIHombres, @NMSMujeres + @NSMujeres + @POSMujeres + @EGMujeres + @EIMujeres, @NMSHombres + @NSHombres + @POSHombres + @EGHombres + @EIHombres + @NMSMujeres + @NSMujeres + @POSMujeres + @EGMujeres + @EIMujeres,
CASE WHEN @PGHombres = 0 THEN ' ' ELSE CAST(@PGHombres AS VARCHAR(255)) END, CASE WHEN @PGMujeres = 0 THEN ' ' ELSE CAST(@PGMujeres AS VARCHAR(255)) END, @PGHombres + @PGMujeres,
@NMSHombres + @NSHombres + @POSHombres + @EGHombres + @EIHombres + @PGHombres, @NMSMujeres + @NSMujeres + @POSMujeres + @EGMujeres + @EIMujeres + @PGMujeres,
@NMSHombres + @NSHombres + @POSHombres + @EGHombres + @EIHombres + @PGHombres + @NMSMujeres + @NSMujeres + @POSMujeres + @EGMujeres + @EIMujeres + @PGMujeres, (SELECT Idioma FROM @Idiomas WHERE ID_Idioma = @contadorIdioma) ) SET @contadorIdioma = @contadorIdioma + 1; END SELECT * FROM @PoblacionAtendida END

--Formato 10: Enseñanza de lenguas Unidades Académicas
CREATE PROCEDURE DFLE_EnsenanzaLenguasUA
    @SiglasUnidadAcademica VARCHAR(255),
    @Competencia VARCHAR(255),
    @anio INT,
    @id_Trimestre INT,
    @Total varchar(15)
AS

BEGIN SET NOCOUNT ON;
DECLARE @AccionFormativa TABLE ( ID_AccionFormativa INT IDENTITY(1, 1), AccionFormacion VARCHAR(255) COLLATE Latin1_General_CI_AI, Total INT );
DECLARE @Contador INT = 1; DECLARE @Hombres INT = 0; DECLARE @Mujeres INT = 0; DECLARE @AccionActual VARCHAR(255) = ' ';
IF @Final = 'FINAL' BEGIN INSERT INTO @AccionFormativa( AccionFormacion ) SELECT Desc_AccionFormativa FROM DFLE_AccionesFormativas WHERE id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio); SET @Contador = 1; WHILE @Contador <= ISNULL((SELECT COUNT(*) FROM @AccionFormativa),0) BEGIN SET @AccionActual = (SELECT AccionFormacion FROM @AccionFormativa WHERE ID_AccionFormativa = @Contador);
SET @Hombres = (SELECT SUM(Desc_Hombres) FROM DFLE_AccionesFormativas WHERE Desc_AccionFormativa = @AccionActual AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio));
SET @Mujeres = (SELECT SUM(Desc_Mujeres) FROM DFLE_AccionesFormativas WHERE Desc_AccionFormativa = @AccionActual AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)); UPDATE @AccionFormativa SET Total = @Hombres + @Mujeres WHERE ID_AccionFormativa = @Contador;
SET @Contador = @Contador + 1; END
SELECT daf.Desc_Tipo_Accion, daf.Desc_AccionFormativa, daf.Desc_Modalidad, did.Desc_Idioma, UA.Siglas, daf.Desc_Hombres, daf.Desc_Mujeres, af.Total FROM DFLE_AccionesFormativas daf JOIN @AccionFormativa af ON daf.Desc_AccionFormativa = af.AccionFormacion JOIN DFLE_Idiomas did ON daf.id_Idioma = did.ID_Idioma JOIN UnidadesAcademicas UA ON daf.id_UnidadAcademica = UA.ID_UnidadAcademica
SELECT ID_RegistroFormato12DFLE FROM DFLE_AccionesFormativas WHERE id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio); END ELSE BEGIN INSERT INTO @AccionFormativa( AccionFormacion ) SELECT Desc_AccionFormativa FROM DFLE_AccionesFormativas WHERE id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio); SET @Contador = 1; SET @Hombres = 0; SET @Mujeres = 0; WHILE @Contador <= ISNULL((SELECT COUNT(*) FROM @AccionFormativa),0) BEGIN SET @AccionActual = (SELECT AccionFormacion FROM @AccionFormativa WHERE ID_AccionFormativa = @Contador);
SET @Hombres = (SELECT SUM(Desc_Hombres) FROM DFLE_AccionesFormativas_Temporal WHERE Desc_AccionFormativa = @AccionActual AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio));
SET @Mujeres = (SELECT SUM(Desc_Mujeres) FROM DFLE_AccionesFormativas_Temporal WHERE Desc_AccionFormativa = @AccionActual AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)); UPDATE @AccionFormativa SET Total = @Hombres + @Mujeres WHERE ID_AccionFormativa = @Contador;
SET @Contador = @Contador + 1; END
SELECT daf.Desc_Tipo_Accion, daf.Desc_AccionFormativa, daf.Desc_Modalidad, did.Desc_Idioma, UA.Siglas, daf.Desc_Hombres, daf.Desc_Mujeres, af.Total FROM DFLE_AccionesFormativas daf JOIN @AccionFormativa af ON daf.Desc_AccionFormativa = af.AccionFormacion JOIN DFLE_Idiomas did ON daf.id_Idioma = did.ID_Idioma JOIN UnidadesAcademicas UA ON daf.id_UnidadAcademica = UA.ID_UnidadAcademica
SELECT ID_RegistroFormato12DFLE FROM DFLE_AccionesFormativas_Temporal WHERE id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio); END END

--Procedimiento para retornar informacion a la interfaz que representa el formato 12
CREATE PROCEDURE DFLE_InterAccionFormativa
    @anio INT,
    @id_Trimestre INT,
    @Final varchar(15)
AS

BEGIN SET NOCOUNT ON;
DECLARE @TablaLenguasUA TABLE( Lengua VARCHAR(255) NOT NULL DEFAULT '',
CenlexHombres INT NOT NULL DEFAULT 0, CenlexMujeres INT NOT NULL DEFAULT 0, CenlexTotal INT NOT NULL DEFAULT 0 );
DECLARE @Identificadores TABLE( Lengua varchar(255) NOT NULL DEFAULT ' ',
IdentificadorRegistro INT NOT NULL DEFAULT 0 );
DECLARE @ContadorLenguas INT = 1; DECLARE @CenlexHombres INT = 0; DECLARE @CenlexMujeres INT = 0;
IF @Final = 'FINAL' BEGIN SET @ContadorLenguas = 1; WHILE @ContadorLenguas <= (SELECT COUNT(*) FROM DFLE_Idiomas) BEGIN
SET @CenlexHombres = ISNULL((SELECT Desc_Hombres FROM DFLE_DocentesAccionesFormativas WHERE id_UnidadCenlex = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasCENLEX) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre), 0);
SET @CenlexMujeres = ISNULL((SELECT Desc_Mujeres FROM DFLE_DocentesAccionesFormativas WHERE id_UnidadCenlex = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasCENLEX) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre), 0);
INSERT INTO @TablaLenguasUA( Lengua, CenlexHombres, CenlexMujeres, CenlexTotal )VALUES( (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorLenguas), @CenlexHombres, @CenlexMujeres, (@CenlexHombres + @CenlexMujeres) );
INSERT INTO @Identificadores( Lengua, IdentificadorRegistro )VALUES( (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorLenguas), ISNULL(((SELECT ID_DocentesAccionesFormativas FROM DFLE_DocentesAccionesFormativas WHERE id_UnidadCenlex = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasCENLEX) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre)), 0) )
SET @ContadorLenguas = @ContadorLenguas + 1; END END ELSE BEGIN SET @ContadorLenguas = 1; WHILE @ContadorLenguas <= (SELECT COUNT(*) FROM DFLE_Idiomas) BEGIN
SET @CenlexHombres = ISNULL((SELECT Desc_Hombres FROM DFLE_DocentesAccionesFormativas_Temporal WHERE id_UnidadCenlex = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasCENLEX) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre), 0);
SET @CenlexMujeres = ISNULL((SELECT Desc_Mujeres FROM DFLE_DocentesAccionesFormativas_Temporal WHERE id_UnidadCenlex = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasCENLEX) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre), 0);
INSERT INTO @TablaLenguasUA( Lengua, CenlexHombres, CenlexMujeres, CenlexTotal )VALUES( (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorLenguas), @CenlexHombres, @CenlexMujeres, (@CenlexHombres + @CenlexMujeres) );
INSERT INTO @Identificadores( Lengua, IdentificadorRegistro )VALUES( (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorLenguas), (ISNULL((SELECT ID_DocentesAccionesFormativas FROM DFLE_DocentesAccionesFormativas_Temporal WHERE id_UnidadCenlex = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasCENLEX) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre), 0)) )
SET @ContadorLenguas = @ContadorLenguas + 1; END END
SELECT * FROM @TablaLenguasUA SELECT * FROM @Identificadores END

CREATE PROCEDURE DFLE_InterDocentesAccionesFormacion
    @SiglasCENLEX varchar(255),
    @anio INT,
    @id_Trimestre INT,
    @Final varchar(15)
AS

BEGIN SET NOCOUNT ON; DECLARE @NMS VARCHAR(255) = 'MEDIO SUPERIOR'; DECLARE @NS VARCHAR(255) = 'SUPERIOR'; DECLARE @PG VARCHAR(255) = 'POSTGRADO'; DECLARE @EG VARCHAR(255) = 'EGRESADOS'; DECLARE @EM VARCHAR(255) = 'EMPLEADOS'; DECLARE @GEN VARCHAR(255) = 'No aplica';
DECLARE @EnsenanzaLenguas TABLE( ID_Lengua INT IDENTITY (1,1), Lengua VARCHAR(255)); INSERT INTO @EnsenanzaLenguas SELECT Desc_Idioma FROM DFLE_Idiomas;
DECLARE @PoblacionAtendida TABLE ( Idioma VARCHAR(255), NMSHombres VARCHAR(255) NOT NULL DEFAULT ' ', NMSMujeres VARCHAR(255) NOT NULL DEFAULT ' ', NMSTotal VARCHAR(255) NOT NULL DEFAULT '0',
NSHombres VARCHAR(255) NOT NULL DEFAULT ' ', NSMujeres VARCHAR(255) NOT NULL DEFAULT ' ', NSTotal VARCHAR(255) NOT NULL DEFAULT '0',
POSHombres VARCHAR(255) NOT NULL DEFAULT ' ', POSMujeres VARCHAR(255) NOT NULL DEFAULT ' ', POSTotal VARCHAR(255) NOT NULL DEFAULT '0',
EGHombres VARCHAR(255) NOT NULL DEFAULT ' ', EGMujeres VARCHAR(255) NOT NULL DEFAULT ' ', EGTotal VARCHAR(255) NOT NULL DEFAULT '0',
EIHombres VARCHAR(255) NOT NULL DEFAULT ' ', EIMujeres VARCHAR(255) NOT NULL DEFAULT ' ', EITotal VARCHAR(255) NOT NULL DEFAULT '0',
PGHombres VARCHAR(255) NOT NULL DEFAULT ' ', PGMujeres VARCHAR(255) NOT NULL DEFAULT ' ', PGTotal VARCHAR(255) NOT NULL DEFAULT '0'
); DECLARE @NMSHombres INT; DECLARE @NMSMujeres INT; DECLARE @NSHombres INT; DECLARE @NSMujeres INT; DECLARE @POSHombres INT; DECLARE @POSMujeres INT; DECLARE @EGHombres INT; DECLARE @EGMujeres INT; DECLARE @EIHombres INT; DECLARE @EIMujeres INT; DECLARE @PGHombres INT; DECLARE @PGMujeres INT; DECLARE @Contador INT = 1;
DECLARE @TablaIden TABLE( Idioma VARCHAR(255), Ident_NMS INT NOT NULL DEFAULT 0, Ident_NS INT NOT NULL DEFAULT 0, Ident_POS INT NOT NULL DEFAULT 0, Ident_EG INT NOT NULL DEFAULT 0, Ident_EI INT NOT NULL DEFAULT 0, Ident_PG INT NOT NULL DEFAULT 0 );
DECLARE @Ident_NMS INT = 0; DECLARE @Ident_NS INT = 0; DECLARE @Ident_POS INT = 0; DECLARE @Ident_EG INT = 0; DECLARE @Ident_EI INT = 0; DECLARE @Ident_PG INT = 0;
IF @Final = 'FINAL' BEGIN SET @Contador = 1; WHILE @Contador <= (SELECT COUNT(*) FROM @EnsenanzaLenguas) BEGIN SET @NMSHombres = ISNULL((SELECT Desc_Hombres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @NMS ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
SET @NMSMujeres = ISNULL((SELECT Desc_Mujeres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @NMS ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
SET @NSHombres = ISNULL((SELECT Desc_Hombres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @NS ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
SET @NSMujeres = ISNULL((SELECT Desc_Mujeres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @NS ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
SET @POSHombres = ISNULL((SELECT Desc_Hombres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @PG ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
SET @POSMujeres = ISNULL((SELECT Desc_Mujeres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @PG ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
SET @EGHombres = ISNULL((SELECT Desc_Hombres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @EG ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
SET @EGMujeres = ISNULL((SELECT Desc_Mujeres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @EG ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
SET @EIHombres = ISNULL((SELECT Desc_Hombres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @EM ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
SET @EIMujeres = ISNULL((SELECT Desc_Mujeres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @EM ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
SET @PGHombres = ISNULL((SELECT Desc_Hombres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @GEN ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
SET @PGMujeres = ISNULL((SELECT Desc_Mujeres FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @GEN ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
INSERT INTO @PoblacionAtendida VALUES( (SELECT Lengua FROM @EnsenanzaLenguas WHERE ID_Lengua = @Contador), CASE WHEN @NMSHombres = 0 THEN 0 ELSE CAST(@NMSHombres AS VARCHAR(255)) END,
CASE WHEN @NMSMujeres = 0 THEN 0 ELSE CAST(@NMSMujeres AS VARCHAR(255)) END, @NMSHombres + @NMSMujeres,
CASE WHEN @NSHombres = 0 THEN 0 ELSE CAST(@NSHombres AS VARCHAR(255)) END, CASE WHEN @NSMujeres = 0 THEN 0 ELSE CAST(@NSMujeres AS VARCHAR(255)) END, @NSHombres + @NSMujeres,
CASE WHEN @POSHombres = 0 THEN 0 ELSE CAST(@POSHombres AS VARCHAR(255)) END, CASE WHEN @POSMujeres = 0 THEN 0 ELSE CAST(@POSMujeres AS VARCHAR(255)) END, @POSHombres + @POSMujeres,
CASE WHEN @EGHombres = 0 THEN 0 ELSE CAST(@EGHombres AS VARCHAR(255)) END, CASE WHEN @EGMujeres = 0 THEN 0 ELSE CAST(@EGMujeres AS VARCHAR(255)) END, @EGHombres + @EGMujeres,
CASE WHEN @EIHombres = 0 THEN 0 ELSE CAST(@EIHombres AS VARCHAR(255)) END,
CASE WHEN @EIMujeres = 0 THEN 0 ELSE CAST(@EIMujeres AS VARCHAR(255)) END, @EIHombres + @EIMujeres, CASE WHEN @PGHombres = 0 THEN 0 ELSE CAST(@PGHombres AS VARCHAR(255)) END, CASE WHEN @PGMujeres = 0 THEN 0 ELSE CAST(@PGMujeres AS VARCHAR(255)) END, @PGHombres + @PGMujeres ) SET @Contador = @Contador + 1; END
INSERT INTO @TablaIden(Idioma) SELECT Lengua FROM @EnsenanzaLenguas; SET @Contador = 1; WHILE @Contador <= (SELECT COUNT(*) FROM @EnsenanzaLenguas) BEGIN SET @Ident_NMS = ISNULL((SELECT ID_CantidadesAlumnos FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @NMS)),0);
SET @Ident_NS = ISNULL((SELECT ID_CantidadesAlumnos FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @NS)),0);
SET @Ident_POS = ISNULL((SELECT ID_CantidadesAlumnos FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @PG)),0);
SET @Ident_EG = ISNULL((SELECT ID_CantidadesAlumnos FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @EG)),0);
SET @Ident_EI = ISNULL((SELECT ID_CantidadesAlumnos FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @EM)),0);
SET @Ident_PG = ISNULL((SELECT ID_CantidadesAlumnos FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @GEN)),0);
UPDATE @TablaIden SET Ident_NMS = @Ident_NMS, Ident_NS = @Ident_NS, Ident_POS = @Ident_POS, Ident_EG = @Ident_EG, Ident_EI = @Ident_EI, Ident_PG = @Ident_PG WHERE Idioma = (SELECT Lengua FROM @EnsenanzaLenguas WHERE ID_Lengua = @Contador); SET @Contador = @Contador + 1; END END ELSE BEGIN SET @Contador = 1; WHILE @Contador <= (SELECT COUNT(*) FROM @EnsenanzaLenguas) BEGIN SET @NMSHombres = ISNULL((SELECT Desc_Hombres FROM DFLE_AlumnosEnsenanzaLenguas_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @NMS ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
SET @NMSMujeres = ISNULL((SELECT Desc_Mujeres FROM DFLE_AlumnosEnsenanzaLenguas_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @NMS ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
SET @NSHombres = ISNULL((SELECT Desc_Hombres FROM DFLE_AlumnosEnsenanzaLenguas_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @NS ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
SET @NSMujeres = ISNULL((SELECT Desc_Mujeres FROM DFLE_AlumnosEnsenanzaLenguas_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @NS ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
SET @POSHombres = ISNULL((SELECT Desc_Hombres FROM DFLE_AlumnosEnsenanzaLenguas_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @PG ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
SET @POSMujeres = ISNULL((SELECT Desc_Mujeres FROM DFLE_AlumnosEnsenanzaLenguas_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @PG ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
SET @EGHombres = ISNULL((SELECT Desc_Hombres FROM DFLE_AlumnosEnsenanzaLenguas_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @EG ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
SET @EGMujeres = ISNULL((SELECT Desc_Mujeres FROM DFLE_AlumnosEnsenanzaLenguas_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @EG ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
SET @EIHombres = ISNULL((SELECT Desc_Hombres FROM DFLE_AlumnosEnsenanzaLenguas_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @EM ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
SET @EIMujeres = ISNULL((SELECT Desc_Mujeres FROM DFLE_AlumnosEnsenanzaLenguas_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @EM ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
SET @PGHombres = ISNULL((SELECT Desc_Hombres FROM DFLE_AlumnosEnsenanzaLenguas_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @GEN ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
SET @PGMujeres = ISNULL((SELECT Desc_Mujeres FROM DFLE_AlumnosEnsenanzaLenguas_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @GEN ) AND id_UnidadAcademica = (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador), 0);
INSERT INTO @PoblacionAtendida VALUES( (SELECT Lengua FROM @EnsenanzaLenguas WHERE ID_Lengua = @Contador), CASE WHEN @NMSHombres = 0 THEN 0 ELSE CAST(@NMSHombres AS VARCHAR(255)) END,
CASE WHEN @NMSMujeres = 0 THEN 0 ELSE CAST(@NMSMujeres AS VARCHAR(255)) END, @NMSHombres + @NMSMujeres,
CASE WHEN @NSHombres = 0 THEN 0 ELSE CAST(@NSHombres AS VARCHAR(255)) END, CASE WHEN @NSMujeres = 0 THEN 0 ELSE CAST(@NSMujeres AS VARCHAR(255)) END, @NSHombres + @NSMujeres,
CASE WHEN @POSHombres = 0 THEN 0 ELSE CAST(@POSHombres AS VARCHAR(255)) END, CASE WHEN @POSMujeres = 0 THEN 0 ELSE CAST(@POSMujeres AS VARCHAR(255)) END, @POSHombres + @POSMujeres,
CASE WHEN @EGHombres = 0 THEN 0 ELSE CAST(@EGHombres AS VARCHAR(255)) END, CASE WHEN @EGMujeres = 0 THEN 0 ELSE CAST(@EGMujeres AS VARCHAR(255)) END, @EGHombres + @EGMujeres,
CASE WHEN @EIHombres = 0 THEN 0 ELSE CAST(@EIHombres AS VARCHAR(255)) END,
CASE WHEN @EIMujeres = 0 THEN 0 ELSE CAST(@EIMujeres AS VARCHAR(255)) END, @EIHombres + @EIMujeres, CASE WHEN @PGHombres = 0 THEN 0 ELSE CAST(@PGHombres AS VARCHAR(255)) END, CASE WHEN @PGMujeres = 0 THEN 0 ELSE CAST(@PGMujeres AS VARCHAR(255)) END, @PGHombres + @PGMujeres ) SET @Contador = @Contador + 1; END
INSERT INTO @TablaIden(Idioma) SELECT Lengua FROM @EnsenanzaLenguas; SET @Contador = 1; WHILE @Contador <= (SELECT COUNT(*) FROM @EnsenanzaLenguas) BEGIN SET @Ident_NMS = ISNULL((SELECT ID_CantidadesAlumnos FROM DFLE_AlumnosEnsenanzaLenguas_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @NMS)),0);
SET @Ident_NS = ISNULL((SELECT ID_CantidadesAlumnos FROM DFLE_AlumnosEnsenanzaLenguas_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @NS)),0);
SET @Ident_POS = ISNULL((SELECT ID_CantidadesAlumnos FROM DFLE_AlumnosEnsenanzaLenguas_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @PG)),0);
SET @Ident_EG = ISNULL((SELECT ID_CantidadesAlumnos FROM DFLE_AlumnosEnsenanzaLenguas_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @EG)),0);
SET @Ident_EI = ISNULL((SELECT ID_CantidadesAlumnos FROM DFLE_AlumnosEnsenanzaLenguas_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @EM)),0);
SET @Ident_PG = ISNULL((SELECT ID_CantidadesAlumnos FROM DFLE_AlumnosEnsenanzaLenguas_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @UnidadAcademica) AND id_Idioma = @Contador AND id_NivelEducativo = (SELECT id_NivelEducativo FROM DFLE_NivelEducativo WHERE Desc_NivelEducativo = @GEN)),0);
UPDATE @TablaIden SET Ident_NMS = @Ident_NMS, Ident_NS = @Ident_NS, Ident_POS = @Ident_POS, Ident_EG = @Ident_EG, Ident_EI = @Ident_EI, Ident_PG = @Ident_PG WHERE Idioma = (SELECT Lengua FROM @EnsenanzaLenguas WHERE ID_Lengua = @Contador); SET @Contador = @Contador + 1; END END
SELECT * FROM @PoblacionAtendida; SELECT * FROM @TablaIden; END

--Formato 10: Procedimiento para retornar la informacion a la interfaz que representa el formato 10
CREATE PROCEDURE DFLE_InterEnsenanzaLenguasUA
    @TipoUnidadAcademica VARCHAR(255),
    @UnidadAcademica VARCHAR(255),
    @Anio INT,
    @id_Trimestre INT,
    @Final varchar(15)
AS

BEGIN -- SET NOCOUNT ON; DECLARE @LenguasRegistro TABLE( AntNMS INT NOT NULL DEFAULT 0, AntNS INT NOT NULL DEFAULT 0, AntCINV INT NOT NULL DEFAULT 0, AntCVDR INT NOT NULL DEFAULT 0, AntCIITA INT NOT NULL DEFAULT 0, AntCENLEX INT NOT NULL DEFAULT 0, AntTotal INT NOT NULL DEFAULT 0,
ActNMS INT NOT NULL DEFAULT 0, ActNS INT NOT NULL DEFAULT 0, ActCINV INT NOT NULL DEFAULT 0, ActCVDR INT NOT NULL DEFAULT 0, ActCIITA INT NOT NULL DEFAULT 0, ActCENLEX INT NOT NULL DEFAULT 0, ActTotal INT NOT NULL DEFAULT 0,
Variacion VARCHAR(255) NOT NULL DEFAULT ' ', Justicacion VARCHAR(255) NOT NULL DEFAULT ' ' );
DECLARE @Identificadores TABLE( IdentificadorJustificacion INT );
DECLARE @AntNMS INT = 0; DECLARE @AntNS INT = 0; DECLARE @AntCINV INT = 0; DECLARE @AntCVDR INT = 0; DECLARE @AntCIITA INT = 0; DECLARE @AntCENLEX INT = 0; DECLARE @AntTotal INT = 0;
DECLARE @ActNMS INT = 0; DECLARE @ActNS INT = 0; DECLARE @ActCINV INT = 0; DECLARE @ActCVDR INT = 0; DECLARE @ActCIITA INT = 0; DECLARE @ActCENLEX INT = 0; DECLARE @ActTotal INT = 0;
DECLARE @Variacion DECIMAL(10,2) = 0; DECLARE @Justicacion VARCHAR(255) = ' ';
DECLARE @AnioAnterior INT = @anio - 1;
DECLARE @ContadorTrimestre INT = 1;
IF @Final = 'FINAL' BEGIN WHILE @ContadorTrimestre <= @id_trimestre BEGIN IF @Idioma = 'TOTAL' BEGIN SET @AntNMS = @AntNMS + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'NMS')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre), 0);
SET @AntNS = @AntNS + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'NS')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre),0);
SET @AntCINV = @AntCINV + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'C INV')) AND id_Anio = (SELECT id_Anio from Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre),0);
SET @AntCVDR = @AntCVDR + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'UAVDR')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre),0);
SET @AntCIITA = @AntCIITA + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'CIITA')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre),0);
SET @AntCENLEX = @AntCENLEX + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'UAAE')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre),0);
SET @ActNMS = @ActNMS + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'NMS')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre),0);
SET @ActNS = @ActNS + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'NS')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre),0);
SET @ActCINV = @ActCINV + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'C INV')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre),0);
SET @ActCVDR = @ActCVDR + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'UAVDR')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre),0);
SET @ActCIITA = @ActCIITA + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'CIITA')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre),0);
SET @ActCENLEX = @ActCENLEX + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'UAAE')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre),0); END ELSE BEGIN SET @AntNMS = @AntNMS + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'NMS')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)), 0); SET @AntNS = @AntNS + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'NS')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)),0);
SET @AntCINV = @AntCINV + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'C INV')) AND id_Anio = (SELECT id_Anio from Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)),0);
SET @AntCVDR = @AntCVDR + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'UAVDR')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)),0);
SET @AntCIITA = @AntCIITA + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'CIITA')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)),0);
SET @AntCENLEX = @AntCENLEX + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'UAAE')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)),0);
SET @ActNMS = @ActNMS + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'NMS')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)),0);
SET @ActNS = @ActNS + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'NS')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)),0);
SET @ActCINV = @ActCINV + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'C INV')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)),0);
SET @ActCVDR = @ActCVDR + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'UAVDR')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)),0);
SET @ActCIITA = @ActCIITA + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'CIITA')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)),0);
SET @ActCENLEX = @ActCENLEX + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'UAAE')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)),0); END SET @ContadorTrimestre = @ContadorTrimestre + 1; END SET @AntTotal = @AntNMS + @AntNS + @AntCINV + @AntCVDR + @AntCIITA + @AntCENLEX; SET @ActTotal = @ActNMS + @ActNS + @ActCINV + @ActCVDR + @ActCIITA + @ActCENLEX;
SET @Variacion = CASE WHEN @AntTotal = 0 THEN 0 ELSE ((@ActTotal/ @AntTotal)-1) END;
SET @Justicacion = ISNULL((SELECT Desc_Justificacion FROM DFLE_JustificacionesFormato5_9 WHERE id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_trimestre AND id_FormatoAutoevaluacion = (SELECT ID_Formato FROM Formatos WHERE Desc_NumeroFormato = 5 AND id_DependenciaEvaluada = (SELECT ID_DependenciasEvaluadas FROM Dependencias_Evaluadas WHERE Desc_SiglasDependencia = 'DFLE'))), ' ');
INSERT INTO @LenguasRegistro VALUES(@AntNMS, @AntNS, @AntCINV, @AntCVDR, @AntCIITA, @AntCENLEX, @AntTotal, @ActNMS, @ActNS, @ActCINV, @ActCVDR, @ActCIITA, @ActCENLEX, @ActTotal, CASE WHEN @AntTotal = 0 AND @Variacion = 0 THEN ' ' ELSE CONCAT(@Variacion, '%') END, ISNULL(@Justicacion, ' '));
INSERT INTO @Identificadores( IdentificadorJustificacion )SELECT ID_JustificacionesFormato5_9 FROM DFLE_JustificacionesFormato5_9 WHERE id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_trimestre AND id_FormatoAutoevaluacion = (SELECT ID_Formato FROM Formatos WHERE Desc_NumeroFormato = 5 AND id_DependenciaEvaluada = (SELECT ID_DependenciasEvaluadas FROM Dependencias_Evaluadas WHERE Desc_SiglasDependencia = 'DFLE')); END ELSE BEGIN WHILE @ContadorTrimestre <= @id_trimestre BEGIN SET @AntNMS = @AntNMS + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'NMS')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)), 0); SET @AntNS = @AntNS + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'NS')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)),0);
SET @AntCINV = @AntCINV + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'C INV')) AND id_Anio = (SELECT id_Anio from Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)),0);
SET @AntCVDR = @AntCVDR + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'UAVDR')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)),0);
SET @AntCIITA = @AntCIITA + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'CIITA')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)),0);
SET @AntCENLEX = @AntCENLEX + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'UAAE')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)),0);
SET @ActNMS = @ActNMS + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI_Temporal WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'NMS')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)),0);
SET @ActNS = @ActNS + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI_Temporal WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'NS')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)),0);
SET @ActCINV = @ActCINV + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI_Temporal WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'C INV')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)),0);
SET @ActCVDR = @ActCVDR + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI_Temporal WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'UAVDR')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)),0);
SET @ActCIITA = @ActCIITA + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI_Temporal WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'CIITA')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)),0);
SET @ActCENLEX = @ActCENLEX + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI_Temporal WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'UAAE')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma)),0);
SET @ContadorTrimestre = @ContadorTrimestre + 1; END SET @AntTotal = @AntNMS + @AntNS + @AntCINV + @AntCVDR + @AntCIITA + @AntCENLEX; SET @ActTotal = @ActNMS + @ActNS + @ActCINV + @ActCVDR + @ActCIITA + @ActCENLEX;
SET @Variacion = CASE WHEN @AntTotal = 0 THEN 0 ELSE ((@ActTotal/ @AntTotal)-1) END;
SET @Justicacion = ISNULL((SELECT Desc_Justificacion FROM DFLE_JustificacionesFormato5_9_Temporal WHERE id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_trimestre AND id_FormatoAutoevaluacion = (SELECT ID_Formato FROM Formatos WHERE Desc_NumeroFormato = 5 AND id_DependenciaEvaluada = (SELECT ID_DependenciasEvaluadas FROM Dependencias_Evaluadas WHERE Desc_SiglasDependencia = 'DFLE'))), 0);
INSERT INTO @LenguasRegistro VALUES(@AntNMS, @AntNS, @AntCINV, @AntCVDR, @AntCIITA, @AntCENLEX, @AntTotal, @ActNMS, @ActNS, @ActCINV, @ActCVDR, @ActCIITA, @ActCENLEX, @ActTotal, CASE WHEN @AntTotal = 0 AND @Variacion = 0 THEN ' ' ELSE CONCAT(@Variacion, '%') END, ISNULL(@Justicacion, ' '));
INSERT INTO @Identificadores( IdentificadorJustificacion )SELECT ID_JustificacionesFormato5_9 FROM DFLE_JustificacionesFormato5_9_Temporal WHERE id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_trimestre AND id_FormatoAutoevaluacion = (SELECT ID_Formato FROM Formatos WHERE Desc_NumeroFormato = 5 AND id_DependenciaEvaluada = (SELECT ID_DependenciasEvaluadas FROM Dependencias_Evaluadas WHERE Desc_SiglasDependencia = 'DFLE')) END
SELECT * FROM @LenguasRegistro; SELECT * FROM @Identificadores; END

--Procedimiento para el rellenado de la interfaz que representa el fomato 5

CREATE PROCEDURE DFLE_InterLenguasRegistroComparativo
    @Idioma varchar(255),
    @anio INT,
    @id_trimestre INT,
    @Final varchar(15)
AS

BEGIN SET NOCOUNT ON; DECLARE @PGIAlumnosAtendidos TABLE( PGI VARCHAR(10) NOT NULL DEFAULT '', Idioma VARCHAR(255) NOT NULL DEFAULT '',
IntroductorioH INT NOT NULL DEFAULT 0, IntroductorioM INT NOT NULL DEFAULT 0, IntroductorioT INT NOT NULL DEFAULT 0, BasicoH INT NOT NULL DEFAULT 0, BasicoM INT NOT NULL DEFAULT 0, BasicoT INT NOT NULL DEFAULT 0, IntermedioH INT NOT NULL DEFAULT 0, IntermedioM INT NOT NULL DEFAULT 0, IntermedioT INT NOT NULL DEFAULT 0, AvanzadoH INT NOT NULL DEFAULT 0, AvanzadoM INT NOT NULL DEFAULT 0, AvanzadoT INT NOT NULL DEFAULT 0, SuperiorH INT NOT NULL DEFAULT 0, SuperiorM INT NOT NULL DEFAULT 0, SuperiorT INT NOT NULL DEFAULT 0 );
DECLARE @Identificadores TABLE( PGI VARCHAR(10) NOT NULL DEFAULT '', Idioma VARCHAR(255) NOT NULL DEFAULT '',
IdentificadorIntroductorio INT NOT NULL DEFAULT 0, IdentificadorBasico INT NOT NULL DEFAULT 0, IdentificadorIntermedio INT NOT NULL DEFAULT 0, IdentificadorAvanzado INT NOT NULL DEFAULT 0, IdentificadorSuperior INT NOT NULL DEFAULT 0 );
DECLARE @Identificador INT; DECLARE @Desc_Hombres INT = 0; DECLARE @Desc_Mujeres INT = 0; DECLARE @Total INT = 0;
DECLARE @ContadorIdiomas INT = 1;
DECLARE @ContadorCompetencia INT = 1;
IF @Final = 'FINAL' BEGIN INSERT INTO @PGIAlumnosAtendidos(PGI, Idioma) SELECT Desc_PGI, Desc_Idioma FROM DFLE_Idiomas WHERE Desc_PGI != ' ';
INSERT INTO @Identificadores(PGI, Idioma) SELECT Desc_PGI, Desc_Idioma FROM DFLE_Idiomas WHERE Desc_PGI != ' ';
WHILE @ContadorIdiomas <= (SELECT COUNT(*) FROM DFLE_Idiomas) BEGIN SET @ContadorCompetencia = 1; WHILE @ContadorCompetencia <= (SELECT COUNT(*) FROM DFLE_NivelesCompetenciaPGI) BEGIN SET @Desc_Hombres = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_Idioma = @ContadorIdiomas AND id_CompetenciaPGI = @ContadorCompetencia AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0);
SET @Desc_Mujeres = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_Idioma = @ContadorIdiomas AND id_CompetenciaPGI = @ContadorCompetencia AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0);
SET @Identificador = ISNULL((SELECT ID_AlumnosLenguasPGI FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_Idioma = @ContadorIdiomas AND id_CompetenciaPGI = @ContadorCompetencia AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0);
SET @Total = @Desc_Hombres + @Desc_Mujeres;
IF @ContadorCompetencia = 1 BEGIN UPDATE @PGIAlumnosAtendidos SET IntroductorioH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, IntroductorioM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, IntroductorioT = CAST(@Total AS VARCHAR(10)) WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas);
UPDATE @Identificadores SET IdentificadorIntroductorio = @Identificador WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas); END ELSE IF @ContadorCompetencia = 2 BEGIN UPDATE @PGIAlumnosAtendidos SET BasicoH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, BasicoM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, BasicoT = CAST(@Total AS VARCHAR(10)) WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas);
UPDATE @Identificadores SET IdentificadorBasico = @Identificador WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas); END ELSE IF @ContadorCompetencia = 3 BEGIN UPDATE @PGIAlumnosAtendidos SET IntermedioH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, IntermedioM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, IntermedioT = CAST(@Total AS VARCHAR(10)) WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas);
UPDATE @Identificadores SET IdentificadorIntermedio = @Identificador WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas); END ELSE IF @ContadorCompetencia = 4 BEGIN UPDATE @PGIAlumnosAtendidos SET AvanzadoH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, AvanzadoM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, AvanzadoT = CAST(@Total AS VARCHAR(10)) WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas);
UPDATE @Identificadores SET IdentificadorAvanzado = @Identificador WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas); END ELSE IF @ContadorCompetencia = 5 BEGIN UPDATE @PGIAlumnosAtendidos SET SuperiorH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, SuperiorM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, SuperiorT = CAST(@Total AS VARCHAR(10)) WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas);
UPDATE @Identificadores SET IdentificadorSuperior = @Identificador WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas); END SET @ContadorCompetencia = @ContadorCompetencia + 1; END
SET @ContadorIdiomas = @ContadorIdiomas + 1; END END ELSE BEGIN INSERT INTO @PGIAlumnosAtendidos(PGI, Idioma) SELECT Desc_PGI, Desc_Idioma FROM DFLE_Idiomas WHERE Desc_PGI != ' ';
INSERT INTO @Identificadores(PGI, Idioma) SELECT Desc_PGI, Desc_Idioma FROM DFLE_Idiomas WHERE Desc_PGI != ' ';
WHILE @ContadorIdiomas <= (SELECT COUNT(*) FROM DFLE_Idiomas) BEGIN SET @ContadorCompetencia = 1; WHILE @ContadorCompetencia <= (SELECT COUNT(*) FROM DFLE_NivelesCompetenciaPGI) BEGIN SET @Desc_Hombres = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosLenguasPGI_Temporal WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_Idioma = @ContadorIdiomas AND id_CompetenciaPGI = @ContadorCompetencia AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0);
SET @Desc_Mujeres = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosLenguasPGI_Temporal WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_Idioma = @ContadorIdiomas AND id_CompetenciaPGI = @ContadorCompetencia AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0);
SET @Identificador = ISNULL((SELECT ID_AlumnosLenguasPGI FROM DFLE_AlumnosLenguasPGI_Temporal WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_Idioma = @ContadorIdiomas AND id_CompetenciaPGI = @ContadorCompetencia AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0);
SET @Total = @Desc_Hombres + @Desc_Mujeres;
IF @ContadorCompetencia = 1 BEGIN UPDATE @PGIAlumnosAtendidos SET IntroductorioH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, IntroductorioM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, IntroductorioT = CAST(@Total AS VARCHAR(10)) WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas);
UPDATE @Identificadores SET IdentificadorIntroductorio = @Identificador WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas); END ELSE IF @ContadorCompetencia = 2 BEGIN UPDATE @PGIAlumnosAtendidos SET BasicoH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, BasicoM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, BasicoT = CAST(@Total AS VARCHAR(10)) WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas);
UPDATE @Identificadores SET IdentificadorBasico = @Identificador WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas); END ELSE IF @ContadorCompetencia = 3 BEGIN UPDATE @PGIAlumnosAtendidos SET IntermedioH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, IntermedioM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, IntermedioT = CAST(@Total AS VARCHAR(10)) WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas);
UPDATE @Identificadores SET IdentificadorIntermedio = @Identificador WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas); END ELSE IF @ContadorCompetencia = 4 BEGIN UPDATE @PGIAlumnosAtendidos SET AvanzadoH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, AvanzadoM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, AvanzadoT = CAST(@Total AS VARCHAR(10)) WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas);
UPDATE @Identificadores SET IdentificadorAvanzado = @Identificador WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas); END ELSE IF @ContadorCompetencia = 5 BEGIN UPDATE @PGIAlumnosAtendidos SET SuperiorH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, SuperiorM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, SuperiorT = CAST(@Total AS VARCHAR(10)) WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas);
UPDATE @Identificadores SET IdentificadorSuperior = @Identificador WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas); END SET @ContadorCompetencia = @ContadorCompetencia + 1; END
SET @ContadorIdiomas = @ContadorIdiomas + 1; END END
SELECT * FROM @PGIAlumnosAtendidos SELECT * FROM @Identificadores END

CREATE PROCEDURE DFLE_InterPGIAlumnosAtendidos
    @SiglasUnidadAcademica VARCHAR(255),
    @anio INT,
    @id_Trimestre INT,
    @Final VARCHAR(255)
AS

BEGIN SET NOCOUNT ON; DECLARE @AlumnosAtendidosLenguasComparativo TABLE( AntCenlexH INT NOT NULL DEFAULT 0, AntCenlexM INT NOT NULL DEFAULT 0, AntCenlexTotal INT NOT NULL DEFAULT 0,
AntCelexH INT NOT NULL DEFAULT 0, AntCelexM INT NOT NULL DEFAULT 0, AntCelexTotal INT NOT NULL DEFAULT 0,
AnteriorTotal INT NOT NULL DEFAULT 0,
ActCenlexH INT NOT NULL DEFAULT 0, ActCenlexM INT NOT NULL DEFAULT 0, ActCenlexTotal INT NOT NULL DEFAULT 0,
ActCelexH INT NOT NULL DEFAULT 0, ActCelexM INT NOT NULL DEFAULT 0, ActCelexTotal INT NOT NULL DEFAULT 0,
ActSubHombres INT NOT NULL DEFAULT 0, ActSubMujeres INT NOT NULL DEFAULT 0, ActualTotal INT NOT NULL DEFAULT 0,
Variacion VARCHAR(255) NOT NULL DEFAULT ' ',
Justificacion VARCHAR(255) NOT NULL DEFAULT ' ' );
DECLARE @Identificadores TABLE( IdentificadorJustificacion INT );
DECLARE @CenlexSantoTomas VARCHAR(255) = 'CENLEX UST'; DECLARE @CenlexZacatenco VARCHAR(255) = 'CENLEX Zacatenco'; DECLARE @AnioAnterior INT = @anio - 1;
DECLARE @AntCenlexH INT = 0; DECLARE @AntCenlexM INT = 0; DECLARE @AntCenlexTotal INT = 0;
DECLARE @AntCelexH INT = 0; DECLARE @AntCelexM INT = 0; DECLARE @AntCelexTotal INT = 0;
DECLARE @AnteriorTotal INT = 0;
DECLARE @ActCenlexH INT = 0; DECLARE @ActCenlexM INT = 0; DECLARE @ActCenlexTotal INT = 0;
DECLARE @ActCelexH INT = 0; DECLARE @ActCelexM INT = 0; DECLARE @ActCelexTotal INT = 0;
DECLARE @ActSubHombres INT = 0; DECLARE @ActSubMujeres INT = 0; DECLARE @ActualTotal INT = 0;
DECLARE @Variacion DECIMAL(10, 2) = 0; DECLARE @Justificacion VARCHAR(255) = '';
DECLARE @ContadorTrimestres INT = 1;
IF @Final = 'FINAL' BEGIN WHILE @ContadorTrimestres <= @id_Trimestre BEGIN -- CENLEX Anio Anterior SET @AntCenlexH = @AntCenlexH + ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestres), 0); SET @AntCenlexM = @AntCenlexM + ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestres), 0);
-- CELEX Anio Anterior SET @AntCelexH = @AntCelexH + ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica NOT IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestres), 0); SET @AntCelexM = @AntCelexM + ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica NOT IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestres), 0);
-- CENLEX Anio Actual SET @ActCenlexH = @ActCenlexH + ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestres), 0); SET @ActCenlexM = @ActCenlexM + ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestres), 0);
-- CELEX Anio Actual SET @ActCelexH = @ActCelexH + ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica NOT IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestres), 0); SET @ActCelexM = @ActCelexM + ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica NOT IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestres), 0);
SET @ContadorTrimestres = @ContadorTrimestres + 1; END
SET @AntCenlexTotal = @AntCenlexH + @AntCenlexM; SET @AntCelexTotal = @AntCelexH + @AntCelexM; SET @AnteriorTotal = @AntCenlexTotal + @AntCelexTotal;
SET @ActCenlexTotal = @ActCenlexH + @ActCenlexM; SET @ActCelexTotal = @ActCelexH + @ActCelexM; SET @ActSubHombres = @ActCenlexH + @ActCelexH; SET @ActSubMujeres = @ActCenlexM + @ActCelexM; SET @ActualTotal = @ActCenlexTotal + @ActCelexTotal;
SET @Variacion = CASE WHEN @AnteriorTotal <> 0 THEN (((@ActualTotal - @AnteriorTotal) * 100) / @AnteriorTotal) ELSE 0 END; SET @Justificacion = (SELECT Desc_Justificacion FROM DFLE_JustificacionesFormato5_9 WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_FormatoAutoevaluacion = (SELECT ID_Formato FROM Formatos WHERE Desc_NumeroFormato = 9 AND id_DependenciaEvaluada = (SELECT ID_DependenciasEvaluadas FROM Dependencias_Evaluadas WHERE Desc_SiglasDependencia = 'DFLE')));
INSERT INTO @AlumnosAtendidosLenguasComparativo VALUES( @AntCenlexH, @AntCenlexM, @AntCenlexTotal, @AntCelexH, @AntCelexM, @AntCelexTotal, @AnteriorTotal, @ActCenlexH, @ActCenlexM, @ActCenlexTotal, @ActCelexH, @ActCelexM, @ActCelexTotal, @ActSubHombres, @ActSubMujeres, @ActualTotal, CASE WHEN @AnteriorTotal = 0 AND @Variacion = 0 THEN ' ' ELSE CONCAT(@Variacion, '%') END, ISNULL(@Justificacion, ' ') );
INSERT INTO @Identificadores VALUES( (SELECT ID_JustificacionesFormato5_9 FROM DFLE_JustificacionesFormato5_9 WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_FormatoAutoevaluacion = (SELECT ID_Formato FROM Formatos WHERE Desc_NumeroFormato = 9 AND id_DependenciaEvaluada = (SELECT ID_DependenciasEvaluadas FROM Dependencias_Evaluadas WHERE Desc_SiglasDependencia = 'DFLE'))) ); END ELSE BEGIN WHILE @ContadorTrimestres <= @id_Trimestre BEGIN -- CENLEX Anio Anterior SET @AntCenlexH = @AntCenlexH + ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestres), 0); SET @AntCenlexM = @AntCenlexM + ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestres), 0);
-- CELEX Anio Anterior SET @AntCelexH = @AntCelexH + ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica NOT IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestres), 0); SET @AntCelexM = @AntCelexM + ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica NOT IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestres), 0);
-- CENLEX Anio Actual SET @ActCenlexH = @ActCenlexH + ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas_Temporal WHERE id_UnidadAcademica IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestres), 0); SET @ActCenlexM = @ActCenlexM + ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas_Temporal WHERE id_UnidadAcademica IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestres), 0);
-- CELEX Anio Actual SET @ActCelexH = @ActCelexH + ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas_Temporal WHERE id_UnidadAcademica NOT IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestres), 0); SET @ActCelexM = @ActCelexM + ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas_Temporal WHERE id_UnidadAcademica NOT IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestres), 0);
SET @ContadorTrimestres = @ContadorTrimestres + 1; END
SET @AntCenlexTotal = @AntCenlexH + @AntCenlexM; SET @AntCelexTotal = @AntCelexH + @AntCelexM; SET @AnteriorTotal = @AntCenlexTotal + @AntCelexTotal;
SET @ActCenlexTotal = @ActCenlexH + @ActCenlexM; SET @ActCelexTotal = @ActCelexH + @ActCelexM; SET @ActSubHombres = @ActCenlexH + @ActCelexH; SET @ActSubMujeres = @ActCenlexM + @ActCelexM; SET @ActualTotal = @ActCenlexTotal + @ActCelexTotal;
SET @Variacion = CASE WHEN @AnteriorTotal <> 0 THEN (((@ActualTotal - @AnteriorTotal) * 100) / @AnteriorTotal) ELSE 0 END; SET @Justificacion = (SELECT Desc_Justificacion FROM DFLE_JustificacionesFormato5_9_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_FormatoAutoevaluacion = (SELECT ID_Formato FROM Formatos WHERE Desc_NumeroFormato = 9 AND id_DependenciaEvaluada = (SELECT ID_DependenciasEvaluadas FROM Dependencias_Evaluadas WHERE Desc_SiglasDependencia = 'DFLE')));
INSERT INTO @AlumnosAtendidosLenguasComparativo VALUES( @AntCenlexH, @AntCenlexM, @AntCenlexTotal, @AntCelexH, @AntCelexM, @AntCelexTotal, @AnteriorTotal, @ActCenlexH, @ActCenlexM, @ActCenlexTotal, @ActCelexH, @ActCelexM, @ActCelexTotal, @ActSubHombres, @ActSubMujeres, @ActualTotal, CASE WHEN @AnteriorTotal = 0 AND @Variacion = 0 THEN ' ' ELSE CONCAT(@Variacion, '%') END, ISNULL(@Justificacion, ' ') );
INSERT INTO @Identificadores VALUES( (SELECT ID_JustificacionesFormato5_9 FROM DFLE_JustificacionesFormato5_9_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @Idioma) AND id_FormatoAutoevaluacion = (SELECT ID_Formato FROM Formatos WHERE Desc_NumeroFormato = 9 AND id_DependenciaEvaluada = (SELECT ID_DependenciasEvaluadas FROM Dependencias_Evaluadas WHERE Desc_SiglasDependencia = 'DFLE'))) ); END
SELECT * FROM @AlumnosAtendidosLenguasComparativo; SELECT * FROM @Identificadores; END

--Procedimiento para el rellenado de la informacion temporal de la interfaz que representa al formato 9
CREATE PROCEDURE DFLE_InterPromedioAlumnosAtendidosComparativo
    @Idioma varchar(255),
    @anio INT,
    @id_Trimestre INT,
    @Final VARCHAR(255)
AS

BEGIN
    SET NOCOUNT ON;
    DECLARE @PeriodoAnterior INT
    DECLARE @PeriodoActual INT
    DECLARE @VariacionPorcentual DECIMAL(5, 2)
    DECLARE @Justificacion NVARCHAR(MAX)

    DECLARE @Comparativo TABLE (
        PeriodoAnterior INT NOT NULL DEFAULT 0,
        PeriodoActual INT NOT NULL DEFAULT 0,
        VariacionPorcentual VARCHAR(10) NOT NULL DEFAULT ' ',
        Justificacion NVARCHAR(MAX) NOT NULL DEFAULT ' '
    );

    DECLARE @Identificadores TABLE(
        IdentificadorJustificacion INT
    );

    DECLARE @UA TABLE(
        ID_UnidadAcademica INT IDENTITY(1,1),
        SiglasUnidad varchar(255)
    );

    INSERT INTO @UA (
        SiglasUnidad
    )SELECT Siglas 
    FROM UnidadesAcademicas 
    WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica 
                                    FROM TipoUnidadAcademica 
                                    WHERE Desc_SiglasTipo = @TipoUnidadAcademica);

    DECLARE @AcumuladoActual INT = 0;
    DECLARE @AcumuladoAnterior INT = 0;
    DECLARE @contadorUA INT = 1;

    DECLARE @SiglasUAActual varchar(255) = '';

    IF @Final = 'FINAL'
    BEGIN
        WHILE @contadorUA <= (SELECT COUNT(ID_UnidadAcademica) FROM @UA) 
        BEGIN
            SET @SiglasUAActual = (SELECT SiglasUnidad 
                                                    FROM @UA 
                                                    WHERE ID_UnidadAcademica = @contadorUA)
            SET @AcumuladoActual = @AcumuladoActual + CASE WHEN EXISTS(SELECT * 
                                                                FROM DFLE_AlumnosEnsenanzaLenguas 
                                                                WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica 
                                                                                            FROM UnidadesAcademicas 
                                                                                            WHERE Siglas = @SiglasUAActual) 
                                                                AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)
                                                                AND id_Trimestre = 2) THEN 1 ELSE 0 END;
            SET @AcumuladoAnterior = @AcumuladoAnterior + CASE WHEN EXISTS(SELECT * 
                                                            FROM DFLE_AlumnosEnsenanzaLenguas
                                                            WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica 
                                                                                        FROM UnidadesAcademicas 
                                                                                        WHERE Siglas = @SiglasUAActual) 
                                                            AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio - 1)
                                                            AND id_Trimestre = 2) THEN 1 ELSE 0 END;
            SET @contadorUA = @contadorUA + 1;
        END;

        SET @Justificacion = ISNULL((SELECT TOP(1) Desc_Justificacion 
                                FROM DFLE_JustificacionesUACelex 
                                WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) 
                                AND id_Trimestre = @id_Trimestre
                                AND id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica 
                                                            FROM TipoUnidadAcademica
                                                            WHERE Desc_SiglasTipo = @TipoUnidadAcademica)), ' ');

        INSERT INTO @Identificadores(
            IdentificadorJustificacion
        )SELECT ID_JustificacionesUACelex 
        FROM DFLE_JustificacionesUACelex 
        WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) 
        AND id_Trimestre = @id_Trimestre
        AND id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica 
                                    FROM TipoUnidadAcademica
                                    WHERE Desc_SiglasTipo = @TipoUnidadAcademica);

        SET @VariacionPorcentual = CASE WHEN @AcumuladoAnterior != 0
                                    THEN (@AcumuladoActual - @AcumuladoAnterior) * 100 / @AcumuladoAnterior
                                    ELSE ' ' END;

        INSERT INTO @Comparativo(
            PeriodoAnterior,
            PeriodoActual,
            VariacionPorcentual,
            Justificacion
        )VALUES(
            @AcumuladoAnterior,
            @AcumuladoActual,
            @VariacionPorcentual,
            @Justificacion
        );
    END
    ELSE
    BEGIN
        WHILE @contadorUA <= (SELECT COUNT(ID_UnidadAcademica) FROM @UA) 
        BEGIN
            SET @SiglasUAActual = (SELECT SiglasUnidad 
                                                    FROM @UA 
                                                    WHERE ID_UnidadAcademica = @contadorUA)
            SET @AcumuladoActual = @AcumuladoActual + CASE WHEN EXISTS(SELECT * 
                                                                FROM DFLE_AlumnosEnsenanzaLenguas_Temporal
                                                                WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica 
                                                                                            FROM UnidadesAcademicas 
                                                                                            WHERE Siglas = @SiglasUAActual) 
                                                                AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)
                                                                AND id_Trimestre = 2) THEN 1 ELSE 0 END;
            SET @AcumuladoAnterior = @AcumuladoAnterior + CASE WHEN EXISTS(SELECT * 
                                                            FROM DFLE_AlumnosEnsenanzaLenguas
                                                            WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica 
                                                                                        FROM UnidadesAcademicas 
                                                                                        WHERE Siglas = @SiglasUAActual) 
                                                            AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio - 1)
                                                            AND id_Trimestre = 2) THEN 1 ELSE 0 END;
            SET @contadorUA = @contadorUA + 1;
        END;

        SET @Justificacion = ISNULL((SELECT TOP(1) Desc_Justificacion 
                                FROM DFLE_JustificacionesUACelex_Temporal
                                WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) 
                                AND id_Trimestre = @id_Trimestre
                                AND id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica 
                                                            FROM TipoUnidadAcademica
                                                            WHERE Desc_SiglasTipo = @TipoUnidadAcademica)), ' ');

        INSERT INTO @Identificadores(
            IdentificadorJustificacion
        )SELECT ID_JustificacionesUACelex 
        FROM DFLE_JustificacionesUACelex_Temporal
        WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) 
        AND id_Trimestre = @id_Trimestre
        AND id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica 
                                    FROM TipoUnidadAcademica
                                    WHERE Desc_SiglasTipo = @TipoUnidadAcademica);

        SET @VariacionPorcentual = CASE WHEN @AcumuladoAnterior != 0
                                    THEN (@AcumuladoActual - @AcumuladoAnterior) * 100 / @AcumuladoAnterior
                                    ELSE ' ' END;

        INSERT INTO @Comparativo(
            PeriodoAnterior,
            PeriodoActual,
            VariacionPorcentual,
            Justificacion
        )VALUES(
            @AcumuladoAnterior,
            @AcumuladoActual,
            @VariacionPorcentual,
            @Justificacion
        );
    END

    SELECT * FROM @Comparativo
    SELECT * FROM @Identificadores
END

--Procedimiento para la interfaz que representa el ingreso de informacion del formato 2

CREATE PROCEDURE DFLE_InterUACelexComparativo
    @TipoUnidadAcademica varchar(255),
    @anio INT,
    @id_Trimestre INT,
    @Final VARCHAR(255)
AS

BEGIN SET NOCOUNT ON; DECLARE @TablaUnidadesAcademicas TABLE ( UnidadAcademica VARCHAR(255), ID_UnidadAcademica INT IDENTITY (1,1) ); INSERT INTO @TablaUnidadesAcademicas SELECT Siglas FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @SiglasTipoUnidadAcademico);
DECLARE @TablaSupervision TABLE( SiglasUA VARCHAR(255), TrimestreUno INT NOT NULL DEFAULT 0, TrimestreDos INT NOT NULL DEFAULT 0, TrimestreTres INT NOT NULL DEFAULT 0, TrimestreCuatro INT NOT NULL DEFAULT 0,
Total INT NOT NULL DEFAULT 0 ) INSERT INTO @TablaSupervision(SiglasUA) SELECT Siglas FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @SiglasTipoUnidadAcademico); DECLARE @TablaIDs TABLE( SiglasUA VARCHAR(255), Ident_TrimestreUno INT, Ident_TrimestreDos INT, Ident_TrimestreTres INT, Ident_TrimestreCuatro INT ); INSERT INTO @TablaIDs(SiglasUA) SELECT Siglas FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @SiglasTipoUnidadAcademico);
DECLARE @SiglasUA VARCHAR(255); DECLARE @contadorUA INT = 1; DECLARE @TrimeUno INT = 0; DECLARE @TrimeDos INT = 0; DECLARE @TrimeTres INT = 0; DECLARE @TrimeCuatro INT = 0; DECLARE @Total INT = 0;
DECLARE @IdentUno INT = 0; DECLARE @IdentDos INT = 0; DECLARE @IdentTres INT = 0; DECLARE @IdentCuatro INT = 0;
IF @Final = 'FINAL' BEGIN WHILE @contadorUA <= (SELECT COUNT(*) FROM @TablaUnidadesAcademicas) BEGIN SET @SiglasUA = (SELECT UnidadAcademica FROM @TablaUnidadesAcademicas WHERE ID_UnidadAcademica = @contadorUA);
SET @TrimeUno = ISNULL((SELECT TOP(1) TRIM_UNO_Supervicion FROM DFLE_SupervicionCelex WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 1 AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA)),0); SET @TrimeDos = ISNULL((SELECT TOP(1) TRIM_UNO_Supervicion FROM DFLE_SupervicionCelex WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 2 AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA)),0); SET @TrimeTres = ISNULL((SELECT TOP(1) TRIM_UNO_Supervicion FROM DFLE_SupervicionCelex WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 3 AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA)),0); SET @TrimeCuatro = ISNULL((SELECT TOP(1) TRIM_UNO_Supervicion FROM DFLE_SupervicionCelex WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 4 AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA)),0);
SET @Total = @TrimeUno + @TrimeDos + @TrimeTres + @TrimeCuatro; SET @contadorUA = @contadorUA + 1;
UPDATE @TablaSupervision SET SiglasUA = @SiglasUA, TrimestreUno = @TrimeUno, TrimestreDos = @TrimeDos, TrimestreTres = @TrimeTres, TrimestreCuatro = @TrimeCuatro, Total = @Total WHERE SiglasUA = @SiglasUA; END
--****************************************************************************-- SET @contadorUA = 1; WHILE @contadorUA <= (SELECT COUNT(*) FROM @TablaUnidadesAcademicas) BEGIN SET @SiglasUA = (SELECT UnidadAcademica FROM @TablaUnidadesAcademicas WHERE ID_UnidadAcademica = @contadorUA);
SET @IdentUno = ISNULL((SELECT TOP(1) ID_SupervicionCelex FROM DFLE_SupervicionCelex WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 1 AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA)),0); SET @IdentDos = ISNULL((SELECT TOP(1) ID_SupervicionCelex FROM DFLE_SupervicionCelex WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 2 AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA)),0); SET @IdentTres = ISNULL((SELECT TOP(1) ID_SupervicionCelex FROM DFLE_SupervicionCelex WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 3 AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA)),0); SET @IdentCuatro = ISNULL((SELECT TOP(1) ID_SupervicionCelex FROM DFLE_SupervicionCelex WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 4 AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA)),0); SET @contadorUA = @contadorUA + 1;
UPDATE @TablaIDs SET Ident_TrimestreUno = @IdentUno, Ident_TrimestreDos = @IdentDos, Ident_TrimestreTres = @IdentTres, Ident_TrimestreCuatro = @IdentCuatro WHERE SiglasUA = @SiglasUA; END END ELSE BEGIN WHILE @contadorUA <= (SELECT COUNT(*) FROM @TablaUnidadesAcademicas) BEGIN SET @SiglasUA = (SELECT UnidadAcademica FROM @TablaUnidadesAcademicas WHERE ID_UnidadAcademica = @contadorUA);
SET @TrimeUno = ISNULL((SELECT TOP(1) TRIM_UNO_Supervicion FROM DFLE_SupervicionCelex_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 1 AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA)),0); SET @TrimeDos = ISNULL((SELECT TOP(1) TRIM_UNO_Supervicion FROM DFLE_SupervicionCelex_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 2 AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA)),0); SET @TrimeTres = ISNULL((SELECT TOP(1) TRIM_UNO_Supervicion FROM DFLE_SupervicionCelex_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 3 AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA)),0); SET @TrimeCuatro = ISNULL((SELECT TOP(1) TRIM_UNO_Supervicion FROM DFLE_SupervicionCelex_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 4 AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA)),0);
SET @Total = @TrimeUno + @TrimeDos + @TrimeTres + @TrimeCuatro; SET @contadorUA = @contadorUA + 1;
UPDATE @TablaSupervision SET SiglasUA = @SiglasUA, TrimestreUno = @TrimeUno, TrimestreDos = @TrimeDos, TrimestreTres = @TrimeTres, TrimestreCuatro = @TrimeCuatro, Total = @Total WHERE SiglasUA = @SiglasUA; END
--****************************************************************************-- SET @contadorUA = 1; WHILE @contadorUA <= (SELECT COUNT(*) FROM @TablaUnidadesAcademicas) BEGIN SET @SiglasUA = (SELECT UnidadAcademica FROM @TablaUnidadesAcademicas WHERE ID_UnidadAcademica = @contadorUA);
SET @IdentUno = ISNULL((SELECT TOP(1) ID_SupervicionCelex FROM DFLE_SupervicionCelex_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 1 AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA)),0); SET @IdentDos = ISNULL((SELECT TOP(1) ID_SupervicionCelex FROM DFLE_SupervicionCelex_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 2 AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA)),0); SET @IdentTres = ISNULL((SELECT TOP(1) ID_SupervicionCelex FROM DFLE_SupervicionCelex_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 3 AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA)),0); SET @IdentCuatro = ISNULL((SELECT TOP(1) ID_SupervicionCelex FROM DFLE_SupervicionCelex_Temporal WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 4 AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA)),0); SET @contadorUA = @contadorUA + 1;
UPDATE @TablaIDs SET Ident_TrimestreUno = @IdentUno, Ident_TrimestreDos = @IdentDos, Ident_TrimestreTres = @IdentTres, Ident_TrimestreCuatro = @IdentCuatro WHERE SiglasUA = @SiglasUA; END END
--****************************************************************************-- SELECT * FROM @TablaSupervision; SELECT * FROM @TablaIDs;
END

CREATE PROCEDURE DFLE_InterUACelexSupervisadas
    @SiglasTipoUnidadAcademico VARCHAR(255),
    @Anio INT,
    @Final VARCHAR(255)
AS

CREATE PROCEDURE DFLE_JustificacionesTotales
    @NumeroFormato INT,
    @id_Trimestre INT,
    @Anio INT
AS

BEGIN SET NOCOUNT ON; DECLARE @contadorIdioma INT = 1;
DECLARE @TablaUnidadesAcademicas TABLE ( UnidadAcademica VARCHAR(255), ID_UnidadAcademica INT IDENTITY (1,1) ); INSERT INTO @TablaUnidadesAcademicas SELECT Siglas FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @SiglasTipoUnidad); DECLARE @contadorUA INT = 1;
DECLARE @TablaLenguasRegistradasUA TABLE( UnidadAcademica VARCHAR(255), Cuenta VARCHAR(1) NOT NULL DEFAULT ' ',
InglesTrimUno VARCHAR(1) NOT NULL DEFAULT ' ', InglesTrimDos VARCHAR(1) NOT NULL DEFAULT ' ', InglesTrimTres VARCHAR(1) NOT NULL DEFAULT ' ', InglesTrimCuatro VARCHAR(1) NOT NULL DEFAULT ' ',
FrancesTrimUno VARCHAR(1) NOT NULL DEFAULT ' ', FrancesTrimDos VARCHAR(1) NOT NULL DEFAULT ' ', FrancesTrimTres VARCHAR(1) NOT NULL DEFAULT ' ', FrancesTrimCuatro VARCHAR(1) NOT NULL DEFAULT ' ',
AlemanTrimUno VARCHAR(1) NOT NULL DEFAULT ' ', AlemanTrimDos VARCHAR(1) NOT NULL DEFAULT ' ', AlemanTrimTres VARCHAR(1) NOT NULL DEFAULT ' ', AlemanTrimCuatro VARCHAR(1) NOT NULL DEFAULT ' ',
ItalianoTrimUno VARCHAR(1) NOT NULL DEFAULT ' ', ItalianoTrimDos VARCHAR(1) NOT NULL DEFAULT ' ', ItalianoTrimTres VARCHAR(1) NOT NULL DEFAULT ' ', ItalianoTrimCuatro VARCHAR(1) NOT NULL DEFAULT ' ',
JaponesTrimUno VARCHAR(1) NOT NULL DEFAULT ' ', JaponesTrimDos VARCHAR(1) NOT NULL DEFAULT ' ', JaponesTrimTres VARCHAR(1) NOT NULL DEFAULT ' ', JaponesTrimCuatro VARCHAR(1) NOT NULL DEFAULT ' ',
ChinoTrimUno VARCHAR(1) NOT NULL DEFAULT ' ', ChinoTrimDos VARCHAR(1) NOT NULL DEFAULT ' ', ChinoTrimTres VARCHAR(1) NOT NULL DEFAULT ' ', ChinoTrimCuatro VARCHAR(1) NOT NULL DEFAULT ' ',
PortuguesTrimUno VARCHAR(1) NOT NULL DEFAULT ' ', PortuguesTrimDos VARCHAR(1) NOT NULL DEFAULT ' ', PortuguesTrimTres VARCHAR(1) NOT NULL DEFAULT ' ', PortuguesTrimCuatro VARCHAR(1) NOT NULL DEFAULT ' ',
RusoTrimUno VARCHAR(1) NOT NULL DEFAULT ' ', RusoTrimDos VARCHAR(1) NOT NULL DEFAULT ' ', RusoTrimTres VARCHAR(1) NOT NULL DEFAULT ' ', RusoTrimCuatro VARCHAR(1) NOT NULL DEFAULT ' ',
NahualtTrimUno VARCHAR(1) NOT NULL DEFAULT ' ', NahualtTrimDos VARCHAR(1) NOT NULL DEFAULT ' ', NahualtTrimTres VARCHAR(1) NOT NULL DEFAULT ' ', NahualtTrimCuatro VARCHAR(1) NOT NULL DEFAULT ' ',
EspanolTrimUno VARCHAR(1) NOT NULL DEFAULT ' ', EspanolTrimDos VARCHAR(1) NOT NULL DEFAULT ' ', EspanolTrimTres VARCHAR(1) NOT NULL DEFAULT ' ', EspanolTrimCuatro VARCHAR(1) NOT NULL DEFAULT ' ',
SenasTrimUno VARCHAR(1) NOT NULL DEFAULT ' ', SenasTrimDos VARCHAR(1) NOT NULL DEFAULT ' ', SenasTrimTres VARCHAR(1) NOT NULL DEFAULT ' ', SenasTrimCuatro VARCHAR(1) NOT NULL DEFAULT ' ',
CoreanoTrimUno VARCHAR(1) NOT NULL DEFAULT ' ', CoreanoTrimDos VARCHAR(1) NOT NULL DEFAULT ' ', CoreanoTrimTres VARCHAR(1) NOT NULL DEFAULT ' ', CoreanoTrimCuatro VARCHAR(1) NOT NULL DEFAULT ' ',
HindiTrimUno VARCHAR(1) NOT NULL DEFAULT ' ', HindiTrimDos VARCHAR(1) NOT NULL DEFAULT ' ', HindiTrimTres VARCHAR(1) NOT NULL DEFAULT ' ', HindiTrimCuatro VARCHAR(1) NOT NULL DEFAULT ' ',
TotalTrimUno INT NOT NULL DEFAULT 0, TotalTrimDos INT NOT NULL DEFAULT 0, TotalTrimTres INT NOT NULL DEFAULT 0, TotalTrimCuatro INT NOT NULL DEFAULT 0 ); DECLARE @SiglasUA VARCHAR(255);
WHILE @contadorUA <= (SELECT COUNT(*) FROM @TablaUnidadesAcademicas) BEGIN SET @SiglasUA = (SELECT UnidadAcademica FROM @TablaUnidadesAcademicas WHERE ID_UnidadAcademica = @contadorUA);
DECLARE @TotTrim1 INT = 0; DECLARE @TotTrim2 INT = 0; DECLARE @TotTrim3 INT = 0; DECLARE @TotTrim4 INT = 0;
INSERT INTO @TablaLenguasRegistradasUA ( UnidadAcademica ) VALUES( @SiglasUA ) SET @contadorIdioma = 1; WHILE @contadorIdioma <= (SELECT COUNT(*) FROM DFLE_Idiomas) BEGIN
DECLARE @UATrimUno VARCHAR(1) = CASE WHEN EXISTS(SELECT * FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA) AND id_Idioma = @contadorIdioma AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 1) THEN '1' ELSE ' ' END ;
DECLARE @UATrimDos VARCHAR(1) = CASE WHEN EXISTS(SELECT * FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA) AND id_Idioma = @contadorIdioma AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 2) THEN '1' ELSE ' ' END ; DECLARE @UATrimTres VARCHAR(1) = CASE WHEN EXISTS(SELECT * FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA) AND id_Idioma = @contadorIdioma AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 3) THEN '1' ELSE ' ' END ; DECLARE @UATrimCuatro VARCHAR(1) = CASE WHEN EXISTS(SELECT * FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA) AND id_Idioma = @contadorIdioma AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 4) THEN '1' ELSE ' ' END ;
SET @TotTrim1 = @TotTrim1 + (CASE WHEN @UATrimUno = '1' THEN 1 ELSE 0 END); SET @TotTrim2 = @TotTrim2 + (CASE WHEN @UATrimDos = '1' THEN 1 ELSE 0 END); SET @TotTrim3 = @TotTrim3 + (CASE WHEN @UATrimTres = '1' THEN 1 ELSE 0 END); SET @TotTrim4 = @TotTrim4 + (CASE WHEN @UATrimCuatro = '1' THEN 1 ELSE 0 END);
IF @contadorIdioma = 1 BEGIN UPDATE @TablaLenguasRegistradasUA SET InglesTrimUno = @UATrimUno, InglesTrimDos = @UATrimDos, InglesTrimTres = @UATrimTres, InglesTrimCuatro = @UATrimCuatro WHERE UnidadAcademica = @SiglasUA END ELSE IF @contadorIdioma = 2 BEGIN UPDATE @TablaLenguasRegistradasUA SET FrancesTrimUno = @UATrimUno, FrancesTrimDos = @UATrimDos, FrancesTrimTres = @UATrimTres, FrancesTrimCuatro = @UATrimCuatro WHERE UnidadAcademica = @SiglasUA END ELSE IF @contadorIdioma = 3 BEGIN UPDATE @TablaLenguasRegistradasUA SET AlemanTrimUno = @UATrimUno, AlemanTrimDos = @UATrimDos, AlemanTrimTres = @UATrimTres, AlemanTrimCuatro = @UATrimCuatro WHERE UnidadAcademica = @SiglasUA END ELSE IF @contadorIdioma = 4 BEGIN UPDATE @TablaLenguasRegistradasUA SET ItalianoTrimUno = @UATrimUno, ItalianoTrimDos = @UATrimDos, ItalianoTrimTres = @UATrimTres, ItalianoTrimCuatro = @UATrimCuatro WHERE UnidadAcademica = @SiglasUA END ELSE IF @contadorIdioma = 5 BEGIN UPDATE @TablaLenguasRegistradasUA SET JaponesTrimUno = @UATrimUno, JaponesTrimDos = @UATrimDos, JaponesTrimTres = @UATrimTres, JaponesTrimCuatro = @UATrimCuatro WHERE UnidadAcademica = @SiglasUA END ELSE IF @contadorIdioma = 6 BEGIN UPDATE @TablaLenguasRegistradasUA SET ChinoTrimUno = @UATrimUno, ChinoTrimDos = @UATrimDos, ChinoTrimTres = @UATrimTres, ChinoTrimCuatro = @UATrimCuatro WHERE UnidadAcademica = @SiglasUA END ELSE IF @contadorIdioma = 7 BEGIN UPDATE @TablaLenguasRegistradasUA SET PortuguesTrimUno = @UATrimUno, PortuguesTrimDos = @UATrimDos, PortuguesTrimTres = @UATrimTres, PortuguesTrimCuatro = @UATrimCuatro WHERE UnidadAcademica = @SiglasUA END ELSE IF @contadorIdioma = 8 BEGIN UPDATE @TablaLenguasRegistradasUA SET RusoTrimUno = @UATrimUno, RusoTrimDos = @UATrimDos, RusoTrimTres = @UATrimTres, RusoTrimCuatro = @UATrimCuatro WHERE UnidadAcademica = @SiglasUA END ELSE IF @contadorIdioma = 9 BEGIN UPDATE @TablaLenguasRegistradasUA SET NahualtTrimUno = @UATrimUno, NahualtTrimDos = @UATrimDos, NahualtTrimTres = @UATrimTres, NahualtTrimCuatro = @UATrimCuatro WHERE UnidadAcademica = @SiglasUA END ELSE IF @contadorIdioma = 10 BEGIN UPDATE @TablaLenguasRegistradasUA SET EspanolTrimUno = @UATrimUno, EspanolTrimDos = @UATrimDos, EspanolTrimTres = @UATrimTres, EspanolTrimCuatro = @UATrimCuatro WHERE UnidadAcademica = @SiglasUA END ELSE IF @contadorIdioma = 11 BEGIN UPDATE @TablaLenguasRegistradasUA SET SenasTrimUno = @UATrimUno, SenasTrimDos = @UATrimDos, SenasTrimTres = @UATrimTres, SenasTrimCuatro = @UATrimCuatro WHERE UnidadAcademica = @SiglasUA END ELSE IF @contadorIdioma = 12 BEGIN UPDATE @TablaLenguasRegistradasUA SET CoreanoTrimUno = @UATrimUno, CoreanoTrimDos = @UATrimDos, CoreanoTrimTres = @UATrimTres, CoreanoTrimCuatro = @UATrimCuatro WHERE UnidadAcademica = @SiglasUA END ELSE IF @contadorIdioma = 13 BEGIN UPDATE @TablaLenguasRegistradasUA SET HindiTrimUno = @UATrimUno, HindiTrimDos = @UATrimDos, HindiTrimTres = @UATrimTres, HindiTrimCuatro = @UATrimCuatro WHERE UnidadAcademica = @SiglasUA END
SET @contadorIdioma = @contadorIdioma + 1; END DECLARE @Cuenta VARCHAR(1) = CASE WHEN (@TotTrim4 >0 OR @TotTrim3 >0 OR @TotTrim2 >0 OR @TotTrim1 >0 ) THEN '1' ELSE ' ' END ;
UPDATE @TablaLenguasRegistradasUA SET TotalTrimUno = @TotTrim1, TotalTrimDos = @TotTrim2, TotalTrimTres = @TotTrim3, TotalTrimCuatro = @TotTrim4, Cuenta = @Cuenta WHERE UnidadAcademica = @SiglasUA;
SET @contadorUA = @contadorUA + 1; SET @Cuenta = ' '; END SELECT * FROM @TablaLenguasRegistradasUA END

--Numero de Formato: 4 Lenguas registradas por las Unidades Académicas CONCENTRADO
CREATE PROCEDURE DFLE_LenguasRegistradasUA
@SiglasTipoUnidad VARCHAR(255),
@anio INT

AS

BEGIN SET NOCOUNT ON; DECLARE @LenguasRegistro TABLE( Lengua VARCHAR(50) NOT NULL DEFAULT ' ', AntNMS INT NOT NULL DEFAULT 0, AntNS INT NOT NULL DEFAULT 0, AntCINV INT NOT NULL DEFAULT 0, AntCVDR INT NOT NULL DEFAULT 0, AntCIITA INT NOT NULL DEFAULT 0, AntCENLEX INT NOT NULL DEFAULT 0, AntTotal INT NOT NULL DEFAULT 0,
ActNMS INT NOT NULL DEFAULT 0, ActNS INT NOT NULL DEFAULT 0, ActCINV INT NOT NULL DEFAULT 0, ActCVDR INT NOT NULL DEFAULT 0, ActCIITA INT NOT NULL DEFAULT 0, ActCENLEX INT NOT NULL DEFAULT 0, ActTotal INT NOT NULL DEFAULT 0,
Variacion VARCHAR(255) NOT NULL DEFAULT ' ', Justicacion VARCHAR(255) NOT NULL DEFAULT ' ' ); INSERT INTO @LenguasRegistro(Lengua) SELECT Desc_Idioma FROM DFLE_Idiomas;
DECLARE @AntNMS INT = 0; DECLARE @AntNS INT = 0; DECLARE @AntCINV INT = 0; DECLARE @AntCVDR INT = 0; DECLARE @AntCIITA INT = 0; DECLARE @AntCENLEX INT = 0; DECLARE @AntTotal INT = 0;
DECLARE @ActNMS INT = 0; DECLARE @ActNS INT = 0; DECLARE @ActCINV INT = 0; DECLARE @ActCVDR INT = 0; DECLARE @ActCIITA INT = 0; DECLARE @ActCENLEX INT = 0; DECLARE @ActTotal INT = 0;
DECLARE @Variacion DECIMAL(10,2) = 0; DECLARE @Justicacion VARCHAR(255) = ' ';
DECLARE @AnioAnterior INT = @anio - 1;
DECLARE @ContadorIdiomas INT = 1; DECLARE @ContadorTrimestre INT;
WHILE @ContadorIdiomas <= (SELECT COUNT(*) FROM DFLE_Idiomas) BEGIN SET @ContadorTrimestre = 1; WHILE @ContadorTrimestre <= @id_trimestre BEGIN SET @AntNMS = @AntNMS + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'NMS')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = @ContadorIdiomas), 0); SET @AntNS = @AntNS + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'NS')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = @ContadorIdiomas),0);
SET @AntCINV = @AntCINV + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'C INV')) AND id_Anio = (SELECT id_Anio from Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = @ContadorIdiomas),0);
SET @AntCVDR = @AntCVDR + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'UAVDR')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = @ContadorIdiomas),0);
SET @AntCIITA = @AntCIITA + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'CIITA')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = @ContadorIdiomas),0);
SET @AntCENLEX = @AntCENLEX + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'UAAE')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = @ContadorIdiomas),0);
SET @ActNMS = @ActNMS + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'NMS')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = @ContadorIdiomas),0);
SET @ActNS = @ActNS + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'NS')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = @ContadorIdiomas),0);
SET @ActCINV = @ActCINV + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'C INV')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = @ContadorIdiomas),0);
SET @ActCVDR = @ActCVDR + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'UAVDR')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = @ContadorIdiomas),0);
SET @ActCIITA = @ActCIITA + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'CIITA')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = @ContadorIdiomas),0);
SET @ActCENLEX = @ActCENLEX + ISNULL((SELECT COUNT(DISTINCT(id_UnidadAcademica)) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica in (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'UAAE')) AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestre AND id_Idioma = @ContadorIdiomas),0);
SET @ContadorTrimestre = @ContadorTrimestre + 1; END SET @AntTotal = @AntNMS + @AntNS + @AntCINV + @AntCVDR + @AntCIITA + @AntCENLEX; SET @ActTotal = @ActNMS + @ActNS + @ActCINV + @ActCVDR + @ActCIITA + @ActCENLEX;
SET @Variacion = CASE WHEN @AntTotal = 0 THEN 0 ELSE ((@ActTotal/ @AntTotal)-1) END;
SET @Justicacion = (SELECT Desc_Justificacion FROM DFLE_JustificacionesFormato5_9 WHERE id_Idioma = @ContadorIdiomas AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_trimestre AND id_FormatoAutoevaluacion = (SELECT ID_Formato FROM Formatos WHERE Desc_NumeroFormato = 5 AND id_DependenciaEvaluada = (SELECT ID_DependenciasEvaluadas FROM Dependencias_Evaluadas WHERE Desc_SiglasDependencia = 'DFLE')));
UPDATE @LenguasRegistro SET AntNMS = @AntNMS, AntNS = @AntNS, AntCINV = @AntCINV, AntCVDR = @AntCVDR, AntCIITA = @AntCIITA, AntCENLEX = @AntCENLEX, AntTotal = @AntTotal, ActNMS = @ActNMS, ActNS = @ActNS, ActCINV = @ActCINV, ActCVDR = @ActCVDR, ActCIITA = @ActCIITA, ActCENLEX = @ActCENLEX, ActTotal = @ActTotal, Variacion = CASE WHEN @AntTotal = 0 AND @Variacion = 0 THEN ' ' ELSE CONCAT(@Variacion, '%') END, Justicacion = ISNULL(@Justicacion, ' ') WHERE Lengua = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas);
SET @AntNMS = 0; SET @AntNS = 0; SET @AntCINV = 0; SET @AntCVDR = 0; SET @AntCIITA = 0; SET @AntCENLEX = 0;
SET @ActNMS = 0; SET @ActNS = 0; SET @ActCINV = 0; SET @ActCVDR = 0; SET @ActCIITA = 0; SET @ActCENLEX = 0;
SET @ContadorIdiomas = @ContadorIdiomas + 1; END SELECT * FROM @LenguasRegistro; END

--Numero de Formato: 5 Lenguas con registro COMPARATIVO
CREATE PROCEDURE DFLE_LenguasRegistroComparativo
    @anio INT,
    @id_trimestre INT
AS

BEGIN
    SELECT Desc_NombreRama, Desc_SiglasRama FROM RamaConocimiento;
END

BEGIN
    SELECT * FROM Trimestre;
END

--Numero de Formato: 6 Nivel de estudios en lenguas de la población atendida
CREATE PROCEDURE DFLE_NivelLenguasPoblacionAtendida
    @SiglasTipoUnidadAcademica varchar(255),
    @anio INT,
    @id_Trimestre INT
AS

BEGIN SET NOCOUNT ON; DECLARE @PGIAlumnosAtendidos TABLE( PGI VARCHAR(10) NOT NULL DEFAULT '', Idioma VARCHAR(255) NOT NULL DEFAULT '',
IntroductorioH VARCHAR(255) NOT NULL DEFAULT ' ', IntroductorioM VARCHAR(255) NOT NULL DEFAULT ' ', IntroductorioT VARCHAR(255) NOT NULL DEFAULT ' ', BasicoH VARCHAR(255) NOT NULL DEFAULT ' ', BasicoM VARCHAR(255) NOT NULL DEFAULT ' ', BasicoT VARCHAR(255) NOT NULL DEFAULT ' ', IntermedioH VARCHAR(255) NOT NULL DEFAULT ' ', IntermedioM VARCHAR(255) NOT NULL DEFAULT ' ', IntermedioT VARCHAR(255) NOT NULL DEFAULT ' ', AvanzadoH VARCHAR(255) NOT NULL DEFAULT ' ', AvanzadoM VARCHAR(255) NOT NULL DEFAULT ' ', AvanzadoT VARCHAR(255) NOT NULL DEFAULT ' ', SuperiorH VARCHAR(255) NOT NULL DEFAULT ' ', SuperiorM VARCHAR(255) NOT NULL DEFAULT ' ', SuperiorT VARCHAR(255) NOT NULL DEFAULT ' ',
PobAtendidaH VARCHAR(255) NOT NULL DEFAULT ' ', PobAtendidaM VARCHAR(255) NOT NULL DEFAULT ' ',
Total VARCHAR(255) NOT NULL DEFAULT ' ' );
INSERT INTO @PGIAlumnosAtendidos(PGI, Idioma) SELECT Desc_PGI, Desc_Idioma FROM DFLE_Idiomas WHERE Desc_PGI != ' ';
DECLARE @Desc_Hombres INT = 0; DECLARE @Desc_Mujeres INT = 0; DECLARE @Total INT = 0;
DECLARE @PA_Hombres INT = 0; DECLARE @PA_Mujeres INT = 0;
DECLARE @Total_global INT = 0;
DECLARE @ContadorIdiomas INT = 1; WHILE @ContadorIdiomas <= (SELECT COUNT(*) FROM DFLE_Idiomas) BEGIN
DECLARE @ContadorCompetencia INT = 1; WHILE @ContadorCompetencia <= (SELECT COUNT(*) FROM DFLE_NivelesCompetenciaPGI) BEGIN SET @Desc_Hombres = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_Idioma = @ContadorIdiomas AND id_CompetenciaPGI = @ContadorCompetencia AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0);
SET @Desc_Mujeres = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadAcademica) AND id_Idioma = @ContadorIdiomas AND id_CompetenciaPGI = @ContadorCompetencia AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0);
SET @Total = @Desc_Hombres + @Desc_Mujeres; SET @PA_Hombres = @PA_Hombres + @Desc_Hombres; SET @PA_Mujeres = @PA_Mujeres + @Desc_Mujeres;
IF @ContadorCompetencia = 1 BEGIN UPDATE @PGIAlumnosAtendidos SET IntroductorioH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, IntroductorioM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, IntroductorioT = CAST(@Total AS VARCHAR(10)) WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas) END ELSE IF @ContadorCompetencia = 2 BEGIN UPDATE @PGIAlumnosAtendidos SET BasicoH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, BasicoM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, BasicoT = CAST(@Total AS VARCHAR(10)) WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas) END ELSE IF @ContadorCompetencia = 3 BEGIN UPDATE @PGIAlumnosAtendidos SET IntermedioH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, IntermedioM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, IntermedioT = CAST(@Total AS VARCHAR(10)) WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas) END ELSE IF @ContadorCompetencia = 4 BEGIN UPDATE @PGIAlumnosAtendidos SET AvanzadoH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, AvanzadoM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, AvanzadoT = CAST(@Total AS VARCHAR(10)) WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas) END ELSE IF @ContadorCompetencia = 5 BEGIN SET @Total_global = @PA_Hombres + @PA_Mujeres; UPDATE @PGIAlumnosAtendidos SET SuperiorH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, SuperiorM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, SuperiorT = CAST(@Total AS VARCHAR(10)), PobAtendidaH = CAST(@PA_Hombres AS VARCHAR(10)), PobAtendidaM = CAST(@PA_Mujeres AS VARCHAR(10)), Total = CAST(@Total_global AS VARCHAR(10)) WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorIdiomas) END
SET @ContadorCompetencia = @ContadorCompetencia + 1; END
SET @PA_Hombres = 0; SET @PA_Mujeres = 0; SET @ContadorIdiomas = @ContadorIdiomas + 1; END
SELECT * FROM @PGIAlumnosAtendidos END

--Numero de Formato: 13 Usuarios atendidos en enseñanza de lenguas extranjeras. Programa General Institucional
CREATE PROCEDURE DFLE_PGIAlumnosAtendidos
    @SiglasUnidadAcademica varchar(255),
    @anio INT,
    @id_Trimestre INT
AS

BEGIN SET NOCOUNT ON; DECLARE @AlumnosAtendidosLenguasComparativo TABLE( Idioma VARCHAR(255) NOT NULL DEFAULT ' ',
AntCenlexH INT NOT NULL DEFAULT 0, AntCenlexM INT NOT NULL DEFAULT 0, AntCenlexTotal INT NOT NULL DEFAULT 0,
AntCelexH INT NOT NULL DEFAULT 0, AntCelexM INT NOT NULL DEFAULT 0, AntCelexTotal INT NOT NULL DEFAULT 0,
AnteriorTotal INT NOT NULL DEFAULT 0,
ActCenlexH INT NOT NULL DEFAULT 0, ActCenlexM INT NOT NULL DEFAULT 0, ActCenlexTotal INT NOT NULL DEFAULT 0,
ActCelexH INT NOT NULL DEFAULT 0, ActCelexM INT NOT NULL DEFAULT 0, ActCelexTotal INT NOT NULL DEFAULT 0,
ActSubHombres INT NOT NULL DEFAULT 0, ActSubMujeres INT NOT NULL DEFAULT 0, ActualTotal INT NOT NULL DEFAULT 0,
Variacion VARCHAR(255) NOT NULL DEFAULT ' ',
Justificacion VARCHAR(255) NOT NULL DEFAULT ' ' );
INSERT INTO @AlumnosAtendidosLenguasComparativo(Idioma) SELECT Desc_Idioma FROM DFLE_Idiomas;
DECLARE @CenlexSantoTomas VARCHAR(255) = 'CENLEX UST'; DECLARE @CenlexZacatenco VARCHAR(255) = 'CENLEX Zacatenco'; DECLARE @AnioAnterior INT = @anio - 1;
DECLARE @AntCenlexH INT = 0; DECLARE @AntCenlexM INT = 0; DECLARE @AntCenlexTotal INT = 0;
DECLARE @AntCelexH INT = 0; DECLARE @AntCelexM INT = 0; DECLARE @AntCelexTotal INT = 0;
DECLARE @AnteriorTotal INT = 0;
DECLARE @ActCenlexH INT = 0; DECLARE @ActCenlexM INT = 0; DECLARE @ActCenlexTotal INT = 0;
DECLARE @ActCelexH INT = 0; DECLARE @ActCelexM INT = 0; DECLARE @ActCelexTotal INT = 0;
DECLARE @ActSubHombres INT = 0; DECLARE @ActSubMujeres INT = 0; DECLARE @ActualTotal INT = 0;
DECLARE @Variacion DECIMAL(10, 2) = 0; DECLARE @Justificacion VARCHAR(255) = '';
DECLARE @ContadorLenguas INT = 1; DECLARE @ContadorTrimestres INT = 1; WHILE @ContadorLenguas <= (SELECT COUNT(*) FROM DFLE_Idiomas) BEGIN SET @ContadorTrimestres = 1; WHILE @ContadorTrimestres <= @id_Trimestre BEGIN -- CENLEX Anio Anterior SET @AntCenlexH = @AntCenlexH + ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestres), 0); SET @AntCenlexM = @AntCenlexM + ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestres), 0);
-- CELEX Anio Anterior SET @AntCelexH = @AntCelexH + ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica NOT IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestres), 0); SET @AntCelexM = @AntCelexM + ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica NOT IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Trimestre = @ContadorTrimestres), 0);
-- CENLEX Anio Actual SET @ActCenlexH = @ActCenlexH + ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestres), 0); SET @ActCenlexM = @ActCenlexM + ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestres), 0);
-- CELEX Anio Actual SET @ActCelexH = @ActCelexH + ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica NOT IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestres), 0); SET @ActCelexM = @ActCelexM + ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica NOT IN ((SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexSantoTomas), (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @CenlexZacatenco)) AND id_Idioma = @ContadorLenguas AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @ContadorTrimestres), 0);
SET @ContadorTrimestres = @ContadorTrimestres + 1; END
SET @AntCenlexTotal = @AntCenlexH + @AntCenlexM; SET @AntCelexTotal = @AntCelexH + @AntCelexM; SET @AnteriorTotal = @AntCenlexTotal + @AntCelexTotal;
SET @ActCenlexTotal = @ActCenlexH + @ActCenlexM; SET @ActCelexTotal = @ActCelexH + @ActCelexM; SET @ActSubHombres = @ActCenlexH + @ActCelexH; SET @ActSubMujeres = @ActCenlexM + @ActCelexM; SET @ActualTotal = @ActCenlexTotal + @ActCelexTotal;
SET @Variacion = CASE WHEN @AnteriorTotal <> 0 THEN (((@ActualTotal - @AnteriorTotal) * 100) / @AnteriorTotal) ELSE 0 END; SET @Justificacion = (SELECT Desc_Justificacion FROM DFLE_JustificacionesFormato5_9 WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre AND id_Idioma = @ContadorLenguas AND id_FormatoAutoevaluacion = (SELECT ID_Formato FROM Formatos WHERE Desc_NumeroFormato = 9 AND id_DependenciaEvaluada = (SELECT ID_DependenciasEvaluadas FROM Dependencias_Evaluadas WHERE Desc_SiglasDependencia = 'DFLE')));
UPDATE @AlumnosAtendidosLenguasComparativo SET AntCenlexH = @AntCenlexH, AntCenlexM = @AntCenlexM, AntCenlexTotal = @AntCenlexTotal, AntCelexH = @AntCelexH, AntCelexM = @AntCelexM, AntCelexTotal = @AntCelexTotal, AnteriorTotal = @AnteriorTotal, ActCenlexH = @ActCenlexH, ActCenlexM = @ActCenlexM, ActCenlexTotal = @ActCenlexTotal, ActCelexH = @ActCelexH, ActCelexM = @ActCelexM, ActCelexTotal = @ActCelexTotal, ActSubHombres = @ActSubHombres, ActSubMujeres = @ActSubMujeres, ActualTotal = @ActualTotal, Variacion = CASE WHEN @AnteriorTotal = 0 AND @Variacion = 0 THEN ' ' ELSE CONCAT(@Variacion, '%') END, Justificacion = ISNULL(@Justificacion, ' ') WHERE Idioma = (SELECT Desc_Idioma FROM DFLE_Idiomas WHERE ID_Idioma = @ContadorLenguas)
SET @AntCenlexH = 0; SET @AntCenlexM = 0;
SET @AntCelexH = 0; SET @AntCelexM = 0;
SET @ActCenlexH = 0; SET @ActCenlexM = 0;
SET @ActCelexH = 0; SET @ActCelexM = 0;
SET @ContadorLenguas = @ContadorLenguas + 1; END
SELECT * FROM @AlumnosAtendidosLenguasComparativo; END

--Numero de Formato: 9 Promedio de alumnos atendidos en lenguas - COMPARATIVO
CREATE PROCEDURE DFLE_PromedioAlumnosAtendidosComparativo
    @anio INT,
    @id_Trimestre INT
AS

BEGIN
    SET NOCOUNT ON;
    DECLARE @PeriodoAnterior INT
    DECLARE @PeriodoActual INT
    DECLARE @VariacionPorcentual DECIMAL(5, 2)
    DECLARE @Justificacion NVARCHAR(MAX)

    DECLARE @NMS VARCHAR(255) = 'NIVEL MEDIO SUPERIOR' COLLATE Latin1_General_CI_AI;
    DECLARE @NS VARCHAR(255) = 'NIVEL SUPERIOR' COLLATE Latin1_General_CI_AI;
    DECLARE @CIR VARCHAR(255) = 'UNIDADES ACADÉMICAS DE INVESTIGACIÓN CIENTÍFICA Y TECNOLÓGICA' COLLATE Latin1_General_CI_AI;
    DECLARE @UAVDR VARCHAR(255) = 'UNIDADES ACADÉMICAS DE VINCULACION Y DESARROLLO REGIONAL' COLLATE Latin1_General_CI_AI;
    DECLARE @CIITA VARCHAR(255) = 'UNIDADES ACADÉMICAS DE INNOVACION E INTEGRACION DE TECNOLOGÍAS AVANZADAS' COLLATE Latin1_General_CI_AI;

    DECLARE @Comparativo TABLE (
        ID_Comparativo INT IDENTITY(1,1),
        NivelUnidad VARCHAR(255),
        PeriodoAnterior INT NOT NULL DEFAULT 0,
        PeriodoActual INT NOT NULL DEFAULT 0,
        VariacionPorcentual VARCHAR(10) NOT NULL DEFAULT ' ',
        Justificacion NVARCHAR(MAX) NOT NULL DEFAULT ' '
    )

    INSERT INTO @Comparativo (
        NivelUnidad
    )VALUES(
        @NMS
    ),
    (
        @NS
    ),
    (
        @CIR
    ),
    (
        @UAVDR
    ),
    (
        @CIITA
    );

    DECLARE @UA TABLE(
        ID_UnidadAcademica INT IDENTITY(1,1),
        SiglasUnidad varchar(255)
    )

    DECLARE @contadorTipoUA INT = 1;
    WHILE @contadorTipoUA <= (SELECT COUNT(ID_Comparativo) FROM @Comparativo)
    BEGIN
        DECLARE @TipoUAActual varchar(255) = (SELECT NivelUnidad 
                                            FROM @Comparativo   
                                            WHERE ID_Comparativo = @contadorTipoUA )
        DELETE FROM @UA
        INSERT INTO @UA (SiglasUnidad)
        (SELECT Siglas 
        FROM UnidadesAcademicas 
        WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica 
                                        FROM TipoUnidadAcademica 
                                        WHERE Desc_TipoUnidadAcademica = @TipoUAActual))

        DECLARE @AcumuladoActual INT = 0;
        DECLARE @AcumuladoAnterior INT = 0;
        DECLARE @contadorUA INT = 1;

        WHILE @contadorUA <= (SELECT COUNT(ID_UnidadAcademica) FROM @UA) 
        BEGIN
            DECLARE @SiglasUAActual varchar(255) = (SELECT SiglasUnidad 
                                            FROM @UA 
                                            WHERE ID_UnidadAcademica = @contadorUA)
            SET @AcumuladoActual = @AcumuladoActual + CASE WHEN EXISTS(SELECT * 
                                                                FROM DFLE_AlumnosEnsenanzaLenguas 
                                                                WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica 
                                                                                            FROM UnidadesAcademicas 
                                                                                            WHERE Siglas = @SiglasUAActual) 
                                                                AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)
                                                                AND id_Trimestre = @id_Trimestre) THEN 1 ELSE 0 END;
            SET @AcumuladoAnterior = @AcumuladoAnterior + CASE WHEN EXISTS(SELECT * 
                                                            FROM DFLE_AlumnosEnsenanzaLenguas 
                                                            WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica 
                                                                                        FROM UnidadesAcademicas 
                                                                                        WHERE Siglas = @SiglasUAActual) 
                                                            AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio - 1)
                                                            AND id_Trimestre = @id_Trimestre) THEN 1 ELSE 0 END;
            SET @contadorUA = @contadorUA + 1;
        END;
        SET @Justificacion = ISNULL((SELECT Desc_Justificacion 
                            FROM DFLE_JustificacionesUACelex 
                            WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) 
                            AND id_Trimestre = @id_Trimestre
                            AND id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica 
                                                        FROM TipoUnidadAcademica
                                                        WHERE Desc_TipoUnidadAcademica = @TipoUAActual)), '');

        UPDATE @Comparativo 
        SET PeriodoActual = @AcumuladoActual,
            PeriodoAnterior = @AcumuladoAnterior,
            @VariacionPorcentual = CASE WHEN @AcumuladoAnterior != 0
            THEN (@AcumuladoActual - @AcumuladoAnterior) * 100 / @AcumuladoAnterior
            ELSE ' ' END,
            Justificacion = @Justificacion
        WHERE ID_Comparativo = @contadorTipoUA
        SET @contadorTipoUA = @contadorTipoUA + 1;
    END;

    SELECT NivelUnidad,
            PeriodoAnterior,
            PeriodoActual,
            VariacionPorcentual,
            Justificacion 
    FROM @Comparativo
END;

-- Formato 2: Unidades Académicas que cuentan con CELEX COMPARATIVO
CREATE PROCEDURE DFLE_UACelexComparativo
    @anio INT,
    @id_Trimestre INT

AS

BEGIN SET NOCOUNT ON; DECLARE @TablaUnidadesAcademicas TABLE ( UnidadAcademica VARCHAR(255), ID_UnidadAcademica INT IDENTITY (1,1) ); INSERT INTO @TablaUnidadesAcademicas SELECT Siglas FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @SiglasTipoUnidad);
DECLARE @Supervision TABLE ( SiglasUA VARCHAR(255), UATrimUno VARCHAR(1) NOT NULL DEFAULT ' ', UATrimDos VARCHAR(1) NOT NULL DEFAULT ' ', UATrimTres VARCHAR(1) NOT NULL DEFAULT ' ', UATrimCuatro VARCHAR(1) NOT NULL DEFAULT ' ',
TrimeUno VARCHAR(1) NOT NULL DEFAULT ' ', TrimeDos VARCHAR(1) NOT NULL DEFAULT ' ', TrimeTres VARCHAR(1) NOT NULL DEFAULT ' ', TrimeCuatro VARCHAR(1) NOT NULL DEFAULT ' ', TotalSupervision INT NOT NULL DEFAULT 0 );
DECLARE @contadorUA INT = 1; DECLARE @SiglasUA VARCHAR(255); WHILE @contadorUA <= (SELECT COUNT(*) FROM @TablaUnidadesAcademicas) BEGIN SET @SiglasUA = (SELECT UnidadAcademica FROM @TablaUnidadesAcademicas WHERE ID_UnidadAcademica = @contadorUA);
DECLARE @UATrimUno VARCHAR(1) = CASE WHEN EXISTS(SELECT * FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 1) THEN '1' ELSE ' ' END ;
DECLARE @UATrimDos VARCHAR(1) = CASE WHEN EXISTS(SELECT * FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 2) THEN '1' ELSE ' ' END ; DECLARE @UATrimTres VARCHAR(1) = CASE WHEN EXISTS(SELECT * FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 3) THEN '1' ELSE ' ' END ; DECLARE @UATrimCuatro VARCHAR(1) = CASE WHEN EXISTS(SELECT * FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 4) THEN '1' ELSE ' ' END ;
DECLARE @TrimeUno INT = ISNULL((SELECT TRIM_UNO_Supervicion FROM DFLE_SupervicionCelex WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 1 AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA)),0); DECLARE @TrimeDos INT = ISNULL((SELECT TRIM_UNO_Supervicion FROM DFLE_SupervicionCelex WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 2 AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA)),0); DECLARE @TrimeTres INT = ISNULL((SELECT TRIM_UNO_Supervicion FROM DFLE_SupervicionCelex WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 3 AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA)),0); DECLARE @TrimeCuatro INT = ISNULL((SELECT TRIM_UNO_Supervicion FROM DFLE_SupervicionCelex WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = 4 AND id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUA)),0);
INSERT INTO @Supervision( SiglasUA, UATrimUno, UATrimDos, UATrimTres, UATrimCuatro, TrimeUno, TrimeDos, TrimeTres, TrimeCuatro, TotalSupervision )VALUES( @SiglasUA, CASE WHEN @UATrimUno = 0 THEN ' ' ELSE CAST(@UATrimUno AS VARCHAR(1)) END, CASE WHEN @UATrimDos = 0 THEN ' ' ELSE CAST(@UATrimDos AS VARCHAR(1)) END, CASE WHEN @UATrimTres = 0 THEN ' ' ELSE CAST(@UATrimTres AS VARCHAR(1)) END, CASE WHEN @UATrimCuatro = 0 THEN ' ' ELSE CAST(@UATrimCuatro AS VARCHAR(1)) END, CASE WHEN @TrimeUno = 0 THEN ' ' ELSE CAST(@TrimeUno AS VARCHAR(1)) END, CASE WHEN @TrimeDos = 0 THEN ' ' ELSE CAST(@TrimeDos AS VARCHAR(1)) END, CASE WHEN @TrimeTres = 0 THEN ' ' ELSE CAST(@TrimeTres AS VARCHAR(1)) END, CASE WHEN @TrimeCuatro = 0 THEN ' ' ELSE CAST(@TrimeCuatro AS VARCHAR(1)) END, @TrimeUno + @TrimeDos + @TrimeTres + @TrimeCuatro );
SET @contadorUA = @contadorUA + 1; END SELECT * FROM @Supervision S ; END

--Formato 1: Unidades Académicas que cuentan con CELEX supervisadas ACUMULADO
CREATE PROCEDURE DFLE_UACelexSupervisado
    @SiglasTipoUnidad VARCHAR(255),
    @anio INT,
    @id_Trimestre INT

AS

BEGIN SET NOCOUNT ON; --********************************************************************************** --Declaraciones importantes a tomar en cuenta --**********************************************************************************
--!Declaracion de la tabla provisional para organizar los datos que se mostraran con --!procedimiento DECLARE @UnidadesProgramaGeneral TABLE( SiglasUnidadAcademica varchar(255), CELEX varchar(1),
InglesPrimerAnio varchar(1) NOT NULL DEFAULT ' ', InglesSegundoAnio varchar(1) NOT NULL DEFAULT ' ', InglesTercerAnio varchar(1) NOT NULL DEFAULT ' ', InglesCuartoAnio varchar(1) NOT NULL DEFAULT ' ', InglesAnioTrimestreActual varchar(1) NOT NULL DEFAULT ' ', InglesTotal varchar(1) NOT NULL DEFAULT '0',
AlemanPrimerAnio varchar(1) NOT NULL DEFAULT ' ', AlemanSegundoAnio varchar(1) NOT NULL DEFAULT ' ', AlemanAnioTrimestreActual varchar(1) NOT NULL DEFAULT ' ', AlemanTotal varchar(1) NOT NULL DEFAULT '0',
JaponesPrimerAnio varchar(1) NOT NULL DEFAULT ' ' , JaponesSegundoAnio varchar(1) NOT NULL DEFAULT ' ', JaponesAnioTrimestreActual varchar(1) NOT NULL DEFAULT ' ', JaponesTotal varchar(1) NOT NULL DEFAULT '0',
CoreanoPrimerAnio varchar(1) NOT NULL DEFAULT ' ', CoreanoSegundoAnio varchar(1) NOT NULL DEFAULT ' ', CoreanoAnioTrimestreActual varchar(1) NOT NULL DEFAULT ' ', CoreanoTotal varchar(1) NOT NULL DEFAULT '0',
PortuguesPrimerAnio varchar(1) NOT NULL DEFAULT ' ', PortuguesSegundoAnio varchar(1) NOT NULL DEFAULT ' ', PortuguesAnioTrimestreActual varchar(1) NOT NULL DEFAULT ' ', PortuguesTotal varchar(1) NOT NULL DEFAULT '0',
LenguaSenasPrimerAnio varchar(1) NOT NULL DEFAULT ' ', LenguaSenasSegundoAnio varchar(1) NOT NULL DEFAULT ' ', LenguaSenasAnioTrimestreActual varchar(1) NOT NULL DEFAULT ' ', LenguaSenasTotal varchar(1) NOT NULL DEFAULT '0',
FrancesPrimerAnio varchar(1) NOT NULL DEFAULT ' ', FrancesSegundoAnio varchar(1) NOT NULL DEFAULT ' ', FrancesAnioTrimestreActual varchar(1) NOT NULL DEFAULT ' ', FrancesTotal varchar(1) NOT NULL DEFAULT '0',
EspanolPrimerAnio varchar(1) NOT NULL DEFAULT ' ', EspanolSegundoAnio varchar(1) NOT NULL DEFAULT ' ', EspanolAnioTrimestreActual varchar(1) NOT NULL DEFAULT ' ', EspanolTotal varchar(1) NOT NULL DEFAULT '0',
ChinoMandarinPrimerAnio varchar(1) NOT NULL DEFAULT ' ', ChinoMandarinSegundoAnio varchar(1) NOT NULL DEFAULT ' ', ChinoMandarinAnioTrimestreActual varchar(1) NOT NULL DEFAULT ' ', ChinoMandarinTotal varchar(1) NOT NULL DEFAULT '0',
HindiPrimerAnio varchar(1) NOT NULL DEFAULT ' ', HindiSegundoAnio varchar(1) NOT NULL DEFAULT ' ', HindiAnioTrimestreActual varchar(1) NOT NULL DEFAULT ' ', HindiTotal varchar(1) NOT NULL DEFAULT '0',
InglesImplementacion varchar(1) NOT NULL DEFAULT ' ', AlemanImplementacion varchar(1) NOT NULL DEFAULT ' ', JaponesImplementacion varchar(1) NOT NULL DEFAULT ' ', ChinoMandarinImplementacion varchar(1) NOT NULL DEFAULT ' ', PortuguesImplementacion varchar(1) NOT NULL DEFAULT ' ', LenguaSenasImplementacion varchar(1) NOT NULL DEFAULT ' ', FrancesImplementacion varchar(1) NOT NULL DEFAULT ' ', EspanolImplementacion varchar(1) NOT NULL DEFAULT ' ', CoreanoImplementacion varchar(1) NOT NULL DEFAULT ' ', HindiImplementacion varchar(1) NOT NULL DEFAULT ' ' );
--!Declaramos la tabla que contendra las unidades academicas para poder iterar --!sobre ellas DECLARE @UnidadesAcademicas TABLE( ID_Unidad INT IDENTITY(1,1), SiglasUnidadAcademica varchar(255) );
--!Declaramos la tabla que contendra los idiomas que cuenten con el programa general DECLARE @IdiomasPGI TABLE( ID_Idioma INT IDENTITY(1,1), Desc_Idioma varchar(255) )
--!Declaramos el contador para recorrer las unidades academicas así como --!la variable que guarda el valor de la unidad academica actual DECLARE @ContadorUA INT = 1; DECLARE @ContadorIdioma INT = 1; DECLARE @SiglasUnidadActual varchar(255);
--!Declaramos variables para que guarden la información sobre la existencia del --!programa general institucional en la unidad academica actual
--DECLARE @CELEX INT; DECLARE @CELEX VARCHAR(1); DECLARE @InglesPrimerAnio INT; DECLARE @InglesSegundoAnio INT; DECLARE @InglesTercerAnio INT; DECLARE @InglesCuartoAnio INT; DECLARE @InglesAnioTrimestreActual INT; DECLARE @InglesTotal INT;
DECLARE @PrimerAnio INT; DECLARE @SegundoAnio INT; DECLARE @AnioTrimestreActual INT; DECLARE @Total INT;
--!Declaracion variables para años anteriores DECLARE @AnioAnterior INT = @Anio - 1; DECLARE @AnioAnteAnterior INT = @Anio - 2; DECLARE @AnioAnteAnteAnterior INT = @Anio - 3; DECLARE @AnioAnteAnteAnteAnterior INT = @Anio - 4;
--**************************************************************************** --En esta parte se comienza el codigo para el relleno de la tabla provisional --****************************************************************************
--!Se insertan las unidades academicas correspondientes al tipo solicitado INSERT INTO @UnidadesAcademicas( SiglasUnidadAcademica ) SELECT Siglas FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @SiglasTipoUnidadAcademica);
INSERT INTO @UnidadesProgramaGeneral( SiglasUnidadAcademica ) SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas
--!Insertamos los idiomas que cuentan con PGII INSERT INTO @IdiomasPGI( Desc_Idioma ) SELECT Desc_Idioma FROM DFLE_Idiomas WHERE Desc_PGI != ' ';
--!While para el recorrido de las unidades academicas WHILE @ContadorUA <= (SELECT COUNT(*) FROM @UnidadesAcademicas) BEGIN
--!Asignacion de la unidad actual SET @SiglasUnidadActual = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE ID_Unidad = @ContadorUA);
SET @CELEX = CASE WHEN EXISTS(SELECT * FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadActual) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre) THEN '1' ELSE ' ' END ;
UPDATE @UnidadesProgramaGeneral SET CELEX = @CELEX WHERE SiglasUnidadAcademica = @SiglasUnidadActual;
SET @ContadorIdioma = 1;
WHILE @ContadorIdioma <= (SELECT COUNT(*) FROM @IdiomasPGI) BEGIN IF @ContadorIdioma = 1 BEGIN SET @InglesPrimerAnio = CASE WHEN EXISTS(SELECT * FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadActual) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = (SELECT Desc_Idioma FROM @IdiomasPGI WHERE ID_Idioma = @ContadorIdioma)) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @AnioAnteAnteAnteAnterior) ) THEN 1 ELSE 0 END ; SET @InglesSegundoAnio = CASE WHEN EXISTS(SELECT * FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadActual) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = (SELECT Desc_Idioma FROM @IdiomasPGI WHERE ID_Idioma = @ContadorIdioma)) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @AnioAnteAnteAnterior) ) THEN 1 ELSE 0 END ; SET @InglesTercerAnio = CASE WHEN EXISTS(SELECT * FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadActual) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = (SELECT Desc_Idioma FROM @IdiomasPGI WHERE ID_Idioma = @ContadorIdioma)) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @AnioAnteAnterior) ) THEN 1 ELSE 0 END ; SET @InglesCuartoAnio = CASE WHEN EXISTS(SELECT * FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadActual) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = (SELECT Desc_Idioma FROM @IdiomasPGI WHERE ID_Idioma = @ContadorIdioma)) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) ) THEN 1 ELSE 0 END ; SET @InglesAnioTrimestreActual = CASE WHEN EXISTS(SELECT * FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadActual) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = (SELECT Desc_Idioma FROM @IdiomasPGI WHERE ID_Idioma = @ContadorIdioma)) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre ) THEN 1 ELSE 0 END ; SET @InglesTotal = @InglesPrimerAnio + @InglesSegundoAnio + @InglesTercerAnio + @InglesCuartoAnio + @InglesAnioTrimestreActual;
UPDATE @UnidadesProgramaGeneral SET InglesPrimerAnio = CASE WHEN @InglesPrimerAnio = 0 THEN ' ' ELSE CAST(@InglesPrimerAnio AS VARCHAR(1)) END, InglesSegundoAnio = CASE WHEN @InglesSegundoAnio = 0 THEN ' ' ELSE CAST(@InglesSegundoAnio AS VARCHAR(1)) END, InglesTercerAnio = CASE WHEN @InglesTercerAnio = 0 THEN ' ' ELSE CAST(@InglesTercerAnio AS VARCHAR(1)) END, InglesCuartoAnio = CASE WHEN @InglesCuartoAnio = 0 THEN ' ' ELSE CAST(@InglesCuartoAnio AS VARCHAR(1)) END, InglesAnioTrimestreActual = CASE WHEN @InglesAnioTrimestreActual = 0 THEN ' ' ELSE CAST(@InglesAnioTrimestreActual AS VARCHAR(1)) END, InglesTotal = CASE WHEN @InglesTotal = 0 THEN '0' ELSE CAST(@InglesTotal AS VARCHAR(1)) END, InglesImplementacion = CASE WHEN @InglesTotal > 0 THEN '1' ELSE ' ' END
WHERE SiglasUnidadAcademica = @SiglasUnidadActual; END ELSE BEGIN SET @PrimerAnio = CASE WHEN EXISTS(SELECT * FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadActual) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = (SELECT Desc_Idioma FROM @IdiomasPGI WHERE ID_Idioma = @ContadorIdioma)) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @AnioAnteAnterior) ) THEN 1 ELSE 0 END ; SET @SegundoAnio = CASE WHEN EXISTS(SELECT * FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadActual) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = (SELECT Desc_Idioma FROM @IdiomasPGI WHERE ID_Idioma = @ContadorIdioma)) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) ) THEN 1 ELSE 0 END ; SET @AnioTrimestreActual = CASE WHEN EXISTS(SELECT * FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasUnidadActual) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = (SELECT Desc_Idioma FROM @IdiomasPGI WHERE ID_Idioma = @ContadorIdioma)) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @id_Trimestre ) THEN 1 ELSE 0 END ; SET @Total = @PrimerAnio + @SegundoAnio + @AnioTrimestreActual;
IF @ContadorIdioma = 2 BEGIN UPDATE @UnidadesProgramaGeneral SET FrancesPrimerAnio = CASE WHEN @PrimerAnio = 0 THEN ' ' ELSE CAST(@PrimerAnio AS VARCHAR(1)) END, FrancesSegundoAnio = CASE WHEN @SegundoAnio= 0 THEN ' ' ELSE CAST(@SegundoAnio AS VARCHAR(1)) END, FrancesAnioTrimestreActual = CASE WHEN @AnioTrimestreActual= 0 THEN ' ' ELSE CAST(@AnioTrimestreActual AS VARCHAR(1)) END, FrancesTotal = @Total, FrancesImplementacion = CASE WHEN @Total > 0 THEN '1' ELSE ' ' END WHERE SiglasUnidadAcademica = @SiglasUnidadActual; END ELSE IF @ContadorIdioma = 3 BEGIN UPDATE @UnidadesProgramaGeneral SET AlemanPrimerAnio = CASE WHEN @PrimerAnio = 0 THEN ' ' ELSE CAST(@PrimerAnio AS VARCHAR(1)) END, AlemanSegundoAnio = CASE WHEN @SegundoAnio= 0 THEN ' ' ELSE CAST(@SegundoAnio AS VARCHAR(1)) END, AlemanAnioTrimestreActual = CASE WHEN @AnioTrimestreActual= 0 THEN ' ' ELSE CAST(@AnioTrimestreActual AS VARCHAR(1)) END, AlemanTotal = @Total, AlemanImplementacion = CASE WHEN @Total > 0 THEN '1' ELSE ' ' END WHERE SiglasUnidadAcademica = @SiglasUnidadActual; END ELSE IF @ContadorIdioma = 4 BEGIN UPDATE @UnidadesProgramaGeneral SET JaponesPrimerAnio = CASE WHEN @PrimerAnio = 0 THEN ' ' ELSE CAST(@PrimerAnio AS VARCHAR(1)) END, JaponesSegundoAnio = CASE WHEN @SegundoAnio= 0 THEN ' ' ELSE CAST(@SegundoAnio AS VARCHAR(1)) END, JaponesAnioTrimestreActual = CASE WHEN @AnioTrimestreActual= 0 THEN ' ' ELSE CAST(@AnioTrimestreActual AS VARCHAR(1)) END, JaponesTotal = @Total, JaponesImplementacion = CASE WHEN @Total > 0 THEN '1' ELSE ' ' END WHERE SiglasUnidadAcademica = @SiglasUnidadActual; END ELSE IF @ContadorIdioma = 5 BEGIN UPDATE @UnidadesProgramaGeneral SET ChinoMandarinPrimerAnio = CASE WHEN @PrimerAnio = 0 THEN ' ' ELSE CAST(@PrimerAnio AS VARCHAR(1)) END, ChinoMandarinSegundoAnio = CASE WHEN @SegundoAnio= 0 THEN ' ' ELSE CAST(@SegundoAnio AS VARCHAR(1)) END, ChinoMandarinAnioTrimestreActual = CASE WHEN @AnioTrimestreActual= 0 THEN ' ' ELSE CAST(@AnioTrimestreActual AS VARCHAR(1)) END, ChinoMandarinTotal = @Total, ChinoMandarinImplementacion = CASE WHEN @Total > 0 THEN '1' ELSE ' ' END WHERE SiglasUnidadAcademica = @SiglasUnidadActual; END ELSE IF @ContadorIdioma = 6 BEGIN UPDATE @UnidadesProgramaGeneral SET PortuguesPrimerAnio = CASE WHEN @PrimerAnio = 0 THEN ' ' ELSE CAST(@PrimerAnio AS VARCHAR(1)) END, PortuguesSegundoAnio = CASE WHEN @SegundoAnio= 0 THEN ' ' ELSE CAST(@SegundoAnio AS VARCHAR(1)) END, PortuguesAnioTrimestreActual = CASE WHEN @AnioTrimestreActual= 0 THEN ' ' ELSE CAST(@AnioTrimestreActual AS VARCHAR(1)) END, PortuguesTotal = @Total, PortuguesImplementacion = CASE WHEN @Total > 0 THEN '1' ELSE ' ' END WHERE SiglasUnidadAcademica = @SiglasUnidadActual; END ELSE IF @ContadorIdioma = 7 BEGIN UPDATE @UnidadesProgramaGeneral SET EspanolPrimerAnio = CASE WHEN @PrimerAnio = 0 THEN ' ' ELSE CAST(@PrimerAnio AS VARCHAR(1)) END, EspanolSegundoAnio = CASE WHEN @SegundoAnio= 0 THEN ' ' ELSE CAST(@SegundoAnio AS VARCHAR(1)) END, EspanolAnioTrimestreActual = CASE WHEN @AnioTrimestreActual= 0 THEN ' ' ELSE CAST(@AnioTrimestreActual AS VARCHAR(1)) END, EspanolTotal = @Total, EspanolImplementacion = CASE WHEN @Total > 0 THEN '1' ELSE ' ' END WHERE SiglasUnidadAcademica = @SiglasUnidadActual; END ELSE IF @ContadorIdioma = 8 BEGIN UPDATE @UnidadesProgramaGeneral SET LenguaSenasPrimerAnio = CASE WHEN @PrimerAnio = 0 THEN ' ' ELSE CAST(@PrimerAnio AS VARCHAR(1)) END, LenguaSenasSegundoAnio = CASE WHEN @SegundoAnio= 0 THEN ' ' ELSE CAST(@SegundoAnio AS VARCHAR(1)) END, LenguaSenasAnioTrimestreActual = CASE WHEN @AnioTrimestreActual= 0 THEN ' ' ELSE CAST(@AnioTrimestreActual AS VARCHAR(1)) END, LenguaSenasTotal = @Total, LenguaSenasImplementacion = CASE WHEN @Total > 0 THEN '1' ELSE ' ' END WHERE SiglasUnidadAcademica = @SiglasUnidadActual; END ELSE IF @ContadorIdioma = 9 BEGIN UPDATE @UnidadesProgramaGeneral SET CoreanoPrimerAnio = CASE WHEN @PrimerAnio = 0 THEN ' ' ELSE CAST(@PrimerAnio AS VARCHAR(1)) END, CoreanoSegundoAnio = CASE WHEN @SegundoAnio= 0 THEN ' ' ELSE CAST(@SegundoAnio AS VARCHAR(1)) END, CoreanoAnioTrimestreActual = CASE WHEN @AnioTrimestreActual= 0 THEN ' ' ELSE CAST(@AnioTrimestreActual AS VARCHAR(1)) END, CoreanoTotal = @Total, CoreanoImplementacion = CASE WHEN @Total > 0 THEN '1' ELSE ' ' END WHERE SiglasUnidadAcademica = @SiglasUnidadActual; END ELSE IF @ContadorIdioma = 10 BEGIN UPDATE @UnidadesProgramaGeneral SET HindiPrimerAnio = CASE WHEN @PrimerAnio = 0 THEN ' ' ELSE CAST(@PrimerAnio AS VARCHAR(1)) END, HindiSegundoAnio = CASE WHEN @SegundoAnio= 0 THEN ' ' ELSE CAST(@SegundoAnio AS VARCHAR(1)) END, HindiAnioTrimestreActual = CASE WHEN @AnioTrimestreActual= 0 THEN ' ' ELSE CAST(@AnioTrimestreActual AS VARCHAR(1)) END, HindiTotal = @Total, HindiImplementacion = CASE WHEN @Total > 0 THEN '1' ELSE ' ' END WHERE SiglasUnidadAcademica = @SiglasUnidadActual; END END SET @ContadorIdioma = @ContadorIdioma + 1; END SET @ContadorUA = @ContadorUA + 1; END --****************************************************************************
--!Select para mostrar la tabla que rellena la parte correspondiente al formato de --!este procedimiento SELECT * FROM @UnidadesProgramaGeneral END

--Numero de formato: 3 UNIDADES ACADÉMICAS QUE IMPLEMENTAN EL PROGRAMA GENERAL DE LENGUAS Acumulado
CREATE PROCEDURE DFLE_UAProgramaGeneral
    @SiglasTipoUnidadAcademica VARCHAR(50),
    @Anio INT,
    @id_Trimestre INT
AS

BEGIN SET NOCOUNT ON; DECLARE @UsuariosAtendidosUnidad TABLE( UnidadAcademica VARCHAR(255),
InglesH VARCHAR(255) NOT NULL DEFAULT ' ', InglesM VARCHAR(255) NOT NULL DEFAULT ' ', FrancesH VARCHAR(255) NOT NULL DEFAULT ' ', FrancesM VARCHAR(255) NOT NULL DEFAULT ' ', AlemanH VARCHAR(255) NOT NULL DEFAULT ' ', AlemanM VARCHAR(255) NOT NULL DEFAULT ' ', ItalianoH VARCHAR(255) NOT NULL DEFAULT ' ', ItalianoM VARCHAR(255) NOT NULL DEFAULT ' ', JaponesH VARCHAR(255) NOT NULL DEFAULT ' ', JaponesM VARCHAR(255) NOT NULL DEFAULT ' ', ChinoH VARCHAR(255) NOT NULL DEFAULT ' ', ChinoM VARCHAR(255) NOT NULL DEFAULT ' ', PortuguesH VARCHAR(255) NOT NULL DEFAULT ' ', PortuguesM VARCHAR(255) NOT NULL DEFAULT ' ', RusoH VARCHAR(255) NOT NULL DEFAULT ' ', RusoM VARCHAR(255) NOT NULL DEFAULT ' ', NahuatlH VARCHAR(255) NOT NULL DEFAULT ' ', NahuatlM VARCHAR(255) NOT NULL DEFAULT ' ', EspanolH VARCHAR(255) NOT NULL DEFAULT ' ', EspanolM VARCHAR(255) NOT NULL DEFAULT ' ', SenasH VARCHAR(255) NOT NULL DEFAULT ' ', SenasM VARCHAR(255) NOT NULL DEFAULT ' ', CoreanoH VARCHAR(255) NOT NULL DEFAULT ' ', CoreanoM VARCHAR(255) NOT NULL DEFAULT ' ', HindiH VARCHAR(255) NOT NULL DEFAULT ' ', HindiM VARCHAR(255) NOT NULL DEFAULT ' ',
Total INT DEFAULT 0 ); INSERT INTO @UsuariosAtendidosUnidad(UnidadAcademica) SELECT Siglas FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @SiglasTipoUnidadAcademica);
DECLARE @InglesH INT = 0; DECLARE @InglesM INT = 0; DECLARE @FrancesH INT = 0; DECLARE @FrancesM INT = 0; DECLARE @AlemanH INT = 0; DECLARE @AlemanM INT = 0; DECLARE @ItalianoH INT = 0; DECLARE @ItalianoM INT = 0; DECLARE @JaponesH INT = 0; DECLARE @JaponesM INT = 0; DECLARE @ChinoH INT = 0; DECLARE @ChinoM INT = 0; DECLARE @PortuguesH INT = 0; DECLARE @PortuguesM INT = 0; DECLARE @RusoH INT =0; DECLARE @RusoM INT =0; DECLARE @NahuatlH INT = 0; DECLARE @NahuatlM INT =0; DECLARE @EspanolH INT =0; DECLARE @EspanolM INT =0; DECLARE @SenasH INT =0; DECLARE @SenasM INT =0; DECLARE @CoreanoH INT =0; DECLARE @CoreanoM INT =0; DECLARE @HindiH INT =0; DECLARE @HindiM INT =0;
DECLARE @UnidadesAcademicas TABLE( Id_UnidadAcademica INT IDENTITY(1,1), SiglasUnidadAcademica VARCHAR(255) ); INSERT INTO @UnidadesAcademicas SELECT Siglas FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @SiglasTipoUnidadAcademica);
DECLARE @NumUA INT = (SELECT COUNT(*) FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @SiglasTipoUnidadAcademica)); DECLARE @ContadorUA INT = 1;
WHILE @ContadorUA <= @NumUA BEGIN SET @InglesH = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'INGLÉS')), 0); SET @InglesM = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'INGLÉS')), 0);
SET @FrancesH = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'FRANCÉS')), 0); SET @FrancesM = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'FRANCÉS')), 0);
SET @AlemanH = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'ALEMÁN')), 0); SET @AlemanM = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'ALEMÁN')), 0);
SET @ItalianoH = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'ITALIANO')), 0); SET @ItalianoM = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'ITALIANO')), 0);
SET @JaponesH = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'JAPONÉS')), 0); SET @JaponesM = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'JAPONÉS')), 0);
SET @ChinoH = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'CHINO-MANDARÍN')), 0);
SET @ChinoH = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'CHINO-MANDARÍN')), 0); SET @PortuguesH = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'PORTUGUÉS')), 0);
SET @PortuguesM = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'PORTUGUÉS')), 0);
SET @RusoH = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'RUSO')), 0);
SET @RusoM = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'RUSO')), 0);
SET @NahuatlH = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'NAHUATL')), 0);
SET @NahuatlM = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'NAHUATL')), 0);
SET @EspanolH = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'ESPAÑOL')), 0);
SET @EspanolM = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'ESPAÑOL')), 0);
SET @SenasH = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'SEÑAS')), 0);
SET @SenasM = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'SEÑAS MEXICANAS')), 0);
SET @CoreanoH = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'COREANO')), 0);
SET @CoreanoM = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'COREANO')), 0);
SET @HindiH = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'HINDI')), 0);
SET @HindiM = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosEnsenanzaLenguas WHERE Id_UnidadAcademica = (SELECT Id_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA)) AND Id_Trimestre = @id_Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Idioma = (SELECT Id_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = 'HINDI')), 0);
UPDATE @UsuariosAtendidosUnidad SET InglesH = CASE WHEN @InglesH = 0 THEN ' ' ELSE CAST(@InglesH AS VARCHAR(255)) END, InglesM = CASE WHEN @InglesM = 0 THEN ' ' ELSE CAST(@InglesM AS VARCHAR(255)) END, FrancesH = CASE WHEN @FrancesH = 0 THEN ' ' ELSE CAST(@FrancesH AS VARCHAR(255)) END, FrancesM = CASE WHEN @FrancesM = 0 THEN ' ' ELSE CAST(@FrancesM AS VARCHAR(255)) END, AlemanH = CASE WHEN @AlemanH = 0 THEN ' ' ELSE CAST(@AlemanH AS VARCHAR(255)) END, AlemanM = CASE WHEN @AlemanM = 0 THEN ' ' ELSE CAST(@AlemanM AS VARCHAR(255)) END, ItalianoH = CASE WHEN @ItalianoH = 0 THEN ' ' ELSE CAST(@ItalianoH AS VARCHAR(255)) END,
ItalianoM = CASE WHEN @ItalianoM = 0 THEN ' ' ELSE CAST(@ItalianoM AS VARCHAR(255)) END,
JaponesH = CASE WHEN @JaponesH = 0 THEN ' ' ELSE CAST(@JaponesH AS VARCHAR(255)) END,
JaponesM = CASE WHEN @JaponesM = 0 THEN ' ' ELSE CAST(@JaponesM AS VARCHAR(255)) END, ChinoH = CASE WHEN @ChinoH = 0 THEN ' ' ELSE CAST(@ChinoH AS VARCHAR(255)) END, ChinoM = CASE WHEN @ChinoM = 0 THEN ' ' ELSE CAST(@ChinoM AS VARCHAR(255)) END, PortuguesH = CASE WHEN @PortuguesH = 0 THEN ' ' ELSE CAST(@PortuguesH AS VARCHAR(255)) END, PortuguesM = CASE WHEN @PortuguesM = 0 THEN ' ' ELSE CAST(@PortuguesM AS VARCHAR(255)) END, RusoH = CASE WHEN @RusoH = 0 THEN ' ' ELSE CAST(@RusoH AS VARCHAR(255)) END, RusoM = CASE WHEN @RusoM = 0 THEN ' ' ELSE CAST(@RusoM AS VARCHAR(255)) END, NahuatlH = CASE WHEN @NahuatlH = 0 THEN ' ' ELSE CAST(@NahuatlH AS VARCHAR(255)) END, NahuatlM = CASE WHEN @NahuatlM = 0 THEN ' ' ELSE CAST(@NahuatlM AS VARCHAR(255)) END, EspanolH = CASE WHEN @EspanolH = 0 THEN ' ' ELSE CAST(@EspanolH AS VARCHAR(255)) END, EspanolM = CASE WHEN @EspanolM = 0 THEN ' ' ELSE CAST(@EspanolM AS VARCHAR(255)) END, SenasH = CASE WHEN @SenasH = 0 THEN ' ' ELSE CAST(@SenasH AS VARCHAR(255)) END, SenasM = CASE WHEN @SenasM = 0 THEN ' ' ELSE CAST(@SenasM AS VARCHAR(255)) END, CoreanoH = CASE WHEN @CoreanoH = 0 THEN ' ' ELSE CAST(@CoreanoH AS VARCHAR(255)) END, CoreanoM = CASE WHEN @CoreanoM = 0 THEN ' ' ELSE CAST(@CoreanoM AS VARCHAR(255)) END, HindiH = CASE WHEN @HindiH = 0 THEN ' ' ELSE CAST(@HindiH AS VARCHAR(255)) END, HindiM = CASE WHEN @HindiM = 0 THEN ' ' ELSE CAST(@HindiM AS VARCHAR(255)) END, Total = @InglesH + @InglesM + @FrancesH + @FrancesM + @AlemanH + @AlemanM + @ItalianoH + @ItalianoM + @JaponesH + @JaponesM + @ChinoH + @ChinoM + @PortuguesH + @PortuguesM + @RusoH + @RusoM + @NahuatlH + @NahuatlM + @EspanolH + @EspanolM + @SenasH + @SenasM + @CoreanoH + @CoreanoM + @HindiH + @HindiM WHERE UnidadAcademica = (SELECT Siglas FROM UnidadesAcademicas WHERE Siglas = (SELECT SiglasUnidadAcademica FROM @UnidadesAcademicas WHERE Id_UnidadAcademica = @ContadorUA));
SET @ContadorUA = @ContadorUA + 1; END
SELECT * FROM @UsuariosAtendidosUnidad; END

-- Numero de Formato: 14 Usuarios atendidos por idioma
CREATE PROCEDURE DFLE_UsuariosAtendidosIdioma
    @SiglasTipoUnidadAcademica VARCHAR(255),
    @anio INT,
    @id_Trimestre INT
AS

BEGIN SET NOCOUNT ON; DECLARE @UsuariosAtendidosUnidad TABLE( UnidadAcademica VARCHAR(255),
PGIIH VARCHAR(10) NOT NULL DEFAULT ' ', PGIIM VARCHAR(10) NOT NULL DEFAULT ' ', PGIIT INT NOT NULL DEFAULT 0,
PGIAH VARCHAR(10) NOT NULL DEFAULT ' ', PGIAM VARCHAR(10) NOT NULL DEFAULT ' ', PGIAT INT NOT NULL DEFAULT 0,
PGIJH VARCHAR(10) NOT NULL DEFAULT ' ', PGIJM VARCHAR(10) NOT NULL DEFAULT ' ', PGIJT INT NOT NULL DEFAULT 0,
PGIKH VARCHAR(10) NOT NULL DEFAULT ' ', PGIKM VARCHAR(10) NOT NULL DEFAULT ' ', PGIKT INT NOT NULL DEFAULT 0,
PGIPH VARCHAR(10) NOT NULL DEFAULT ' ', PGIPM VARCHAR(10) NOT NULL DEFAULT ' ', PGIPT INT NOT NULL DEFAULT 0,
PGILSMH VARCHAR(10) NOT NULL DEFAULT ' ', PGILSMM VARCHAR(10) NOT NULL DEFAULT ' ', PGILSMT INT NOT NULL DEFAULT 0,
PGIFH VARCHAR(10) NOT NULL DEFAULT ' ', PGIFM VARCHAR(10) NOT NULL DEFAULT ' ', PGIFT INT NOT NULL DEFAULT 0,
PGIEH VARCHAR(10) NOT NULL DEFAULT ' ', PGIEM VARCHAR(10) NOT NULL DEFAULT ' ', PGIET INT NOT NULL DEFAULT 0,
PGICH VARCHAR(10) NOT NULL DEFAULT ' ', PGICM VARCHAR(10) NOT NULL DEFAULT ' ', PGICT INT NOT NULL DEFAULT 0,
PGIHH VARCHAR(10) NOT NULL DEFAULT ' ', PGIHM VARCHAR(10) NOT NULL DEFAULT ' ', PGIHT INT NOT NULL DEFAULT 0,
PobAtendidaH INT NOT NULL DEFAULT 0, PobAtendidaM INT NOT NULL DEFAULT 0, PobAtendidaT INT NOT NULL DEFAULT 0 );
DECLARE @PGI TABLE( ID_PGI INT IDENTITY(1, 1), PGI_Idioma VARCHAR(255) NOT NULL DEFAULT '' );
INSERT INTO @PGI (PGI_Idioma) VALUES ('INGLÉS'), ('ALEMÁN'), ('JAPONÉS'), ('COREANO'), ('PORTUGUÉS'), ('SEÑAS MEXICANAS'), ('FRANCÉS'), ('ESPAÑOL'), ('CHINO-MANDARÍN'), ('HINDI');
DECLARE @UnidadesAcademicas TABLE( ID_UnidadesAcademicas INT IDENTITY(1, 1), SiglasUnidad VARCHAR(255) );
INSERT INTO @UnidadesAcademicas ( SiglasUnidad ) SELECT Siglas FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @SiglasTipoUnidadAcademica) AND id_RamaConocimiento = (SELECT ID_RamaConocimiento FROM RamaConocimiento WHERE Desc_SiglasRama = @SiglasTipoRama);
DECLARE @Desc_Hombres INT = 0; DECLARE @Desc_Mujeres INT = 0; DECLARE @Total INT = 0;
DECLARE @TotalHombres INT = 0; DECLARE @TotalMujeres INT = 0;
DECLARE @SiglasActual VARCHAR(255); DECLARE @IdiomaActual VARCHAR(255);
DECLARE @ContadorUA INT = 1; WHILE @ContadorUA <= (SELECT COUNT(*) FROM @UnidadesAcademicas) BEGIN SET @SiglasActual = (SELECT SiglasUnidad FROM @UnidadesAcademicas WHERE ID_UnidadesAcademicas = @ContadorUA);
INSERT INTO @UsuariosAtendidosUnidad( UnidadAcademica ) VALUES ( @SiglasActual );
DECLARE @ContadorIdioma INT = 1; WHILE @ContadorIdioma <= (SELECT COUNT(*) FROM DFLE_Idiomas WHERE Desc_PGI <> '') BEGIN SET @IdiomaActual = (SELECT PGI_Idioma FROM @PGI WHERE ID_PGI = @ContadorIdioma);
SET @Desc_Hombres = ISNULL((SELECT SUM(Desc_Hombres) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasActual) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @IdiomaActual) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0);
SET @Desc_Mujeres = ISNULL((SELECT SUM(Desc_Mujeres) FROM DFLE_AlumnosLenguasPGI WHERE id_UnidadAcademica = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasActual) AND id_Idioma = (SELECT ID_Idioma FROM DFLE_Idiomas WHERE Desc_Idioma = @IdiomaActual) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0); SET @Total = @Desc_Hombres + @Desc_Mujeres; SET @TotalHombres = @TotalHombres + @Desc_Hombres; SET @TotalMujeres = @TotalMujeres + @Desc_Mujeres;
IF @ContadorIdioma = 1 BEGIN UPDATE @UsuariosAtendidosUnidad SET PGIIH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, PGIIM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, PGIIT = @Total WHERE UnidadAcademica = @SiglasActual END ELSE IF @ContadorIdioma = 2 BEGIN UPDATE @UsuariosAtendidosUnidad SET PGIAH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, PGIAM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, PGIAT = @Total WHERE UnidadAcademica = @SiglasActual END ELSE IF @ContadorIdioma = 3 BEGIN UPDATE @UsuariosAtendidosUnidad SET PGIJH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, PGIJM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, PGIJT = @Total WHERE UnidadAcademica = @SiglasActual END ELSE IF @ContadorIdioma = 4 BEGIN UPDATE @UsuariosAtendidosUnidad SET PGIKH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, PGIKM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, PGIKT = @Total WHERE UnidadAcademica = @SiglasActual END ELSE IF @ContadorIdioma = 5 BEGIN UPDATE @UsuariosAtendidosUnidad SET PGIPH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, PGIPM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, PGIPT = @Total WHERE UnidadAcademica = @SiglasActual END ELSE IF @ContadorIdioma = 6 BEGIN UPDATE @UsuariosAtendidosUnidad SET PGILSMH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, PGILSMM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, PGILSMT = @Total WHERE UnidadAcademica = @SiglasActual END ELSE IF @ContadorIdioma = 7 BEGIN UPDATE @UsuariosAtendidosUnidad SET PGIFH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, PGIFM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, PGIFT = @Total WHERE UnidadAcademica = @SiglasActual END ELSE IF @ContadorIdioma = 8 BEGIN UPDATE @UsuariosAtendidosUnidad SET PGIEH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, PGIEM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, PGIET = @Total WHERE UnidadAcademica = @SiglasActual END ELSE IF @ContadorIdioma = 9 BEGIN UPDATE @UsuariosAtendidosUnidad SET PGICH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, PGICM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, PGICT = @Total WHERE UnidadAcademica = @SiglasActual END ELSE IF @ContadorIdioma = 10 BEGIN UPDATE @UsuariosAtendidosUnidad SET PGIHH = CASE WHEN @Desc_Hombres = 0 THEN ' ' ELSE CAST(@Desc_Hombres AS VARCHAR(10)) END, PGIHM = CASE WHEN @Desc_Mujeres = 0 THEN ' ' ELSE CAST(@Desc_Mujeres AS VARCHAR(10)) END, PGIHT = @Total, PobAtendidaH = @TotalHombres, PobAtendidaM = @TotalMujeres, PobAtendidaT = @TotalHombres + @TotalMujeres WHERE UnidadAcademica = @SiglasActual END
SET @ContadorIdioma = @ContadorIdioma + 1; END
SET @TotalHombres = 0; SET @TotalMujeres = 0;
SET @ContadorUA = @ContadorUA + 1; END
SELECT * FROM @UsuariosAtendidosUnidad END

