CREATE PROCEDURE MuestraEstadoDocumento
    @SiglasDependencia varchar(255),
    @Anio varchar(4),
    @Trimestre INT
AS

BEGIN DECLARE @Estado varchar(255);
-- Obtener el estado del documento SELECT @Estado = ES.Desc_EstadoSemaforo FROM Documento D LEFT JOIN EstadosSemaforo ES ON D.id_EstadoActual = ES.ID_EstadosSemaforo WHERE D.Desc_TituloDocumento = @NombreDocumento;
-- Verificar si el estado es NULL y retornar la columna con el alias unificado IF @Estado IS NULL BEGIN SELECT 'No Capturado' AS Estado; END ELSE BEGIN SELECT @Estado AS Estado; END END;

CREATE PROCEDURE MuestraEstadoDocumentoNombre
    @NombreDocumento varchar(255)
AS

BEGIN
	SELECT Desc_Estatus_Bloqueo FROM Estatus_Bloqueado
END;

BEGIN
	SELECT Desc_Estatus_Deshabilitado FROM Estatus_Deshabilitado
END;

CREATE PROCEDURE MuestraHistoricoDocumento
    @TituloDocumento varchar(255)
AS

BEGIN
SELECT Desc_SiglasDependencia, Desc_DependenciasEvaluadas FROM Dependencias_Evaluadas
END;

	BEGIN
  	SELECT Siglas, Desc_Unidad_Academica FROM UnidadesAcademicas
	END;

BEGIN DECLARE @DocentesAnioSabatico TABLE ( ID_DocentesAnioSabatico INT IDENTITY(1,1), ID_Rama INT NOT NULL DEFAULT 0, UnidadAcademica VARCHAR(255) NOT NULL DEFAULT ' ', A1_Hombres VARCHAR(255) NOT NULL DEFAULT ' ', A1_Mujeres VARCHAR(255) NOT NULL DEFAULT ' ', A2_Hombres VARCHAR(255) NOT NULL DEFAULT ' ', A2_Mujeres VARCHAR(255) NOT NULL DEFAULT ' ', Actividades_Hombres VARCHAR(255) NOT NULL DEFAULT ' ', Actividades_Mujeres VARCHAR(255) NOT NULL DEFAULT ' ', EstParciales_Hombres VARCHAR(255) NOT NULL DEFAULT ' ', EstParciales_Mujeres VARCHAR(255) NOT NULL DEFAULT ' ', Individuales_Hombres VARCHAR(255) NOT NULL DEFAULT ' ', Individuales_Mujeres VARCHAR(255) NOT NULL DEFAULT ' ', Total_NoPrioritario INT NOT NULL DEFAULT 0,
Investigacion_Hombres VARCHAR(255) NOT NULL DEFAULT ' ', Investigacion_Mujeres VARCHAR(255) NOT NULL DEFAULT ' ', EstPosgrado_Hombres VARCHAR(255) NOT NULL DEFAULT ' ', EstPosgrado_Mujeres VARCHAR(255) NOT NULL DEFAULT ' ', Diplomados_Hombres VARCHAR(255) NOT NULL DEFAULT ' ', Diplomados_Mujeres VARCHAR(255) NOT NULL DEFAULT ' ', Estancias_Hombres VARCHAR(255) NOT NULL DEFAULT ' ', Estancias_Mujeres VARCHAR(255) NOT NULL DEFAULT ' ', Elaboracion_Hombres VARCHAR(255) NOT NULL DEFAULT ' ', Elaboracion_Mujeres VARCHAR(255) NOT NULL DEFAULT ' ', Total_Prioritario INT NOT NULL DEFAULT 0,
Total_Hombres INT NOT NULL DEFAULT 0, Total_Mujeres INT NOT NULL DEFAULT 0, Total_General INT NOT NULL DEFAULT 0 );
-- Tabla provisional estática para Unidades Academicas
DECLARE @UnidadesAcademicas TABLE( ID_UnidadAcademica INT IDENTITY(1,1), Siglas VARCHAR(255) NOT NULL DEFAULT ' ', ID_Rama INT NOT NULL DEFAULT 0 );
-- Aquí me aseguro de obtener las siglas de los Centros de la Base de Datos (por precaución) DECLARE @Centros_Investigacion VARCHAR(255) = (SELECT Desc_SiglasTipo FROM TipoUnidadAcademica WHERE Desc_TipoUnidadAcademica = 'UNIDADES ACADÉMICAS DE INVESTIGACIÓN CIENTÍFICA Y TECNOLÓGICA'); DECLARE @Centros_Innovacion VARCHAR(255) = (SELECT Desc_SiglasTipo FROM TipoUnidadAcademica WHERE Desc_TipoUnidadAcademica = 'UNIDADES ACADÉMICAS DE INNOVACION E INTEGRACION DE TECNOLOGÍAS AVANZADAS'); DECLARE @Unidades_Centrales VARCHAR(255) = (SELECT Desc_SiglasTipo FROM TipoUnidadAcademica WHERE Desc_TipoUnidadAcademica = 'UNIDADES DE ÁREA CENTRAL o ADMNISTRATIVAS'); DECLARE @UnidadesVinculacion VARCHAR(255) = (SELECT Desc_SiglasTipo FROM TipoUnidadAcademica WHERE Desc_TipoUnidadAcademica = 'UNIDADES ACADÉMICAS DE VINCULACION Y DESARROLLO REGIONAL'); -- Si se reciben Centros de Investigación (C INV), se agregan también los de Innovación IF @SiglasTipoUnidadAcademica = @Centros_Investigacion BEGIN INSERT INTO @UnidadesAcademicas(Siglas, ID_Rama) (SELECT UA.Siglas, RC.ID_RamaConocimiento FROM UnidadesAcademicas UA JOIN RamaConocimiento RC ON UA.id_RamaConocimiento = RC.ID_RamaConocimiento WHERE UA.id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @Centros_Investigacion) OR UA.id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @Centros_Innovacion) GROUP BY RC.ID_RamaConocimiento, UA.Siglas); END ELSE IF @SiglasTipoUnidadAcademica = @Unidades_Centrales -- Si se reciben Unidades de Área Central (UACA), se agregan también las de Vinculación BEGIN INSERT INTO @UnidadesAcademicas(Siglas, ID_Rama) (SELECT UA.Siglas, RC.ID_RamaConocimiento FROM UnidadesAcademicas UA JOIN RamaConocimiento RC ON UA.id_RamaConocimiento = RC.ID_RamaConocimiento WHERE UA.id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @Unidades_Centrales) OR UA.id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @UnidadesVinculacion) GROUP BY RC.ID_RamaConocimiento, UA.Siglas); END ELSE -- Si no se insertan solo las recibidas (NS, NMS) BEGIN INSERT INTO @UnidadesAcademicas(Siglas, ID_Rama) (SELECT UA.Siglas, RC.ID_RamaConocimiento FROM UnidadesAcademicas UA JOIN RamaConocimiento RC ON UA.id_RamaConocimiento = RC.ID_RamaConocimiento WHERE UA.id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @SiglasTipoUnidadAcademica) GROUP BY RC.ID_RamaConocimiento, UA.Siglas); END
-- Tabla provisional para programas No Prioritarios
DECLARE @TablaNoPrioritarios TABLE( ID_TablaNoPrioritarios INT IDENTITY(1,1), Desc_ProgramaSabatico VARCHAR(255) NOT NULL DEFAULT ' ' );
INSERT INTO @TablaNoPrioritarios SELECT Desc_ProgramasSabaticoSAC FROM SAC_ProgramasSabatico WHERE Desc_Prioridad = 'NO PRIORITARIOS';
-- Programas Prioritarios DECLARE @TablaPrioritarios TABLE( ID_TablaPrioritarios INT IDENTITY(1,1), Desc_ProgramaSabatico VARCHAR(255) );
INSERT INTO @TablaPrioritarios SELECT Desc_ProgramasSabaticoSAC FROM SAC_ProgramasSabatico WHERE Desc_Prioridad = 'PRIORITARIOS';
DECLARE @ContSiglasUA INT = 1; DECLARE @TituloRama VARCHAR(255) = ' ';
-- Iteraremos sobre las Ramas de Conocimiento para agregar su TOTAL al final de cada una DECLARE @ContRama INT = 1; WHILE @ContRama <= (SELECT COUNT(*) FROM RamaConocimiento) BEGIN -- Ingresamos a la tabla principal las Unidades Académicas de la Rama Actual INSERT INTO @DocentesAnioSabatico (ID_Rama, UnidadAcademica) SELECT ID_Rama, Siglas FROM @UnidadesAcademicas WHERE ID_Rama = @ContRama;
-- Iteraremos sobre cada Unidad Académica de la Rama Actual DECLARE @ContSiglasxRama INT = 1; WHILE @ContSiglasxRama <= (SELECT COUNT(*) FROM @UnidadesAcademicas WHERE ID_Rama = @ContRama) BEGIN -- Guardamos la Sigla Actual DECLARE @SiglasActual VARCHAR(255) = (SELECT Siglas FROM @UnidadesAcademicas WHERE ID_UnidadAcademica = @ContSiglasUA AND ID_Rama = @ContRama); DECLARE @Total_Hombres INT = 0; DECLARE @Total_Mujeres INT = 0;
-- Dividimos 2 WHILE en Programas No prioritarios y Programas prioritarios -- Esto para sacar el Total al final de cada Programa
DECLARE @Total_NoPrioritario INT = 0; -- WHILE que itera sobre cada Programa No Prioritario DECLARE @ContNoPrioritario INT = 1; WHILE @ContNoPrioritario <= (SELECT COUNT(*) FROM SAC_ProgramasSabatico WHERE Desc_Prioridad = 'NO PRIORITARIOS') BEGIN DECLARE @Hombres INT = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroSabaticos WHERE id_UnidadAcademicaSAC = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasActual) AND id_ProgramasSabaticoSAC = (SELECT id_ProgramasSabaticoSAC FROM SAC_ProgramasSabatico WHERE Desc_ProgramasSabaticoSAC = (SELECT Desc_ProgramaSabatico FROM @TablaNoPrioritarios WHERE ID_TablaNoPrioritarios = @ContNoPrioritario)) AND id_AnioSemestre = (SELECT ID_AnioSemestreSAC FROM SAC_AnioSemestre WHERE Desc_AnioSemestreSAC = @AnioSemestre) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0); SET @Total_Hombres = @Total_Hombres + @Hombres;
DECLARE @Mujeres INT = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroSabaticos WHERE id_UnidadAcademicaSAC = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasActual) AND id_ProgramasSabaticoSAC = (SELECT id_ProgramasSabaticoSAC FROM SAC_ProgramasSabatico WHERE Desc_ProgramasSabaticoSAC = (SELECT Desc_ProgramaSabatico FROM @TablaNoPrioritarios WHERE ID_TablaNoPrioritarios = @ContNoPrioritario)) AND id_AnioSemestre = (SELECT ID_AnioSemestreSAC FROM SAC_AnioSemestre WHERE Desc_AnioSemestreSAC = @AnioSemestre) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0); SET @Total_Mujeres = @Total_Mujeres + @Mujeres;
-- Condiciones que actualizarán en tabla principal los valores de Hombres y Mujeres IF @ContNoPrioritario = 1 BEGIN UPDATE @DocentesAnioSabatico SET A1_Hombres = CASE WHEN @Hombres = 0 THEN ' ' ELSE CAST(@Hombres AS VARCHAR(255)) END, A1_Mujeres = CASE WHEN @Mujeres = 0 THEN ' ' ELSE CAST(@Mujeres AS VARCHAR(255)) END WHERE UnidadAcademica = @SiglasActual; END ELSE IF @ContNoPrioritario = 2 BEGIN UPDATE @DocentesAnioSabatico SET A2_Hombres = CASE WHEN @Hombres = 0 THEN ' ' ELSE CAST(@Hombres AS VARCHAR(255)) END, A2_Mujeres = CASE WHEN @Mujeres = 0 THEN ' ' ELSE CAST(@Mujeres AS VARCHAR(255)) END WHERE UnidadAcademica = @SiglasActual; END ELSE IF @ContNoPrioritario = 3 BEGIN UPDATE @DocentesAnioSabatico SET Actividades_Hombres = CASE WHEN @Hombres = 0 THEN ' ' ELSE CAST(@Hombres AS VARCHAR(255)) END, Actividades_Mujeres = CASE WHEN @Mujeres = 0 THEN ' ' ELSE CAST(@Mujeres AS VARCHAR(255)) END WHERE UnidadAcademica = @SiglasActual; END ELSE IF @ContNoPrioritario = 4 BEGIN UPDATE @DocentesAnioSabatico SET EstParciales_Hombres = CASE WHEN @Hombres = 0 THEN ' ' ELSE CAST(@Hombres AS VARCHAR(255)) END, EstParciales_Mujeres = CASE WHEN @Mujeres = 0 THEN ' ' ELSE CAST(@Mujeres AS VARCHAR(255)) END WHERE UnidadAcademica = @SiglasActual; END ELSE IF @ContNoPrioritario = 5 BEGIN UPDATE @DocentesAnioSabatico SET Individuales_Hombres = CASE WHEN @Hombres = 0 THEN ' ' ELSE CAST(@Hombres AS VARCHAR(255)) END, Individuales_Mujeres = CASE WHEN @Mujeres = 0 THEN ' ' ELSE CAST(@Mujeres AS VARCHAR(255)) END WHERE UnidadAcademica = @SiglasActual; END
SET @Total_NoPrioritario = @Total_NoPrioritario + @Hombres + @Mujeres;
SET @ContNoPrioritario = @ContNoPrioritario + 1; END -- Actualizamos el Total de No Prioritarios UPDATE @DocentesAnioSabatico SET Total_NoPrioritario = @Total_NoPrioritario WHERE UnidadAcademica = @SiglasActual;
DECLARE @Total_Prioritario INT = 0; -- WHILE que itera sobre cada Programa Prioritario
DECLARE @ContPrioritario INT = 1; WHILE @ContPrioritario <= (SELECT COUNT(*) FROM SAC_ProgramasSabatico WHERE Desc_Prioridad = 'PRIORITARIOS') BEGIN SET @Hombres = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroSabaticos WHERE id_UnidadAcademicaSAC = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasActual) AND id_ProgramasSabaticoSAC = (SELECT id_ProgramasSabaticoSAC FROM SAC_ProgramasSabatico WHERE Desc_ProgramasSabaticoSAC = (SELECT Desc_ProgramaSabatico FROM @TablaPrioritarios WHERE ID_TablaPrioritarios = @ContPrioritario)) AND id_AnioSemestre = (SELECT ID_AnioSemestreSAC FROM SAC_AnioSemestre WHERE Desc_AnioSemestreSAC = @AnioSemestre) AND id_Trimestre = 4 AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0);
SET @Total_Hombres = @Total_Hombres + @Hombres;
SET @Mujeres = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroSabaticos WHERE id_UnidadAcademicaSAC = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasActual) AND id_ProgramasSabaticoSAC = (SELECT id_ProgramasSabaticoSAC FROM SAC_ProgramasSabatico WHERE Desc_ProgramasSabaticoSAC = (SELECT Desc_ProgramaSabatico FROM @TablaPrioritarios WHERE ID_TablaPrioritarios = @ContPrioritario)) AND id_AnioSemestre = (SELECT ID_AnioSemestreSAC FROM SAC_AnioSemestre WHERE Desc_AnioSemestreSAC = @AnioSemestre) AND id_Trimestre = 4 AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0);
SET @Total_Mujeres = @Total_Mujeres + @Mujeres;
-- Condiciones IF @ContPrioritario = 1 BEGIN UPDATE @DocentesAnioSabatico SET Investigacion_Hombres = CASE WHEN @Hombres = 0 THEN ' ' ELSE CAST(@Hombres AS VARCHAR(255)) END, Investigacion_Mujeres = CASE WHEN @Mujeres = 0 THEN ' ' ELSE CAST(@Mujeres AS VARCHAR(255)) END WHERE UnidadAcademica = @SiglasActual; END ELSE IF @ContPrioritario = 2 BEGIN UPDATE @DocentesAnioSabatico SET EstPosgrado_Hombres = CASE WHEN @Hombres = 0 THEN ' ' ELSE CAST(@Hombres AS VARCHAR(255)) END, EstPosgrado_Mujeres = CASE WHEN @Mujeres = 0 THEN ' ' ELSE CAST(@Mujeres AS VARCHAR(255)) END WHERE UnidadAcademica = @SiglasActual; END ELSE IF @ContPrioritario = 3 BEGIN UPDATE @DocentesAnioSabatico SET Diplomados_Hombres = CASE WHEN @Hombres = 0 THEN ' ' ELSE CAST(@Hombres AS VARCHAR(255)) END, Diplomados_Mujeres = CASE WHEN @Mujeres = 0 THEN ' ' ELSE CAST(@Mujeres AS VARCHAR(255)) END WHERE UnidadAcademica = @SiglasActual; END ELSE IF @ContPrioritario = 4 BEGIN UPDATE @DocentesAnioSabatico SET Estancias_Hombres = CASE WHEN @Hombres = 0 THEN ' ' ELSE CAST(@Hombres AS VARCHAR(255)) END, Estancias_Mujeres = CASE WHEN @Mujeres = 0 THEN ' ' ELSE CAST(@Mujeres AS VARCHAR(255)) END WHERE UnidadAcademica = @SiglasActual; END ELSE IF @ContPrioritario = 5 BEGIN UPDATE @DocentesAnioSabatico SET Elaboracion_Hombres = CASE WHEN @Hombres = 0 THEN ' ' ELSE CAST(@Hombres AS VARCHAR(255)) END, Elaboracion_Mujeres = CASE WHEN @Mujeres = 0 THEN ' ' ELSE CAST(@Mujeres AS VARCHAR(255)) END WHERE UnidadAcademica = @SiglasActual; END
SET @Total_Prioritario = @Total_Prioritario + @Hombres + @Mujeres;
SET @ContPrioritario = @ContPrioritario + 1; END -- Actualizamos el Total de Prioritarios UPDATE @DocentesAnioSabatico SET Total_Prioritario = @Total_Prioritario, Total_Hombres = @Total_Hombres, Total_Mujeres = @Total_Mujeres, Total_General = @Total_NoPrioritario + @Total_Prioritario WHERE UnidadAcademica = @SiglasActual;
SET @ContSiglasUA = @ContSiglasUA + 1; SET @ContSiglasxRama = @ContSiglasxRama + 1; END
-- Agregamos una fila de TOTAL al final de cada Rama
IF @ContRama = 1 BEGIN SET @TituloRama = 'TOTAL RAMA ICFM'; END ELSE IF @ContRama = 2 BEGIN SET @TituloRama = 'TOTAL RAMA CMB'; END ELSE IF @ContRama = 3 BEGIN SET @TituloRama = 'TOTAL RAMA CSA'; END ELSE IF @ContRama = 4 BEGIN SET @TituloRama = 'TOTAL RAMA INTERDISCIPLINARIA'; END ELSE IF @ContRama = 5 BEGIN SET @TituloRama = 'TOTAL RAMA NO APLICA'; END INSERT INTO @DocentesAnioSabatico (UnidadAcademica, A1_Hombres, A1_Mujeres, A2_Hombres, A2_Mujeres, Actividades_Hombres, Actividades_Mujeres, EstParciales_Hombres, EstParciales_Mujeres, Individuales_Hombres, Individuales_Mujeres, Total_NoPrioritario, Investigacion_Hombres, Investigacion_Mujeres, EstPosgrado_Hombres, EstPosgrado_Mujeres, Diplomados_Hombres, Diplomados_Mujeres, Estancias_Hombres, Estancias_Mujeres, Elaboracion_Hombres, Elaboracion_Mujeres, Total_Prioritario, Total_Hombres, Total_Mujeres, Total_General) VALUES (@TituloRama, (SELECT ISNULL(SUM(CASE WHEN A1_Hombres = ' ' THEN 0 ELSE CAST(A1_Hombres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN A1_Mujeres = ' ' THEN 0 ELSE CAST(A1_Mujeres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN A2_Hombres = ' ' THEN 0 ELSE CAST(A2_Hombres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN A2_Mujeres = ' ' THEN 0 ELSE CAST(A2_Mujeres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Actividades_Hombres = ' ' THEN 0 ELSE CAST(Actividades_Hombres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Actividades_Mujeres = ' ' THEN 0 ELSE CAST(Actividades_Mujeres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN EstParciales_Hombres = ' ' THEN 0 ELSE CAST(EstParciales_Hombres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN EstParciales_Mujeres = ' ' THEN 0 ELSE CAST(EstParciales_Mujeres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Individuales_Hombres = ' ' THEN 0 ELSE CAST(Individuales_Hombres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Individuales_Mujeres = ' ' THEN 0 ELSE CAST(Individuales_Mujeres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(Total_NoPrioritario), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Investigacion_Hombres = ' ' THEN 0 ELSE CAST(Investigacion_Hombres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Investigacion_Mujeres = ' ' THEN 0 ELSE CAST(Investigacion_Mujeres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN EstPosgrado_Hombres = ' ' THEN 0 ELSE CAST(EstPosgrado_Hombres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN EstPosgrado_Mujeres = ' ' THEN 0 ELSE CAST(EstPosgrado_Mujeres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Diplomados_Hombres = ' ' THEN 0 ELSE CAST(Diplomados_Hombres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Diplomados_Mujeres = ' ' THEN 0 ELSE CAST(Diplomados_Mujeres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Estancias_Hombres = ' ' THEN 0 ELSE CAST(Estancias_Hombres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Estancias_Mujeres = ' ' THEN 0 ELSE CAST(Estancias_Mujeres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Elaboracion_Hombres = ' ' THEN 0 ELSE CAST(Elaboracion_Hombres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Elaboracion_Mujeres = ' ' THEN 0 ELSE CAST(Elaboracion_Mujeres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(Total_Prioritario), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(Total_Hombres), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(Total_Mujeres), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(Total_General), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama));
SET @ContRama = @ContRama + 1; END
INSERT INTO @DocentesAnioSabatico (UnidadAcademica, A1_Hombres, A1_Mujeres, A2_Hombres, A2_Mujeres, Actividades_Hombres, Actividades_Mujeres, EstParciales_Hombres, EstParciales_Mujeres, Individuales_Hombres, Individuales_Mujeres, Total_NoPrioritario, Investigacion_Hombres, Investigacion_Mujeres, EstPosgrado_Hombres, EstPosgrado_Mujeres, Diplomados_Hombres, Diplomados_Mujeres, Estancias_Hombres, Estancias_Mujeres, Elaboracion_Hombres, Elaboracion_Mujeres, Total_Prioritario, Total_Hombres, Total_Mujeres, Total_General) VALUES ('TOTAL', (SELECT ISNULL(SUM(CASE WHEN A1_Hombres = ' ' THEN 0 ELSE CAST(A1_Hombres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN A1_Mujeres = ' ' THEN 0 ELSE CAST(A1_Mujeres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN A2_Hombres = ' ' THEN 0 ELSE CAST(A2_Hombres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN A2_Mujeres = ' ' THEN 0 ELSE CAST(A2_Mujeres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Actividades_Hombres = ' ' THEN 0 ELSE CAST(Actividades_Hombres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Actividades_Mujeres = ' ' THEN 0 ELSE CAST(Actividades_Mujeres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN EstParciales_Hombres = ' ' THEN 0 ELSE CAST(EstParciales_Hombres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN EstParciales_Mujeres = ' ' THEN 0 ELSE CAST(EstParciales_Mujeres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Individuales_Hombres = ' ' THEN 0 ELSE CAST(Individuales_Hombres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Individuales_Mujeres = ' ' THEN 0 ELSE CAST(Individuales_Mujeres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(Total_NoPrioritario)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Investigacion_Hombres = ' ' THEN 0 ELSE CAST(Investigacion_Hombres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Investigacion_Mujeres = ' ' THEN 0 ELSE CAST(Investigacion_Mujeres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN EstPosgrado_Hombres = ' ' THEN 0 ELSE CAST(EstPosgrado_Hombres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN EstPosgrado_Mujeres = ' ' THEN 0 ELSE CAST(EstPosgrado_Mujeres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Diplomados_Hombres = ' ' THEN 0 ELSE CAST(Diplomados_Hombres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Diplomados_Mujeres = ' ' THEN 0 ELSE CAST(Diplomados_Mujeres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Estancias_Hombres = ' ' THEN 0 ELSE CAST(Estancias_Hombres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Estancias_Mujeres = ' ' THEN 0 ELSE CAST(Estancias_Mujeres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Elaboracion_Hombres = ' ' THEN 0 ELSE CAST(Elaboracion_Hombres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Elaboracion_Mujeres = ' ' THEN 0 ELSE CAST(Elaboracion_Mujeres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(Total_Prioritario)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(Total_Hombres)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(Total_Mujeres)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(Total_General)/2, 0) FROM @DocentesAnioSabatico));
SELECT UnidadAcademica, A1_Hombres, A1_Mujeres, A2_Hombres, A2_Mujeres, Actividades_Hombres, Actividades_Mujeres, EstParciales_Hombres, EstParciales_Mujeres, Individuales_Hombres, Individuales_Mujeres, Total_NoPrioritario, Investigacion_Hombres, Investigacion_Mujeres, EstPosgrado_Hombres, EstPosgrado_Mujeres, Diplomados_Hombres, Diplomados_Mujeres, Estancias_Hombres, Estancias_Mujeres, Elaboracion_Hombres, Elaboracion_Mujeres, Total_Prioritario, Total_Hombres, Total_Mujeres, Total_General FROM @DocentesAnioSabatico; END

-- Autor: Eduardo Castillo
CREATE PROCEDURE SAC_DocentesAnioSabaticoNMS
    @SiglasTipoUnidadAcademica VARCHAR(255),
    @AnioSemestre VARCHAR(255),
    @anio INT,
    @id_Trimestre INT
AS

BEGIN SET NOCOUNT ON; DECLARE @DocentesAnioSabatico TABLE ( ID_DocentesAnioSabatico INT IDENTITY(1,1), ID_Rama INT NOT NULL DEFAULT 0, UnidadAcademica VARCHAR(255) NOT NULL DEFAULT ' ', A1_Hombres VARCHAR(255) NOT NULL DEFAULT ' ', A1_Mujeres VARCHAR(255) NOT NULL DEFAULT ' ', A2_Hombres VARCHAR(255) NOT NULL DEFAULT ' ', A2_Mujeres VARCHAR(255) NOT NULL DEFAULT ' ', Actividades_Hombres VARCHAR(255) NOT NULL DEFAULT ' ', Actividades_Mujeres VARCHAR(255) NOT NULL DEFAULT ' ', EstParciales_Hombres VARCHAR(255) NOT NULL DEFAULT ' ', EstParciales_Mujeres VARCHAR(255) NOT NULL DEFAULT ' ', Individuales_Hombres VARCHAR(255) NOT NULL DEFAULT ' ', Individuales_Mujeres VARCHAR(255) NOT NULL DEFAULT ' ', Total_NoPrioritario INT NOT NULL DEFAULT 0,
Investigacion_Hombres VARCHAR(255) NOT NULL DEFAULT ' ', Investigacion_Mujeres VARCHAR(255) NOT NULL DEFAULT ' ', EstPosgrado_Hombres VARCHAR(255) NOT NULL DEFAULT ' ', EstPosgrado_Mujeres VARCHAR(255) NOT NULL DEFAULT ' ', Diplomados_Hombres VARCHAR(255) NOT NULL DEFAULT ' ', Diplomados_Mujeres VARCHAR(255) NOT NULL DEFAULT ' ', Estancias_Hombres VARCHAR(255) NOT NULL DEFAULT ' ', Estancias_Mujeres VARCHAR(255) NOT NULL DEFAULT ' ', Elaboracion_Hombres VARCHAR(255) NOT NULL DEFAULT ' ', Elaboracion_Mujeres VARCHAR(255) NOT NULL DEFAULT ' ', Total_Prioritario INT NOT NULL DEFAULT 0,
Total_Hombres INT NOT NULL DEFAULT 0, Total_Mujeres INT NOT NULL DEFAULT 0, Total_General INT NOT NULL DEFAULT 0 );
-- Tabla provisional estática para Unidades Academicas
DECLARE @UnidadesAcademicas TABLE( ID_UnidadAcademica INT IDENTITY(1,1), Siglas VARCHAR(255) NOT NULL DEFAULT ' ', ID_Rama INT NOT NULL DEFAULT 0 );
-- Aquí me aseguro de obtener las siglas de los Centros de la Base de Datos (por precaución) DECLARE @Centros_Investigacion VARCHAR(255) = (SELECT Desc_SiglasTipo FROM TipoUnidadAcademica WHERE Desc_TipoUnidadAcademica = 'UNIDADES ACADÉMICAS DE INVESTIGACIÓN CIENTÍFICA Y TECNOLÓGICA'); DECLARE @Centros_Innovacion VARCHAR(255) = (SELECT Desc_SiglasTipo FROM TipoUnidadAcademica WHERE Desc_TipoUnidadAcademica = 'UNIDADES ACADÉMICAS DE INNOVACION E INTEGRACION DE TECNOLOGÍAS AVANZADAS'); DECLARE @Unidades_Centrales VARCHAR(255) = (SELECT Desc_SiglasTipo FROM TipoUnidadAcademica WHERE Desc_TipoUnidadAcademica = 'UNIDADES DE ÁREA CENTRAL o ADMNISTRATIVAS'); DECLARE @UnidadesVinculacion VARCHAR(255) = (SELECT Desc_SiglasTipo FROM TipoUnidadAcademica WHERE Desc_TipoUnidadAcademica = 'UNIDADES ACADÉMICAS DE VINCULACION Y DESARROLLO REGIONAL'); -- Si se reciben Centros de Investigación (C INV), se agregan también los de Innovación IF @SiglasTipoUnidadAcademica = @Centros_Investigacion BEGIN INSERT INTO @UnidadesAcademicas(Siglas, ID_Rama) (SELECT UA.Siglas, RC.ID_RamaConocimiento FROM UnidadesAcademicas UA JOIN RamaConocimiento RC ON UA.id_RamaConocimiento = RC.ID_RamaConocimiento WHERE UA.id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @Centros_Investigacion) OR UA.id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @Centros_Innovacion) GROUP BY RC.ID_RamaConocimiento, UA.Siglas); END ELSE IF @SiglasTipoUnidadAcademica = @Unidades_Centrales -- Si se reciben Unidades de Área Central (UACA), se agregan también las de Vinculación BEGIN INSERT INTO @UnidadesAcademicas(Siglas, ID_Rama) (SELECT UA.Siglas, RC.ID_RamaConocimiento FROM UnidadesAcademicas UA JOIN RamaConocimiento RC ON UA.id_RamaConocimiento = RC.ID_RamaConocimiento WHERE UA.id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @Unidades_Centrales) OR UA.id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @UnidadesVinculacion) GROUP BY RC.ID_RamaConocimiento, UA.Siglas); END ELSE -- Si no se insertan solo las recibidas (NS, NMS) BEGIN INSERT INTO @UnidadesAcademicas(Siglas, ID_Rama) (SELECT UA.Siglas, RC.ID_RamaConocimiento FROM UnidadesAcademicas UA JOIN RamaConocimiento RC ON UA.id_RamaConocimiento = RC.ID_RamaConocimiento WHERE UA.id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @SiglasTipoUnidadAcademica) GROUP BY RC.ID_RamaConocimiento, UA.Siglas); END
-- Tabla provisional para programas No Prioritarios
DECLARE @TablaNoPrioritarios TABLE( ID_TablaNoPrioritarios INT IDENTITY(1,1), Desc_ProgramaSabatico VARCHAR(255) NOT NULL DEFAULT ' ' );
INSERT INTO @TablaNoPrioritarios SELECT Desc_ProgramasSabaticoSAC FROM SAC_ProgramasSabatico WHERE Desc_Prioridad = 'NO PRIORITARIOS';
-- Programas Prioritarios DECLARE @TablaPrioritarios TABLE( ID_TablaPrioritarios INT IDENTITY(1,1), Desc_ProgramaSabatico VARCHAR(255) );
INSERT INTO @TablaPrioritarios SELECT Desc_ProgramasSabaticoSAC FROM SAC_ProgramasSabatico WHERE Desc_Prioridad = 'PRIORITARIOS';
DECLARE @ContSiglasUA INT = 1; DECLARE @TituloRama VARCHAR(255) = ' ';
-- Iteraremos sobre las Ramas de Conocimiento para agregar su TOTAL al final de cada una DECLARE @ContRama INT = 1; WHILE @ContRama <= (SELECT COUNT(*) FROM RamaConocimiento) BEGIN -- Ingresamos a la tabla principal las Unidades Académicas de la Rama Actual INSERT INTO @DocentesAnioSabatico (ID_Rama, UnidadAcademica) SELECT ID_Rama, Siglas FROM @UnidadesAcademicas WHERE ID_Rama = @ContRama;
-- Iteraremos sobre cada Unidad Académica de la Rama Actual DECLARE @ContSiglasxRama INT = 1; WHILE @ContSiglasxRama <= (SELECT COUNT(*) FROM @UnidadesAcademicas WHERE ID_Rama = @ContRama) BEGIN -- Guardamos la Sigla Actual DECLARE @SiglasActual VARCHAR(255) = (SELECT Siglas FROM @UnidadesAcademicas WHERE ID_UnidadAcademica = @ContSiglasUA AND ID_Rama = @ContRama); DECLARE @Total_Hombres INT = 0; DECLARE @Total_Mujeres INT = 0;
-- Dividimos 2 WHILE en Programas No prioritarios y Programas prioritarios -- Esto para sacar el Total al final de cada Programa
DECLARE @Total_NoPrioritario INT = 0; -- WHILE que itera sobre cada Programa No Prioritario DECLARE @ContNoPrioritario INT = 1; WHILE @ContNoPrioritario <= (SELECT COUNT(*) FROM SAC_ProgramasSabatico WHERE Desc_Prioridad = 'NO PRIORITARIOS') BEGIN DECLARE @Hombres INT = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroSabaticos WHERE id_UnidadAcademicaSAC = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasActual) AND id_ProgramasSabaticoSAC = (SELECT id_ProgramasSabaticoSAC FROM SAC_ProgramasSabatico WHERE Desc_ProgramasSabaticoSAC = (SELECT Desc_ProgramaSabatico FROM @TablaNoPrioritarios WHERE ID_TablaNoPrioritarios = @ContNoPrioritario)) AND id_AnioSemestre = (SELECT ID_AnioSemestreSAC FROM SAC_AnioSemestre WHERE Desc_AnioSemestreSAC = @AnioSemestre) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0); SET @Total_Hombres = @Total_Hombres + @Hombres;
DECLARE @Mujeres INT = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroSabaticos WHERE id_UnidadAcademicaSAC = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasActual) AND id_ProgramasSabaticoSAC = (SELECT id_ProgramasSabaticoSAC FROM SAC_ProgramasSabatico WHERE Desc_ProgramasSabaticoSAC = (SELECT Desc_ProgramaSabatico FROM @TablaNoPrioritarios WHERE ID_TablaNoPrioritarios = @ContNoPrioritario)) AND id_AnioSemestre = (SELECT ID_AnioSemestreSAC FROM SAC_AnioSemestre WHERE Desc_AnioSemestreSAC = @AnioSemestre) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0); SET @Total_Mujeres = @Total_Mujeres + @Mujeres;
-- Condiciones que actualizarán en tabla principal los valores de Hombres y Mujeres IF @ContNoPrioritario = 1 BEGIN UPDATE @DocentesAnioSabatico SET A1_Hombres = CASE WHEN @Hombres = 0 THEN ' ' ELSE CAST(@Hombres AS VARCHAR(255)) END, A1_Mujeres = CASE WHEN @Mujeres = 0 THEN ' ' ELSE CAST(@Mujeres AS VARCHAR(255)) END WHERE UnidadAcademica = @SiglasActual; END ELSE IF @ContNoPrioritario = 2 BEGIN UPDATE @DocentesAnioSabatico SET A2_Hombres = CASE WHEN @Hombres = 0 THEN ' ' ELSE CAST(@Hombres AS VARCHAR(255)) END, A2_Mujeres = CASE WHEN @Mujeres = 0 THEN ' ' ELSE CAST(@Mujeres AS VARCHAR(255)) END WHERE UnidadAcademica = @SiglasActual; END ELSE IF @ContNoPrioritario = 3 BEGIN UPDATE @DocentesAnioSabatico SET Actividades_Hombres = CASE WHEN @Hombres = 0 THEN ' ' ELSE CAST(@Hombres AS VARCHAR(255)) END, Actividades_Mujeres = CASE WHEN @Mujeres = 0 THEN ' ' ELSE CAST(@Mujeres AS VARCHAR(255)) END WHERE UnidadAcademica = @SiglasActual; END ELSE IF @ContNoPrioritario = 4 BEGIN UPDATE @DocentesAnioSabatico SET EstParciales_Hombres = CASE WHEN @Hombres = 0 THEN ' ' ELSE CAST(@Hombres AS VARCHAR(255)) END, EstParciales_Mujeres = CASE WHEN @Mujeres = 0 THEN ' ' ELSE CAST(@Mujeres AS VARCHAR(255)) END WHERE UnidadAcademica = @SiglasActual; END ELSE IF @ContNoPrioritario = 5 BEGIN UPDATE @DocentesAnioSabatico SET Individuales_Hombres = CASE WHEN @Hombres = 0 THEN ' ' ELSE CAST(@Hombres AS VARCHAR(255)) END, Individuales_Mujeres = CASE WHEN @Mujeres = 0 THEN ' ' ELSE CAST(@Mujeres AS VARCHAR(255)) END WHERE UnidadAcademica = @SiglasActual; END
SET @Total_NoPrioritario = @Total_NoPrioritario + @Hombres + @Mujeres;
SET @ContNoPrioritario = @ContNoPrioritario + 1; END -- Actualizamos el Total de No Prioritarios UPDATE @DocentesAnioSabatico SET Total_NoPrioritario = @Total_NoPrioritario WHERE UnidadAcademica = @SiglasActual;
DECLARE @Total_Prioritario INT = 0; -- WHILE que itera sobre cada Programa Prioritario
DECLARE @ContPrioritario INT = 1; WHILE @ContPrioritario <= (SELECT COUNT(*) FROM SAC_ProgramasSabatico WHERE Desc_Prioridad = 'PRIORITARIOS') BEGIN SET @Hombres = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroSabaticos WHERE id_UnidadAcademicaSAC = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasActual) AND id_ProgramasSabaticoSAC = (SELECT id_ProgramasSabaticoSAC FROM SAC_ProgramasSabatico WHERE Desc_ProgramasSabaticoSAC = (SELECT Desc_ProgramaSabatico FROM @TablaPrioritarios WHERE ID_TablaPrioritarios = @ContPrioritario)) AND id_AnioSemestre = (SELECT ID_AnioSemestreSAC FROM SAC_AnioSemestre WHERE Desc_AnioSemestreSAC = @AnioSemestre) AND id_Trimestre = 4 AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0);
SET @Total_Hombres = @Total_Hombres + @Hombres;
SET @Mujeres = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroSabaticos WHERE id_UnidadAcademicaSAC = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @SiglasActual) AND id_ProgramasSabaticoSAC = (SELECT id_ProgramasSabaticoSAC FROM SAC_ProgramasSabatico WHERE Desc_ProgramasSabaticoSAC = (SELECT Desc_ProgramaSabatico FROM @TablaPrioritarios WHERE ID_TablaPrioritarios = @ContPrioritario)) AND id_AnioSemestre = (SELECT ID_AnioSemestreSAC FROM SAC_AnioSemestre WHERE Desc_AnioSemestreSAC = @AnioSemestre) AND id_Trimestre = 4 AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0);
SET @Total_Mujeres = @Total_Mujeres + @Mujeres;
-- Condiciones IF @ContPrioritario = 1 BEGIN UPDATE @DocentesAnioSabatico SET Investigacion_Hombres = CASE WHEN @Hombres = 0 THEN ' ' ELSE CAST(@Hombres AS VARCHAR(255)) END, Investigacion_Mujeres = CASE WHEN @Mujeres = 0 THEN ' ' ELSE CAST(@Mujeres AS VARCHAR(255)) END WHERE UnidadAcademica = @SiglasActual; END ELSE IF @ContPrioritario = 2 BEGIN UPDATE @DocentesAnioSabatico SET EstPosgrado_Hombres = CASE WHEN @Hombres = 0 THEN ' ' ELSE CAST(@Hombres AS VARCHAR(255)) END, EstPosgrado_Mujeres = CASE WHEN @Mujeres = 0 THEN ' ' ELSE CAST(@Mujeres AS VARCHAR(255)) END WHERE UnidadAcademica = @SiglasActual; END ELSE IF @ContPrioritario = 3 BEGIN UPDATE @DocentesAnioSabatico SET Diplomados_Hombres = CASE WHEN @Hombres = 0 THEN ' ' ELSE CAST(@Hombres AS VARCHAR(255)) END, Diplomados_Mujeres = CASE WHEN @Mujeres = 0 THEN ' ' ELSE CAST(@Mujeres AS VARCHAR(255)) END WHERE UnidadAcademica = @SiglasActual; END ELSE IF @ContPrioritario = 4 BEGIN UPDATE @DocentesAnioSabatico SET Estancias_Hombres = CASE WHEN @Hombres = 0 THEN ' ' ELSE CAST(@Hombres AS VARCHAR(255)) END, Estancias_Mujeres = CASE WHEN @Mujeres = 0 THEN ' ' ELSE CAST(@Mujeres AS VARCHAR(255)) END WHERE UnidadAcademica = @SiglasActual; END ELSE IF @ContPrioritario = 5 BEGIN UPDATE @DocentesAnioSabatico SET Elaboracion_Hombres = CASE WHEN @Hombres = 0 THEN ' ' ELSE CAST(@Hombres AS VARCHAR(255)) END, Elaboracion_Mujeres = CASE WHEN @Mujeres = 0 THEN ' ' ELSE CAST(@Mujeres AS VARCHAR(255)) END WHERE UnidadAcademica = @SiglasActual; END
SET @Total_Prioritario = @Total_Prioritario + @Hombres + @Mujeres;
SET @ContPrioritario = @ContPrioritario + 1; END -- Actualizamos el Total de Prioritarios UPDATE @DocentesAnioSabatico SET Total_Prioritario = @Total_Prioritario, Total_Hombres = @Total_Hombres, Total_Mujeres = @Total_Mujeres, Total_General = @Total_NoPrioritario + @Total_Prioritario WHERE UnidadAcademica = @SiglasActual;
SET @ContSiglasUA = @ContSiglasUA + 1; SET @ContSiglasxRama = @ContSiglasxRama + 1; END
-- Agregamos una fila de TOTAL al final de cada Rama
IF @ContRama = 1 BEGIN SET @TituloRama = 'TOTAL RAMA ICFM'; END ELSE IF @ContRama = 2 BEGIN SET @TituloRama = 'TOTAL RAMA CMB'; END ELSE IF @ContRama = 3 BEGIN SET @TituloRama = 'TOTAL RAMA CSA'; END ELSE IF @ContRama = 4 BEGIN SET @TituloRama = 'TOTAL RAMA INTERDISCIPLINARIA'; END ELSE IF @ContRama = 5 BEGIN SET @TituloRama = 'TOTAL RAMA NO APLICA'; END INSERT INTO @DocentesAnioSabatico (UnidadAcademica, A1_Hombres, A1_Mujeres, A2_Hombres, A2_Mujeres, Actividades_Hombres, Actividades_Mujeres, EstParciales_Hombres, EstParciales_Mujeres, Individuales_Hombres, Individuales_Mujeres, Total_NoPrioritario, Investigacion_Hombres, Investigacion_Mujeres, EstPosgrado_Hombres, EstPosgrado_Mujeres, Diplomados_Hombres, Diplomados_Mujeres, Estancias_Hombres, Estancias_Mujeres, Elaboracion_Hombres, Elaboracion_Mujeres, Total_Prioritario, Total_Hombres, Total_Mujeres, Total_General) VALUES (@TituloRama, (SELECT ISNULL(SUM(CASE WHEN A1_Hombres = ' ' THEN 0 ELSE CAST(A1_Hombres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN A1_Mujeres = ' ' THEN 0 ELSE CAST(A1_Mujeres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN A2_Hombres = ' ' THEN 0 ELSE CAST(A2_Hombres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN A2_Mujeres = ' ' THEN 0 ELSE CAST(A2_Mujeres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Actividades_Hombres = ' ' THEN 0 ELSE CAST(Actividades_Hombres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Actividades_Mujeres = ' ' THEN 0 ELSE CAST(Actividades_Mujeres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN EstParciales_Hombres = ' ' THEN 0 ELSE CAST(EstParciales_Hombres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN EstParciales_Mujeres = ' ' THEN 0 ELSE CAST(EstParciales_Mujeres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Individuales_Hombres = ' ' THEN 0 ELSE CAST(Individuales_Hombres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Individuales_Mujeres = ' ' THEN 0 ELSE CAST(Individuales_Mujeres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(Total_NoPrioritario), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Investigacion_Hombres = ' ' THEN 0 ELSE CAST(Investigacion_Hombres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Investigacion_Mujeres = ' ' THEN 0 ELSE CAST(Investigacion_Mujeres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN EstPosgrado_Hombres = ' ' THEN 0 ELSE CAST(EstPosgrado_Hombres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN EstPosgrado_Mujeres = ' ' THEN 0 ELSE CAST(EstPosgrado_Mujeres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Diplomados_Hombres = ' ' THEN 0 ELSE CAST(Diplomados_Hombres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Diplomados_Mujeres = ' ' THEN 0 ELSE CAST(Diplomados_Mujeres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Estancias_Hombres = ' ' THEN 0 ELSE CAST(Estancias_Hombres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Estancias_Mujeres = ' ' THEN 0 ELSE CAST(Estancias_Mujeres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Elaboracion_Hombres = ' ' THEN 0 ELSE CAST(Elaboracion_Hombres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(CASE WHEN Elaboracion_Mujeres = ' ' THEN 0 ELSE CAST(Elaboracion_Mujeres AS VARCHAR(255)) END), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(Total_Prioritario), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(Total_Hombres), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(Total_Mujeres), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama), (SELECT ISNULL(SUM(Total_General), 0) FROM @DocentesAnioSabatico WHERE ID_Rama = @ContRama));
SET @ContRama = @ContRama + 1; END
INSERT INTO @DocentesAnioSabatico (UnidadAcademica, A1_Hombres, A1_Mujeres, A2_Hombres, A2_Mujeres, Actividades_Hombres, Actividades_Mujeres, EstParciales_Hombres, EstParciales_Mujeres, Individuales_Hombres, Individuales_Mujeres, Total_NoPrioritario, Investigacion_Hombres, Investigacion_Mujeres, EstPosgrado_Hombres, EstPosgrado_Mujeres, Diplomados_Hombres, Diplomados_Mujeres, Estancias_Hombres, Estancias_Mujeres, Elaboracion_Hombres, Elaboracion_Mujeres, Total_Prioritario, Total_Hombres, Total_Mujeres, Total_General) VALUES ('TOTAL', (SELECT ISNULL(SUM(CASE WHEN A1_Hombres = ' ' THEN 0 ELSE CAST(A1_Hombres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN A1_Mujeres = ' ' THEN 0 ELSE CAST(A1_Mujeres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN A2_Hombres = ' ' THEN 0 ELSE CAST(A2_Hombres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN A2_Mujeres = ' ' THEN 0 ELSE CAST(A2_Mujeres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Actividades_Hombres = ' ' THEN 0 ELSE CAST(Actividades_Hombres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Actividades_Mujeres = ' ' THEN 0 ELSE CAST(Actividades_Mujeres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN EstParciales_Hombres = ' ' THEN 0 ELSE CAST(EstParciales_Hombres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN EstParciales_Mujeres = ' ' THEN 0 ELSE CAST(EstParciales_Mujeres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Individuales_Hombres = ' ' THEN 0 ELSE CAST(Individuales_Hombres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Individuales_Mujeres = ' ' THEN 0 ELSE CAST(Individuales_Mujeres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(Total_NoPrioritario)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Investigacion_Hombres = ' ' THEN 0 ELSE CAST(Investigacion_Hombres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Investigacion_Mujeres = ' ' THEN 0 ELSE CAST(Investigacion_Mujeres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN EstPosgrado_Hombres = ' ' THEN 0 ELSE CAST(EstPosgrado_Hombres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN EstPosgrado_Mujeres = ' ' THEN 0 ELSE CAST(EstPosgrado_Mujeres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Diplomados_Hombres = ' ' THEN 0 ELSE CAST(Diplomados_Hombres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Diplomados_Mujeres = ' ' THEN 0 ELSE CAST(Diplomados_Mujeres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Estancias_Hombres = ' ' THEN 0 ELSE CAST(Estancias_Hombres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Estancias_Mujeres = ' ' THEN 0 ELSE CAST(Estancias_Mujeres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Elaboracion_Hombres = ' ' THEN 0 ELSE CAST(Elaboracion_Hombres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(CASE WHEN Elaboracion_Mujeres = ' ' THEN 0 ELSE CAST(Elaboracion_Mujeres AS VARCHAR(255)) END)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(Total_Prioritario)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(Total_Hombres)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(Total_Mujeres)/2, 0) FROM @DocentesAnioSabatico), (SELECT ISNULL(SUM(Total_General)/2, 0) FROM @DocentesAnioSabatico));
SELECT UnidadAcademica, A1_Hombres, A1_Mujeres, A2_Hombres, A2_Mujeres, Actividades_Hombres, Actividades_Mujeres, EstParciales_Hombres, EstParciales_Mujeres, Individuales_Hombres, Individuales_Mujeres, Total_NoPrioritario, Investigacion_Hombres, Investigacion_Mujeres, EstPosgrado_Hombres, EstPosgrado_Mujeres, Diplomados_Hombres, Diplomados_Mujeres, Estancias_Hombres, Estancias_Mujeres, Elaboracion_Hombres, Elaboracion_Mujeres, Total_Prioritario, Total_Hombres, Total_Mujeres, Total_General FROM @DocentesAnioSabatico; END

-- Formato [1, 2, 3, 4, 6, 7, 8, 9] Docentes en año/semestre Todas las áreas
-- Autor: Eduardo Castillo
CREATE PROCEDURE SAC_DocentesAnioSemestreSabatico
    @SiglasTipoUnidadAcademica VARCHAR(255),
    @AnioSemestre VARCHAR(255),
    @anio INT,
    @id_Trimestre INT
AS

BEGIN SET NOCOUNT ON; DECLARE @DocentesSabaticoAcumulado TABLE( Nivel VARCHAR(255) NOT NULL DEFAULT ' ',
T1_AnioHombres VARCHAR(255) NOT NULL DEFAULT ' ', T1_AnioMujeres VARCHAR(255) NOT NULL DEFAULT ' ', T1_SemestreHombres VARCHAR(255) NOT NULL DEFAULT ' ', T1_SemestreMujeres VARCHAR(255) NOT NULL DEFAULT ' ', T1_TotalHombres INT NOT NULL DEFAULT 0, T1_TotalMujeres INT NOT NULL DEFAULT 0, T1_Total INT NOT NULL DEFAULT 0,
T2_AnioHombres VARCHAR(255) NOT NULL DEFAULT ' ', T2_AnioMujeres VARCHAR(255) NOT NULL DEFAULT ' ', T2_SemestreHombres VARCHAR(255) NOT NULL DEFAULT ' ', T2_SemestreMujeres VARCHAR(255) NOT NULL DEFAULT ' ', T2_TotalHombres INT NOT NULL DEFAULT 0, T2_TotalMujeres INT NOT NULL DEFAULT 0, T2_Total INT NOT NULL DEFAULT 0,
T3_AnioHombres VARCHAR(255) NOT NULL DEFAULT ' ', T3_AnioMujeres VARCHAR(255) NOT NULL DEFAULT ' ', T3_SemestreHombres VARCHAR(255) NOT NULL DEFAULT ' ', T3_SemestreMujeres VARCHAR(255) NOT NULL DEFAULT ' ', T3_TotalHombres INT NOT NULL DEFAULT 0, T3_TotalMujeres INT NOT NULL DEFAULT 0, T3_Total INT NOT NULL DEFAULT 0,
T4_AnioHombres VARCHAR(255) NOT NULL DEFAULT ' ', T4_AnioMujeres VARCHAR(255) NOT NULL DEFAULT ' ', T4_SemestreHombres VARCHAR(255) NOT NULL DEFAULT ' ', T4_SemestreMujeres VARCHAR(255) NOT NULL DEFAULT ' ', T4_TotalHombres INT NOT NULL DEFAULT 0, T4_TotalMujeres INT NOT NULL DEFAULT 0, T4_Total INT NOT NULL DEFAULT 0,
Cifras_AnioHombres INT NOT NULL DEFAULT 0, Cifras_AnioMujeres INT NOT NULL DEFAULT 0, Cifras_SemestreHombres INT NOT NULL DEFAULT 0, Cifras_SemestreMujeres INT NOT NULL DEFAULT 0, Cifras_TotalHombres INT NOT NULL DEFAULT 0, Cifras_TotalMujeres INT NOT NULL DEFAULT 0, Cifras_Total INT NOT NULL DEFAULT 0 );
-- Cómo quiero mostrar el texto de cada Nivel DECLARE @NMS VARCHAR(255) = 'MEDIO SUPERIOR'; DECLARE @NS_PS VARCHAR(255) = 'SUPERIOR Y POSGRADO'; DECLARE @CINV_CIITA VARCHAR(255) = 'CENTROS DE INVESTIGACIÓN'; DECLARE @UACA_UAVDR VARCHAR(255) = 'AREA CENTRAL'; -- Tabla de Niveles para poder iterar sobre su ID y además respetar el orden DECLARE @Niveles TABLE ( ID_Nivel INT IDENTITY(1,1), Desc_Nivel VARCHAR(255) NOT NULL DEFAULT ' ', Origen_Justificaciones INT DEFAULT 0 -- Aquí tendrá la id de TipoUnidadAcademica de donde se sacará la justificación );
INSERT INTO @Niveles (Desc_Nivel) VALUES (@NMS), (@NS_PS), (@CINV_CIITA), (@UACA_UAVDR); -- Tabla de las áreas incluídas en cada Nivel porque algunos niveles contienen más de una DECLARE @TipoUA TABLE ( ID_Areas INT IDENTITY(1,1), Desc_TipoUA VARCHAR(255) NOT NULL DEFAULT ' ', Desc_Nivel VARCHAR(255) NOT NULL DEFAULT ' ' );
INSERT INTO @TipoUA (Desc_TipoUA, Desc_Nivel) VALUES ('NMS', @NMS), ('NS', @NS_PS), ('C INV', @CINV_CIITA), ('CIITA', @CINV_CIITA), ('UACA', @UACA_UAVDR), ('UAVDR', @UACA_UAVDR); -- Declaro todas las variables a usar DECLARE @NivelActual VARCHAR(255) = ''; DECLARE @CifrasParaT4_AnioHombres INT = 0; DECLARE @CifrasParaT4_AnioMujeres INT = 0; DECLARE @CifrasParaT4_SemestreHombres INT = 0; DECLARE @CifrasParaT4_SemestreMujeres INT = 0;
DECLARE @AnioHombres INT = 0; DECLARE @AnioMujeres INT = 0; DECLARE @SemestreHombres INT = 0; DECLARE @SemestreMujeres INT = 0;
DECLARE @ContNiveles INT = 1; DECLARE @ContTrimestres INT = 1; -- Itero por los 4 Niveles WHILE @ContNiveles <= (SELECT COUNT(*) FROM @Niveles) BEGIN
SET @NivelActual = (SELECT Desc_Nivel FROM @Niveles WHERE ID_Nivel = @ContNiveles);
INSERT INTO @DocentesSabaticoAcumulado (Nivel) VALUES (@NivelActual);
-- Itero por el no. de Trimestres recibidos por parámetro SET @ContTrimestres = 1; WHILE @ContTrimestres <= @id_trimestre BEGIN SET @AnioHombres = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroSabaticos WHERE id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas UA JOIN TipoUnidadAcademica TUA ON UA.id_TipoUnidadAcademica = TUA.ID_TipoUnidadAcademica WHERE TUA.Desc_SiglasTipo IN (SELECT Desc_TipoUA FROM @TipoUA WHERE Desc_Nivel IN (SELECT Desc_Nivel FROM @Niveles WHERE ID_Nivel = @ContNiveles))) AND id_AnioSemestre = (SELECT ID_AnioSemestreSAC FROM SAC_AnioSemestre WHERE Desc_AnioSemestreSAC = 'AÑO') AND id_Trimestre = @ContTrimestres AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0); SET @AnioMujeres = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroSabaticos WHERE id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas UA JOIN TipoUnidadAcademica TUA ON UA.id_TipoUnidadAcademica = TUA.ID_TipoUnidadAcademica WHERE TUA.Desc_SiglasTipo IN (SELECT Desc_TipoUA FROM @TipoUA WHERE Desc_Nivel IN (SELECT Desc_Nivel FROM @Niveles WHERE ID_Nivel = @ContNiveles))) AND id_AnioSemestre = (SELECT ID_AnioSemestreSAC FROM SAC_AnioSemestre WHERE Desc_AnioSemestreSAC = 'AÑO') AND id_Trimestre = @ContTrimestres AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0);
SET @SemestreHombres = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroSabaticos WHERE id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas UA JOIN TipoUnidadAcademica TUA ON UA.id_TipoUnidadAcademica = TUA.ID_TipoUnidadAcademica WHERE TUA.Desc_SiglasTipo IN (SELECT Desc_TipoUA FROM @TipoUA WHERE Desc_Nivel IN (SELECT Desc_Nivel FROM @Niveles WHERE ID_Nivel = @ContNiveles))) AND id_AnioSemestre = (SELECT ID_AnioSemestreSAC FROM SAC_AnioSemestre WHERE Desc_AnioSemestreSAC = 'SEMESTRE') AND id_Trimestre = @ContTrimestres AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0); SET @SemestreMujeres = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroSabaticos WHERE id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas UA JOIN TipoUnidadAcademica TUA ON UA.id_TipoUnidadAcademica = TUA.ID_TipoUnidadAcademica WHERE TUA.Desc_SiglasTipo IN (SELECT Desc_TipoUA FROM @TipoUA WHERE Desc_Nivel IN (SELECT Desc_Nivel FROM @Niveles WHERE ID_Nivel = @ContNiveles))) AND id_AnioSemestre = (SELECT ID_AnioSemestreSAC FROM SAC_AnioSemestre WHERE Desc_AnioSemestreSAC = 'SEMESTRE') AND id_Trimestre = @ContTrimestres AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0);
IF @ContTrimestres = 1 BEGIN UPDATE @DocentesSabaticoAcumulado SET T1_AnioHombres = CAST(@AnioHombres AS VARCHAR(255)), T1_AnioMujeres = CAST(@AnioMujeres AS VARCHAR(255)), T1_SemestreHombres = CAST(@SemestreHombres AS VARCHAR(255)), T1_SemestreMujeres = CAST(@SemestreMujeres AS VARCHAR(255)), T1_TotalHombres = @AnioHombres + @SemestreHombres, T1_TotalMujeres = @AnioMujeres + @SemestreMujeres, T1_Total = @AnioHombres + @SemestreHombres + @AnioMujeres + @SemestreMujeres, Cifras_AnioHombres = @AnioHombres, Cifras_AnioMujeres = @AnioMujeres, Cifras_SemestreHombres = @SemestreHombres, Cifras_SemestreMujeres = @SemestreMujeres, Cifras_TotalHombres = @AnioHombres + @SemestreHombres, Cifras_TotalMujeres = @AnioMujeres + @SemestreMujeres, Cifras_Total = @AnioHombres + @SemestreHombres + @AnioMujeres + @SemestreMujeres WHERE Nivel = @NivelActual; END ELSE IF @ContTrimestres = 2 BEGIN UPDATE @DocentesSabaticoAcumulado SET T2_AnioHombres = CAST(@AnioHombres AS VARCHAR(255)), T2_AnioMujeres = CAST(@AnioMujeres AS VARCHAR(255)), T2_SemestreHombres = CAST(@SemestreHombres AS VARCHAR(255)), T2_SemestreMujeres = CAST(@SemestreMujeres AS VARCHAR(255)), T2_TotalHombres = @AnioHombres + @SemestreHombres, T2_TotalMujeres = @AnioMujeres + @SemestreMujeres, T2_Total = @AnioHombres + @SemestreHombres + @AnioMujeres + @SemestreMujeres, Cifras_AnioHombres = @AnioHombres, Cifras_AnioMujeres = @AnioMujeres, Cifras_SemestreHombres = @SemestreHombres, Cifras_SemestreMujeres = @SemestreMujeres, Cifras_TotalHombres = @AnioHombres + @SemestreHombres, Cifras_TotalMujeres = @AnioMujeres + @SemestreMujeres, Cifras_Total = @AnioHombres + @SemestreHombres + @AnioMujeres + @SemestreMujeres WHERE Nivel = @NivelActual; END ELSE IF @ContTrimestres = 3 BEGIN -- Se guardarán los resultados del trimestre 3 -- Para copiarlos en caso de trimestre = 4 SET @CifrasParaT4_AnioHombres = @AnioHombres; SET @CifrasParaT4_AnioMujeres = @AnioMujeres; SET @CifrasParaT4_SemestreHombres = @SemestreHombres; SET @CifrasParaT4_SemestreMujeres = @SemestreMujeres; UPDATE @DocentesSabaticoAcumulado SET T3_AnioHombres = CAST(@AnioHombres AS VARCHAR(255)), T3_AnioMujeres = CAST(@AnioMujeres AS VARCHAR(255)), T3_SemestreHombres = CAST(@SemestreHombres AS VARCHAR(255)), T3_SemestreMujeres = CAST(@SemestreMujeres AS VARCHAR(255)), T3_TotalHombres = @AnioHombres + @SemestreHombres, T3_TotalMujeres = @AnioMujeres + @SemestreMujeres, T3_Total = @AnioHombres + @SemestreHombres + @AnioMujeres + @SemestreMujeres, Cifras_AnioHombres = @AnioHombres, Cifras_AnioMujeres = @AnioMujeres, Cifras_SemestreHombres = @SemestreHombres, Cifras_SemestreMujeres = @SemestreMujeres, Cifras_TotalHombres = @AnioHombres + @SemestreHombres, Cifras_TotalMujeres = @AnioMujeres + @SemestreMujeres, Cifras_Total = @AnioHombres + @SemestreHombres + @AnioMujeres + @SemestreMujeres WHERE Nivel = @NivelActual; END ELSE IF @ContTrimestres = 4 BEGIN UPDATE @DocentesSabaticoAcumulado SET T4_AnioHombres = CAST(@AnioHombres AS VARCHAR(255)), T4_AnioMujeres = CAST(@AnioMujeres AS VARCHAR(255)), T4_SemestreHombres = CAST(@SemestreHombres AS VARCHAR(255)), T4_SemestreMujeres = CAST(@SemestreMujeres AS VARCHAR(255)), T4_TotalHombres = @AnioHombres + @SemestreHombres, T4_TotalMujeres = @AnioMujeres + @SemestreMujeres, T4_Total = @AnioHombres + @SemestreHombres + @AnioMujeres + @SemestreMujeres, Cifras_AnioHombres = @CifrasParaT4_AnioHombres, Cifras_AnioMujeres = @CifrasParaT4_AnioMujeres, Cifras_SemestreHombres = @CifrasParaT4_SemestreHombres, Cifras_SemestreMujeres = @CifrasParaT4_SemestreMujeres, Cifras_TotalHombres = @CifrasParaT4_AnioHombres + @CifrasParaT4_SemestreHombres, Cifras_TotalMujeres = @CifrasParaT4_AnioMujeres + @CifrasParaT4_SemestreMujeres, Cifras_Total = @CifrasParaT4_AnioHombres + @CifrasParaT4_SemestreHombres + @CifrasParaT4_AnioMujeres + @CifrasParaT4_SemestreMujeres WHERE Nivel = @NivelActual; END
SET @ContTrimestres = @ContTrimestres + 1; END
SET @ContNiveles = @ContNiveles + 1; END
INSERT INTO @DocentesSabaticoAcumulado SELECT 'TOTAL', CAST(SUM(CAST(T1_AnioHombres AS INT)) AS VARCHAR(255)), CAST(SUM(CAST(T1_AnioMujeres AS INT)) AS VARCHAR(255)), CAST(SUM(CAST(T1_SemestreHombres AS INT)) AS VARCHAR(255)), CAST(SUM(CAST(T1_SemestreMujeres AS INT)) AS VARCHAR(255)), SUM(T1_TotalHombres), SUM(T1_TotalMujeres), SUM(T1_Total), CAST(SUM(CAST(T2_AnioHombres AS INT)) AS VARCHAR(255)), CAST(SUM(CAST(T2_AnioMujeres AS INT)) AS VARCHAR(255)), CAST(SUM(CAST(T2_SemestreHombres AS INT)) AS VARCHAR(255)), CAST(SUM(CAST(T2_SemestreMujeres AS INT)) AS VARCHAR(255)), SUM(T2_TotalHombres), SUM(T2_TotalMujeres), SUM(T2_Total), CAST(SUM(CAST(T3_AnioHombres AS INT)) AS VARCHAR(255)), CAST(SUM(CAST(T3_AnioMujeres AS INT)) AS VARCHAR(255)), CAST(SUM(CAST(T3_SemestreHombres AS INT)) AS VARCHAR(255)), CAST(SUM(CAST(T3_SemestreMujeres AS INT)) AS VARCHAR(255)), SUM(T3_TotalHombres), SUM(T3_TotalMujeres), SUM(T3_Total), CAST(SUM(CAST(T4_AnioHombres AS INT)) AS VARCHAR(255)), CAST(SUM(CAST(T4_AnioMujeres AS INT)) AS VARCHAR(255)), CAST(SUM(CAST(T4_SemestreHombres AS INT)) AS VARCHAR(255)), CAST(SUM(CAST(T4_SemestreMujeres AS INT)) AS VARCHAR(255)), SUM(T4_TotalHombres), SUM(T4_TotalMujeres), SUM(T4_Total), SUM(Cifras_AnioHombres), SUM(Cifras_AnioMujeres), SUM(Cifras_SemestreHombres), SUM(Cifras_SemestreMujeres), SUM(Cifras_TotalHombres), SUM(Cifras_TotalMujeres), SUM(Cifras_Total) FROM @DocentesSabaticoAcumulado;
SELECT * FROM @DocentesSabaticoAcumulado;
END

-- Formato 11 Docentes en anio y semestre sabatico acumulado al periodo
-- Autor: Eduardo Castillo
CREATE PROCEDURE SAC_DocentesAnioSemestreSabaticoAcumulado
    @anio INT,
    @id_trimestre INT
AS

BEGIN SET NOCOUNT ON; DECLARE @DocentesSabaticoComparativo TABLE( Nivel VARCHAR(255) NOT NULL DEFAULT ' ',
AntAnio INT NOT NULL DEFAULT 0, AntSemestre INT NOT NULL DEFAULT 0, AntTotal INT NOT NULL DEFAULT 0,
ActHombresAnio INT NOT NULL DEFAULT 0, ActMujeresAnio INT NOT NULL DEFAULT 0, ActTotalAnio INT NOT NULL DEFAULT 0, ActHombresSemestre INT NOT NULL DEFAULT 0, ActMujeresSemestre INT NOT NULL DEFAULT 0, ActTotalSemestre INT NOT NULL DEFAULT 0,
Variacion DECIMAL(3, 2) NOT NULL DEFAULT 0, Justificacion VARCHAR(255) NOT NULL DEFAULT ' ' ); -- Cómo quiero mostrar el texto de cada Nivel DECLARE @NMS VARCHAR(255) = 'MEDIO SUPERIOR'; DECLARE @NS_PS VARCHAR(255) = 'SUPERIOR Y POSGRADO'; DECLARE @CINV_CIITA VARCHAR(255) = 'CENTROS DE INVESTIGACIÓN'; DECLARE @UACA_UAVDR VARCHAR(255) = 'AREA CENTRAL'; -- Tabla de Niveles para poder iterar sobre su ID y además respetar el orden DECLARE @Niveles TABLE ( ID_Nivel INT IDENTITY(1,1), Desc_Nivel VARCHAR(255) NOT NULL DEFAULT ' ', Origen_Justificaciones INT DEFAULT 0 -- Aquí tendrá la id de TipoUnidadAcademica de donde se sacará la justificación ); -- La justificación de C INV y CIITA se encuentra en SAC_Justificaciones -> C INV -- Y la de UACA y UAVDR en SAC_Justificaciones -> UACA DECLARE @OrigenNMS INT = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'NMS'); DECLARE @OrigenNS INT = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'NS'); DECLARE @OrigenCINV INT = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'C INV'); DECLARE @OrigenUACA INT = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = 'UACA');
INSERT INTO @Niveles (Desc_Nivel, Origen_Justificaciones) VALUES (@NMS, @OrigenNMS), (@NS_PS, @OrigenNS), (@CINV_CIITA, @OrigenCINV), (@UACA_UAVDR, @OrigenUACA); -- Tabla de las áreas incluídas en cada Nivel porque algunos niveles contienen más de una DECLARE @TipoUA TABLE ( ID_Areas INT IDENTITY(1,1), Desc_TipoUA VARCHAR(255) NOT NULL DEFAULT ' ', Desc_Nivel VARCHAR(255) NOT NULL DEFAULT ' ' );
INSERT INTO @TipoUA (Desc_TipoUA, Desc_Nivel) VALUES ('NMS', @NMS), ('NS', @NS_PS), ('C INV', @CINV_CIITA), ('CIITA', @CINV_CIITA), ('UACA', @UACA_UAVDR), ('UAVDR', @UACA_UAVDR); -- Declaro todas las variables a usar DECLARE @ProgramaApoyo VARCHAR(255) = 'AÑO Y SEMESTRE SABÁTICO'; -- Para el filtro al recuperar de tabla SAC_Justificaciones DECLARE @anio_anterior INT = @anio - 1; DECLARE @NivelActual VARCHAR(255) = '';
DECLARE @AntAnio INT = 0; DECLARE @AntSemestre INT = 0; DECLARE @ActHombresAnio INT = 0; DECLARE @ActMujeresAnio INT = 0; DECLARE @ActHombresSemestre INT = 0; DECLARE @ActMujeresSemestre INT = 0; DECLARE @Variacion DECIMAL(3, 2) = 0; DECLARE @Justificacion VARCHAR(255) = '';
DECLARE @ContNiveles INT = 1; -- Itero por los 4 Niveles WHILE @ContNiveles <= (SELECT COUNT(*) FROM @Niveles) BEGIN
SET @NivelActual = (SELECT Desc_Nivel FROM @Niveles WHERE ID_Nivel = @ContNiveles);
SET @AntAnio = ISNULL((SELECT SUM(Desc_Hombres + Desc_Mujeres) FROM SAC_RegistroSabaticos WHERE id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas UA JOIN TipoUnidadAcademica TUA ON UA.id_TipoUnidadAcademica = TUA.ID_TipoUnidadAcademica WHERE TUA.Desc_SiglasTipo IN (SELECT Desc_TipoUA FROM @TipoUA WHERE Desc_Nivel IN (SELECT Desc_Nivel FROM @Niveles WHERE ID_Nivel = @ContNiveles))) AND id_AnioSemestre = (SELECT ID_AnioSemestreSAC FROM SAC_AnioSemestre WHERE Desc_AnioSemestreSAC = 'AÑO') AND id_Trimestre BETWEEN 1 AND @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio_anterior)), 0);
SET @AntSemestre = ISNULL((SELECT SUM(Desc_Hombres + Desc_Mujeres) FROM SAC_RegistroSabaticos WHERE id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas UA JOIN TipoUnidadAcademica TUA ON UA.id_TipoUnidadAcademica = TUA.ID_TipoUnidadAcademica WHERE TUA.Desc_SiglasTipo IN (SELECT Desc_TipoUA FROM @TipoUA WHERE Desc_Nivel IN (SELECT Desc_Nivel FROM @Niveles WHERE ID_Nivel = @ContNiveles))) AND id_AnioSemestre = (SELECT ID_AnioSemestreSAC FROM SAC_AnioSemestre WHERE Desc_AnioSemestreSAC = 'SEMESTRE') AND id_Trimestre BETWEEN 1 AND @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio_anterior)), 0);
SET @ActHombresAnio = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroSabaticos WHERE id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas UA JOIN TipoUnidadAcademica TUA ON UA.id_TipoUnidadAcademica = TUA.ID_TipoUnidadAcademica WHERE TUA.Desc_SiglasTipo IN (SELECT Desc_TipoUA FROM @TipoUA WHERE Desc_Nivel IN (SELECT Desc_Nivel FROM @Niveles WHERE ID_Nivel = @ContNiveles))) AND id_AnioSemestre = (SELECT ID_AnioSemestreSAC FROM SAC_AnioSemestre WHERE Desc_AnioSemestreSAC = 'AÑO') AND id_Trimestre BETWEEN 1 AND @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0); SET @ActMujeresAnio = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroSabaticos WHERE id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas UA JOIN TipoUnidadAcademica TUA ON UA.id_TipoUnidadAcademica = TUA.ID_TipoUnidadAcademica WHERE TUA.Desc_SiglasTipo IN (SELECT Desc_TipoUA FROM @TipoUA WHERE Desc_Nivel IN (SELECT Desc_Nivel FROM @Niveles WHERE ID_Nivel = @ContNiveles))) AND id_AnioSemestre = (SELECT ID_AnioSemestreSAC FROM SAC_AnioSemestre WHERE Desc_AnioSemestreSAC = 'AÑO') AND id_Trimestre BETWEEN 1 AND @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0);
SET @ActHombresSemestre = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroSabaticos WHERE id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas UA JOIN TipoUnidadAcademica TUA ON UA.id_TipoUnidadAcademica = TUA.ID_TipoUnidadAcademica WHERE TUA.Desc_SiglasTipo IN (SELECT Desc_TipoUA FROM @TipoUA WHERE Desc_Nivel IN (SELECT Desc_Nivel FROM @Niveles WHERE ID_Nivel = @ContNiveles))) AND id_AnioSemestre = (SELECT ID_AnioSemestreSAC FROM SAC_AnioSemestre WHERE Desc_AnioSemestreSAC = 'SEMESTRE') AND id_Trimestre BETWEEN 1 AND @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0); SET @ActMujeresSemestre = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroSabaticos WHERE id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas UA JOIN TipoUnidadAcademica TUA ON UA.id_TipoUnidadAcademica = TUA.ID_TipoUnidadAcademica WHERE TUA.Desc_SiglasTipo IN (SELECT Desc_TipoUA FROM @TipoUA WHERE Desc_Nivel IN (SELECT Desc_Nivel FROM @Niveles WHERE ID_Nivel = @ContNiveles))) AND id_AnioSemestre = (SELECT ID_AnioSemestreSAC FROM SAC_AnioSemestre WHERE Desc_AnioSemestreSAC = 'SEMESTRE') AND id_Trimestre BETWEEN 1 AND @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0); SET @Variacion = CASE WHEN (@AntAnio + @AntSemestre) = 0 THEN 0 ELSE ((@ActHombresAnio + @ActMujeresAnio + @ActHombresSemestre + @ActMujeresSemestre) - (@AntAnio + @AntSemestre)) / (@AntAnio + @AntSemestre) * 100 END; SET @Justificacion = ISNULL((SELECT TOP(1) Desc_Justificacion FROM SAC_Justificaciones WHERE id_TipoUnidadAcademicaSAC = (SELECT Origen_Justificaciones FROM @Niveles WHERE ID_Nivel = @ContNiveles) AND id_ProgramaApoyoSAC = (SELECT ID_ProgramaApoyoSAC FROM SAC_ProgramaApoyo WHERE Desc_ProgramaApoyoSAC = @ProgramaApoyo) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @id_Trimestre), ' ');
INSERT INTO @DocentesSabaticoComparativo (Nivel, AntAnio, AntSemestre, AntTotal, ActHombresAnio, ActMujeresAnio, ActTotalAnio, ActHombresSemestre, ActMujeresSemestre, ActTotalSemestre, Variacion, Justificacion) VALUES (@NivelActual, @AntAnio, @AntSemestre, @AntAnio + @AntSemestre, @ActHombresAnio, @ActMujeresAnio, @ActHombresAnio + @ActMujeresAnio, @ActHombresSemestre, @ActMujeresSemestre, @ActHombresSemestre + @ActMujeresSemestre, @Variacion, @Justificacion);
SET @ContNiveles = @ContNiveles + 1; END
SELECT * FROM @DocentesSabaticoComparativo UNION ALL SELECT 'TOTAL', SUM(AntAnio), SUM(AntSemestre), SUM(AntTotal), SUM(ActHombresAnio), SUM(ActMujeresAnio), SUM(ActTotalAnio), SUM(ActHombresSemestre), SUM(ActMujeresSemestre), SUM(ActTotalSemestre), CASE WHEN SUM(AntAnio + AntSemestre) = 0 THEN 0 ELSE (SUM(ActHombresAnio + ActMujeresAnio + ActHombresSemestre + ActMujeresSemestre) - SUM(AntAnio + AntSemestre)) / (SUM(AntAnio + AntSemestre)) * 100 END, ' ' FROM @DocentesSabaticoComparativo;
END

-- Formato 12 Docentes en anio y semestre sabatico comparativo
-- Autor: Eduardo Castillo
CREATE PROCEDURE SAC_DocentesAnioSemestreSabaticoComparativo
    @anio INT,
    @id_Trimestre INT
AS

BEGIN SET NOCOUNT ON; DECLARE @DocentesConcentrado TABLE ( Desc_Seccion VARCHAR(255) NOT NULL DEFAULT ' ',
A1_Hombres INT NOT NULL DEFAULT 0, A1_Mujeres INT NOT NULL DEFAULT 0, A2_Hombres INT NOT NULL DEFAULT 0, A2_Mujeres INT NOT NULL DEFAULT 0, Actividades_Hombres INT NOT NULL DEFAULT 0, Actividades_Mujeres INT NOT NULL DEFAULT 0, EstParciales_Hombres INT NOT NULL DEFAULT 0, EstParciales_Mujeres INT NOT NULL DEFAULT 0, Individuales_Hombres INT NOT NULL DEFAULT 0, Individuales_Mujeres INT NOT NULL DEFAULT 0, Total_NoPrioritario INT NOT NULL DEFAULT 0,
Investigacion_Hombres INT NOT NULL DEFAULT 0, Investigacion_Mujeres INT NOT NULL DEFAULT 0, EstPosgrado_Hombres INT NOT NULL DEFAULT 0, EstPosgrado_Mujeres INT NOT NULL DEFAULT 0, Diplomados_Hombres INT NOT NULL DEFAULT 0, Diplomados_Mujeres INT NOT NULL DEFAULT 0, Estancias_Hombres INT NOT NULL DEFAULT 0, Estancias_Mujeres INT NOT NULL DEFAULT 0, Elaboracion_Hombres INT NOT NULL DEFAULT 0, Elaboracion_Mujeres INT NOT NULL DEFAULT 0, Total_Prioritario INT NOT NULL DEFAULT 0,
Total_Hombres INT NOT NULL DEFAULT 0, Total_Mujeres INT NOT NULL DEFAULT 0, Total_General INT NOT NULL DEFAULT 0 );
DECLARE @TablaNoPrioritarios TABLE( ID_TablaNoPrioritarios INT IDENTITY(1,1), Desc_ProgramaSabatico VARCHAR(255) NOT NULL DEFAULT ' ' );
-- Tabla provisional para programas No Prioritarios INSERT INTO @TablaNoPrioritarios SELECT Desc_ProgramasSabaticoSAC FROM SAC_ProgramasSabatico WHERE Desc_Prioridad = 'NO PRIORITARIOS';
-- Programas Prioritarios DECLARE @TablaPrioritarios TABLE( ID_TablaPrioritarios INT IDENTITY(1,1), Desc_ProgramaSabatico VARCHAR(255) );
INSERT INTO @TablaPrioritarios SELECT Desc_ProgramasSabaticoSAC FROM SAC_ProgramasSabatico WHERE Desc_Prioridad = 'PRIORITARIOS';
DECLARE @NMS VARCHAR(255) = 'NIVEL MEDIO SUPERIOR'; DECLARE @NS_PS VARCHAR(255) = 'NIVEL SUPERIOR'; DECLARE @CINV_CIITA VARCHAR(255) = 'CENTROS DE INVESTIGACIÓN'; DECLARE @UACA_UAVDR VARCHAR(255) = 'AREA CENTRAL';
DECLARE @Secciones TABLE ( ID_Seccion INT IDENTITY(1,1), Desc_Seccion VARCHAR(255) NOT NULL DEFAULT ' ' );
INSERT INTO @Secciones (Desc_Seccion) VALUES (@NMS), (@NS_PS), (@CINV_CIITA), (@UACA_UAVDR);
DECLARE @TipoUA TABLE ( ID_Areas INT IDENTITY(1,1), Desc_TipoUA VARCHAR(255) NOT NULL DEFAULT ' ', Desc_Seccion VARCHAR(255) NOT NULL DEFAULT ' ' );
INSERT INTO @TipoUA (Desc_TipoUA, Desc_Seccion) VALUES ('NMS', @NMS), ('NS', @NS_PS), ('C INV', @CINV_CIITA), ('CIITA', @CINV_CIITA), ('UACA', @UACA_UAVDR), ('UAVDR', @UACA_UAVDR);
DECLARE @Hombres INT = 0; DECLARE @Mujeres INT = 0; DECLARE @Total_Hombres INT = 0; DECLARE @Total_Mujeres INT = 0; DECLARE @Total_NoPrioritario INT = 0; DECLARE @Total_Prioritario INT = 0;
DECLARE @SeccionActual VARCHAR(255) = ' ';
DECLARE @ContNoPrioritarios INT = 1; DECLARE @ContPrioritarios INT = 1; DECLARE @ContAreas INT = 1;
WHILE @ContAreas <= (SELECT COUNT(*) FROM @Secciones) BEGIN -- Obtenemos la seccion actual y la insertamos SET @SeccionActual = (SELECT Desc_Seccion FROM @Secciones WHERE ID_Seccion = @ContAreas); INSERT INTO @DocentesConcentrado (Desc_Seccion) VALUES (@SeccionActual);
-- WHILE itera sobre programas No Prioritarios WHILE @ContNoPrioritarios <= (SELECT COUNT(*) FROM SAC_ProgramasSabatico WHERE Desc_Prioridad = 'NO PRIORITARIOS') BEGIN SET @Hombres = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroSabaticos WHERE id_ProgramasSabaticoSAC = (SELECT id_ProgramasSabaticoSAC FROM SAC_ProgramasSabatico WHERE Desc_ProgramasSabaticoSAC = (SELECT Desc_ProgramaSabatico FROM @TablaNoPrioritarios WHERE ID_TablaNoPrioritarios = @ContNoPrioritarios)) AND id_AnioSemestre = (SELECT ID_AnioSemestreSAC FROM SAC_AnioSemestre WHERE Desc_AnioSemestreSAC = @AnioSemestre) AND id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas UA JOIN TipoUnidadAcademica TUA ON UA.id_TipoUnidadAcademica = TUA.ID_TipoUnidadAcademica WHERE TUA.Desc_SiglasTipo IN (SELECT Desc_TipoUA FROM @TipoUA WHERE Desc_Seccion IN (SELECT Desc_Seccion FROM @Secciones WHERE ID_Seccion = @ContAreas))) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0); SET @Mujeres = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroSabaticos WHERE id_ProgramasSabaticoSAC = (SELECT id_ProgramasSabaticoSAC FROM SAC_ProgramasSabatico WHERE Desc_ProgramasSabaticoSAC = (SELECT Desc_ProgramaSabatico FROM @TablaNoPrioritarios WHERE ID_TablaNoPrioritarios = @ContNoPrioritarios)) AND id_AnioSemestre = (SELECT ID_AnioSemestreSAC FROM SAC_AnioSemestre WHERE Desc_AnioSemestreSAC = @AnioSemestre) AND id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas UA JOIN TipoUnidadAcademica TUA ON UA.id_TipoUnidadAcademica = TUA.ID_TipoUnidadAcademica WHERE TUA.Desc_SiglasTipo IN (SELECT Desc_TipoUA FROM @TipoUA WHERE Desc_Seccion IN (SELECT Desc_Seccion FROM @Secciones WHERE ID_Seccion = @ContAreas))) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0);
IF @ContNoPrioritarios = 1 BEGIN UPDATE @DocentesConcentrado SET A1_Hombres = @Hombres, A1_Mujeres = @Mujeres WHERE Desc_Seccion = @SeccionActual; END ELSE IF @ContNoPrioritarios = 2 BEGIN UPDATE @DocentesConcentrado SET A2_Hombres = @Hombres, A2_Mujeres = @Mujeres WHERE Desc_Seccion = @SeccionActual; END ELSE IF @ContNoPrioritarios = 3 BEGIN UPDATE @DocentesConcentrado SET Actividades_Hombres = @Hombres, Actividades_Mujeres = @Mujeres WHERE Desc_Seccion = @SeccionActual; END ELSE IF @ContNoPrioritarios = 4 BEGIN UPDATE @DocentesConcentrado SET EstParciales_Hombres = @Hombres, EstParciales_Mujeres = @Mujeres WHERE Desc_Seccion = @SeccionActual; END ELSE IF @ContNoPrioritarios = 5 BEGIN UPDATE @DocentesConcentrado SET Individuales_Hombres = @Hombres, Individuales_Mujeres = @Mujeres WHERE Desc_Seccion = @SeccionActual; END
SET @Total_NoPrioritario = @Total_NoPrioritario + @Hombres + @Mujeres; SET @Total_Hombres = @Total_Hombres + @Hombres; SET @Total_Mujeres = @Total_Mujeres + @Mujeres;
SET @ContNoPrioritarios = @ContNoPrioritarios + 1; END
UPDATE @DocentesConcentrado SET Total_NoPrioritario = @Total_NoPrioritario WHERE Desc_Seccion = @SeccionActual; -- Reseteo @Total_NoPrioritario SET @Total_NoPrioritario = 0; -- Reseteo @ContNoPrioritarios SET @ContNoPrioritarios = 1;
-- WHILE itera sobre programas Prioritarios WHILE @ContPrioritarios <= (SELECT COUNT(*) FROM SAC_ProgramasSabatico WHERE Desc_Prioridad = 'PRIORITARIOS') BEGIN SET @Hombres = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroSabaticos WHERE id_ProgramasSabaticoSAC = (SELECT id_ProgramasSabaticoSAC FROM SAC_ProgramasSabatico WHERE Desc_ProgramasSabaticoSAC = (SELECT Desc_ProgramaSabatico FROM @TablaPrioritarios WHERE ID_TablaPrioritarios = @ContPrioritarios)) AND id_AnioSemestre = (SELECT ID_AnioSemestreSAC FROM SAC_AnioSemestre WHERE Desc_AnioSemestreSAC = @AnioSemestre) AND id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas UA JOIN TipoUnidadAcademica TUA ON UA.id_TipoUnidadAcademica = TUA.ID_TipoUnidadAcademica WHERE TUA.Desc_SiglasTipo IN (SELECT Desc_TipoUA FROM @TipoUA WHERE Desc_Seccion IN (SELECT Desc_Seccion FROM @Secciones WHERE ID_Seccion = @ContAreas))) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0); SET @Mujeres = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroSabaticos WHERE id_ProgramasSabaticoSAC = (SELECT id_ProgramasSabaticoSAC FROM SAC_ProgramasSabatico WHERE Desc_ProgramasSabaticoSAC = (SELECT Desc_ProgramaSabatico FROM @TablaPrioritarios WHERE ID_TablaPrioritarios = @ContPrioritarios)) AND id_AnioSemestre = (SELECT ID_AnioSemestreSAC FROM SAC_AnioSemestre WHERE Desc_AnioSemestreSAC = @AnioSemestre) AND id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas UA JOIN TipoUnidadAcademica TUA ON UA.id_TipoUnidadAcademica = TUA.ID_TipoUnidadAcademica WHERE TUA.Desc_SiglasTipo IN (SELECT Desc_TipoUA FROM @TipoUA WHERE Desc_Seccion IN (SELECT Desc_Seccion FROM @Secciones WHERE ID_Seccion = @ContAreas))) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio)), 0);
IF @ContPrioritarios = 1 BEGIN UPDATE @DocentesConcentrado SET Investigacion_Hombres = @Hombres, Investigacion_Mujeres = @Mujeres WHERE Desc_Seccion = @SeccionActual; END ELSE IF @ContPrioritarios = 2 BEGIN UPDATE @DocentesConcentrado SET EstPosgrado_Hombres = @Hombres, EstPosgrado_Mujeres = @Mujeres WHERE Desc_Seccion = @SeccionActual; END ELSE IF @ContPrioritarios = 3 BEGIN UPDATE @DocentesConcentrado SET Diplomados_Hombres = @Hombres, Diplomados_Mujeres = @Mujeres WHERE Desc_Seccion = @SeccionActual; END ELSE IF @ContPrioritarios = 4 BEGIN UPDATE @DocentesConcentrado SET Estancias_Hombres = @Hombres, Estancias_Mujeres = @Mujeres WHERE Desc_Seccion = @SeccionActual; END ELSE IF @ContPrioritarios = 5 BEGIN UPDATE @DocentesConcentrado SET Elaboracion_Hombres = @Hombres, Elaboracion_Mujeres = @Mujeres WHERE Desc_Seccion = @SeccionActual; END
SET @Total_Prioritario = @Total_Prioritario + @Hombres + @Mujeres; SET @Total_Hombres = @Total_Hombres + @Hombres; SET @Total_Mujeres = @Total_Mujeres + @Mujeres;
SET @ContPrioritarios = @ContPrioritarios + 1; END
UPDATE @DocentesConcentrado SET Total_Prioritario = @Total_Prioritario, Total_Hombres = @Total_Hombres, Total_Mujeres = @Total_Mujeres, Total_General = @Total_Hombres + @Total_Mujeres WHERE Desc_Seccion = @SeccionActual; -- Reseteo @Total_Prioritario SET @Total_Prioritario = 0; -- Reseteo @Total_Hombres SET @Total_Hombres = 0; -- Reseteo @Total_Mujeres SET @Total_Mujeres = 0; -- Reseteo @ContPrioritarios SET @ContPrioritarios = 1;
SET @ContAreas = @ContAreas + 1; END
SELECT * FROM @DocentesConcentrado UNION ALL SELECT 'TOTAL', SUM(A1_Hombres), SUM(A1_Mujeres), SUM(A2_Hombres), SUM(A2_Mujeres), SUM(Actividades_Hombres), SUM(Actividades_Mujeres), SUM(EstParciales_Hombres), SUM(EstParciales_Mujeres), SUM(Individuales_Hombres), SUM(Individuales_Mujeres), SUM(Total_NoPrioritario), SUM(Investigacion_Hombres), SUM(Investigacion_Mujeres), SUM(EstPosgrado_Hombres), SUM(EstPosgrado_Mujeres), SUM(Diplomados_Hombres), SUM(Diplomados_Mujeres), SUM(Estancias_Hombres), SUM(Estancias_Mujeres), SUM(Elaboracion_Hombres), SUM(Elaboracion_Mujeres), SUM(Total_Prioritario), SUM(Total_Hombres), SUM(Total_Mujeres), SUM(Total_General) FROM @DocentesConcentrado; END

-- Formato 5 y 10 Docentes en año/semestre sabatico concentrado
-- Autor: Eduardo Castillo
CREATE PROCEDURE SAC_DocentesAnioSemestreSabaticoConcentrado
    @AnioSemestre VARCHAR(255),
    @anio INT,
    @id_Trimestre INT
AS

BEGIN
    DECLARE @EstimuloAlDesempenioDocente TABLE(
        UnidadAcademica varchar(50),

        PrimeroHombres varchar(10) NOT NULL DEFAULT ' ',
        PrimeroMujeres varchar(10) NOT NULL DEFAULT ' ',
        PrimeroTotal varchar(10) NOT NULL DEFAULT ' ',

        SegundoHombres varchar(10) NOT NULL DEFAULT ' ',
        SegundoMujeres varchar(10) NOT NULL DEFAULT ' ',
        SegundoTotal varchar(10) NOT NULL DEFAULT ' ',

        TerceroHombres varchar(10) NOT NULL DEFAULT ' ',
        TerceroMujeres varchar(10) NOT NULL DEFAULT ' ',
        TerceroTotal varchar(10) NOT NULL DEFAULT ' ',

        CuartoHombres varchar(10) NOT NULL DEFAULT ' ',
        CuartoMujeres varchar(10) NOT NULL DEFAULT ' ',
        CuartoTotal varchar(10) NOT NULL DEFAULT ' ',

        QuintoHombres varchar(10) NOT NULL DEFAULT ' ',
        QuintoMujeres varchar(10) NOT NULL DEFAULT ' ',
        QuintoTotal varchar(10) NOT NULL DEFAULT ' ',

        SextoHombres varchar(10) NOT NULL DEFAULT ' ',
        SextoMujeres varchar(10) NOT NULL DEFAULT ' ', 
        SextoTotal varchar(10) NOT NULL DEFAULT ' ',

        SeptimoHombres varchar(10) NOT NULL DEFAULT ' ',
        SeptimoMujeres varchar(10) NOT NULL DEFAULT ' ',
        SeptimoTotal varchar(10) NOT NULL DEFAULT ' ',

        OctavoHombres varchar(10) NOT NULL DEFAULT ' ',
        OctavoMujeres varchar(10) NOT NULL DEFAULT ' ',
        OctavoTotal varchar(10) NOT NULL DEFAULT ' ',

        NovenoHombres varchar(10) NOT NULL DEFAULT ' ',
        NovenoMujeres varchar(10) NOT NULL DEFAULT ' ',
        NovenoTotal varchar(10) NOT NULL DEFAULT ' ',

        TotalHombres varchar(10) NOT NULL DEFAULT ' ',
        TotalMujeres varchar(10) NOT NULL DEFAULT ' ',
        TotalTotal varchar(10) NOT NULL DEFAULT ' ',

        MontoPagado varchar(10) NOT NULL DEFAULT ' '
    );

    --Variables por periodo
    DECLARE @Hombres INT = 0;
    DECLARE @Mujeres INT = 0;
    DECLARE @Total INT = 0;

    --Varianbles totales por unidad academica (suma de periodos)
    DECLARE @TotalHombresUnidad INT = 0;
    DECLARE @TotalMujeresUnidad INT = 0;
    DECLARE @TotalTotalUnidad INT = 0;

    --Variables totales por rama (Suma unidades academicas por periodo)
    DECLARE @TotalHombresRama1 INT = 0;
    DECLARE @TotalMujeresRama1 INT = 0;
    DECLARE @TotalRama1 INT = 0;
    DECLARE @TotalHombresRama2 INT = 0;
    DECLARE @TotalMujeresRama2 INT = 0;
    DECLARE @TotalRama2 INT = 0;
    DECLARE @TotalHombresRama3 INT = 0;
    DECLARE @TotalMujeresRama3 INT = 0;
    DECLARE @TotalRama3 INT = 0;
    DECLARE @TotalHombresRama4 INT = 0;
    DECLARE @TotalMujeresRama4 INT = 0;
    DECLARE @TotalRama4 INT = 0;
    DECLARE @TotalHombresRama5 INT = 0;
    DECLARE @TotalMujeresRama5 INT = 0;
    DECLARE @TotalRama5 INT = 0;
    DECLARE @TotalHombresRama6 INT = 0;
    DECLARE @TotalMujeresRama6 INT = 0;
    DECLARE @TotalRama6 INT = 0;
    DECLARE @TotalHombresRama7 INT = 0;
    DECLARE @TotalMujeresRama7 INT = 0;
    DECLARE @TotalRama7 INT = 0;
    DECLARE @TotalHombresRama8 INT = 0;
    DECLARE @TotalMujeresRama8 INT = 0;
    DECLARE @TotalRama8 INT = 0;
    DECLARE @TotalHombresRama9 INT = 0;
    DECLARE @TotalMujeresRama9 INT = 0;
    DECLARE @TotalRama9 INT = 0;
    DECLARE @TotalTotalHombres INT = 0;
    DECLARE @TotalTotalMujeres INT = 0;
    DECLARE @TotalTotalTotal INT = 0;
    DECLARE @MontoTotal MONEY = 0;

    -- Montos pagados, obtenidos de otra tabla
    DECLARE @MontoPagado varchar(100) = 0;

    -- Declaraciones iniciales de variables contadores
    DECLARE @ContadorUA INT = 1;
    DECLARE @ContadorRama INT = 1;
    DECLARE @ContadorPeriodo INT = 1;

    -- Obtenemos las ramas totales (numero) para el tipo de unidad academica actual
    DECLARE @RamasTotales INT = ISNULL((SELECT 
                                            COUNT(DISTINCT(RC.Desc_SiglasRama))
                                        FROM UnidadesAcademicas UA
                                        JOIN RamaConocimiento RC ON UA.id_RamaConocimiento = RC.ID_RamaConocimiento
                                        WHERE UA.id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica 
                                                                        FROM TipoUnidadAcademica 
                                                                        WHERE Desc_SiglasTipo = @SiglasTipoUnidadAcademica)), 0)

    -- Tabla para guardar las ramas que corresponden al tipo que se ha pedido 
    -- se manejaran ID para simplificar el proceso de busqueda
    DECLARE @RamasPorTipo TABLE (
        ID_Numeral INT IDENTITY(1,1),
        id_RamaConocimiento INT
    );

    CREATE TABLE #UnidadesPorRama (
            ID_Numeral INT IDENTITY(1,1),
            SiglasUnidad varchar(25) COLLATE Latin1_General_CI_AI
    );

    INSERT INTO @RamasPorTipo(
        id_RamaConocimiento
    )SELECT 
        DISTINCT(id_RamaConocimiento)
    FROM UnidadesAcademicas 
    WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica 
                                    FROM TipoUnidadAcademica 
                                    WHERE Desc_SiglasTipo = @SiglasTipoUnidadAcademica);

    SET @ContadorRama = 1;
    WHILE @ContadorRama <= @RamasTotales
        BEGIN
            DECLARE @id_RamaActual INT = (SELECT id_RamaConocimiento 
                                            FROM @RamasPorTipo
                                            WHERE ID_Numeral = @ContadorRama);

            INSERT INTO #UnidadesPorRama(
                SiglasUnidad
            )SELECT
                Siglas
            FROM UnidadesAcademicas
            WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica 
                                FROM TipoUnidadAcademica 
                                WHERE Desc_SiglasTipo = @SiglasTipoUnidadAcademica) 
            AND id_RamaConocimiento = @id_RamaActual;

            SET @ContadorUA = 1;
            WHILE @ContadorUA <= (SELECT COUNT(*) FROM #UnidadesPorRama)
                BEGIN
                    DECLARE @UnidadActual varchar(25) = (SELECT SiglasUnidad 
                                                        FROM #UnidadesPorRama
                                                        WHERE ID_Numeral = @ContadorUA);

                    SET @TotalHombresUnidad = 0;
                    SET @TotalMujeresUnidad = 0;
                    SET @TotalTotalUnidad = 0;

                    SET @ContadorPeriodo = 1;
                    WHILE @ContadorPeriodo <= 9
                        BEGIN
                            SET @Hombres = ISNULL((SELECT SUM(Desc_Hombres) 
                                            FROM SAC_RegistroEstimulosAlDesempenioDocente
                                            WHERE id_UnidadAcademicaSAC = (SELECT ID_UnidadAcademica 
                                                                        FROM UnidadesAcademicas
                                                                        WHERE Siglas = @UnidadActual)
                                            AND Desc_PeriodoSAC = @ContadorPeriodo
                                            AND id_Anio = (SELECT ID_Anio
                                                            FROM Anio
                                                            WHERE Desc_Anio = @Anio)
                                            AND id_Trimestre = @id_Trimestre), 0);

                            SET @Mujeres = ISNULL((SELECT SUM(Desc_Mujeres) 
                                            FROM SAC_RegistroEstimulosAlDesempenioDocente
                                            WHERE id_UnidadAcademicaSAC = (SELECT ID_UnidadAcademica 
                                                                        FROM UnidadesAcademicas
                                                                        WHERE Siglas = @UnidadActual)
                                            AND Desc_PeriodoSAC = @ContadorPeriodo
                                            AND id_Anio = (SELECT ID_Anio
                                                            FROM Anio
                                                            WHERE Desc_Anio = @Anio)
                                            AND id_Trimestre = @id_Trimestre), 0);

                            SET @Total = @Hombres + @Mujeres;

                            SET @MontoPagado = ISNULL((SELECT SUM(Desc_MontoPagado) 
                                                        FROM SAC_RegistroEDDMontosPagados
                                                        WHERE id_UnidadAcademicaSAC = (SELECT ID_UnidadAcademica
                                                                                        FROM UnidadesAcademicas
                                                                                        WHERE Siglas = @UnidadActual)
                                                        AND id_Anio = (SELECT ID_Anio
                                                                        FROM Anio
                                                                        WHERE Desc_Anio = @Anio)
                                                        AND id_Trimestre = @id_Trimestre), 0)

                            SET @TotalHombresUnidad = @TotalHombresUnidad + @Hombres;
                            SET @TotalMujeresUnidad = @TotalMujeresUnidad + @Mujeres;
                            SET @TotalTotalUnidad = @TotalTotalUnidad + @Total;

                            IF @ContadorPeriodo = 1
                                BEGIN
                                    INSERT INTO @EstimuloAlDesempenioDocente(
                                        UnidadAcademica,
                                        PrimeroHombres,
                                        PrimeroMujeres,
                                        PrimeroTotal,
                                        MontoPagado
                                    )VALUES(
                                        @UnidadActual,
                                        @Hombres,
                                        @Mujeres,
                                        @Total,
                                        @MontoPagado
                                    );
                                END
                            ELSE IF @ContadorPeriodo = 2
                            BEGIN
                                UPDATE @EstimuloAlDesempenioDocente
                                SET SegundoHombres = @Hombres,
                                    SegundoMujeres = @Mujeres,
                                    SegundoTotal = @Total
                                WHERE UnidadAcademica = @UnidadActual;
                            END
                            ELSE IF @ContadorPeriodo = 3
                            BEGIN
                                UPDATE @EstimuloAlDesempenioDocente
                                SET TerceroHombres = @Hombres,
                                    TerceroMujeres = @Mujeres,
                                    TerceroTotal = @Total
                                WHERE UnidadAcademica = @UnidadActual;
                            END
                            ELSE IF @ContadorPeriodo = 4
                            BEGIN
                                UPDATE @EstimuloAlDesempenioDocente
                                SET CuartoHombres = @Hombres,
                                    CuartoMujeres = @Mujeres,
                                    CuartoTotal = @Total
                                WHERE UnidadAcademica = @UnidadActual;
                            END
                            ELSE IF @ContadorPeriodo = 5
                            BEGIN
                                UPDATE @EstimuloAlDesempenioDocente
                                SET QuintoHombres = @Hombres,
                                    QuintoMujeres = @Mujeres,
                                    QuintoTotal = @Total
                                WHERE UnidadAcademica = @UnidadActual;
                            END
                            ELSE IF @ContadorPeriodo = 6
                            BEGIN
                                UPDATE @EstimuloAlDesempenioDocente
                                SET SextoHombres = @Hombres,
                                    SextoMujeres = @Mujeres,
                                    SextoTotal = @Total
                                WHERE UnidadAcademica = @UnidadActual;
                            END
                            ELSE IF @ContadorPeriodo = 7
                            BEGIN
                                UPDATE @EstimuloAlDesempenioDocente
                                SET SeptimoHombres = @Hombres,
                                    SeptimoMujeres = @Mujeres,
                                    SeptimoTotal = @Total
                                WHERE UnidadAcademica = @UnidadActual;
                            END
                            ELSE IF @ContadorPeriodo = 8
                            BEGIN
                                UPDATE @EstimuloAlDesempenioDocente
                                SET OctavoHombres = @Hombres,
                                    OctavoMujeres = @Mujeres,
                                    OctavoTotal = @Total
                                WHERE UnidadAcademica = @UnidadActual;
                            END
                            ELSE IF @ContadorPeriodo = 9
                            BEGIN
                                UPDATE @EstimuloAlDesempenioDocente
                                SET NovenoHombres = @Hombres,
                                    NovenoMujeres = @Mujeres,
                                    NovenoTotal = @Total,
                                    TotalHombres = @TotalHombresUnidad,
                                    TotalMujeres = @TotalMujeresUnidad,
                                    TotalTotal = @TotalTotalUnidad
                                WHERE UnidadAcademica = @UnidadActual;
                            END
                        SET @ContadorPeriodo = @ContadorPeriodo + 1;
                        END
                    SET @ContadorUA = @ContadorUA + 1;
                END

            SET @TotalHombresRama1 = ISNULL((SELECT SUM(CAST(PrimeroHombres AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);
            SET @TotalMujeresRama1 = ISNULL((SELECT SUM(CAST(PrimeroMujeres AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);
            SET @TotalRama1 = ISNULL((SELECT SUM(CAST(PrimeroTotal AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);

            SET @TotalHombresRama2 = ISNULL((SELECT SUM(CAST(SegundoHombres AS INT))
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);
            SET @TotalMujeresRama2 = ISNULL((SELECT SUM(CAST(SegundoMujeres AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);
            SET @TotalRama2 = ISNULL((SELECT SUM(CAST(SegundoTotal AS INT))
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);

            SET @TotalHombresRama3 = ISNULL((SELECT SUM(CAST(TerceroHombres AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);
            SET @TotalMujeresRama3 = ISNULL((SELECT SUM(CAST(TerceroMujeres AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);
            SET @TotalRama3 = ISNULL((SELECT SUM(CAST(TerceroTotal AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);

            SET @TotalHombresRama4 = ISNULL((SELECT SUM(CAST(CuartoHombres AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);
            SET @TotalMujeresRama4 = ISNULL((SELECT SUM(CAST(CuartoMujeres AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);
            SET @TotalRama4 = ISNULL((SELECT SUM(CAST(CuartoTotal AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);
            PRINT @TotalHombresRama5
            SET @TotalHombresRama5 = ISNULL((SELECT SUM(CAST(QuintoHombres AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);
            SET @TotalMujeresRama5 = ISNULL((SELECT SUM(CAST(QuintoMujeres AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);
            SET @TotalRama5 = ISNULL((SELECT SUM(CAST(QuintoTotal AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);
            PRINT @TotalHombresRama5;
            SET @TotalHombresRama6 = ISNULL((SELECT SUM(CAST(SextoHombres AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);
            SET @TotalMujeresRama6 = ISNULL((SELECT SUM(CAST(SextoMujeres AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);
            SET @TotalRama6 = ISNULL((SELECT SUM(CAST(SextoTotal AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);

            SET @TotalHombresRama7 = ISNULL((SELECT SUM(CAST(SeptimoHombres AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);
            SET @TotalMujeresRama7 = ISNULL((SELECT SUM(CAST(SeptimoMujeres AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);
            SET @TotalRama7 = ISNULL((SELECT SUM(CAST(SeptimoTotal AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);

            SET @TotalHombresRama8 = ISNULL((SELECT SUM(CAST(OctavoHombres AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);
            SET @TotalMujeresRama8 = ISNULL((SELECT SUM(CAST (OctavoMujeres AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);
            SET @TotalRama8 = ISNULL((SELECT SUM(CAST(OctavoTotal AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);

            SET @TotalHombresRama9 = ISNULL((SELECT SUM(CAST(NovenoHombres AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);
            SET @TotalMujeresRama9 = ISNULL((SELECT SUM(CAST(NovenoMujeres AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);
            SET @TotalRama9 = ISNULL((SELECT SUM(CAST(NovenoTotal AS INT)) 
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);

            SET @TotalTotalHombres = ISNULL((SELECT SUM(CAST(TotalHombres AS INT))
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);
            SET @TotalTotalMujeres = ISNULL((SELECT SUM(CAST(TotalMujeres AS INT))
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);
            SET @TotalTotalTotal = ISNULL((SELECT SUM(CAST(TotalTotal AS INT))
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);

            SET @MontoTotal = ISNULL((SELECT SUM(CAST(MontoPagado AS MONEY))
                                            FROM @EstimuloAlDesempenioDocente
                                            WHERE UnidadAcademica IN (SELECT SiglasUnidad 
                                                                    FROM #UnidadesPorRama)), 0);

            INSERT INTO @EstimuloAlDesempenioDocente
            VALUES(
                CONCAT('SUBTOTAL RAMA ', (SELECT TOP(1) Desc_SiglasRama FROM RamaConocimiento WHERE ID_RamaConocimiento = @id_RamaActual)),
                @TotalHombresRama1,
                @TotalMujeresRama1,
                @TotalRama1,
                @TotalHombresRama2,
                @TotalMujeresRama2,
                @TotalRama2,
                @TotalHombresRama3,
                @TotalMujeresRama3,
                @TotalRama3,
                @TotalHombresRama4,
                @TotalMujeresRama4,
                @TotalRama4,
                @TotalHombresRama5,
                @TotalMujeresRama5,
                @TotalRama5,
                @TotalHombresRama6,
                @TotalMujeresRama6,
                @TotalRama6,
                @TotalHombresRama7,
                @TotalMujeresRama7,
                @TotalRama7,
                @TotalHombresRama8,
                @TotalMujeresRama8,
                @TotalRama8,
                @TotalHombresRama9,
                @TotalMujeresRama9,
                @TotalRama9,
                @TotalTotalHombres,
                @TotalTotalMujeres,
                @TotalTotalTotal,
                @MontoTotal
            );

            DELETE FROM #UnidadesPorRama;
            DBCC CHECKIDENT ("#UnidadesPorRama", RESEED,0);
            SET @ContadorRama = @ContadorRama + 1;
        END
    SELECT * FROM @EstimuloAlDesempenioDocente
END

-- Formato 13, 14, 15, 16 Estimulo al desempeno docente vigentes en el periodo NMS, NS, C INV, UACA
CREATE PROCEDURE SAC_EstimuloDocente
    @SiglasTipoUnidadAcademica varchar(255),
    @Anio INT,
    @id_Trimestre INT
AS

BEGIN SET NOCOUNT ON; DECLARE @EstimuloDocenteAcumulado TABLE( Nivel varchar(255),
PrimerEDDHombres varchar(255) NOT NULL DEFAULT ' ', PrimerEDDMujeres varchar(255) NOT NULL DEFAULT ' ', PrimerEDDTotal varchar(255) NOT NULL DEFAULT ' ', PrimerMonto varchar(255) NOT NULL DEFAULT ' ',
SegundoEDDHombres varchar(255) NOT NULL DEFAULT ' ', SegundoEDDMujeres varchar(255) NOT NULL DEFAULT ' ', SegundoEDDTotal varchar(255) NOT NULL DEFAULT ' ', SegundoMonto varchar(255) NOT NULL DEFAULT ' ',
TercerEDDHombres varchar(255) NOT NULL DEFAULT ' ', TercerEDDMujeres varchar(255) NOT NULL DEFAULT ' ', TercerEDDTotal varchar(255) NOT NULL DEFAULT ' ', TercerMonto varchar(255) NOT NULL DEFAULT ' ',
CuartoEDDHombres varchar(255) NOT NULL DEFAULT ' ', CuartoEDDMujeres varchar(255) NOT NULL DEFAULT ' ', CuartoEDDTotal varchar(255) NOT NULL DEFAULT ' ', CuartoMonto varchar(255) NOT NULL DEFAULT ' ',
CifrasEDDHombres varchar(255) NOT NULL DEFAULT ' ', CifrasEDDMujeres varchar(255) NOT NULL DEFAULT ' ', CifrasEDDTotal varchar(255) NOT NULL DEFAULT ' ', CifrasMonto varchar(255) NOT NULL DEFAULT ' ' );
DECLARE @EDDHombres INT; DECLARE @EDDMujeres INT; DECLARE @EDDTotal INT; DECLARE @Monto MONEY;
DECLARE @CifrasEDDHombres INT; DECLARE @CifrasEDDMujeres INT; DECLARE @CifrasEDDTotal INT; DECLARE @CifrasMonto MONEY;
DECLARE @NivelActual varchar(255); DECLARE @ContadorNivel INT = 1; DECLARE @ContadorTrimestre INT = 1;
--Declaración de niveles DECLARE @TablaNiveles TABLE( ID_Numeral INT IDENTITY(1, 1), TipoUnidadAcademica varchar(50) );
INSERT INTO @TablaNiveles( TipoUnidadAcademica )VALUES ('NMS'), ('NS'), ('C INV'), ('UACA');
DECLARE @NMS varchar(50) = 'MEDIO SUPERIOR'; DECLARE @NS varchar(50) = 'SUPERIOR Y POSGRADO'; DECLARE @CINV varchar(50) = 'CENTROS DE INVESTIGACIÓN'; DECLARE @UACA varchar(50) = 'ÁREA CENTRAL';
SET @ContadorNivel = 1; WHILE @ContadorNivel <= (SELECT COUNT(*) FROM @TablaNiveles) BEGIN SET @NivelActual = (SELECT TipoUnidadAcademica FROM @TablaNiveles WHERE ID_Numeral = @ContadorNivel); SET @CifrasEDDHombres = 0; SET @CifrasEDDMujeres = 0; SET @CifrasEDDTotal = 0; SET @CifrasMonto = 0;
INSERT INTO @EstimuloDocenteAcumulado( Nivel )VALUES( @NivelActual );
SET @ContadorTrimestre = 1; WHILE @ContadorTrimestre <= @id_Trimestre BEGIN SET @EDDHombres = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroEstimulosAlDesempenioDocente WHERE id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @NivelActual)) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @ContadorTrimestre), 0);
SET @EDDMujeres = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroEstimulosAlDesempenioDocente WHERE id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @NivelActual)) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @ContadorTrimestre), 0);
SET @EDDTotal = ISNULL((@EDDHombres + @EDDMujeres), 0);
SET @Monto = ISNULL((SELECT SUM(Desc_MontoPagado) FROM SAC_RegistroEDDMontosPagados WHERE id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @NivelActual)) AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Trimestre = @ContadorTrimestre), 0);
SET @CifrasEDDHombres = CASE WHEN @EDDTotal > @CifrasEDDTotal THEN @EDDHombres ELSE @CifrasEDDHombres END;
SET @CifrasEDDMujeres = CASE WHEN @EDDTotal > @CifrasEDDTotal THEN @EDDMujeres ELSE @CifrasEDDMujeres END;
SET @CifrasEDDTotal = CASE WHEN @EDDTotal > @CifrasEDDTotal THEN @EDDTotal ELSE @CifrasEDDTotal END;
SET @CifrasMonto = @CifrasMonto + @Monto;
IF @ContadorTrimestre = 1 BEGIN UPDATE @EstimuloDocenteAcumulado SET PrimerEDDHombres = @EDDHombres, PrimerEDDMujeres = @EDDMujeres, PrimerEDDTotal = @EDDTotal, PrimerMonto = @Monto WHERE Nivel = @NivelActual; END ELSE IF @ContadorTrimestre = 2 BEGIN UPDATE @EstimuloDocenteAcumulado SET SegundoEDDHombres = @EDDHombres, SegundoEDDMujeres = @EDDMujeres, SegundoEDDTotal = @EDDTotal, SegundoMonto = @Monto WHERE Nivel = @NivelActual; END ELSE IF @ContadorTrimestre = 3 BEGIN UPDATE @EstimuloDocenteAcumulado SET TercerEDDHombres = @EDDHombres, TercerEDDMujeres = @EDDMujeres, TercerEDDTotal = @EDDTotal, TercerMonto = @Monto WHERE Nivel = @NivelActual; END ELSE IF @ContadorTrimestre = 4 BEGIN UPDATE @EstimuloDocenteAcumulado SET CuartoEDDHombres = @EDDHombres, CuartoEDDMujeres = @EDDMujeres, CuartoEDDTotal = @EDDTotal, CuartoMonto = @Monto WHERE Nivel = @NivelActual; END
SET @ContadorTrimestre = @ContadorTrimestre + 1; END
UPDATE @EstimuloDocenteAcumulado SET CifrasEDDHombres = @CifrasEDDHombres, CifrasEDDMujeres = @CifrasEDDMujeres, CifrasEDDTotal = @CifrasEDDTotal, CifrasMonto = @CifrasMonto WHERE Nivel = @NivelActual;
SET @ContadorNivel = @ContadorNivel + 1; END
INSERT INTO @EstimuloDocenteAcumulado VALUES( 'TOTAL', ISNULL((SELECT SUM(CAST(PrimerEDDHombres AS INT)) FROM @EstimuloDocenteAcumulado), 0), ISNULL((SELECT SUM(CAST(PrimerEDDMujeres AS INT)) FROM @EstimuloDocenteAcumulado), 0), ISNULL((SELECT SUM(CAST(PrimerEDDTotal AS INT)) FROM @EstimuloDocenteAcumulado), 0), ISNULL((SELECT SUM(CAST(PrimerMonto AS MONEY)) FROM @EstimuloDocenteAcumulado), 0),
ISNULL((SELECT SUM(CAST(SegundoEDDHombres AS INT)) FROM @EstimuloDocenteAcumulado), 0), ISNULL((SELECT SUM(CAST(SegundoEDDMujeres AS INT)) FROM @EstimuloDocenteAcumulado), 0), ISNULL((SELECT SUM(CAST(SegundoEDDTotal AS INT)) FROM @EstimuloDocenteAcumulado), 0), ISNULL((SELECT SUM(CAST(SegundoMonto AS MONEY)) FROM @EstimuloDocenteAcumulado), 0),
ISNULL((SELECT SUM(CAST(TercerEDDHombres AS INT)) FROM @EstimuloDocenteAcumulado), 0), ISNULL((SELECT SUM(CAST(TercerEDDMujeres AS INT)) FROM @EstimuloDocenteAcumulado), 0), ISNULL((SELECT SUM(CAST(TercerEDDTotal AS INT)) FROM @EstimuloDocenteAcumulado), 0), ISNULL((SELECT SUM(CAST(TercerMonto AS MONEY)) FROM @EstimuloDocenteAcumulado), 0),
ISNULL((SELECT SUM(CAST(CuartoEDDHombres AS INT)) FROM @EstimuloDocenteAcumulado), 0), ISNULL((SELECT SUM(CAST(CuartoEDDMujeres AS INT)) FROM @EstimuloDocenteAcumulado), 0), ISNULL((SELECT SUM(CAST(CuartoEDDTotal AS INT)) FROM @EstimuloDocenteAcumulado), 0), ISNULL((SELECT SUM(CAST(CuartoMonto AS MONEY)) FROM @EstimuloDocenteAcumulado), 0),
ISNULL((SELECT SUM(CAST(CifrasEDDHombres AS INT)) FROM @EstimuloDocenteAcumulado), 0), ISNULL((SELECT SUM(CAST(CifrasEDDMujeres AS INT)) FROM @EstimuloDocenteAcumulado), 0), ISNULL((SELECT SUM(CAST(CifrasEDDTotal AS INT)) FROM @EstimuloDocenteAcumulado), 0), ISNULL((SELECT SUM(CAST(CifrasMonto AS MONEY)) FROM @EstimuloDocenteAcumulado), 0) );
UPDATE @EstimuloDocenteAcumulado SET Nivel = @NMS WHERE Nivel = 'NMS';
UPDATE @EstimuloDocenteAcumulado SET Nivel = @NS WHERE Nivel = 'NS';
UPDATE @EstimuloDocenteAcumulado SET Nivel = @CINV WHERE Nivel = 'C INV';
UPDATE @EstimuloDocenteAcumulado SET Nivel = @UACA WHERE Nivel = 'UACA';
SELECT * FROM @EstimuloDocenteAcumulado END

-- Formato 18 Estimulo al desempeno docente acumulado vigentes
-- Autor: Kitl Hernández Jesús Rafael
CREATE PROCEDURE SAC_EstimuloDocenteAcumulado
    @Anio INT,
    @id_Trimestre INT
AS

BEGIN SET NOCOUNT ON;
DECLARE @AnioAnterior INT = @anio - 1; DECLARE @NMS VARCHAR(100) = 'NMS'; DECLARE @NS VARCHAR(100) = 'NS'; DECLARE @CINV VARCHAR(100) = 'C INV'; DECLARE @AC VARCHAR(100) = 'UACA'; DECLARE @ProgramaApoyoSAC VARCHAR(100) = 'ESTÍMULO AL DESEMPEÑO DOCENTE';
DECLARE @EDDAnteriorHom INT = 0; DECLARE @EDDAnteriorMuj INT = 0; DECLARE @EDDAnteriorTotal INT = 0; DECLARE @MontoAnterior INT = 0;
DECLARE @EDDActualHom INT =0 ; DECLARE @EDDActualMuj INT =0 ; DECLARE @EDDActualTotal INT = 0; DECLARE @MontoActual INT = 0;
DECLARE @VariacionEDD DECIMAL(10,2) = 0; DECLARE @VariacionMonto DECIMAL(10,2) = 0; DECLARE @Justificacion VARCHAR(255) = '';
DECLARE @TablaNiveles TABLE( identificador INT IDENTITY(1,1), Nivel VARCHAR(255) NOT NULL DEFAULT '', Desc_Nivel VARCHAR(255) NOT NULL DEFAULT '', id_TipoUnidadAcademica INT NOT NULL DEFAULT 0 ) INSERT INTO @TablaNiveles (Nivel, Desc_Nivel) VALUES (@NMS, 'Medio Superior'), (@NS, 'Superior'), (@CINV, 'Centros de Investigación'), (@AC, 'Área Central'); UPDATE @TablaNiveles SET id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @NMS) WHERE Nivel = @NMS; UPDATE @TablaNiveles SET id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @NS) WHERE Nivel = @NS; UPDATE @TablaNiveles SET id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @CINV) WHERE Nivel = @CINV; UPDATE @TablaNiveles SET id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @AC) WHERE Nivel = @AC;
DECLARE @TablaEstimuloDocenteComparativo TABLE( Nivel VARCHAR(255),
EDDAnteriorHom INT NOT NULL DEFAULT 0, EDDAnteriorMuj INT NOT NULL DEFAULT 0, EDDAnteriorTotal INT NOT NULL DEFAULT 0, MontoAnterior INT NOT NULL DEFAULT 0,
EDDActualHom INT NOT NULL DEFAULT 0, EDDActualMuj INT NOT NULL DEFAULT 0, EDDActualTotal INT NOT NULL DEFAULT 0, MontoActual INT NOT NULL DEFAULT 0,
VariacionEDD DECIMAL(10,2) NOT NULL DEFAULT 0, VariacionMonto DECIMAL(10,2) NOT NULL DEFAULT 0, Justificacion VARCHAR(255) NOT NULL DEFAULT '' ); INSERT INTO @TablaEstimuloDocenteComparativo (Nivel) SELECT Desc_Nivel FROM @TablaNiveles;
DECLARE @ContadorNivel INT = 1; WHILE @ContadorNivel <= (SELECT COUNT(*) FROM @TablaNiveles) BEGIN SET @EDDAnteriorHom = ISNULL( (SELECT SUM(Desc_Hombres) FROM SAC_RegistroEstimulosAlDesempenioDocente WHERE id_Trimestre <= @Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @EDDAnteriorMuj = ISNULL( (SELECT SUM(Desc_Mujeres) FROM SAC_RegistroEstimulosAlDesempenioDocente WHERE id_Trimestre <= @Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @EDDAnteriorTotal = @EDDAnteriorHom + @EDDAnteriorMuj; SET @MontoAnterior = ISNULL((SELECT SUM(Desc_MontoPagado) FROM SAC_RegistroEDDMontosPagados WHERE id_Trimestre <= @Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )), 0);
SET @EDDActualHom = ISNULL( (SELECT SUM(Desc_Hombres) FROM SAC_RegistroEstimulosAlDesempenioDocente WHERE id_Trimestre <= @Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0);
SET @EDDActualMuj = ISNULL( (SELECT SUM(Desc_Mujeres) FROM SAC_RegistroEstimulosAlDesempenioDocente WHERE id_Trimestre <= @Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @EDDActualTotal = @EDDActualHom + @EDDActualMuj; SET @MontoActual = ISNULL((SELECT SUM(Desc_MontoPagado) FROM SAC_RegistroEDDMontosPagados WHERE id_Trimestre <= @Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )), 0);
SET @VariacionEDD = CASE WHEN @EDDActualTotal = 0 THEN 0 ELSE (CAST(@EDDActualTotal AS DECIMAL(10,2)) - CAST(@EDDAnteriorTotal AS DECIMAL(10,2)))/CAST(@EDDAnteriorTotal AS DECIMAL(10,2))*100 END; SET @VariacionMonto = CASE WHEN @MontoActual = 0 THEN 0 ELSE (CAST(@MontoActual AS DECIMAL(10,2)) - CAST(@MontoAnterior AS DECIMAL(10,2)))/CAST(@MontoAnterior AS DECIMAL(10,2))*100 END;
SET @Justificacion = ISNULL((SELECT TOP 1 Desc_Justificacion FROM SAC_Justificaciones WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @Trimestre AND id_ProgramaApoyoSAC = (SELECT ID_ProgramaApoyoSAC FROM SAC_ProgramaApoyo WHERE Desc_ProgramaApoyoSAC = @ProgramaApoyoSAC) AND id_TipoUnidadAcademicaSAC = (SELECT id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel))), '');
UPDATE @TablaEstimuloDocenteComparativo SET EDDAnteriorHom = @EDDAnteriorHom, EDDAnteriorMuj = @EDDAnteriorMuj, EDDAnteriorTotal = @EDDAnteriorTotal, MontoAnterior = @MontoAnterior, EDDActualHom = @EDDActualHom, EDDActualMuj = @EDDActualMuj, EDDActualTotal = @EDDActualTotal, MontoActual = @MontoActual, VariacionEDD = @VariacionEDD, VariacionMonto = @VariacionMonto, Justificacion = @Justificacion WHERE Nivel = (SELECT Desc_Nivel FROM @TablaNiveles WHERE identificador = @ContadorNivel);
SET @ContadorNivel = @ContadorNivel + 1; END SELECT * FROM @TablaEstimuloDocenteComparativo UNION ALL SELECT 'Total', SUM(EDDAnteriorHom), SUM(EDDAnteriorMuj), SUM(EDDAnteriorTotal), SUM(MontoAnterior), SUM(EDDActualHom), SUM(EDDActualMuj), SUM(EDDActualTotal), SUM(MontoActual), CAST( CASE WHEN SUM(EDDAnteriorTotal) = 0 THEN 0 ELSE (SUM(EDDActualTotal) - SUM(EDDAnteriorTotal)) * 100.0 / NULLIF(SUM(EDDAnteriorTotal), 0) END AS DECIMAL(10,2) ), CAST( CASE WHEN SUM(MontoAnterior) = 0 THEN 0 ELSE (SUM(MontoActual) - SUM(MontoAnterior)) * 100.0 / NULLIF(SUM(MontoAnterior), 0) END AS DECIMAL(10,2) ), @Justificacion FROM @TablaEstimuloDocenteComparativo;
END

-- Jesus Perez
-- Formato 19 Estimulo al desempeño docente comparativo
CREATE PROCEDURE SAC_EstimuloDocenteComparativo
    @anio INT,
    @trimestre INT
AS

BEGIN
    SET NOCOUNT ON;
    DECLARE @EstimulosDocentesConcentrado TABLE(
        Nivel varchar(255),
        PrimeroHombres INT NOT NULL DEFAULT 0,
        PrimeroMujeres INT NOT NULL DEFAULT 0,
        PrimeroTotal INT NOT NULL DEFAULT 0,

        SegundoHombres INT NOT NULL DEFAULT 0,
        SegundoMujeres INT NOT NULL DEFAULT 0,
        SegundoTotal INT NOT NULL DEFAULT 0,

        TerceroHombres INT NOT NULL DEFAULT 0,
        TerceroMujeres INT NOT NULL DEFAULT 0,
        TerceroTotal INT NOT NULL DEFAULT 0,

        CuartoHombres INT NOT NULL DEFAULT 0,
        CuartoMujeres INT NOT NULL DEFAULT 0,
        CuartoTotal INT NOT NULL DEFAULT 0,

        QuintoHombres INT NOT NULL DEFAULT 0,
        QuintoMujeres INT NOT NULL DEFAULT 0,
        QuintoTotal INT NOT NULL DEFAULT 0,

        SextoHombres INT NOT NULL DEFAULT 0,
        SextoMujeres INT NOT NULL DEFAULT 0, 
        SextoTotal INT NOT NULL DEFAULT 0,

        SeptimoHombres INT NOT NULL DEFAULT 0,
        SeptimoMujeres INT NOT NULL DEFAULT 0,
        SeptimoTotal INT NOT NULL DEFAULT 0,

        OctavoHombres INT NOT NULL DEFAULT 0,
        OctavoMujeres INT NOT NULL DEFAULT 0,
        OctavoTotal INT NOT NULL DEFAULT 0,

        NovenoHombres INT NOT NULL DEFAULT 0,
        NovenoMujeres INT NOT NULL DEFAULT 0,
        NovenoTotal INT NOT NULL DEFAULT 0,

        TotalHombres INT NOT NULL DEFAULT 0,
        TotalMujeres INT NOT NULL DEFAULT 0,
        TotalTotal INT NOT NULL DEFAULT 0,

        MontoPagado INT NOT NULL DEFAULT 0
    );

    DECLARE @PrimerTotalHombres INT = 0;
    DECLARE @PrimerTotalMujeres INT = 0;
    DECLARE @PrimerTotalTotal INT = 0;

    DECLARE @SegundoTotalHombres INT = 0;
    DECLARE @SegundoTotalMujeres INT = 0;
    DECLARE @SegundoTotalTotal INT = 0;

    DECLARE @TerceroTotalHombres INT = 0;
    DECLARE @TerceroTotalMujeres INT = 0;
    DECLARE @TerceroTotalTotal INT = 0;

    DECLARE @CuartoTotalHombres INT = 0;
    DECLARE @CuartoTotalMujeres INT = 0;
    DECLARE @CuartoTotalTotal INT = 0;

    DECLARE @QuintoTotalHombres INT = 0;
    DECLARE @QuintoTotalMujeres INT = 0;
    DECLARE @QuintoTotalTotal INT = 0;

    DECLARE @SextoTotalHombres INT = 0;
    DECLARE @SextoTotalMujeres INT = 0;
    DECLARE @SextoTotalTotal INT = 0;

    DECLARE @SeptimoTotalHombres INT = 0;
    DECLARE @SeptimoTotalMujeres INT = 0;
    DECLARE @SeptimoTotalTotal INT = 0;

    DECLARE @OctavoTotalHombres INT = 0;
    DECLARE @OctavoTotalMujeres INT = 0;
    DECLARE @OctavoTotalTotal INT = 0;

    DECLARE @NovenoTotalHombres INT = 0;
    DECLARE @NovenoTotalMujeres INT = 0;
    DECLARE @NovenoTotalTotal INT = 0;

    DECLARE @TotalHombresNiveles INT = 0;
    DECLARE @TotalMujeresNiveles INT = 0;
    DECLARE @TotalTotalNiveles INT = 0;

    DECLARE @TotalMontoPagado INT = 0;

    DECLARE @Hombres INT = 0;
    DECLARE @Mujeres INT = 0;
    DECLARE @Total INT = 0;

    DECLARE @TotalHombres INT = 0;
    DECLARE @TotalMujeres INT = 0;
    DECLARE @TotalTotal INT = 0;

    DECLARE @MontoPagado MONEY = 0;

    DECLARE @NivelActual varchar(50);
    DECLARE @ContadorPeriodo INT = 1;

    --Declaración de niveles
    DECLARE @TablaNiveles TABLE(
        ID_Numeral INT IDENTITY(1, 1),
        TipoUnidadAcademica varchar(50)
    );

    INSERT INTO @TablaNiveles(
        TipoUnidadAcademica
    )VALUES
    ('NMS'),
    ('NS'),
    ('C INV'),
    ('UACA');

    DECLARE @NMS varchar(50) = 'MEDIO SUPERIOR';
    DECLARE @NS varchar(50) = 'SUPERIOR Y POSGRADO';
    DECLARE @CINV varchar(50) = 'CENTROS DE INVESTIGACIÓN';
    DECLARE @UACA varchar(50) = 'ÁREA CENTRAL';

    DECLARE @ContadorNivel INT = 1;
    WHILE @ContadorNivel <= (SELECT COUNT(*) FROM @TablaNiveles)
        BEGIN

            SET @TotalHombres = 0;
            SET @TotalMujeres = 0;
            SET @TotalTotal = 0;
            SET @NivelActual = (SELECT TipoUnidadAcademica FROM @TablaNiveles WHERE ID_Numeral = @ContadorNivel);

            INSERT INTO @EstimulosDocentesConcentrado(
                Nivel
            )VALUES(
                @NivelActual
            );

            SET @ContadorPeriodo = 1;
            WHILE @ContadorPeriodo <= 9
            BEGIN
                SET @Hombres = ISNULL((SELECT SUM(Desc_Hombres)
                                        FROM SAC_RegistroEstimulosAlDesempenioDocente
                                        WHERE Desc_PeriodoSAC = @ContadorPeriodo
                                        AND id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica 
                                                                    FROM UnidadesAcademicas 
                                                                    WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica 
                                                                                                    FROM TipoUnidadAcademica 
                                                                                                    WHERE Desc_SiglasTipo = @NivelActual))
                                        AND id_Anio = (SELECT ID_Anio 
                                                        FROM Anio 
                                                        WHERE Desc_Anio = @Anio)
                                        AND id_Trimestre = @id_Trimestre), 0);

                SET @Mujeres = ISNULL((SELECT SUM(Desc_Mujeres)
                                        FROM SAC_RegistroEstimulosAlDesempenioDocente
                                        WHERE Desc_PeriodoSAC = @ContadorPeriodo
                                        AND id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica 
                                                                    FROM UnidadesAcademicas 
                                                                    WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica 
                                                                                                    FROM TipoUnidadAcademica 
                                                                                                    WHERE Desc_SiglasTipo = @NivelActual))
                                        AND id_Anio = (SELECT ID_Anio 
                                                        FROM Anio 
                                                        WHERE Desc_Anio = @Anio)
                                        AND id_Trimestre = @id_Trimestre), 0);

                SET @MontoPagado = ISNULL((SELECT SUM(Desc_MontoPagado)
                                        FROM SAC_RegistroEDDMontosPagados
                                        WHERE id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica 
                                                                    FROM UnidadesAcademicas 
                                                                    WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica 
                                                                                                    FROM TipoUnidadAcademica 
                                                                                                    WHERE Desc_SiglasTipo = @NivelActual))
                                        AND id_Anio = (SELECT ID_Anio 
                                                        FROM Anio 
                                                        WHERE Desc_Anio = @Anio)
                                        AND id_Trimestre = @id_Trimestre), 0);

                SET @Total = @Hombres + @Mujeres;

                SET @TotalHombres = @TotalHombres + @Hombres;
                SET @TotalMujeres = @TotalMujeres + @Mujeres;
                SET @TotalTotal = @TotalTotal + @Total;

                IF @ContadorPeriodo = 1
                BEGIN
                    UPDATE @EstimulosDocentesConcentrado
                    SET PrimeroHombres = @Hombres,
                    PrimeroMujeres = @Mujeres,
                    PrimeroTotal = @Total
                    WHERE Nivel = @NivelActual;
                END
                ELSE IF @ContadorPeriodo = 2
                BEGIN
                    UPDATE @EstimulosDocentesConcentrado
                    SET SegundoHombres = @Hombres,
                    SegundoMujeres = @Mujeres,
                    SegundoTotal = @Total
                    WHERE Nivel = @NivelActual;
                END
                ELSE IF @ContadorPeriodo = 3
                BEGIN
                    UPDATE @EstimulosDocentesConcentrado
                    SET TerceroHombres = @Hombres,
                    TerceroMujeres = @Mujeres,
                    TerceroTotal = @Total
                    WHERE Nivel = @NivelActual;
                END
                ELSE IF @ContadorPeriodo = 4
                BEGIN
                    UPDATE @EstimulosDocentesConcentrado
                    SET CuartoHombres = @Hombres,
                    CuartoMujeres = @Mujeres,
                    CuartoTotal = @Total
                    WHERE Nivel = @NivelActual;
                END
                ELSE IF @ContadorPeriodo = 5
                BEGIN
                    UPDATE @EstimulosDocentesConcentrado
                    SET QuintoHombres = @Hombres,
                    QuintoMujeres = @Mujeres,
                    QuintoTotal = @Total
                    WHERE Nivel = @NivelActual;
                END
                ELSE IF @ContadorPeriodo = 6
                BEGIN
                    UPDATE @EstimulosDocentesConcentrado
                    SET SextoHombres = @Hombres,
                    SextoMujeres = @Mujeres,
                    SextoTotal = @Total
                    WHERE Nivel = @NivelActual;
                END
                ELSE IF @ContadorPeriodo = 7
                BEGIN
                    UPDATE @EstimulosDocentesConcentrado
                    SET SeptimoHombres = @Hombres,
                    SeptimoMujeres = @Mujeres,
                    SeptimoTotal = @Total
                    WHERE Nivel = @NivelActual;
                END
                ELSE IF @ContadorPeriodo = 8
                BEGIN
                    UPDATE @EstimulosDocentesConcentrado
                    SET OctavoHombres = @Hombres,
                    OctavoMujeres = @Mujeres,
                    OctavoTotal = @Total
                    WHERE Nivel = @NivelActual;
                END
                ELSE IF @ContadorPeriodo = 9
                BEGIN
                    UPDATE @EstimulosDocentesConcentrado
                    SET
                        NovenoHombres = @Hombres,
                        NovenoMujeres = @Mujeres,
                        NovenoTotal = @Total,
                        MontoPagado = @MontoPagado,
                        TotalHombres = @TotalHombres,
                        TotalMujeres = @TotalMujeres,
                        TotalTotal = @TotalTotal
                    WHERE Nivel = @NivelActual;
                END
                SET @ContadorPeriodo = @ContadorPeriodo + 1;
            END
            SET @ContadorNivel = @ContadorNivel + 1;
        END

    SET @PrimerTotalHombres = ISNULL((SELECT SUM(PrimeroHombres) FROM @EstimulosDocentesConcentrado), 0);
    SET @PrimerTotalMujeres = ISNULL((SELECT SUM(PrimeroMujeres) FROM @EstimulosDocentesConcentrado), 0);
    SET @PrimerTotalTotal = ISNULL((SELECT SUM(PrimeroTotal) FROM @EstimulosDocentesConcentrado), 0);
    SET @SegundoTotalHombres = ISNULL((SELECT SUM(SegundoHombres) FROM @EstimulosDocentesConcentrado), 0);
    SET @SegundoTotalMujeres = ISNULL((SELECT SUM(SegundoMujeres) FROM @EstimulosDocentesConcentrado), 0);
    SET @SegundoTotalTotal = ISNULL((SELECT SUM(SegundoTotal) FROM @EstimulosDocentesConcentrado), 0);
    SET @TerceroTotalHombres = ISNULL((SELECT SUM(TerceroHombres) FROM @EstimulosDocentesConcentrado), 0);
    SET @TerceroTotalMujeres = ISNULL((SELECT SUM(TerceroMujeres) FROM @EstimulosDocentesConcentrado), 0);
    SET @TerceroTotalTotal = ISNULL((SELECT SUM(TerceroTotal) FROM @EstimulosDocentesConcentrado), 0);
    SET @CuartoTotalHombres = ISNULL((SELECT SUM(CuartoHombres) FROM @EstimulosDocentesConcentrado), 0);
    SET @CuartoTotalMujeres = ISNULL((SELECT SUM(CuartoMujeres) FROM @EstimulosDocentesConcentrado), 0);
    SET @CuartoTotalTotal = ISNULL((SELECT SUM(CuartoTotal) FROM @EstimulosDocentesConcentrado), 0);
    SET @QuintoTotalHombres = ISNULL((SELECT SUM(QuintoHombres) FROM @EstimulosDocentesConcentrado), 0);
    SET @QuintoTotalMujeres = ISNULL((SELECT SUM(QuintoMujeres) FROM @EstimulosDocentesConcentrado), 0);
    SET @QuintoTotalTotal = ISNULL((SELECT SUM(QuintoTotal) FROM @EstimulosDocentesConcentrado), 0);
    SET @SextoTotalHombres = ISNULL((SELECT SUM(SextoHombres) FROM @EstimulosDocentesConcentrado), 0);
    SET @SextoTotalMujeres = ISNULL((SELECT SUM(SextoMujeres) FROM @EstimulosDocentesConcentrado), 0);
    SET @SextoTotalTotal = ISNULL((SELECT SUM(SextoTotal) FROM @EstimulosDocentesConcentrado), 0);
    SET @SeptimoTotalHombres = ISNULL((SELECT SUM(SeptimoHombres) FROM @EstimulosDocentesConcentrado), 0);
    SET @SeptimoTotalMujeres = ISNULL((SELECT SUM(SeptimoMujeres) FROM @EstimulosDocentesConcentrado), 0);
    SET @SeptimoTotalTotal = ISNULL((SELECT SUM(SeptimoTotal) FROM @EstimulosDocentesConcentrado), 0);
    SET @OctavoTotalHombres = ISNULL((SELECT SUM(OctavoHombres) FROM @EstimulosDocentesConcentrado), 0);
    SET @OctavoTotalMujeres = ISNULL((SELECT SUM(OctavoMujeres) FROM @EstimulosDocentesConcentrado), 0);
    SET @OctavoTotalTotal = ISNULL((SELECT SUM(OctavoTotal) FROM @EstimulosDocentesConcentrado), 0);
    SET @NovenoTotalHombres = ISNULL((SELECT SUM(NovenoHombres) FROM @EstimulosDocentesConcentrado), 0);
    SET @NovenoTotalMujeres = ISNULL((SELECT SUM(NovenoMujeres) FROM @EstimulosDocentesConcentrado), 0);
    SET @NovenoTotalTotal = ISNULL((SELECT SUM(NovenoTotal) FROM @EstimulosDocentesConcentrado), 0);
    SET @TotalHombresNiveles = ISNULL((SELECT SUM(TotalHombres) FROM @EstimulosDocentesConcentrado), 0);
    SET @TotalMujeresNiveles = ISNULL((SELECT SUM(TotalMujeres) FROM @EstimulosDocentesConcentrado), 0);
    SET @TotalTotalNiveles = ISNULL((SELECT SUM(TotalTotal) FROM @EstimulosDocentesConcentrado), 0);
    SET @TotalMontoPagado = ISNULL((SELECT SUM(MontoPagado) FROM @EstimulosDocentesConcentrado), 0);

    INSERT INTO @EstimulosDocentesConcentrado
    VALUES(
        'TOTAL',
        @PrimerTotalHombres,
        @PrimerTotalMujeres,
        @PrimerTotalTotal,
        @SegundoTotalHombres,
        @SegundoTotalMujeres,
        @SegundoTotalTotal,
        @TerceroTotalHombres,
        @TerceroTotalMujeres,
        @TerceroTotalTotal,
        @CuartoTotalHombres,
        @CuartoTotalMujeres,
        @CuartoTotalTotal,
        @QuintoTotalHombres,
        @QuintoTotalMujeres,
        @QuintoTotalTotal,
        @SextoTotalHombres,
        @SextoTotalMujeres,
        @SextoTotalTotal,
        @SeptimoTotalHombres,
        @SeptimoTotalMujeres,
        @SeptimoTotalTotal,
        @OctavoTotalHombres,
        @OctavoTotalMujeres,
        @OctavoTotalTotal,
        @NovenoTotalHombres,
        @NovenoTotalMujeres,
        @NovenoTotalTotal,
        @TotalHombresNiveles,
        @TotalMujeresNiveles,
        @TotalTotalNiveles,
        @TotalMontoPagado
    );

    UPDATE @EstimulosDocentesConcentrado
    SET
        Nivel = @NMS
    WHERE Nivel = 'NMS';

    UPDATE @EstimulosDocentesConcentrado
    SET
        Nivel = @NS
    WHERE Nivel = 'NS';

    UPDATE @EstimulosDocentesConcentrado
    SET
        Nivel = @CINV
    WHERE Nivel = 'C INV';

    UPDATE @EstimulosDocentesConcentrado
    SET
        Nivel = @UACA
    WHERE Nivel = 'UACA';

    SELECT * FROM @EstimulosDocentesConcentrado
END

