CREATE PROCEDURE SAC_InsertRegistroSabaticos
    @CantidadHombres INT,
    @CantidadMujeres INT,
    @SiglasUnidadAcademica varchar(255),
    @NombreProgramaSabatico varchar(255),
    @NumeroFormatoSAC INT,
    @AnioSemestre varchar(255),
    @Fecha varchar(255),
    @id_Trimestre INT,
    @Anio INT,
    @CorreoUsuario varchar(255),
    @GuardarEnviar varchar(7),
    @ID_Registro INT
AS

BEGIN DECLARE @PeriodoAnterior INT = @Trimestre - 1 Set @PeriodoAnterior = CASE WHEN @PeriodoAnterior = 0 THEN 4 ELSE @PeriodoAnterior END
DECLARE @AnioAnterior INT = CASE WHEN @PeriodoAnterior = 4 THEN @anio - 1 ELSE @anio END
DECLARE @ESPECIALIDAD VARCHAR (255) = 'ESPECIALIDAD'; DECLARE @MAESTRIA VARCHAR (255) = 'MAESTRÍA'; DECLARE @DOCTORADO VARCHAR (255) = 'DOCTORADO'; DECLARE @ESTANCIA VARCHAR (255) = 'ESTANCIA DE INVESTIGACIÓN'; DECLARE @NACIONAL VARCHAR (255) = 'NACIONAL'; DECLARE @EXTRANJERO VARCHAR (255) = 'EXTRANJERO'; DECLARE @LICENCASCONGOCE VARCHAR (255) = 'LICENCIAS CON GOCE DE SUELDO'; DECLARE @AJUSTES VARCHAR (255) = 'AJUSTES'; DECLARE @LIBERACIONES VARCHAR (255) = 'LIBERACIONES'; DECLARE @CANCELACIONES VARCHAR (255) = 'CANCELACIONES'; DECLARE @NO_LICENCIAS_OTORGADAS VARCHAR (255) = 'No. DE LICENCIAS OTORGADAS EN EL TRIMESTRE'; DECLARE @NO_PRORROGAS_AUTORIZADAS VARCHAR (255) = 'No. DE PRÓRROGAS AUTORIZADAS EN EL TRIMESTRE'; DECLARE @LICENCIAS_VIGENTES_ANTERIOR VARCHAR (255) = 'LICENCIAS VIGENTES AL PERIODO';
DECLARE @LicenciasVigentesAnteriorHomNac INT = 0 DECLARE @LicenciasVigentesAnteriorMujNac INT = 0 DECLARE @LicenciasVigentesAnteriorHomExt INT = 0 DECLARE @LicenciasVigentesAnteriorMujExt INT = 0 DECLARE @LicenciasVigentesAnteriorTot INT = 0
DECLARE @AjustesHomNac INT = 0 DECLARE @AjustesMujNac INT = 0 DECLARE @AjustesHomExt INT = 0 DECLARE @AjustesMujExt INT = 0
DECLARE @NoLicenciasOtorgadasHomNac INT = 0 DECLARE @NoLicenciasOtorgadasMujNac INT = 0 DECLARE @NoLicenciasOtorgadasHomExt INT = 0 DECLARE @NoLicenciasOtorgadasMujExt INT = 0
DECLARE @NoProrrogasAutorizadasHomNac INT = 0 DECLARE @NoProrrogasAutorizadasMujNac INT = 0 DECLARE @NoProrrogasAutorizadasHomExt INT = 0 DECLARE @NoProrrogasAutorizadasMujExt INT = 0
DECLARE @LiberacionesHomNac INT = 0 DECLARE @LiberacionesMujNac INT = 0 DECLARE @LiberacionesHomExt INT = 0 DECLARE @LiberacionesMujExt INT = 0
DECLARE @CancelacionesHomNac INT = 0 DECLARE @CancelacionesMujNac INT = 0 DECLARE @CancelacionesHomExt INT = 0 DECLARE @CancelacionesMujExt INT = 0
DECLARE @NoLicenciasVigentesActHomNac INT = 0 DECLARE @NoLicenciasVigentesActMujNac INT = 0 DECLARE @NoLicenciasVigentesActHomExt INT = 0 DECLARE @NoLicenciasVigentesActMujExt INT = 0 DECLARE @NoLicenciasVigentesActTot INT = 0
DECLARE @NoLicenciasVigentesActEstudioEspec INT = 0 DECLARE @NoLicenciasVigentesActEstudioMaest INT = 0 DECLARE @NoLicenciasVigentesActEstudioDoct INT = 0 DECLARE @NoLicenciasVigentesActEstudioEstan INT = 0 DECLARE @NoLicenciasVigentesActEstudioTot INT = 0
DECLARE @TablaLicenciasGoceSueldo TABLE ( Periodo VARCHAR(255) NOT NULL DEFAULT '',
LicenciasVigentesAnteriorHomNac INT NOT NULL DEFAULT 0, LicenciasVigentesAnteriorMujNac INT NOT NULL DEFAULT 0, LicenciasVigentesAnteriorHomExt INT NOT NULL DEFAULT 0, LicenciasVigentesAnteriorMujExt INT NOT NULL DEFAULT 0, LicenciasVigentesAnteriorTot INT NOT NULL DEFAULT 0,
AjustesHomNac INT NOT NULL DEFAULT 0, AjustesMujNac INT NOT NULL DEFAULT 0, AjustesHomExt INT NOT NULL DEFAULT 0, AjustesMujExt INT NOT NULL DEFAULT 0,
NoLicenciasOtorgadasHomNac INT NOT NULL DEFAULT 0, NoLicenciasOtorgadasMujNac INT NOT NULL DEFAULT 0, NoLicenciasOtorgadasHomExt INT NOT NULL DEFAULT 0, NoLicenciasOtorgadasMujExt INT NOT NULL DEFAULT 0,
NoProrrogasAutorizadasHomNac INT NOT NULL DEFAULT 0, NoProrrogasAutorizadasMujNac INT NOT NULL DEFAULT 0, NoProrrogasAutorizadasHomExt INT NOT NULL DEFAULT 0, NoProrrogasAutorizadasMujExt INT NOT NULL DEFAULT 0,
LiberacionesHomNac INT NOT NULL DEFAULT 0, LiberacionesMujNac INT NOT NULL DEFAULT 0, LiberacionesHomExt INT NOT NULL DEFAULT 0, LiberacionesMujExt INT NOT NULL DEFAULT 0,
CancelacionesHomNac INT NOT NULL DEFAULT 0, CancelacionesMujNac INT NOT NULL DEFAULT 0, CancelacionesHomExt INT NOT NULL DEFAULT 0, CancelacionesMujExt INT NOT NULL DEFAULT 0,
NoLicenciasVigentesActHomNac INT NOT NULL DEFAULT 0, NoLicenciasVigentesActMujNac INT NOT NULL DEFAULT 0, NoLicenciasVigentesActHomExt INT NOT NULL DEFAULT 0, NoLicenciasVigentesActMujExt INT NOT NULL DEFAULT 0, NoLicenciasVigentesActTot INT NOT NULL DEFAULT 0, NoLicenciasVigentesActEstudioEspec INT NOT NULL DEFAULT 0, NoLicenciasVigentesActEstudioMaest INT NOT NULL DEFAULT 0, NoLicenciasVigentesActEstudioDoct INT NOT NULL DEFAULT 0, NoLicenciasVigentesActEstudioEstan INT NOT NULL DEFAULT 0, NoLicenciasVigentesActEstudioTot INT NOT NULL DEFAULT 0 ) INSERT INTO @TablaLicenciasGoceSueldo (Periodo) SELECT Desc_PeriodoTrimestre FROM Trimestre WHERE ID_Trimestre <= @Trimestre;
DECLARE @ContadorPeriodo INT = 1 WHILE @ContadorPeriodo <= @Trimestre BEGIN
SET @AjustesHomNac = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @AJUSTES)), 0); SET @AjustesMujNac = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @AJUSTES)), 0); SET @AjustesHomExt = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @AJUSTES)), 0); SET @AjustesMujExt = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @AJUSTES)), 0);
SET @NoLicenciasOtorgadasHomNac = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_LICENCIAS_OTORGADAS)), 0); SET @NoLicenciasOtorgadasMujNac = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_LICENCIAS_OTORGADAS)), 0); SET @NoLicenciasOtorgadasHomExt = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_LICENCIAS_OTORGADAS)), 0); SET @NoLicenciasOtorgadasMujExt = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_LICENCIAS_OTORGADAS)), 0); SET @NoProrrogasAutorizadasHomNac = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_PRORROGAS_AUTORIZADAS)), 0); SET @NoProrrogasAutorizadasMujNac = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_PRORROGAS_AUTORIZADAS)), 0); SET @NoProrrogasAutorizadasHomExt = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_PRORROGAS_AUTORIZADAS)), 0); SET @NoProrrogasAutorizadasMujExt = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_PRORROGAS_AUTORIZADAS)), 0); SET @LiberacionesHomNac = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LIBERACIONES)), 0); SET @LiberacionesMujNac = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LIBERACIONES)), 0); SET @LiberacionesHomExt = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LIBERACIONES)), 0); SET @LiberacionesMujExt = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LIBERACIONES)), 0); SET @CancelacionesHomNac = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @CANCELACIONES)), 0); SET @CancelacionesMujNac = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @CANCELACIONES)), 0); SET @CancelacionesHomExt = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @CANCELACIONES)), 0);
SET @CancelacionesMujExt = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @CANCELACIONES)), 0);
SET @LicenciasVigentesAnteriorHomNac = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @PeriodoAnterior AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LICENCIAS_VIGENTES_ANTERIOR)),0) + @AjustesHomNac + @NoLicenciasOtorgadasHomNac - @LiberacionesHomNac - @CancelacionesHomNac; SET @LicenciasVigentesAnteriorHomNac = CASE WHEN @LicenciasVigentesAnteriorHomNac < 0 THEN 0 ELSE @LicenciasVigentesAnteriorHomNac END;
SET @LicenciasVigentesAnteriorMujNac = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @PeriodoAnterior AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LICENCIAS_VIGENTES_ANTERIOR)),0) + @AjustesMujNac + @NoLicenciasOtorgadasMujNac - @LiberacionesMujNac - @CancelacionesMujNac; SET @LicenciasVigentesAnteriorMujNac = CASE WHEN @LicenciasVigentesAnteriorMujNac < 0 THEN 0 ELSE @LicenciasVigentesAnteriorMujNac END;
SET @LicenciasVigentesAnteriorHomExt = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @PeriodoAnterior AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LICENCIAS_VIGENTES_ANTERIOR)),0) + @AjustesHomExt + @NoLicenciasOtorgadasHomExt - @LiberacionesHomExt - @CancelacionesHomExt; SET @LicenciasVigentesAnteriorHomExt = CASE WHEN @LicenciasVigentesAnteriorHomExt < 0 THEN 0 ELSE @LicenciasVigentesAnteriorHomExt END;
SET @LicenciasVigentesAnteriorMujExt = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @PeriodoAnterior AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LICENCIAS_VIGENTES_ANTERIOR)),0) + @AjustesMujExt + @NoLicenciasOtorgadasMujExt - @LiberacionesMujExt - @CancelacionesMujExt; SET @LicenciasVigentesAnteriorMujExt = CASE WHEN @LicenciasVigentesAnteriorMujExt < 0 THEN 0 ELSE @LicenciasVigentesAnteriorMujExt END;
SET @LicenciasVigentesAnteriorTot = @LicenciasVigentesAnteriorHomNac + @LicenciasVigentesAnteriorMujNac + @LicenciasVigentesAnteriorHomExt + @LicenciasVigentesAnteriorMujExt;
SET @NoLicenciasVigentesActHomNac = ISNULL(@LicenciasVigentesAnteriorHomNac, 0 ) + ISNULL(@AjustesHomNac, 0) + ISNULL(@NoLicenciasOtorgadasHomNac, 0) - ISNULL(@LiberacionesHomNac, 0) - ISNULL(@CancelacionesHomNac, 0); SET @NoLicenciasVigentesActHomNac = CASE WHEN @NoLicenciasVigentesActHomNac < 0 THEN 0 ELSE @NoLicenciasVigentesActHomNac END; SET @NoLicenciasVigentesActMujNac = ISNULL(@LicenciasVigentesAnteriorMujNac, 0 ) + ISNULL(@AjustesMujNac, 0) + ISNULL(@NoLicenciasOtorgadasMujNac, 0) - ISNULL(@LiberacionesMujNac, 0) - ISNULL(@CancelacionesMujNac, 0); SET @NoLicenciasVigentesActMujNac = CASE WHEN @NoLicenciasVigentesActMujNac < 0 THEN 0 ELSE @NoLicenciasVigentesActMujNac END; SET @NoLicenciasVigentesActHomExt = ISNULL(@LicenciasVigentesAnteriorHomExt, 0 ) + ISNULL(@AjustesHomExt, 0) + ISNULL(@NoLicenciasOtorgadasHomExt, 0) - ISNULL(@LiberacionesHomExt, 0) - ISNULL(@CancelacionesHomExt, 0); SET @NoLicenciasVigentesActHomExt = CASE WHEN @NoLicenciasVigentesActHomExt < 0 THEN 0 ELSE @NoLicenciasVigentesActHomExt END; SET @NoLicenciasVigentesActMujExt = ISNULL(@LicenciasVigentesAnteriorMujExt, 0 ) + ISNULL(@AjustesMujExt, 0) + ISNULL(@NoLicenciasOtorgadasMujExt, 0) - ISNULL(@LiberacionesMujExt, 0) - ISNULL(@CancelacionesMujExt, 0); SET @NoLicenciasVigentesActMujExt = CASE WHEN @NoLicenciasVigentesActMujExt < 0 THEN 0 ELSE @NoLicenciasVigentesActMujExt END;
SET @NoLicenciasVigentesActTot = @NoLicenciasVigentesActHomNac + @NoLicenciasVigentesActHomExt + @NoLicenciasVigentesActMujNac + @NoLicenciasVigentesActMujExt;
SET @NoLicenciasVigentesActEstudioEspec = ISNULL((SELECT COUNT(*) FROM SAC_RegistroCantidadesTipoLCGS WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_TipoEstudioLCGSSAC = (SELECT ID_TipoEstudioLCGSSAC FROM SAC_TipoEstudioLCGS WHERE Desc_TipoEstudioLCGSSAC = @ESPECIALIDAD)),0); SET @NoLicenciasVigentesActEstudioMaest = ISNULL((SELECT COUNT(*) FROM SAC_RegistroCantidadesTipoLCGS WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_TipoEstudioLCGSSAC = (SELECT ID_TipoEstudioLCGSSAC FROM SAC_TipoEstudioLCGS WHERE Desc_TipoEstudioLCGSSAC = @MAESTRIA)),0); SET @NoLicenciasVigentesActEstudioDoct = ISNULL((SELECT COUNT(*) FROM SAC_RegistroCantidadesTipoLCGS WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_TipoEstudioLCGSSAC = (SELECT ID_TipoEstudioLCGSSAC FROM SAC_TipoEstudioLCGS WHERE Desc_TipoEstudioLCGSSAC = @DOCTORADO)),0); SET @NoLicenciasVigentesActEstudioEstan = ISNULL((SELECT COUNT(*) FROM SAC_RegistroCantidadesTipoLCGS WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_TipoEstudioLCGSSAC = (SELECT ID_TipoEstudioLCGSSAC FROM SAC_TipoEstudioLCGS WHERE Desc_TipoEstudioLCGSSAC = @ESTANCIA)),0); SET @NoLicenciasVigentesActEstudioTot = @NoLicenciasVigentesActEstudioEspec + @NoLicenciasVigentesActEstudioDoct + @NoLicenciasVigentesActEstudioMaest + @NoLicenciasVigentesActEstudioEstan;
UPDATE @TablaLicenciasGoceSueldo SET LicenciasVigentesAnteriorHomNac = @LicenciasVigentesAnteriorHomNac, LicenciasVigentesAnteriorMujNac = @LicenciasVigentesAnteriorMujNac, LicenciasVigentesAnteriorHomExt = @LicenciasVigentesAnteriorHomExt, LicenciasVigentesAnteriorMujExt = @LicenciasVigentesAnteriorMujExt, LicenciasVigentesAnteriorTot = @LicenciasVigentesAnteriorTot, AjustesHomNac = @AjustesHomNac, AjustesMujNac = @AjustesMujNac, AjustesHomExt = @AjustesHomExt, AjustesMujExt = @AjustesMujExt, NoLicenciasOtorgadasHomNac = @NoLicenciasOtorgadasHomNac, NoLicenciasOtorgadasMujNac = @NoLicenciasOtorgadasMujNac, NoLicenciasOtorgadasHomExt = @NoLicenciasOtorgadasHomExt, NoLicenciasOtorgadasMujExt = @NoLicenciasOtorgadasMujExt, NoProrrogasAutorizadasHomNac = @NoProrrogasAutorizadasHomNac, NoProrrogasAutorizadasMujNac = @NoProrrogasAutorizadasMujNac, NoProrrogasAutorizadasHomExt = @NoProrrogasAutorizadasHomExt, NoProrrogasAutorizadasMujExt = @NoProrrogasAutorizadasMujExt, LiberacionesHomNac = @LiberacionesHomNac, LiberacionesMujNac = @LiberacionesMujNac, LiberacionesHomExt = @LiberacionesHomExt, LiberacionesMujExt = @LiberacionesMujExt, CancelacionesHomNac = @CancelacionesHomNac, CancelacionesMujNac = @CancelacionesMujNac, CancelacionesHomExt = @CancelacionesHomExt, CancelacionesMujExt = @CancelacionesMujExt, NoLicenciasVigentesActHomNac = @NoLicenciasVigentesActHomNac, NoLicenciasVigentesActMujNac = @NoLicenciasVigentesActMujNac, NoLicenciasVigentesActHomExt = @NoLicenciasVigentesActHomExt, NoLicenciasVigentesActMujExt = @NoLicenciasVigentesActMujExt, NoLicenciasVigentesActTot = @NoLicenciasVigentesActTot, NoLicenciasVigentesActEstudioEspec = @NoLicenciasVigentesActEstudioEspec, NoLicenciasVigentesActEstudioMaest = @NoLicenciasVigentesActEstudioMaest, NoLicenciasVigentesActEstudioDoct = @NoLicenciasVigentesActEstudioDoct, NoLicenciasVigentesActEstudioEstan = @NoLicenciasVigentesActEstudioEstan, NoLicenciasVigentesActEstudioTot = @NoLicenciasVigentesActEstudioTot WHERE Periodo = (SELECT Desc_PeriodoTrimestre FROM Trimestre WHERE ID_Trimestre = @ContadorPeriodo);
SET @ContadorPeriodo = @ContadorPeriodo + 1 SET @PeriodoAnterior = CASE WHEN @PeriodoAnterior = 4 THEN 1 ELSE @PeriodoAnterior + 1 END SET @AnioAnterior = CASE WHEN @PeriodoAnterior = 1 THEN @AnioAnterior + 1 ELSE @AnioAnterior END END
SELECT * FROM @TablaLicenciasGoceSueldo UNION ALL SELECT 'CIFRAS AL PERIODO', SUM(LicenciasVigentesAnteriorHomNac), SUM(LicenciasVigentesAnteriorMujNac), SUM(LicenciasVigentesAnteriorHomExt), SUM(LicenciasVigentesAnteriorMujExt), SUM(LicenciasVigentesAnteriorTot), SUM(AjustesHomNac), SUM(AjustesMujNac), SUM(AjustesHomExt), SUM(AjustesMujExt), SUM(NoLicenciasOtorgadasHomNac), SUM(NoLicenciasOtorgadasMujNac), SUM(NoLicenciasOtorgadasHomExt), SUM(NoLicenciasOtorgadasMujExt), SUM(NoProrrogasAutorizadasHomNac), SUM(NoProrrogasAutorizadasMujNac), SUM(NoProrrogasAutorizadasHomExt), SUM(NoProrrogasAutorizadasMujExt), SUM(LiberacionesHomNac), SUM(LiberacionesMujNac), SUM(LiberacionesHomExt), SUM(LiberacionesMujExt), SUM(CancelacionesHomNac), SUM(CancelacionesMujNac), SUM(CancelacionesHomExt), SUM(CancelacionesMujExt), SUM(NoLicenciasVigentesActHomNac), SUM(NoLicenciasVigentesActMujNac), SUM(NoLicenciasVigentesActHomExt), SUM(NoLicenciasVigentesActMujExt), SUM(NoLicenciasVigentesActTot), SUM(NoLicenciasVigentesActEstudioEspec), SUM(NoLicenciasVigentesActEstudioMaest), SUM(NoLicenciasVigentesActEstudioDoct), SUM(NoLicenciasVigentesActEstudioEstan), SUM(NoLicenciasVigentesActEstudioTot) FROM @TablaLicenciasGoceSueldo;
END

-- Jesus Perez 
-- Formato 25 Licencias con goce de sueldo acumulado
CREATE PROCEDURE SAC_LicenciasGoceSueldoAcumulado
    @Anio INT,
    @Trimestre INT
AS

BEGIN -- SET NOCOUNT ON; DECLARE @PeriodoAnterior INT = @Trimestre - 1 Set @PeriodoAnterior = CASE WHEN @PeriodoAnterior = 0 THEN 4 ELSE @PeriodoAnterior END
DECLARE @AnioAnterior INT = CASE WHEN @PeriodoAnterior = 4 THEN @anio - 1 ELSE @anio END
DECLARE @ESPECIALIDAD VARCHAR (255) = 'ESPECIALIDAD'; DECLARE @MAESTRIA VARCHAR (255) = 'MAESTRÍA'; DECLARE @DOCTORADO VARCHAR (255) = 'DOCTORADO'; DECLARE @ESTANCIA VARCHAR (255) = 'ESTANCIA DE INVESTIGACIÓN'; DECLARE @NACIONAL VARCHAR (255) = 'NACIONAL'; DECLARE @EXTRANJERO VARCHAR (255) = 'EXTRANJERO'; DECLARE @LICENCASCONGOCE VARCHAR (255) = 'LICENCIAS CON GOCE DE SUELDO'; DECLARE @AJUSTES VARCHAR (255) = 'AJUSTES'; DECLARE @LIBERACIONES VARCHAR (255) = 'LIBERACIONES'; DECLARE @CANCELACIONES VARCHAR (255) = 'CANCELACIONES'; DECLARE @NO_LICENCIAS_OTORGADAS VARCHAR (255) = 'No. DE LICENCIAS OTORGADAS EN EL TRIMESTRE'; DECLARE @NO_PRORROGAS_AUTORIZADAS VARCHAR (255) = 'No. DE PRÓRROGAS AUTORIZADAS EN EL TRIMESTRE'; DECLARE @LICENCIAS_VIGENTES_ANTERIOR VARCHAR (255) = 'LICENCIAS VIGENTES AL PERIODO';
-- Variables para las siglas de los niveles en la tabla, se pueden modificar si cambian en la base de datos DECLARE @NMS VARCHAR(255) = 'NMS'; DECLARE @NS VARCHAR(255) = 'NS'; DECLARE @CINV VARCHAR (255) = 'C INV'; DECLARE @AC VARCHAR (255) = 'UACA';
DECLARE @LicenciasVigentesAnteriorHomNac INT = 0 DECLARE @LicenciasVigentesAnteriorMujNac INT = 0 DECLARE @LicenciasVigentesAnteriorHomExt INT = 0 DECLARE @LicenciasVigentesAnteriorMujExt INT = 0 DECLARE @LicenciasVigentesAnteriorTot INT = 0
DECLARE @AjustesHomNac INT = 0 DECLARE @AjustesMujNac INT = 0 DECLARE @AjustesHomExt INT = 0 DECLARE @AjustesMujExt INT = 0
DECLARE @NoLicenciasOtorgadasHomNac INT = 0 DECLARE @NoLicenciasOtorgadasMujNac INT = 0 DECLARE @NoLicenciasOtorgadasHomExt INT = 0 DECLARE @NoLicenciasOtorgadasMujExt INT = 0
DECLARE @NoProrrogasAutorizadasHomNac INT = 0 DECLARE @NoProrrogasAutorizadasMujNac INT = 0 DECLARE @NoProrrogasAutorizadasHomExt INT = 0 DECLARE @NoProrrogasAutorizadasMujExt INT = 0
DECLARE @LiberacionesHomNac INT = 0 DECLARE @LiberacionesMujNac INT = 0 DECLARE @LiberacionesHomExt INT = 0 DECLARE @LiberacionesMujExt INT = 0
DECLARE @CancelacionesHomNac INT = 0 DECLARE @CancelacionesMujNac INT = 0 DECLARE @CancelacionesHomExt INT = 0 DECLARE @CancelacionesMujExt INT = 0
DECLARE @NoLicenciasVigentesActHomNac INT = 0 DECLARE @NoLicenciasVigentesActMujNac INT = 0 DECLARE @NoLicenciasVigentesActHomExt INT = 0 DECLARE @NoLicenciasVigentesActMujExt INT = 0 DECLARE @NoLicenciasVigentesActTot INT = 0
DECLARE @NoLicenciasVigentesActEstudioEspec INT = 0 DECLARE @NoLicenciasVigentesActEstudioMaest INT = 0 DECLARE @NoLicenciasVigentesActEstudioDoct INT = 0 DECLARE @NoLicenciasVigentesActEstudioEstan INT = 0 DECLARE @NoLicenciasVigentesActEstudioTot INT = 0
DECLARE @TablaNiveles TABLE( identificador INT IDENTITY(1,1), Nivel VARCHAR(255) NOT NULL DEFAULT '', Desc_Nivel VARCHAR(255) NOT NULL DEFAULT '', id_TipoUnidadAcademica INT NOT NULL DEFAULT 0 ) INSERT INTO @TablaNiveles (Nivel, Desc_Nivel) VALUES (@NMS, 'Medio Superior'), (@NS, 'Superior'), (@CINV, 'Centros de Investigación'), (@AC, 'Área Central'); UPDATE @TablaNiveles SET id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @NMS) WHERE Nivel = @NMS; UPDATE @TablaNiveles SET id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @NS) WHERE Nivel = @NS; UPDATE @TablaNiveles SET id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @CINV) WHERE Nivel = @CINV; UPDATE @TablaNiveles SET id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @AC) WHERE Nivel = @AC; -- SELECT * FROM @TablaNiveles
DECLARE @TablaLicenciasConcentrado TABLE( Nivel VARCHAR(255) NOT NULL DEFAULT '', LicenciasVigentesAnteriorHomNac INT NOT NULL DEFAULT 0, LicenciasVigentesAnteriorMujNac INT NOT NULL DEFAULT 0, LicenciasVigentesAnteriorHomExt INT NOT NULL DEFAULT 0, LicenciasVigentesAnteriorMujExt INT NOT NULL DEFAULT 0, LicenciasVigentesAnteriorTot INT NOT NULL DEFAULT 0,
AjustesHomNac INT NOT NULL DEFAULT 0, AjustesMujNac INT NOT NULL DEFAULT 0, AjustesHomExt INT NOT NULL DEFAULT 0, AjustesMujExt INT NOT NULL DEFAULT 0,
NoLicenciasOtorgadasHomNac INT NOT NULL DEFAULT 0, NoLicenciasOtorgadasMujNac INT NOT NULL DEFAULT 0, NoLicenciasOtorgadasHomExt INT NOT NULL DEFAULT 0, NoLicenciasOtorgadasMujExt INT NOT NULL DEFAULT 0,
NoProrrogasAutorizadasHomNac INT NOT NULL DEFAULT 0, NoProrrogasAutorizadasMujNac INT NOT NULL DEFAULT 0, NoProrrogasAutorizadasHomExt INT NOT NULL DEFAULT 0, NoProrrogasAutorizadasMujExt INT NOT NULL DEFAULT 0,
LiberacionesHomNac INT NOT NULL DEFAULT 0, LiberacionesMujNac INT NOT NULL DEFAULT 0, LiberacionesHomExt INT NOT NULL DEFAULT 0, LiberacionesMujExt INT NOT NULL DEFAULT 0,
CancelacionesHomNac INT NOT NULL DEFAULT 0, CancelacionesMujNac INT NOT NULL DEFAULT 0, CancelacionesHomExt INT NOT NULL DEFAULT 0, CancelacionesMujExt INT NOT NULL DEFAULT 0,
NoLicenciasVigentesActHomNac INT NOT NULL DEFAULT 0, NoLicenciasVigentesActMujNac INT NOT NULL DEFAULT 0, NoLicenciasVigentesActHomExt INT NOT NULL DEFAULT 0, NoLicenciasVigentesActMujExt INT NOT NULL DEFAULT 0, NoLicenciasVigentesActTot INT NOT NULL DEFAULT 0, NoLicenciasVigentesActEstudioEspec INT NOT NULL DEFAULT 0, NoLicenciasVigentesActEstudioMaest INT NOT NULL DEFAULT 0, NoLicenciasVigentesActEstudioDoct INT NOT NULL DEFAULT 0, NoLicenciasVigentesActEstudioEstan INT NOT NULL DEFAULT 0, NoLicenciasVigentesActEstudioTot INT NOT NULL DEFAULT 0 ) INSERT INTO @TablaLicenciasConcentrado (Nivel) SELECT Desc_Nivel FROM @TablaNiveles
DECLARE @ContadorNivel INT = 1; WHILE @ContadorNivel <= (SELECT COUNT(*)FROM @TablaLicenciasConcentrado) BEGIN
SET @AjustesHomNac = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @AJUSTES) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @AjustesMujNac = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @AJUSTES) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @AjustesHomExt = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @AJUSTES) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @AjustesMujExt = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @AJUSTES) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0);
SET @NoLicenciasOtorgadasHomNac = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_LICENCIAS_OTORGADAS) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @NoLicenciasOtorgadasMujNac = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_LICENCIAS_OTORGADAS) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @NoLicenciasOtorgadasHomExt = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_LICENCIAS_OTORGADAS) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @NoLicenciasOtorgadasMujExt = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_LICENCIAS_OTORGADAS) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0);
SET @NoProrrogasAutorizadasHomNac = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_PRORROGAS_AUTORIZADAS) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @NoProrrogasAutorizadasMujNac = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_PRORROGAS_AUTORIZADAS) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @NoProrrogasAutorizadasHomExt = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_PRORROGAS_AUTORIZADAS) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @NoProrrogasAutorizadasMujExt = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_PRORROGAS_AUTORIZADAS) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @LiberacionesHomNac = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LIBERACIONES) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @LiberacionesMujNac = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LIBERACIONES) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @LiberacionesHomExt = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LIBERACIONES) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @LiberacionesMujExt = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LIBERACIONES) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @LicenciasVigentesAnteriorHomNac = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @PeriodoAnterior AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) ) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LICENCIAS_VIGENTES_ANTERIOR)),0) + ISNULL(@AjustesHomNac,0) + ISNULL(@NoLicenciasOtorgadasHomNac, 0) - ISNULL(@LiberacionesHomNac, 0) - ISNULL(@CancelacionesHomNac, 0); SET @LicenciasVigentesAnteriorMujNac = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @PeriodoAnterior AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel)) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LICENCIAS_VIGENTES_ANTERIOR)),0) + ISNULL(@AjustesMujNac,0) + ISNULL(@NoLicenciasOtorgadasMujNac, 0) - ISNULL(@LiberacionesMujNac, 0) - ISNULL(@CancelacionesMujNac, 0); SET @LicenciasVigentesAnteriorHomExt = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @PeriodoAnterior AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LICENCIAS_VIGENTES_ANTERIOR))),0) + ISNULL(@AjustesHomExt,0) + ISNULL(@NoLicenciasOtorgadasHomExt, 0) - ISNULL(@LiberacionesHomExt, 0) - ISNULL(@CancelacionesHomExt, 0); SET @LicenciasVigentesAnteriorMujExt = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @PeriodoAnterior AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LICENCIAS_VIGENTES_ANTERIOR))),0) + ISNULL(@AjustesMujExt,0) + ISNULL(@NoLicenciasOtorgadasMujExt, 0) - ISNULL(@LiberacionesMujExt, 0) - ISNULL(@CancelacionesMujExt, 0);
SET @LicenciasVigentesAnteriorTot = @LicenciasVigentesAnteriorHomNac + @LicenciasVigentesAnteriorMujNac + @LicenciasVigentesAnteriorHomExt + @LicenciasVigentesAnteriorMujExt;
SET @NoLicenciasVigentesActHomNac = ISNULL(@LicenciasVigentesAnteriorHomNac, 0 ) + ISNULL(@AjustesHomNac, 0) + ISNULL(@NoLicenciasOtorgadasHomNac, 0) - ISNULL(@LiberacionesHomNac, 0) - ISNULL(@CancelacionesHomNac, 0);
SET @NoLicenciasVigentesActMujNac = ISNULL(@LicenciasVigentesAnteriorMujNac, 0 ) + ISNULL(@AjustesMujNac, 0) + ISNULL(@NoLicenciasOtorgadasMujNac, 0) - ISNULL(@LiberacionesMujNac, 0) - ISNULL(@CancelacionesMujNac, 0);
SET @NoLicenciasVigentesActHomExt = ISNULL(@LicenciasVigentesAnteriorHomExt, 0 ) + ISNULL(@AjustesHomExt, 0) + ISNULL(@NoLicenciasOtorgadasHomExt, 0) - ISNULL(@LiberacionesHomExt, 0) - ISNULL(@CancelacionesHomExt, 0);
SET @NoLicenciasVigentesActMujExt = ISNULL(@LicenciasVigentesAnteriorMujExt, 0 ) + ISNULL(@AjustesMujExt, 0) + ISNULL(@NoLicenciasOtorgadasMujExt, 0) - ISNULL(@LiberacionesMujExt, 0) - ISNULL(@CancelacionesMujExt, 0);
SET @NoLicenciasVigentesActTot = @NoLicenciasVigentesActHomNac + @NoLicenciasVigentesActHomExt + @NoLicenciasVigentesActMujNac + @NoLicenciasVigentesActMujExt;
SET @NoLicenciasVigentesActEstudioEspec = ISNULL((SELECT COUNT(*) FROM SAC_RegistroCantidadesTipoLCGS WHERE id_Trimestre = @Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_TipoEstudioLCGSSAC = (SELECT ID_TipoEstudioLCGSSAC FROM SAC_TipoEstudioLCGS WHERE Desc_TipoEstudioLCGSSAC = @ESPECIALIDAD) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0);
SET @NoLicenciasVigentesActEstudioMaest = ISNULL((SELECT COUNT(*) FROM SAC_RegistroCantidadesTipoLCGS WHERE id_Trimestre = @Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_TipoEstudioLCGSSAC = (SELECT ID_TipoEstudioLCGSSAC FROM SAC_TipoEstudioLCGS WHERE Desc_TipoEstudioLCGSSAC = @MAESTRIA) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0);
SET @NoLicenciasVigentesActEstudioDoct = ISNULL((SELECT COUNT(*) FROM SAC_RegistroCantidadesTipoLCGS WHERE id_Trimestre = @Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_TipoEstudioLCGSSAC = (SELECT ID_TipoEstudioLCGSSAC FROM SAC_TipoEstudioLCGS WHERE Desc_TipoEstudioLCGSSAC = @DOCTORADO) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0);
SET @NoLicenciasVigentesActEstudioEstan = ISNULL((SELECT COUNT(*) FROM SAC_RegistroCantidadesTipoLCGS WHERE id_Trimestre = @Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @Anio) AND id_TipoEstudioLCGSSAC = (SELECT ID_TipoEstudioLCGSSAC FROM SAC_TipoEstudioLCGS WHERE Desc_TipoEstudioLCGSSAC = @ESTANCIA) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0);
SET @NoLicenciasVigentesActEstudioTot = @NoLicenciasVigentesActEstudioEspec + @NoLicenciasVigentesActEstudioMaest + @NoLicenciasVigentesActEstudioDoct + @NoLicenciasVigentesActEstudioEstan;
UPDATE @TablaLicenciasConcentrado SET LicenciasVigentesAnteriorHomNac = @LicenciasVigentesAnteriorHomNac, LicenciasVigentesAnteriorMujNac = @LicenciasVigentesAnteriorMujNac, LicenciasVigentesAnteriorHomExt = @LicenciasVigentesAnteriorHomExt, LicenciasVigentesAnteriorMujExt = @LicenciasVigentesAnteriorMujExt, LicenciasVigentesAnteriorTot = @LicenciasVigentesAnteriorTot, AjustesHomNac = @AjustesHomNac, AjustesMujNac = @AjustesMujNac, AjustesHomExt = @AjustesHomExt, AjustesMujExt = @AjustesMujExt, NoLicenciasOtorgadasHomNac = @NoLicenciasOtorgadasHomNac, NoLicenciasOtorgadasMujNac = @NoLicenciasOtorgadasMujNac, NoLicenciasOtorgadasHomExt = @NoLicenciasOtorgadasHomExt, NoLicenciasOtorgadasMujExt = @NoLicenciasOtorgadasMujExt, NoProrrogasAutorizadasHomNac = @NoProrrogasAutorizadasHomNac, NoProrrogasAutorizadasMujNac = @NoProrrogasAutorizadasMujNac, NoProrrogasAutorizadasHomExt = @NoProrrogasAutorizadasHomExt, NoProrrogasAutorizadasMujExt = @NoProrrogasAutorizadasMujExt, LiberacionesHomNac = @LiberacionesHomNac, LiberacionesMujNac = @LiberacionesMujNac, LiberacionesHomExt = @LiberacionesHomExt, LiberacionesMujExt = @LiberacionesMujExt, CancelacionesHomNac = @CancelacionesHomNac, CancelacionesMujNac = @CancelacionesMujNac, CancelacionesHomExt = @CancelacionesHomExt, CancelacionesMujExt = @CancelacionesMujExt, NoLicenciasVigentesActHomNac = @NoLicenciasVigentesActHomNac, NoLicenciasVigentesActMujNac = @NoLicenciasVigentesActMujNac, NoLicenciasVigentesActHomExt = @NoLicenciasVigentesActHomExt, NoLicenciasVigentesActMujExt = @NoLicenciasVigentesActMujExt, NoLicenciasVigentesActTot = @NoLicenciasVigentesActTot, NoLicenciasVigentesActEstudioEspec = @NoLicenciasVigentesActEstudioEspec, NoLicenciasVigentesActEstudioMaest = @NoLicenciasVigentesActEstudioMaest, NoLicenciasVigentesActEstudioDoct = @NoLicenciasVigentesActEstudioDoct, NoLicenciasVigentesActEstudioEstan = @NoLicenciasVigentesActEstudioEstan, NoLicenciasVigentesActEstudioTot = @NoLicenciasVigentesActEstudioTot WHERE Nivel = (SELECT Desc_Nivel FROM @TablaNiveles WHERE identificador = @ContadorNivel);
SET @PeriodoAnterior = CASE WHEN @PeriodoAnterior = 4 THEN 1 ELSE @PeriodoAnterior + 1 END; SET @AnioAnterior = CASE WHEN @PeriodoAnterior = 1 THEN @AnioAnterior + 1 ELSE @AnioAnterior END;
SET @ContadorNivel = @ContadorNivel + 1; END SELECT * FROM @TablaLicenciasConcentrado UNION ALL SELECT 'Total', SUM(LicenciasVigentesAnteriorHomNac), SUM(LicenciasVigentesAnteriorMujNac), SUM(LicenciasVigentesAnteriorHomExt), SUM(LicenciasVigentesAnteriorMujExt), SUM(LicenciasVigentesAnteriorTot),
SUM(AjustesHomNac), SUM(AjustesMujNac), SUM(AjustesHomExt), SUM(AjustesMujExt),
SUM(NoLicenciasOtorgadasHomNac), SUM(NoLicenciasOtorgadasMujNac), SUM(NoLicenciasOtorgadasHomExt), SUM(NoLicenciasOtorgadasMujExt),
SUM(NoProrrogasAutorizadasHomNac), SUM(NoProrrogasAutorizadasMujNac), SUM(NoProrrogasAutorizadasHomExt), SUM(NoProrrogasAutorizadasMujExt),
SUM(LiberacionesHomNac), SUM(LiberacionesMujNac), SUM(LiberacionesHomExt), SUM(LiberacionesMujExt),
SUM(CancelacionesHomNac), SUM(CancelacionesMujNac), SUM(CancelacionesHomExt), SUM(CancelacionesMujExt),
SUM(NoLicenciasVigentesActHomNac), SUM(NoLicenciasVigentesActMujNac), SUM(NoLicenciasVigentesActHomExt), SUM(NoLicenciasVigentesActMujExt), SUM(NoLicenciasVigentesActTot), SUM(NoLicenciasVigentesActEstudioEspec), SUM(NoLicenciasVigentesActEstudioMaest), SUM(NoLicenciasVigentesActEstudioDoct), SUM(NoLicenciasVigentesActEstudioEstan), SUM(NoLicenciasVigentesActEstudioTot) FROM @TablaLicenciasConcentrado;
END

-- Jesus Perez 
-- Formato 24 Licencias con goce de sueldo concentrado
CREATE PROCEDURE SAC_LicenciasGoceSueldoConcentrado
    @Anio INT,
    @Trimestre INT
AS

BEGIN DECLARE @ESPECIALIDAD VARCHAR (255) = 'ESPECIALIDAD'; DECLARE @MAESTRIA VARCHAR (255) = 'MAESTRÍA'; DECLARE @DOCTORADO VARCHAR (255) = 'DOCTORADO'; DECLARE @ESTANCIA VARCHAR (255) = 'ESTANCIA DE INVESTIGACIÓN'; DECLARE @NACIONAL VARCHAR (255) = 'NACIONAL'; DECLARE @EXTRANJERO VARCHAR (255) = 'EXTRANJERO'; DECLARE @LICENCASCONGOCE VARCHAR (255) = 'LICENCIAS CON GOCE DE SUELDO'; DECLARE @AJUSTES VARCHAR (255) = 'AJUSTES'; DECLARE @LIBERACIONES VARCHAR (255) = 'LIBERACIONES'; DECLARE @CANCELACIONES VARCHAR (255) = 'CANCELACIONES'; DECLARE @NO_LICENCIAS_OTORGADAS VARCHAR (255) = 'No. DE LICENCIAS OTORGADAS EN EL TRIMESTRE'; DECLARE @NO_PRORROGAS_AUTORIZADAS VARCHAR (255) = 'No. DE PRÓRROGAS AUTORIZADAS EN EL TRIMESTRE'; DECLARE @LICENCIAS_VIGENTES_ANTERIOR VARCHAR (255) = 'LICENCIAS VIGENTES AL PERIODO';
DECLARE @PeriodoAnterior INT = CASE WHEN @id_Trimestre = 1 THEN 4 ELSE @id_Trimestre - 1 END DECLARE @AnioAnterior INT = CASE WHEN @id_Trimestre = 1 THEN @anio - 1 ELSE @anio END
DECLARE @LicenciasVigentesAnteriorHomNac INT = 0 DECLARE @LicenciasVigentesAnteriorMujNac INT = 0 DECLARE @LicenciasVigentesAnteriorHomExt INT = 0 DECLARE @LicenciasVigentesAnteriorMujExt INT = 0 DECLARE @LicenciasVigentesAnteriorTot INT = 0
DECLARE @SumLicenciasVigentesAnteriorHomNac INT; DECLARE @SumLicenciasVigentesAnteriorMujNac INT; DECLARE @SumLicenciasVigentesAnteriorHomExt INT; DECLARE @SumLicenciasVigentesAnteriorMujExt INT; DECLARE @SumLicenciasVigentesAnteriorTot INT;
DECLARE @AjustesHomNac VARCHAR(255) = '' DECLARE @AjustesMujNac VARCHAR(255) = '' DECLARE @AjustesHomExt VARCHAR(255) = '' DECLARE @AjustesMujExt VARCHAR(255) = ''
DECLARE @SumAjustesHomNac INT; DECLARE @SumAjustesMujNac INT; DECLARE @SumAjustesHomExt INT; DECLARE @SumAjustesMujExt INT;
DECLARE @NoLicenciasOtorgadasHomNac VARCHAR(255) = '' DECLARE @NoLicenciasOtorgadasMujNac VARCHAR(255) = '' DECLARE @NoLicenciasOtorgadasHomExt VARCHAR(255) = '' DECLARE @NoLicenciasOtorgadasMujExt VARCHAR(255) = ''
DECLARE @SumNoLicenciasOtorgadasHomNac INT; DECLARE @SumNoLicenciasOtorgadasMujNac INT; DECLARE @SumNoLicenciasOtorgadasHomExt INT; DECLARE @SumNoLicenciasOtorgadasMujExt INT;
DECLARE @NoProrrogasAutorizadasHomNac VARCHAR(255) = '' DECLARE @NoProrrogasAutorizadasMujNac VARCHAR(255) = '' DECLARE @NoProrrogasAutorizadasHomExt VARCHAR(255) = '' DECLARE @NoProrrogasAutorizadasMujExt VARCHAR(255) = ''
DECLARE @SumNoProrrogasAutorizadasHomNac INT; DECLARE @SumNoProrrogasAutorizadasMujNac INT; DECLARE @SumNoProrrogasAutorizadasHomExt INT; DECLARE @SumNoProrrogasAutorizadasMujExt INT;
DECLARE @LiberacionesHomNac VARCHAR(255) = '' DECLARE @LiberacionesMujNac VARCHAR(255) = '' DECLARE @LiberacionesHomExt VARCHAR(255) = '' DECLARE @LiberacionesMujExt VARCHAR(255) = ''
DECLARE @SumLiberacionesHomNac INT; DECLARE @SumLiberacionesMujNac INT; DECLARE @SumLiberacionesHomExt INT; DECLARE @SumLiberacionesMujExt INT;
DECLARE @CancelacionesHomNac VARCHAR(255) = '' DECLARE @CancelacionesMujNac VARCHAR(255) = '' DECLARE @CancelacionesHomExt VARCHAR(255) = '' DECLARE @CancelacionesMujExt VARCHAR(255) = ''
DECLARE @SumCancelacionesHomNac INT; DECLARE @SumCancelacionesMujNac INT; DECLARE @SumCancelacionesHomExt INT; DECLARE @SumCancelacionesMujExt INT;
DECLARE @NoLicenciasVigentesActHomNac INT = 0 DECLARE @NoLicenciasVigentesActMujNac INT = 0 DECLARE @NoLicenciasVigentesActHomExt INT = 0 DECLARE @NoLicenciasVigentesActMujExt INT = 0 DECLARE @NoLicenciasVigentesActTot INT = 0
DECLARE @SumNoLicenciasVigentesActHomNac INT; DECLARE @SumNoLicenciasVigentesActMujNac INT; DECLARE @SumNoLicenciasVigentesActHomExt INT; DECLARE @SumNoLicenciasVigentesActMujExt INT; DECLARE @SumNoLicenciasVigentesActTot INT;
DECLARE @NoLicenciasVigentesActEstudioEspec VARCHAR(255) = '' DECLARE @NoLicenciasVigentesActEstudioMaest VARCHAR(255) = '' DECLARE @NoLicenciasVigentesActEstudioDoct VARCHAR(255) = '' DECLARE @NoLicenciasVigentesActEstudioEstan VARCHAR(255) = '' DECLARE @NoLicenciasVigentesActEstudioTot INT = 0
DECLARE @SumNoLicenciasVigentesActEstudioEspec INT; DECLARE @SumNoLicenciasVigentesActEstudioMaest INT; DECLARE @SumNoLicenciasVigentesActEstudioDoct INT; DECLARE @SumNoLicenciasVigentesActEstudioEstan INT; DECLARE @SumNoLicenciasVigentesActEstudioTot INT;
DECLARE @TablaUnidadesRama TABLE( identificador INT IDENTITY(1,1), id_Unidad INT , SiglasUnidadAcademica VARCHAR(255) NOT NULL DEFAULT '', Rama VARCHAR(255) NOT NULL DEFAULT '' )
DECLARE @ResultadosFinales TABLE ( Unidad VARCHAR(255), Rama VARCHAR(255), Orden INT )
INSERT INTO @TablaUnidadesRama (id_Unidad,SiglasUnidadAcademica) SELECT ID_UnidadAcademica, Siglas FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @SiglasTipoUnidadAcademica)
UPDATE @TablaUnidadesRama SET Rama = (SELECT Desc_SiglasRama FROM RamaConocimiento WHERE ID_RamaConocimiento = (SELECT id_RamaConocimiento FROM UnidadesAcademicas WHERE Siglas = SiglasUnidadAcademica));
WITH CTE_RamasDistintas AS ( SELECT DISTINCT Rama, CASE WHEN Rama IS NOT NULL THEN 'Total Rama ' + Rama ELSE 'Total General' END AS NombreTotal FROM @TablaUnidadesRama ), CTE_Totales AS ( SELECT r.NombreTotal AS SiglasUnidadAcademica, r.Rama, COUNT(t.id_Unidad) AS Total FROM CTE_RamasDistintas r LEFT JOIN @TablaUnidadesRama t ON r.Rama = t.Rama GROUP BY r.NombreTotal, r.Rama ), CombinedData AS ( SELECT SiglasUnidadAcademica AS Unidad, Rama, CASE WHEN SiglasUnidadAcademica LIKE 'Total%' THEN 2 ELSE 1 END AS Orden FROM @TablaUnidadesRama UNION ALL SELECT SiglasUnidadAcademica AS Unidad, Rama, 2 AS Orden FROM CTE_Totales ) INSERT INTO @ResultadosFinales SELECT Unidad, Rama, Orden FROM CombinedData;
DECLARE @TablaLicenciasGoceSueldo TABLE ( UnidadAcademica VARCHAR(255) NOT NULL DEFAULT '',
LicenciasVigentesAnteriorHomNac INT NOT NULL DEFAULT 0, LicenciasVigentesAnteriorMujNac INT NOT NULL DEFAULT 0, LicenciasVigentesAnteriorHomExt INT NOT NULL DEFAULT 0, LicenciasVigentesAnteriorMujExt INT NOT NULL DEFAULT 0, LicenciasVigentesAnteriorTot INT NOT NULL DEFAULT 0,
AjustesHomNac VARCHAR(255) NOT NULL DEFAULT '', AjustesMujNac VARCHAR(255) NOT NULL DEFAULT '', AjustesHomExt VARCHAR(255) NOT NULL DEFAULT '', AjustesMujExt VARCHAR(255) NOT NULL DEFAULT '',
NoLicenciasOtorgadasHomNac VARCHAR(255) NOT NULL DEFAULT '', NoLicenciasOtorgadasMujNac VARCHAR(255) NOT NULL DEFAULT '', NoLicenciasOtorgadasHomExt VARCHAR(255) NOT NULL DEFAULT '', NoLicenciasOtorgadasMujExt VARCHAR(255) NOT NULL DEFAULT '',
NoProrrogasAutorizadasHomNac VARCHAR(255) NOT NULL DEFAULT '', NoProrrogasAutorizadasMujNac VARCHAR(255) NOT NULL DEFAULT '', NoProrrogasAutorizadasHomExt VARCHAR(255) NOT NULL DEFAULT '', NoProrrogasAutorizadasMujExt VARCHAR(255) NOT NULL DEFAULT '',
LiberacionesHomNac VARCHAR(255) NOT NULL DEFAULT '', LiberacionesMujNac VARCHAR(255) NOT NULL DEFAULT '', LiberacionesHomExt VARCHAR(255) NOT NULL DEFAULT '', LiberacionesMujExt VARCHAR(255) NOT NULL DEFAULT '',
CancelacionesHomNac VARCHAR(255) NOT NULL DEFAULT '', CancelacionesMujNac VARCHAR(255) NOT NULL DEFAULT '', CancelacionesHomExt VARCHAR(255) NOT NULL DEFAULT '', CancelacionesMujExt VARCHAR(255) NOT NULL DEFAULT '',
NoLicenciasVigentesActHomNac INT NOT NULL DEFAULT 0, NoLicenciasVigentesActMujNac INT NOT NULL DEFAULT 0, NoLicenciasVigentesActHomExt INT NOT NULL DEFAULT 0, NoLicenciasVigentesActMujExt INT NOT NULL DEFAULT 0, NoLicenciasVigentesActTot INT NOT NULL DEFAULT 0, NoLicenciasVigentesActEstudioEspec VARCHAR(255) NOT NULL DEFAULT '', NoLicenciasVigentesActEstudioMaest VARCHAR(255) NOT NULL DEFAULT '', NoLicenciasVigentesActEstudioDoct VARCHAR(255) NOT NULL DEFAULT '', NoLicenciasVigentesActEstudioEstan VARCHAR(255) NOT NULL DEFAULT '', NoLicenciasVigentesActEstudioTot INT NOT NULL DEFAULT 0,
rama VARCHAR(255) NOT NULL DEFAULT '', Orden INT NOT NULL DEFAULT 0 ) INSERT INTO @TablaLicenciasGoceSueldo (UnidadAcademica, Rama, Orden) SELECT Unidad, Rama, Orden FROM @ResultadosFinales; DECLARE @ContadorUnidadesAcademicas INT = 1
WHILE @ContadorUnidadesAcademicas <= (SELECT COUNT(*) FROM @TablaUnidadesRama) BEGIN SET @AjustesHomNac = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador = @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @AJUSTES)), '');
SET @AjustesMujNac = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador = @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @AJUSTES)), '');
SET @AjustesHomExt = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador = @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @AJUSTES)), '');
SET @AjustesMujExt = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador = @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @AJUSTES)), '');
SET @NoLicenciasOtorgadasHomNac = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador = @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_LICENCIAS_OTORGADAS)), '');
SET @NoLicenciasOtorgadasMujNac = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador = @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_LICENCIAS_OTORGADAS)), '');
SET @NoLicenciasOtorgadasHomExt = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador = @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_LICENCIAS_OTORGADAS)), '');
SET @NoLicenciasOtorgadasMujExt = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador = @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_LICENCIAS_OTORGADAS)), '');
SET @NoProrrogasAutorizadasHomNac = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador = @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_PRORROGAS_AUTORIZADAS)), '');
SET @NoProrrogasAutorizadasMujNac = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador = @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_PRORROGAS_AUTORIZADAS)), '');
SET @NoProrrogasAutorizadasHomExt = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador = @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_PRORROGAS_AUTORIZADAS)), '');
SET @NoProrrogasAutorizadasMujExt = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador = @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @NO_PRORROGAS_AUTORIZADAS)), '');
SET @LiberacionesHomNac = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador = @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LIBERACIONES)), '');
SET @LiberacionesMujNac = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador = @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LIBERACIONES)), '');
SET @LiberacionesHomExt = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador= @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LIBERACIONES)), '');
SET @LiberacionesMujExt = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador= @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LIBERACIONES)), '');
SET @CancelacionesHomNac = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador= @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @CANCELACIONES)), '');
SET @CancelacionesMujNac = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador= @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @CANCELACIONES)), '');
SET @CancelacionesHomExt = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador= @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @CANCELACIONES)), ''); SET @CancelacionesMujExt = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador= @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @CANCELACIONES)), '');
SET @LicenciasVigentesAnteriorHomNac = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador = @ContadorUnidadesAcademicas) AND id_Trimestre = @PeriodoAnterior AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LICENCIAS_VIGENTES_ANTERIOR)),0) + CASE WHEN @AjustesHomNac = '' THEN 0 ELSE CAST(@AjustesHomNac AS INT) END + CASE WHEN @NoLicenciasOtorgadasHomNac = '' THEN 0 ELSE CAST(@NoLicenciasOtorgadasHomNac AS INT) END - CASE WHEN @LiberacionesHomNac = '' THEN 0 ELSE CAST(@LiberacionesHomNac AS INT) END - CASE WHEN @CancelacionesHomNac = '' THEN 0 ELSE CAST(@CancelacionesHomNac AS INT) END SET @LicenciasVigentesAnteriorHomNac = CASE WHEN @LicenciasVigentesAnteriorHomNac < 0 THEN 0 ELSE @LicenciasVigentesAnteriorHomNac END
SET @LicenciasVigentesAnteriorMujNac = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador = @ContadorUnidadesAcademicas) AND id_Trimestre = @PeriodoAnterior AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LICENCIAS_VIGENTES_ANTERIOR)), 0) + CASE WHEN @AjustesMujNac = '' THEN 0 ELSE CAST(@AjustesMujNac AS INT) END + CASE WHEN @NoLicenciasOtorgadasMujNac = '' THEN 0 ELSE CAST(@NoLicenciasOtorgadasMujNac AS INT) END - CASE WHEN @LiberacionesMujNac = '' THEN 0 ELSE CAST(@LiberacionesMujNac AS INT) END - CASE WHEN @CancelacionesMujNac = '' THEN 0 ELSE CAST(@CancelacionesMujNac AS INT) END SET @LicenciasVigentesAnteriorMujNac = CASE WHEN @LicenciasVigentesAnteriorMujNac < 0 THEN 0 ELSE @LicenciasVigentesAnteriorMujNac END
SET @LicenciasVigentesAnteriorHomExt = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador = @ContadorUnidadesAcademicas) AND id_Trimestre = @PeriodoAnterior AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LICENCIAS_VIGENTES_ANTERIOR)),0 ) + CASE WHEN @AjustesHomExt = '' THEN 0 ELSE CAST(@AjustesHomExt AS INT) END + CASE WHEN @NoLicenciasOtorgadasHomExt = '' THEN 0 ELSE CAST(@NoLicenciasOtorgadasHomExt AS INT) END - CASE WHEN @LiberacionesHomExt = '' THEN 0 ELSE CAST(@LiberacionesHomExt AS INT) END - CASE WHEN @CancelacionesHomExt = '' THEN 0 ELSE CAST(@CancelacionesHomExt AS INT) END SET @LicenciasVigentesAnteriorHomExt = CASE WHEN @LicenciasVigentesAnteriorHomExt < 0 THEN 0 ELSE @LicenciasVigentesAnteriorHomExt END
SET @LicenciasVigentesAnteriorMujExt = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador = @ContadorUnidadesAcademicas) AND id_Trimestre = @PeriodoAnterior AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO AND id_EstadoLCGSSAC = (SELECT ID_EstadoLCGSSAC FROM SAC_EstadoLCGS WHERE Desc_EstadoLCGSSAC = @LICENCIAS_VIGENTES_ANTERIOR))),0) + CASE WHEN @AjustesMujExt = '' THEN 0 ELSE CAST(@AjustesMujExt AS INT) END + CASE WHEN @NoLicenciasOtorgadasMujExt = '' THEN 0 ELSE CAST(@NoLicenciasOtorgadasMujExt AS INT) END - CASE WHEN @LiberacionesMujExt = '' THEN 0 ELSE CAST(@LiberacionesMujExt AS INT) END - CASE WHEN @CancelacionesMujExt = '' THEN 0 ELSE CAST(@CancelacionesMujExt AS INT) END SET @LicenciasVigentesAnteriorMujExt = CASE WHEN @LicenciasVigentesAnteriorMujExt < 0 THEN 0 ELSE @LicenciasVigentesAnteriorMujExt END
SET @LicenciasVigentesAnteriorTot = @LicenciasVigentesAnteriorHomNac + @LicenciasVigentesAnteriorHomExt + @LicenciasVigentesAnteriorMujNac + @LicenciasVigentesAnteriorMujExt
SET @NoLicenciasVigentesActHomNac = CASE WHEN @LicenciasVigentesAnteriorHomNac= '' THEN 0 ELSE CAST(@LicenciasVigentesAnteriorHomNac AS INT) END + CASE WHEN @AjustesHomNac = '' THEN 0 ELSE CAST(@AjustesHomNac AS INT) END + CASE WHEN @NoLicenciasOtorgadasHomNac = '' THEN 0 ELSE CAST(@NoLicenciasOtorgadasHomNac AS INT) END - CASE WHEN @LiberacionesHomNac = '' THEN 0 ELSE CAST(@LiberacionesHomNac AS INT) END - CASE WHEN @CancelacionesHomNac = '' THEN 0 ELSE CAST(@CancelacionesHomNac AS INT) END SET @NoLicenciasVigentesActHomNac = CASE WHEN @NoLicenciasVigentesActHomNac < 0 THEN 0 ELSE @NoLicenciasVigentesActHomNac END
SET @NoLicenciasVigentesActMujNac = CASE WHEN @LicenciasVigentesAnteriorMujNac = '' THEN 0 ELSE CAST(@LicenciasVigentesAnteriorMujNac AS INT) END + CASE WHEN @AjustesMujNac = '' THEN 0 ELSE CAST(@AjustesMujNac AS INT) END + CASE WHEN @NoLicenciasOtorgadasMujNac = '' THEN 0 ELSE CAST(@NoLicenciasOtorgadasMujNac AS INT) END - CASE WHEN @LiberacionesMujNac = '' THEN 0 ELSE CAST(@LiberacionesMujNac AS INT) END - CASE WHEN @CancelacionesMujNac = '' THEN 0 ELSE CAST(@CancelacionesMujNac AS INT) END SET @NoLicenciasVigentesActMujNac = CASE WHEN @NoLicenciasVigentesActMujNac < 0 THEN 0 ELSE @NoLicenciasVigentesActMujNac END
SET @NoLicenciasVigentesActHomExt = CASE WHEN @LicenciasVigentesAnteriorHomExt='' THEN 0 ELSE CAST(@LicenciasVigentesAnteriorHomExt AS INT) END + CASE WHEN @AjustesHomExt = '' THEN 0 ELSE CAST(@AjustesHomExt AS INT) END + CASE WHEN @NoLicenciasOtorgadasHomExt = '' THEN 0 ELSE CAST(@NoLicenciasOtorgadasHomExt AS INT) END - CASE WHEN @LiberacionesHomExt = '' THEN 0 ELSE CAST(@LiberacionesHomExt AS INT) END - CASE WHEN @CancelacionesHomExt = '' THEN 0 ELSE CAST(@CancelacionesHomExt AS INT) END SET @NoLicenciasVigentesActHomExt = CASE WHEN @NoLicenciasVigentesActHomExt < 0 THEN 0 ELSE @NoLicenciasVigentesActHomExt END
SET @NoLicenciasVigentesActMujExt = CASE WHEN @LicenciasVigentesAnteriorMujExt = '' THEN 0 ELSE CAST(@LicenciasVigentesAnteriorMujExt AS INT) END + CASE WHEN @AjustesMujExt = '' THEN 0 ELSE CAST(@AjustesMujExt AS INT) END + CASE WHEN @NoLicenciasOtorgadasMujExt = '' THEN 0 ELSE CAST(@NoLicenciasOtorgadasMujExt AS INT) END - CASE WHEN @LiberacionesMujExt = '' THEN 0 ELSE CAST(@LiberacionesMujExt AS INT) END - CASE WHEN @CancelacionesMujExt = '' THEN 0 ELSE CAST(@CancelacionesMujExt AS INT) END SET @NoLicenciasVigentesActMujExt = CASE WHEN @NoLicenciasVigentesActMujExt < 0 THEN 0 ELSE @NoLicenciasVigentesActMujExt END
SET @NoLicenciasVigentesActTot = @NoLicenciasVigentesActHomNac + @NoLicenciasVigentesActHomExt + @NoLicenciasVigentesActMujNac + @NoLicenciasVigentesActMujExt
SET @NoLicenciasVigentesActEstudioEspec = ISNULL((SELECT COUNT(*) FROM SAC_RegistroCantidadesTipoLCGS WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador= @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_TipoEstudioLCGSSAC = (SELECT ID_TipoEstudioLCGSSAC FROM SAC_TipoEstudioLCGS WHERE Desc_TipoEstudioLCGSSAC = @ESPECIALIDAD)),'');
SET @NoLicenciasVigentesActEstudioMaest = ISNULL((SELECT COUNT(*) FROM SAC_RegistroCantidadesTipoLCGS WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador= @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_TipoEstudioLCGSSAC = (SELECT ID_TipoEstudioLCGSSAC FROM SAC_TipoEstudioLCGS WHERE Desc_TipoEstudioLCGSSAC = @MAESTRIA)),'');
SET @NoLicenciasVigentesActEstudioDoct = ISNULL((SELECT COUNT(*) FROM SAC_RegistroCantidadesTipoLCGS WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador= @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_TipoEstudioLCGSSAC = (SELECT ID_TipoEstudioLCGSSAC FROM SAC_TipoEstudioLCGS WHERE Desc_TipoEstudioLCGSSAC = @DOCTORADO)), '');
SET @NoLicenciasVigentesActEstudioEstan = ISNULL( (SELECT COUNT(*) FROM SAC_RegistroCantidadesTipoLCGS WHERE id_UnidadAcademicaSAC = (SELECT id_Unidad FROM @TablaUnidadesRama WHERE identificador= @ContadorUnidadesAcademicas) AND id_Trimestre = @id_Trimestre AND id_Anio = (SELECT ID_anio FROM Anio WHERE Desc_Anio = @anio) AND id_TipoEstudioLCGSSAC = (SELECT ID_TipoEstudioLCGSSAC FROM SAC_TipoEstudioLCGS WHERE Desc_TipoEstudioLCGSSAC = @ESTANCIA)), '');
SET @NoLicenciasVigentesActEstudioTot = CASE WHEN @NoLicenciasVigentesActEstudioEspec = '' THEN 0 ELSE CAST(@NoLicenciasVigentesActEstudioEspec AS INT) END + CASE WHEN @NoLicenciasVigentesActEstudioMaest = '' THEN 0 ELSE CAST(@NoLicenciasVigentesActEstudioMaest AS INT) END + CASE WHEN @NoLicenciasVigentesActEstudioDoct = '' THEN 0 ELSE CAST (@NoLicenciasVigentesActEstudioDoct AS INT) END + CASE WHEN @NoLicenciasVigentesActEstudioEstan = '' THEN 0 ELSE CAST (@NoLicenciasVigentesActEstudioEstan AS INT) END
UPDATE @TablaLicenciasGoceSueldo SET LicenciasVigentesAnteriorHomNac = @LicenciasVigentesAnteriorHomNac, LicenciasVigentesAnteriorMujNac = @LicenciasVigentesAnteriorMujNac, LicenciasVigentesAnteriorHomExt = @LicenciasVigentesAnteriorHomExt, LicenciasVigentesAnteriorMujExt = @LicenciasVigentesAnteriorMujExt, LicenciasVigentesAnteriorTot = @LicenciasVigentesAnteriorTot, AjustesHomNac = CASE WHEN @AjustesHomNac = '0' THEN '' ELSE @AjustesHomNac END, AjustesMujNac = CASE WHEN @AjustesMujNac = '0' THEN '' ELSE @AjustesMujNac END, AjustesHomExt = CASE WHEN @AjustesHomExt = '0' THEN '' ELSE @AjustesHomExt END, AjustesMujExt = CASE WHEN @AjustesMujExt = '0' THEN '' ELSE @AjustesMujExt END, NoLicenciasOtorgadasHomNac = CASE WHEN @NoLicenciasOtorgadasHomNac = '0' THEN '' ELSE @NoLicenciasOtorgadasHomNac END, NoLicenciasOtorgadasMujNac = CASE WHEN @NoLicenciasOtorgadasMujNac = '0' THEN '' ELSE @NoLicenciasOtorgadasMujNac END, NoLicenciasOtorgadasHomExt = CASE WHEN @NoLicenciasOtorgadasHomExt = '0' THEN '' ELSE @NoLicenciasOtorgadasHomExt END, NoLicenciasOtorgadasMujExt = CASE WHEN @NoLicenciasOtorgadasMujExt = '0' THEN '' ELSE @NoLicenciasOtorgadasMujExt END, NoProrrogasAutorizadasHomNac = CASE WHEN @NoProrrogasAutorizadasHomNac = '0' THEN '' ELSE @NoProrrogasAutorizadasHomNac END, NoProrrogasAutorizadasMujNac = CASE WHEN @NoProrrogasAutorizadasMujNac = '0' THEN '' ELSE @NoProrrogasAutorizadasMujNac END, NoProrrogasAutorizadasHomExt = CASE WHEN @NoProrrogasAutorizadasHomExt = '0' THEN '' ELSE @NoProrrogasAutorizadasHomExt END, NoProrrogasAutorizadasMujExt = CASE WHEN @NoProrrogasAutorizadasMujExt = '0' THEN '' ELSE @NoProrrogasAutorizadasMujExt END, LiberacionesHomNac = CASE WHEN @LiberacionesHomNac = '0' THEN '' ELSE @LiberacionesHomNac END, LiberacionesMujNac = CASE WHEN @LiberacionesMujNac = '0' THEN '' ELSE @LiberacionesMujNac END, LiberacionesHomExt = CASE WHEN @LiberacionesHomExt = '0' THEN '' ELSE @LiberacionesHomExt END, LiberacionesMujExt = CASE WHEN @LiberacionesMujExt = '0' THEN '' ELSE @LiberacionesMujExt END, CancelacionesHomNac = CASE WHEN @CancelacionesHomNac = '0' THEN '' ELSE @CancelacionesHomNac END, CancelacionesMujNac = CASE WHEN @CancelacionesMujNac = '0' THEN '' ELSE @CancelacionesMujNac END, CancelacionesHomExt = CASE WHEN @CancelacionesHomExt = '0' THEN '' ELSE @CancelacionesHomExt END, CancelacionesMujExt = CASE WHEN @CancelacionesMujExt = '0' THEN '' ELSE @CancelacionesMujExt END, NoLicenciasVigentesActHomNac = @NoLicenciasVigentesActHomNac, NoLicenciasVigentesActMujNac = @NoLicenciasVigentesActMujNac, NoLicenciasVigentesActHomExt = @NoLicenciasVigentesActHomExt, NoLicenciasVigentesActMujExt = @NoLicenciasVigentesActMujExt, NoLicenciasVigentesActTot = @NoLicenciasVigentesActTot, NoLicenciasVigentesActEstudioEspec = CASE WHEN @NoLicenciasVigentesActEstudioEspec = '0' THEN '' ELSE @NoLicenciasVigentesActEstudioEspec END, NoLicenciasVigentesActEstudioMaest = CASE WHEN @NoLicenciasVigentesActEstudioMaest = '0' THEN '' ELSE @NoLicenciasVigentesActEstudioMaest END, NoLicenciasVigentesActEstudioDoct = CASE WHEN @NoLicenciasVigentesActEstudioDoct = '0' THEN '' ELSE @NoLicenciasVigentesActEstudioDoct END, NoLicenciasVigentesActEstudioEstan = CASE WHEN @NoLicenciasVigentesActEstudioEstan = '0' THEN '' ELSE @NoLicenciasVigentesActEstudioEstan END, NoLicenciasVigentesActEstudioTot = @NoLicenciasVigentesActEstudioTot WHERE UnidadAcademica = (SELECT SiglasUnidadAcademica FROM @TablaUnidadesRama WHERE identificador= @ContadorUnidadesAcademicas) SET @ContadorUnidadesAcademicas = @ContadorUnidadesAcademicas + 1
END
DECLARE @ContadorRamas INT = 1 WHILE @ContadorRamas <= (SELECT COUNT(*) FROM RamaConocimiento) BEGIN SELECT @SumLicenciasVigentesAnteriorHomNac = SUM(CAST(LicenciasVigentesAnteriorHomNac AS INT)), @SumLicenciasVigentesAnteriorMujNac = SUM(CAST(LicenciasVigentesAnteriorMujNac AS INT)), @SumLicenciasVigentesAnteriorHomExt = SUM(CAST(LicenciasVigentesAnteriorHomExt AS INT)), @SumLicenciasVigentesAnteriorMujExt = SUM(CAST(LicenciasVigentesAnteriorMujExt AS INT)), @SumLicenciasVigentesAnteriorTot = SUM(CAST(LicenciasVigentesAnteriorTot AS INT)), @SumAjustesHomNac = SUM(CAST(AjustesHomNac AS INT)), @SumAjustesMujNac = SUM(CAST(AjustesMujNac AS INT)), @SumAjustesHomExt = SUM(CAST(AjustesHomExt AS INT)), @SumAjustesMujExt = SUM(CAST(AjustesMujExt AS INT)), @SumNoLicenciasOtorgadasHomNac = SUM(CAST(NoLicenciasOtorgadasHomNac AS INT)), @SumNoLicenciasOtorgadasMujNac = SUM(CAST(NoLicenciasOtorgadasMujNac AS INT)), @SumNoLicenciasOtorgadasHomExt = SUM(CAST(NoLicenciasOtorgadasHomExt AS INT)), @SumNoLicenciasOtorgadasMujExt = SUM(CAST(NoLicenciasOtorgadasMujExt AS INT)), @SumNoProrrogasAutorizadasHomNac = SUM(CAST(NoProrrogasAutorizadasHomNac AS INT)), @SumNoProrrogasAutorizadasMujNac = SUM(CAST(NoProrrogasAutorizadasMujNac AS INT)), @SumNoProrrogasAutorizadasHomExt = SUM(CAST(NoProrrogasAutorizadasHomExt AS INT)), @SumNoProrrogasAutorizadasMujExt = SUM(CAST(NoProrrogasAutorizadasMujExt AS INT)), @SumLiberacionesHomNac = SUM(CAST(LiberacionesHomNac AS INT)), @SumLiberacionesMujNac = SUM(CAST(LiberacionesMujNac AS INT)), @SumLiberacionesHomExt = SUM(CAST(LiberacionesHomExt AS INT)), @SumLiberacionesMujExt = SUM(CAST(LiberacionesMujExt AS INT)), @SumCancelacionesHomNac = SUM(CAST(CancelacionesHomNac AS INT)), @SumCancelacionesMujNac = SUM(CAST(CancelacionesMujNac AS INT)), @SumCancelacionesHomExt = SUM(CAST(CancelacionesHomExt AS INT)), @SumCancelacionesMujExt = SUM(CAST(CancelacionesMujExt AS INT)), @SumNoLicenciasVigentesActHomNac = SUM(CAST(NoLicenciasVigentesActHomNac AS INT)), @SumNoLicenciasVigentesActMujNac = SUM(CAST(NoLicenciasVigentesActMujNac AS INT)), @SumNoLicenciasVigentesActHomExt = SUM(CAST(NoLicenciasVigentesActHomExt AS INT)), @SumNoLicenciasVigentesActMujExt = SUM(CAST(NoLicenciasVigentesActMujExt AS INT)), @SumNoLicenciasVigentesActTot = SUM(CAST(NoLicenciasVigentesActTot AS INT)), @SumNoLicenciasVigentesActEstudioEspec = SUM(CAST(NoLicenciasVigentesActEstudioEspec AS INT)), @SumNoLicenciasVigentesActEstudioMaest = SUM(CAST(NoLicenciasVigentesActEstudioMaest AS INT)), @SumNoLicenciasVigentesActEstudioDoct = SUM(CAST(NoLicenciasVigentesActEstudioDoct AS INT)), @SumNoLicenciasVigentesActEstudioEstan = SUM(CAST(NoLicenciasVigentesActEstudioEstan AS INT)), @SumNoLicenciasVigentesActEstudioTot = SUM(CAST(NoLicenciasVigentesActEstudioTot AS INT)) FROM @TablaLicenciasGoceSueldo WHERE Rama = (SELECT Desc_SiglasRama FROM RamaConocimiento WHERE ID_RamaConocimiento = @ContadorRamas);
UPDATE @TablaLicenciasGoceSueldo SET LicenciasVigentesAnteriorHomNac = @SumLicenciasVigentesAnteriorHomNac, LicenciasVigentesAnteriorMujNac = @SumLicenciasVigentesAnteriorMujNac, LicenciasVigentesAnteriorHomExt = @SumLicenciasVigentesAnteriorHomExt, LicenciasVigentesAnteriorMujExt = @SumLicenciasVigentesAnteriorMujExt, LicenciasVigentesAnteriorTot = @SumLicenciasVigentesAnteriorTot, AjustesHomNac = @SumAjustesHomNac, AjustesMujNac = @SumAjustesMujNac, AjustesHomExt = @SumAjustesHomExt, AjustesMujExt = @SumAjustesMujExt, NoLicenciasOtorgadasHomNac = @SumNoLicenciasOtorgadasHomNac, NoLicenciasOtorgadasMujNac = @SumNoLicenciasOtorgadasMujNac, NoLicenciasOtorgadasHomExt = @SumNoLicenciasOtorgadasHomExt, NoLicenciasOtorgadasMujExt = @SumNoLicenciasOtorgadasMujExt, NoProrrogasAutorizadasHomNac = @SumNoProrrogasAutorizadasHomNac, NoProrrogasAutorizadasMujNac = @SumNoProrrogasAutorizadasMujNac, NoProrrogasAutorizadasHomExt = @SumNoProrrogasAutorizadasHomExt, NoProrrogasAutorizadasMujExt = @SumNoProrrogasAutorizadasMujExt, LiberacionesHomNac = @SumLiberacionesHomNac, LiberacionesMujNac = @SumLiberacionesMujNac, LiberacionesHomExt = @SumLiberacionesHomExt, LiberacionesMujExt = @SumLiberacionesMujExt, CancelacionesHomNac = @SumCancelacionesHomNac, CancelacionesMujNac = @SumCancelacionesMujNac, CancelacionesHomExt = @SumCancelacionesHomExt, CancelacionesMujExt = @SumCancelacionesMujExt, NoLicenciasVigentesActHomNac = @SumNoLicenciasVigentesActHomNac, NoLicenciasVigentesActMujNac = @SumNoLicenciasVigentesActMujNac, NoLicenciasVigentesActHomExt = @SumNoLicenciasVigentesActHomExt, NoLicenciasVigentesActMujExt = @SumNoLicenciasVigentesActMujExt, NoLicenciasVigentesActTot = @SumNoLicenciasVigentesActTot, NoLicenciasVigentesActEstudioEspec = @SumNoLicenciasVigentesActEstudioEspec, NoLicenciasVigentesActEstudioMaest = @SumNoLicenciasVigentesActEstudioMaest, NoLicenciasVigentesActEstudioDoct = @SumNoLicenciasVigentesActEstudioDoct, NoLicenciasVigentesActEstudioEstan = @SumNoLicenciasVigentesActEstudioEstan, NoLicenciasVigentesActEstudioTot = @SumNoLicenciasVigentesActEstudioTot
WHERE Rama = (SELECT Desc_SiglasRama FROM RamaConocimiento WHERE ID_RamaConocimiento = @ContadorRamas) AND UnidadAcademica = 'Total Rama ' + ((SELECT Desc_SiglasRama FROM RamaConocimiento WHERE ID_RamaConocimiento = @ContadorRamas));
SET @ContadorRamas = @ContadorRamas + 1 END
SELECT UnidadAcademica, LicenciasVigentesAnteriorHomNac, LicenciasVigentesAnteriorMujNac, LicenciasVigentesAnteriorHomExt, LicenciasVigentesAnteriorMujExt, LicenciasVigentesAnteriorTot, AjustesHomNac, AjustesMujNac, AjustesHomExt, AjustesMujExt, NoLicenciasOtorgadasHomNac, NoLicenciasOtorgadasMujNac, NoLicenciasOtorgadasHomExt, NoLicenciasOtorgadasMujExt, NoProrrogasAutorizadasHomNac, NoProrrogasAutorizadasMujNac, NoProrrogasAutorizadasHomExt, NoProrrogasAutorizadasMujExt, LiberacionesHomNac, LiberacionesMujNac, LiberacionesHomExt, LiberacionesMujExt, CancelacionesHomNac, CancelacionesMujNac, CancelacionesHomExt, CancelacionesMujExt, NoLicenciasVigentesActHomNac, NoLicenciasVigentesActMujNac, NoLicenciasVigentesActHomExt, NoLicenciasVigentesActMujExt, NoLicenciasVigentesActTot, NoLicenciasVigentesActEstudioEspec, NoLicenciasVigentesActEstudioMaest, NoLicenciasVigentesActEstudioDoct, NoLicenciasVigentesActEstudioEstan, NoLicenciasVigentesActEstudioTot FROM @TablaLicenciasGoceSueldo ORDER BY Rama, Orden, UnidadAcademica
END

-- Author : Samuel Garcia ESCOM
-- Formato 20 Licencias Goce de Sueldo 
CREATE PROCEDURE SAC_LicenciasGoceSueldoNMS
    @SiglasTipoUnidadAcademica VARCHAR(255),
    @anio INT, 
    @id_Trimestre INT
AS

BEGIN DECLARE @NACIONAL VARCHAR(100) = 'NACIONAL'; DECLARE @EXTRANJERO VARCHAR(100) = 'EXTRANJERO'; DECLARE @NMS VARCHAR(100) = 'NMS'; DECLARE @NS VARCHAR(100) = 'NS'; DECLARE @CENTROSINVESTIGACION VARCHAR(100) = 'C INV'; DECLARE @AREACENTRAL VARCHAR(100) = 'UACA';
DECLARE @NMSHomNacional INT = 0; DECLARE @NMSMujNacional INT = 0; DECLARE @NMSHomExtranjero INT = 0; DECLARE @NMSMujExtranjero INT = 0; DECLARE @NSHomNacional INT = 0; DECLARE @NSMujNacional INT = 0; DECLARE @NSHomExtranjero INT = 0; DECLARE @NSMujExtranjero INT = 0; DECLARE @CIHomNacional INT = 0; DECLARE @CIMujNacional INT = 0; DECLARE @CIHomExtranjero INT = 0; DECLARE @CIMujExtranjero INT = 0; DECLARE @ACHomNacional INT = 0; DECLARE @ACMujNacional INT = 0; DECLARE @ACHomExtranjero INT = 0; DECLARE @ACMujExtranjero INT = 0; DECLARE @SubTotalHomNacional INT = 0; DECLARE @SubTotalMujNacional INT = 0; DECLARE @TotalNacional INT = 0; DECLARE @SubTotalHomExtranjero INT = 0; DECLARE @SubTotalMujExtranjero INT = 0; DECLARE @TotalExtranjero INT = 0; DECLARE @Total INT = 0;
DECLARE @TablaLicenciasVigentesAcumulado TABLE( Periodo VARCHAR(100) NOT NULL DEFAULT '', NMSHomNacional INT NOT NULL DEFAULT 0, NMSMujNacional INT NOT NULL DEFAULT 0, NMSHomExtranjero INT NOT NULL DEFAULT 0, NMSMujExtranjero INT NOT NULL DEFAULT 0,
NSHomNacional INT NOT NULL DEFAULT 0, NSMujNacional INT NOT NULL DEFAULT 0, NSHomExtranjero INT NOT NULL DEFAULT 0, NSMujExtranjero INT NOT NULL DEFAULT 0,
CIHomNacional INT NOT NULL DEFAULT 0, CIMujNacional INT NOT NULL DEFAULT 0, CIHomExtranjero INT NOT NULL DEFAULT 0, CIMujExtranjero INT NOT NULL DEFAULT 0,
ACHomNacional INT NOT NULL DEFAULT 0, ACMujNacional INT NOT NULL DEFAULT 0, ACHomExtranjero INT NOT NULL DEFAULT 0, ACMujExtranjero INT NOT NULL DEFAULT 0,
SubTotalHomNacional INT NOT NULL DEFAULT 0, SubTotalMujNacional INT NOT NULL DEFAULT 0, TotalNacional INT NOT NULL DEFAULT 0,
SubTotalHomExtranjero INT NOT NULL DEFAULT 0, SubTotalMujExtranjero INT NOT NULL DEFAULT 0, TotalExtranjero INT NOT NULL DEFAULT 0,
Total INT NOT NULL DEFAULT 0 ) INSERT INTO @TablaLicenciasVigentesAcumulado (Periodo) SELECT Desc_PeriodoTrimestre FROM Trimestre WHERE ID_Trimestre <= @Trimestre;
DECLARE @ContadorPeriodo INT = 1 WHILE @ContadorPeriodo <= @Trimestre BEGIN
SET @NMSHomNacional = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC From SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @NMS))),0); SET @NMSMujNacional = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC From SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @NMS))),0); SET @NMSHomExtranjero = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC From SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @NMS))),0); SET @NMSMujExtranjero = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC From SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @NMS))),0);
SET @NSHomNacional = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC From SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @NS))),0); SET @NSMujNacional = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC From SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @NS))),0); SET @NSHomExtranjero = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC From SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @NS))),0); SET @NSMujExtranjero = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC From SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @NS))),0); SET @CIHomNacional = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC From SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @CENTROSINVESTIGACION))),0); SET @CIMujNacional = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC From SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @CENTROSINVESTIGACION))),0); SET @CIHomExtranjero = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC From SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @CENTROSINVESTIGACION))),0); SET @CIMujExtranjero = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC From SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @CENTROSINVESTIGACION))),0); SET @ACHomNacional = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC From SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @AREACENTRAL))),0); SET @ACMujNacional = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC From SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @AREACENTRAL))),0); SET @ACHomExtranjero = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC From SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @AREACENTRAL))),0); SET @ACMujExtranjero = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @ContadorPeriodo AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC From SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_UnidadAcademicaSAC IN (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @AREACENTRAL))),0);
SET @SubTotalHomNacional = @NMSHomNacional + @NSHomNacional + @CIHomNacional + @ACHomNacional; SET @SubTotalMujNacional = @NMSMujNacional + @NSMujNacional + @CIMujNacional + @ACMujNacional; SET @TotalNacional = @SubTotalHomNacional + @SubTotalMujNacional;
SET @SubTotalHomExtranjero = @NMSHomExtranjero + @NSHomExtranjero + @CIHomExtranjero + @ACHomExtranjero; SET @SubTotalMujExtranjero = @NMSMujExtranjero + @NSMujExtranjero + @CIMujExtranjero + @ACMujExtranjero; SET @TotalExtranjero = @SubTotalHomExtranjero + @SubTotalMujExtranjero;
SET @Total = @TotalNacional + @TotalExtranjero;
UPDATE @TablaLicenciasVigentesAcumulado SET NMSHomNacional = @NMSHomNacional, NMSMujNacional = @NMSMujNacional, NMSHomExtranjero = @NMSHomExtranjero, NMSMujExtranjero = @NMSMujExtranjero, NSHomNacional = @NSHomNacional, NSMujNacional = @NSMujNacional, NSHomExtranjero = @NSHomExtranjero, NSMujExtranjero = @NSMujExtranjero, CIHomNacional = @CIHomNacional, CIMujNacional = @CIMujNacional, CIHomExtranjero = @CIHomExtranjero, CIMujExtranjero = @CIMujExtranjero, ACHomNacional = @ACHomNacional, ACMujNacional = @ACMujNacional, ACHomExtranjero = @ACHomExtranjero, ACMujExtranjero = @ACMujExtranjero, SubTotalHomNacional = @SubTotalHomNacional, SubTotalMujNacional = @SubTotalMujNacional, TotalNacional = @TotalNacional, SubTotalHomExtranjero = @SubTotalHomExtranjero, SubTotalMujExtranjero = @SubTotalMujExtranjero, TotalExtranjero = @TotalExtranjero, Total = @Total WHERE Periodo = (SELECT Desc_PeriodoTrimestre FROM Trimestre WHERE ID_Trimestre = @ContadorPeriodo);
SET @ContadorPeriodo = @ContadorPeriodo + 1; END SELECT * FROM @TablaLicenciasVigentesAcumulado UNION ALL SELECT 'CIFRAS AL PERIODO', SUM(NMSHomNacional), SUM(NMSMujNacional), SUM(NMSHomExtranjero), SUM(NMSMujExtranjero), SUM(NSHomNacional), SUM(NSMujNacional), SUM(NSHomExtranjero), SUM(NSMujExtranjero), SUM(CIHomNacional), SUM(CIMujNacional), SUM(CIHomExtranjero), SUM(CIMujExtranjero), SUM(ACHomNacional), SUM(ACMujNacional), SUM(ACHomExtranjero), SUM(ACMujExtranjero), SUM(SubTotalHomNacional), SUM(SubTotalMujNacional), SUM(TotalNacional), SUM(SubTotalHomExtranjero), SUM(SubTotalMujExtranjero), SUM(TotalExtranjero), SUM(Total) FROM @TablaLicenciasVigentesAcumulado;
END

-- Formato 26 Licencias con goce de sueldo vigentes acumulado
CREATE PROCEDURE SAC_LicenciasGoceSueldoVigentesAcumulado
    @Anio INT,
    @Trimestre INT
AS

BEGIN -- SET NOCOUNT ON; DECLARE @NACIONAL VARCHAR(100) = 'NACIONAL'; DECLARE @EXTRANJERO VARCHAR(100) = 'EXTRANJERO'; DECLARE @NMS VARCHAR(100) = 'NMS'; DECLARE @NS VARCHAR(100) = 'NS'; DECLARE @CINV VARCHAR(100) = 'C INV'; DECLARE @AC VARCHAR(100) = 'UACA'; DECLARE @LICENCIASGOCESUELDO VARCHAR(100) = 'LICENCIAS CON GOCE DE SUELDO';
DECLARE @AnioAnterior INT = @Anio - 1;
DECLARE @PerAntHomNac INT = 0; DECLARE @PerAntMujNac INT = 0; DECLARE @PerAntTotalNac INT = 0; DECLARE @PerAntHomExt INT = 0; DECLARE @PerAntMujExt INT = 0; DECLARE @PerAntTotalExt INT = 0; DECLARE @PerActHomNac INT = 0; DECLARE @PerActMujNac INT = 0; DECLARE @PerActTotalNac INT = 0; DECLARE @PerActHomExt INT = 0; DECLARE @PerActMujExt INT = 0; DECLARE @PerActTotalExt INT = 0;
DECLARE @VariacionNacional DECIMAL(10, 2) = 0; DECLARE @VariacionExtranjero DECIMAL(10, 2) = 0; DECLARE @Justificacion VARCHAR(255) = '';
DECLARE @TablaNiveles TABLE( identificador INT IDENTITY(1,1), Nivel VARCHAR(255) NOT NULL DEFAULT '', Desc_Nivel VARCHAR(255) NOT NULL DEFAULT '', id_TipoUnidadAcademica INT NOT NULL DEFAULT 0 ) INSERT INTO @TablaNiveles (Nivel, Desc_Nivel) VALUES (@NMS, 'Medio Superior'), (@NS, 'Superior'), (@CINV, 'Centros de Investigación'), (@AC, 'Área Central'); UPDATE @TablaNiveles SET id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @NMS) WHERE Nivel = @NMS; UPDATE @TablaNiveles SET id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @NS) WHERE Nivel = @NS; UPDATE @TablaNiveles SET id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @CINV) WHERE Nivel = @CINV; UPDATE @TablaNiveles SET id_TipoUnidadAcademica = (SELECT ID_TipoUnidadAcademica FROM TipoUnidadAcademica WHERE Desc_SiglasTipo = @AC) WHERE Nivel = @AC;
DECLARE @TablaLicenciasComparativo TABLE( Nivel VARCHAR(100), PerAntHomNac INT NOT NULL DEFAULT 0, PerAntMujNac INT NOT NULL DEFAULT 0, PerAntTotalNac INT NOT NULL DEFAULT 0, PerAntHomExt INT NOT NULL DEFAULT 0, PerAntMujExt INT NOT NULL DEFAULT 0, PerAntTotalExt INT NOT NULL DEFAULT 0,
PerActHomNac INT NOT NULL DEFAULT 0, PerActMujNac INT NOT NULL DEFAULT 0, PerActTotalNac INT NOT NULL DEFAULT 0, PerActHomExt INT NOT NULL DEFAULT 0, PerActMujExt INT NOT NULL DEFAULT 0, PerActTotalExt INT NOT NULL DEFAULT 0,
VariacionNacional DECIMAL(10, 2) NOT NULL DEFAULT 0, VariacionExtranjero DECIMAL(10, 2) NOT NULL DEFAULT 0, Justificacion VARCHAR(255) NOT NULL DEFAULT '' ); INSERT INTO @TablaLicenciasComparativo (Nivel) SELECT Desc_Nivel FROM @TablaNiveles;
DECLARE @ContadorNivel INT = 1; WHILE @ContadorNivel <= (SELECT COUNT(*)FROM @TablaLicenciasComparativo) BEGIN SET @PerAntHomNac = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre <= @Trimestre AND id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0);
SET @PerAntMujNac = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre <= @Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0);
SET @PerAntTotalNac = @PerAntHomNac + @PerAntMujNac;
SET @PerAntHomExt = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre <= @Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @PerAntMujExt = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre <= @Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @AnioAnterior) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @PerAntTotalExt = @PerAntHomExt + @PerAntMujExt;
SET @PerActHomNac = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @PerActMujNac = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre = @Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @NACIONAL) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @PerActTotalNac = @PerActHomNac + @PerActMujNac;
SET @PerActHomExt = ISNULL((SELECT SUM(Desc_Hombres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre <= @Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @PerActMujExt = ISNULL((SELECT SUM(Desc_Mujeres) FROM SAC_RegistroLicenciasConGoceDeSueldo WHERE id_Trimestre <= @Trimestre AND id_Anio = (SELECT id_Anio FROM Anio WHERE Desc_Anio = @Anio) AND id_Origen = (SELECT ID_OriginSAC FROM SAC_Origen WHERE Desc_OriginSAC = @EXTRANJERO) AND id_UnidadAcademicaSAC IN (SELECT id_UnidadAcademica FROM UnidadesAcademicas WHERE id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel) )),0); SET @PerActTotalExt = @PerActHomExt + @PerActMujExt;
SET @VariacionNacional = CASE WHEN @PerAntTotalNac = 0 THEN 0 ELSE ((@PerActTotalNac - @PerAntTotalNac) * 100.0) / @PerAntTotalNac END; SET @VariacionExtranjero = CASE WHEN @PerAntTotalExt = 0 THEN 0 ELSE ((@PerActTotalExt - @PerAntTotalExt) * 100.0) / @PerAntTotalExt END;
SET @Justificacion = ISNULL((SELECT TOP 1 Desc_Justificacion FROM SAC_Justificaciones WHERE id_Anio = (SELECT ID_Anio FROM Anio WHERE Desc_Anio = @anio) AND id_Trimestre = @Trimestre AND id_TipoUnidadAcademicaSAC = (SELECT id_TipoUnidadAcademica = (SELECT id_TipoUnidadAcademica FROM @TablaNiveles WHERE identificador = @ContadorNivel)) AND id_ProgramaApoyoSAC = (SELECT ID_ProgramaApoyoSAC FROM SAC_ProgramaApoyo WHERE Desc_ProgramaApoyoSAC = @LICENCIASGOCESUELDO)), '');
UPDATE @TablaLicenciasComparativo SET PerAntHomNac = @PerAntHomNac, PerAntMujNac = @PerAntMujNac, PerAntTotalNac = @PerAntTotalNac, PerAntHomExt = @PerAntHomExt, PerAntMujExt = @PerAntMujExt, PerAntTotalExt = @PerAntTotalExt, PerActHomNac = @PerActHomNac, PerActMujNac = @PerActMujNac, PerActTotalNac = @PerActTotalNac, PerActHomExt = @PerActHomExt, PerActMujExt = @PerActMujExt, PerActTotalExt = @PerActTotalExt, VariacionNacional = @VariacionNacional, VariacionExtranjero = @VariacionExtranjero, Justificacion = @Justificacion WHERE Nivel = (SELECT Desc_Nivel FROM @TablaNiveles WHERE identificador = @ContadorNivel); SET @ContadorNivel = @ContadorNivel + 1; END
SELECT * FROM @TablaLicenciasComparativo UNION ALL SELECT 'Total', SUM(PerAntHomNac), SUM(PerAntMujNac), SUM(PerAntTotalNac), SUM(PerAntHomExt), SUM(PerAntMujExt), SUM(PerAntTotalExt), SUM(PerActHomNac), SUM(PerActMujNac), SUM(PerActTotalNac), SUM(PerActHomExt), SUM(PerActMujExt), SUM(PerActTotalExt), @VariacionNacional, @VariacionExtranjero, @Justificacion FROM @TablaLicenciasComparativo; END

