-- Formato 27 - Licencias con goce de sueldo vigentes comparativo
CREATE PROCEDURE SAC_LicenciasGoceSueldoVigentesComparativo
    @Anio INT,
    @Trimestre INT
AS

CREATE PROCEDURE SAC_ProgramaReconocimientoValidezOficial_Estadistico
    @Anio INT,
    @Trimestre INT
AS

BEGIN
    DECLARE @ID_DependenciaEvaluada INT = (SELECT ID_DependenciasEvaluadas FROM Dependencias_Evaluadas WHERE Desc_SiglasDependencia = @AntiguasSiglasDependenciasEvaluadas);
    IF (SELECT ID_Auditora FROM AuditoraSiglasDependenciasEvaluadas WHERE id_DependenciaEvaluada = @ID_DependenciaEvaluada) IS NULL
        BEGIN
            INSERT INTO AuditoraSiglasDependenciasEvaluadas(
                id_DependenciaEvaluada,
                Desc_ValorAntiguo,
                FechaCambio
            )VALUES(
                @ID_DependenciaEvaluada,
                @AntiguasSiglasDependenciasEvaluadas,
                GETDATE()
            );
        END
    ELSE
        BEGIN
            UPDATE AuditoraSiglasDependenciasEvaluadas
            SET Desc_ValorAntiguo = @AntiguasSiglasDependenciasEvaluadas,
                FechaCambio = GETDATE()
            WHERE id_DependenciaEvaluada = @ID_DependenciaEvaluada;
        END

    UPDATE Dependencias_Evaluadas
    SET Desc_SiglasDependencia = @NuevasSiglasDependenciasEvaluadas
    WHERE Desc_SiglasDependencia = @AntiguasSiglasDependenciasEvaluadas;
END

CREATE PROCEDURE SIA_ActualizacionSiglasDependenciaEvaluada
    @AntiguasSiglasDependenciasEvaluadas varchar(255),
    @NuevasSiglasDependenciasEvaluadas varchar(255)
AS

BEGIN
    DECLARE @ID_UnidadAcademica INT = (SELECT ID_UnidadAcademica FROM UnidadesAcademicas WHERE Siglas = @AntiguasSiglasUnidadAcademica);
    IF (SELECT ID_Auditora FROM AuditoraSiglasUnidadAcademica WHERE id_UnidadAcademica = @ID_UnidadAcademica) IS NULL
        BEGIN
            INSERT INTO AuditoraSiglasUnidadAcademica(
                id_UnidadAcademica,
                Desc_ValorAntiguo,
                FechaCambio
            )VALUES(
                @ID_UnidadAcademica,
                @AntiguasSiglasUnidadAcademica,
                GETDATE()
            );
        END
    ELSE
        BEGIN
            UPDATE AuditoraSiglasUnidadAcademica
            SET Desc_ValorAntiguo = @AntiguasSiglasUnidadAcademica,
                FechaCambio = GETDATE()
            WHERE id_UnidadAcademica = @ID_UnidadAcademica;
        END

    UPDATE UnidadesAcademicas
    SET Siglas = @NuevasSiglasUnidadAcademica
    WHERE Siglas = @AntiguasSiglasUnidadAcademica;
END

CREATE PROCEDURE SIA_ActualizacionSiglasUnidadAcademica
    @AntiguasSiglasUnidadAcademica varchar(255),
    @NuevasSiglasUnidadAcademica varchar(255)
AS
